import React, { useState, useEffect } from 'react';
import { BarChart, TrendingUp, Users, Eye, MousePointer, Clock, PartyPopper, X, Trophy, Star } from 'lucide-react';
import { Checkbox } from '../../components/Checkbox';
import { useLanguage } from '../../contexts/LanguageContext';
import { useTheme } from '../../contexts/ThemeContext';

interface Step4Props {
  onComplete: () => void;
  appData: {
    appId: string;
    dataUrl: string;
    selectedVersion: 'saas' | 'private' | null;
  };
}

export const Step4AnalyticsStart: React.FC<Step4Props> = ({
  onComplete,
  appData
}) => {
  const { t } = useLanguage();
  const { colors } = useTheme();
  const [understood, setUnderstood] = useState(false);
  const [celebrationShown, setCelebrationShown] = useState(false);
  const [hasCompleted, setHasCompleted] = useState(false);

  // 테마 색상 가져오기
  const theme = colors;

  useEffect(() => {
    if (understood && !hasCompleted) {
      onComplete();
      setCelebrationShown(true);
      setHasCompleted(true);

      // 팝업이 표시될 때 화면 중앙으로 부드럽게 스크롤
      setTimeout(() => {
        const windowHeight = window.innerHeight;
        const documentHeight = document.documentElement.scrollHeight;
        const scrollPosition = Math.max(0, (documentHeight - windowHeight) / 2);

        window.scrollTo({
          top: scrollPosition,
          left: 0,
          behavior: 'smooth'
        });
      }, 100);
    }
  }, [understood, hasCompleted, onComplete]);

  // 데이터 수집 플로우 단계
  const dataCollectionFlow = [
    {
      step: 1,
      title: t('step4.userActionCapture'),
      description: t('step4.userActionCaptureDesc'),
      icon: <MousePointer size={20} color="#334155" />
    },
    {
      step: 2,
      title: t('step4.dataProcessing'),
      description: t('step4.dataProcessingDesc'),
      icon: <TrendingUp size={20} color="var(--success)" />
    },
    {
      step: 3,
      title: t('step4.dataTransmission'),
      description: t('step4.dataTransmissionDesc'),
      icon: <Eye size={20} color="var(--warning)" />
    },
    {
      step: 4,
      title: t('step4.dataStorage'),
      description: t('step4.dataStorageDesc'),
      icon: <BarChart size={20} color="var(--info)" />
    }
  ];

  const recommendedMetrics = [
    {
      category: t('step4.userAnalysis'),
      icon: <Users size={24} color="#334155" />,
      metrics: [
        { name: t('step4.dauDaily'), description: t('step4.dauDailyDesc') },
        { name: t('step4.mauMonthly'), description: t('step4.mauMonthlyDesc') },
        { name: t('step4.newUsers'), description: t('step4.newUsersDesc') },
        { name: t('step4.returningUsers'), description: t('step4.returningUsersDesc') }
      ]
    },
    {
      category: t('step4.engagementAnalysis'),
      icon: <MousePointer size={24} color="var(--success)" />,
      metrics: [
        { name: t('step4.pageViews'), description: t('step4.pageViewsDesc') },
        { name: t('step4.sessions'), description: t('step4.sessionsDesc') },
        { name: t('step4.avgSessionTime'), description: t('step4.avgSessionTimeDesc') },
        { name: t('step4.bounceRate'), description: t('step4.bounceRateDesc') }
      ]
    },
    {
      category: t('step4.behaviorAnalysis'),
      icon: <Eye size={24} color="var(--warning)" />,
      metrics: [
        { name: t('step4.clickRate'), description: t('step4.clickRateDesc') },
        { name: t('step4.conversionRate'), description: t('step4.conversionRateDesc') },
        { name: t('step4.funnelAnalysis'), description: t('step4.funnelAnalysisDesc') },
        { name: t('step4.heatmap'), description: t('step4.heatmapDesc') }
      ]
    }
  ];

  return (
    <div style={{ maxWidth: '900px', margin: '0 auto', padding: '0 16px' }}>
      <div style={{ textAlign: 'center', marginBottom: '40px' }}>
        <div style={{
          width: '80px',
          height: '80px',
          background: 'linear-gradient(135deg, #8b5cf6, #3b82f6)',
          borderRadius: '24px',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          margin: '0 auto 24px',
          boxShadow: '0 8px 25px rgba(139, 92, 246, 0.4)'
        }}>
          <BarChart size={40} color="white" />
        </div>
        <h3 style={{ 
          fontSize: '32px', 
          fontWeight: '800', 
          margin: '0 0 16px 0',
          background: 'linear-gradient(135deg, #8b5cf6, #3b82f6)',
          WebkitBackgroundClip: 'text',
          WebkitTextFillColor: 'transparent'
        }}>
          {t('step4.analyticsStart')}
        </h3>
        <p style={{
          fontSize: '18px',
          color: theme.textSecondary,
          margin: 0,
          lineHeight: '1.6',
          maxWidth: '600px',
          margin: '0 auto'
        }}>
          {t('step4.analyticsStartDesc')}
        </p>
      </div>

      {/* Data Collection Flow */}
      <div style={{ marginBottom: '32px' }}>
        <div style={{
          padding: '24px',
          background: colors.cardBg,
          border: `1px solid ${colors.cardBorder}`,
          borderRadius: '20px',
          boxShadow: '0 8px 24px rgba(0, 0, 0, 0.2)',
          backdropFilter: 'blur(20px)',
          position: 'relative',
          overflow: 'hidden'
        }}>
          {/* 배경 장식 */}
          <div style={{
            position: 'absolute',
            top: '-40%',
            right: '-10%',
            width: '200px',
            height: '200px',
            background: 'linear-gradient(135deg, rgba(59, 130, 246, 0.1), rgba(139, 92, 246, 0.1))',
            borderRadius: '50%',
            filter: 'blur(50px)'
          }} />

          <div style={{
            display: 'flex',
            alignItems: 'center',
            gap: '16px',
            marginBottom: '20px',
            position: 'relative',
            zIndex: 1
          }}>
            <div style={{
              width: '48px',
              height: '48px',
              background: 'linear-gradient(135deg, #3b82f6, #1d4ed8)',
              borderRadius: '16px',
              display: 'flex',
