import React, { useState, useEffect } from 'react';
import { Users, UserPlus, Shield, Eye, EyeOff, Settings, Check, X, Crown, Star } from 'lucide-react';
import { Card } from '../../components/Card';
import { Badge } from '../../components/Badge';
import { Checkbox } from '../../components/Checkbox';
import { InfoBox } from '../../components/InfoBox';
import { Tabs } from '../../components/Tabs';
import { useLanguage } from '../../contexts/LanguageContext';

interface Step2Props {
  onComplete: () => void;
  onPrevious: () => void;
}

export const Step2AccountSetup: React.FC<Step2Props> = ({ onComplete, onPrevious }) => {
  const { t } = useLanguage();
  const [understood, setUnderstood] = useState(false);

  // 게이밍 테마
  const theme = {
    cardBg: 'linear-gradient(145deg, rgba(30, 30, 60, 0.9), rgba(15, 15, 35, 0.9))',
    cardBorder: 'rgba(139, 92, 246, 0.3)',
    text: '#ffffff',
    textSecondary: '#a1a1aa'
  };

  const handleNext = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
    onComplete();
  };

  const handlePrevious = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
    onPrevious();
  };

  const roleTypes = [
    {
      name: t('role.projectManager'),
      badge: { text: t('role.projectManagerBadge'), variant: 'danger' as const },
      icon: '👑',
      color: '#a855f7',
      backgroundColor: 'rgba(168, 85, 247, 0.1)',
      permissions: [
        t('step2.projectManagerPerm1'),
        t('step2.projectManagerPerm2'),
        t('step2.projectManagerPerm3')
      ],
      description: t('role.projectManagerDesc')
    },
    {
      name: t('role.analysisManager'),
      badge: { text: t('role.analysisManagerBadge'), variant: 'primary' as const },
      icon: '🔧',
      color: '#3b82f6',
      backgroundColor: 'rgba(59, 130, 246, 0.1)',
      permissions: [
        t('step2.analysisManagerPerm1'),
        t('step2.analysisManagerPerm2')
      ],
      description: t('role.analysisManagerDesc')
    },
    {
      name: t('role.analyst'),
      badge: { text: t('role.analystBadge'), variant: 'success' as const },
      icon: '📊',
      color: '#10b981',
      backgroundColor: 'rgba(16, 185, 129, 0.1)',
      permissions: [
        t('step2.dataEngineerPerm1'),
        t('step2.dataEngineerPerm2')
      ],
      description: t('role.analystDesc')
    },
    {
      name: t('role.viewer'),
      badge: { text: t('role.viewerBadge'), variant: 'secondary' as const },
      icon: '👤',
      color: '#6b7280',
      backgroundColor: 'rgba(107, 114, 128, 0.1)',
      permissions: [
        t('step2.viewerPerm1'),
        t('step2.viewerPerm2')
      ],
      description: t('role.viewerDesc')
    }
  ];

  const dataPermissionSettings = [
    {
      name: t('step2.dataPermissionTitle'),
      icon: '🤔',
      description: t('step2.dataPermissionDesc'),
      example: t('step2.allDataDesc')
    },
    {
      name: t('step2.allDataAccess'),
      icon: '👁️',
      description: t('step2.limitedDataDesc'),
      examples: [
        {
          title: t('step2.eventDisplaySetting'),
          items: [
            { name: t('step2.purchaseComplete'), level: t('step2.checked'), icon: '👁️', checked: true },
            { name: t('step2.paymentFailed'), level: t('step2.unchecked'), icon: '🚫', checked: false },
            { name: t('step2.pageView'), level: t('step2.checked'), icon: '👁️', checked: true }
          ]
        }
      ],
      note: t('step2.readOnlyDesc')
    },
    {
      name: t('step2.dataSourceFilter'),
      icon: '🔧',
      description: t('step2.dataSourceFilterDesc'),
      examples: [
        {
          title: '이벤트 필터',
          subtitle: '상품 카테고리',
          description: '의류, 전자제품만 접근 가능',
          tags: ['의류', '전자제품']
        },
        {
          title: '유저 필터',
          subtitle: '지역',
          description: '서울, 경기 지역 사용자만',
          tags: ['서울', '경기']
        }
      ]
    },
    {
      name: t('step2.fieldLevelSecurity'),
      icon: '🔒',
      description: t('step2.fieldLevelSecurityDesc'),
      examples: [
        {
          title: '마스킹 적용 예시',
          items: [
            { type: '이메일 마스킹', original: 'user@example.com', masked: 'u***@e*******.com' },
            { type: '전화번호 마스킹', original: '010-1234-5678', masked: '010-****-5678' },
            { type: '금액 마스킹', original: '1,250,000원', masked: '1,***,***원' },
            { type: '신용카드 마스킹', original: '1234-5678-9012-3456', masked: '****-****-****-3456' }
          ]
        }
      ]
    },
    {
      name: '데이터 권한 생성 및 적용',
      icon: '⚙️',
      description: '위에서 설정한 데이터 표시, 범위, 마스킹 설정을 조합하여 데이터 권한을 생성하고 구성원에게 부여합니다.',
      process: [
        '체크박스로 이벤트, 이벤트 속성, 유저 속성 선택',
        '데이터 범위 필터 및 마스킹 설정',
        '데이터 권한 정책으로 저장',
        '구성원에게 데이터 권한 할당'
      ],
      note: '핵심: 체크된 항목만 해당 구성원에게 표시되고, 체크하지 않은 항목은 보이지 않습니다.'
    }
  ];

  const memberCreationTab = (
    <div style={{ padding: '24px' }}>
      <div style={{ marginBottom: '24px' }}>
        <div style={{
          display: 'flex',
          alignItems: 'center',
          gap: '16px',
          marginBottom: '20px',
          padding: '20px',
          background: 'rgba(59, 130, 246, 0.1)',
          borderRadius: '16px',
          border: '1px solid rgba(59, 130, 246, 0.3)'
        }}>
          <div style={{
            width: '48px',
            height: '48px',
            background: 'linear-gradient(135deg, #3b82f6, #1d4ed8)',
            borderRadius: '16px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            boxShadow: '0 4px 12px rgba(59, 130, 246, 0.4)'
          }}>
            <UserPlus size={24} color="white" />
          </div>
          <div>
            <h5 style={{ 
              fontSize: '24px', 
              fontWeight: '700', 
              margin: 0,
              color: '#3b82f6',
              userSelect: 'none'
            }}>
              🚀 {t('step2.teamQuest')}
            </h5>
            <p style={{ 
              fontSize: '14px', 
              color: theme.textSecondary, 
              margin: '4px 0 0 0',
              fontWeight: '500'
            }}>
              {t('step2.teamDescription')}
            </p>
          </div>
        </div>

        <div style={{ marginBottom: '24px' }}>
          <h6 style={{
            fontSize: '20px',
            fontWeight: '700',
            marginBottom: '20px',
            color: theme.text,
            display: 'flex',
            alignItems: 'center',
            gap: '12px'
          }}>
            📋 {t('step2.stepByStepProgressGuide')}
          </h6>

          <div style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '16px' }}>
            <div style={{
              width: '32px',
              height: '32px',
              background: 'linear-gradient(135deg, #8b5cf6, #3b82f6)',
              borderRadius: '50%',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              color: 'white',
              fontSize: '14px',
              fontWeight: '700',
              boxShadow: '0 4px 12px rgba(139, 92, 246, 0.4)'
            }}>
              1
            </div>
            <div>
              <h6 style={{
                fontSize: '18px',
                fontWeight: '700',
                margin: 0,
                color: theme.text
              }}>
                🎯 {t('step2.projectManagementAccess2')}
              </h6>
              <p style={{
                fontSize: '15px',
                color: theme.textSecondary,
                margin: 0,
                lineHeight: '1.5'
              }}>
                {t('step2.adminModeDescription')}
              </p>
            </div>
          </div>

          {[
            { step: 2, title: t('step2.memberManagementMenu2'), desc: t('step2.memberManagementDescription'), color: '#10b981' },
            { step: 3, title: t('step2.addNewMember2'), desc: t('step2.addNewMemberDescription'), color: '#f59e0b' },
            { step: 4, title: t('step2.accountInfoInput2'), desc: t('step2.accountInfoDescription'), color: '#ef4444' },
            { step: 5, title: t('step2.roleAssignment2'), desc: t('step2.roleAssignmentDescription'), color: '#8b5cf6' },
            { step: 6, title: t('step2.teamComplete2'), desc: t('step2.teamCompleteDescription'), color: '#06b6d4' }
          ].map((item, index) => (
            <div key={item.step} style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '16px' }}>
              <div style={{
                width: '32px',
                height: '32px',
                background: `linear-gradient(135deg, ${item.color}, ${item.color}dd)`,
                borderRadius: '50%',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                color: 'white',
                fontSize: '14px',
                fontWeight: '700',
                boxShadow: `0 4px 12px ${item.color}40`
              }}>
                {item.step}
              </div>
              <div>
                <h6 style={{
                  fontSize: '18px',
                  fontWeight: '700',
                  margin: 0,
                  color: theme.text
                }}>
                  🎯 {item.title}
                </h6>
                <p style={{
                  fontSize: '15px',
                  color: theme.textSecondary,
                  margin: 0,
                  lineHeight: '1.5'
                }}>
                  {item.desc}
                </p>
              </div>
            </div>
          ))}
        </div>

        {/* 게이밍 스타일 CSV 파일 안내 */}
        <div style={{
          padding: '24px',
          background: 'linear-gradient(145deg, rgba(16, 185, 129, 0.1), rgba(5, 150, 105, 0.05))',
          borderRadius: '16px',
          marginBottom: '20px',
          border: '1px solid rgba(16, 185, 129, 0.2)',
          boxShadow: '0 4px 15px rgba(16, 185, 129, 0.1)'
        }}>
          <div style={{
            display: 'flex',
            alignItems: 'center',
            gap: '12px',
            marginBottom: '16px'
          }}>
            <div style={{
              width: '40px',
              height: '40px',
              background: 'linear-gradient(135deg, #10b981, #059669)',
              borderRadius: '12px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              boxShadow: '0 4px 12px rgba(16, 185, 129, 0.3)'
            }}>
              📋
            </div>
            <h6 style={{
              fontSize: '20px',
              fontWeight: '700',
              margin: 0,
              color: theme.text
            }}>
              🎁 팀 정보 보물상자 (CSV 파일)
            </h6>
          </div>
          <ul style={{
            margin: 0,
            paddingLeft: '20px',
            fontSize: '15px',
            color: theme.text,
            fontWeight: '500'
          }}>
            <li style={{ marginBottom: '8px' }}>🆔 <strong>ID:</strong> 생성된 계정 ID</li>
            <li style={{ marginBottom: '8px' }}>👤 <strong>별칭:</strong> 설정한 사용자 별칭</li>
            <li style={{ marginBottom: '8px' }}>🔐 <strong>초기 비밀번호:</strong> 임시로 생성된 비밀번호</li>
          </ul>
          <div style={{
            marginTop: '16px',
            padding: '16px',
            background: 'linear-gradient(135deg, rgba(245, 158, 11, 0.2), rgba(217, 119, 6, 0.1))',
            borderRadius: '12px',
            fontSize: '14px',
            color: theme.text,
            fontWeight: '600',
            border: '1px solid rgba(245, 158, 11, 0.3)'
          }}>
            💡 <strong>보안 알림:</strong> 다운로드한 CSV 파일의 정보를 해당 구성원에게 안전하게 전달하세요.
          </div>
        </div>

        {/* 게이밍 스타일 여러 구성원 동시 생성 */}
        <div style={{
          padding: '24px',
          background: 'linear-gradient(145deg, rgba(59, 130, 246, 0.1), rgba(29, 78, 216, 0.05))',
          borderRadius: '16px',
          marginBottom: '20px',
          border: '1px solid rgba(59, 130, 246, 0.2)',
          boxShadow: '0 4px 15px rgba(59, 130, 246, 0.1)'
        }}>
          <div style={{
            display: 'flex',
            alignItems: 'center',
            gap: '12px',
            marginBottom: '16px'
          }}>
            <div style={{
              width: '40px',
              height: '40px',
              background: 'linear-gradient(135deg, #3b82f6, #1d4ed8)',
              borderRadius: '12px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              boxShadow: '0 4px 12px rgba(59, 130, 246, 0.3)'
            }}>
              🔄
            </div>
            <h6 style={{
              fontSize: '20px',
              fontWeight: '700',
              margin: 0,
              color: theme.text
            }}>
              ⚡ 대량 팀원 영입 시스템
            </h6>
          </div>
          <p style={{
            fontSize: '15px',
            color: theme.textSecondary,
            marginBottom: '16px',
            lineHeight: '1.5'
          }}>
            한 번에 여러 팀원을 영입하여 효율적으로 최강 팀을 구성할 수 있습니다!
          </p>
          <div style={{
            padding: '16px',
            background: 'rgba(59, 130, 246, 0.1)',
            borderRadius: '12px',
            fontSize: '14px',
            color: theme.text,
            fontWeight: '600',
            border: '1px solid rgba(59, 130, 246, 0.2)',
            marginBottom: '12px'
          }}>
            🚀 <strong>효율성 팁:</strong> 여러 명의 구성원을 먼저 추가한 후, 마지막에 [생성 및 다운로드]를 클릭하면 모든 구성원의 정보가 포함된 CSV 파일을 한 번에 다운로드할 수 있습니다.
          </div>
          <ul style={{
            margin: '12px 0 0 0',
            paddingLeft: '20px',
            fontSize: '15px',
            color: theme.text,
            fontWeight: '500'
          }}>
            <li style={{ marginBottom: '6px' }}>🎯 구성원별로 개별 역할 설정 가능</li>
            <li style={{ marginBottom: '6px' }}>⚡ 일괄 생성으로 시간 절약</li>
            <li style={{ marginBottom: '6px' }}>📦 하나의 CSV 파일에 모든 구성원 정보 포함</li>
          </ul>
        </div>

        {/* 게이밍 스타일 시스템 관리자 권한 */}
        <div style={{
          padding: '24px',
          background: 'linear-gradient(145deg, rgba(139, 92, 246, 0.15), rgba(59, 130, 246, 0.1))',
          borderRadius: '16px',
          border: '1px solid rgba(139, 92, 246, 0.3)',
          boxShadow: '0 8px 24px rgba(139, 92, 246, 0.2)'
        }}>
          <div style={{
            display: 'flex',
            alignItems: 'center',
            gap: '12px',
            marginBottom: '16px'
          }}>
            <div style={{
              width: '40px',
              height: '40px',
              background: 'linear-gradient(135deg, #8b5cf6, #3b82f6)',
              borderRadius: '12px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              boxShadow: '0 4px 12px rgba(139, 92, 246, 0.4)'
            }}>
              🌐
            </div>
            <h6 style={{
              fontSize: '20px',
              fontWeight: '700',
              margin: 0,
              color: theme.text
            }}>
              👑 마스터 관리자 권한
            </h6>
          </div>
          <p style={{
            fontSize: '15px',
            color: theme.textSecondary,
            marginBottom: '16px',
            lineHeight: '1.5'
          }}>
            루트 계정을 가진 시스템 관리자는 모든 프로젝트의 구성원을 통합 관리할 수 있습니다.
          </p>
          <div style={{
            padding: '16px',
            background: 'rgba(139, 92, 246, 0.1)',
            borderRadius: '12px',
            fontSize: '14px',
            color: theme.text,
            fontWeight: '600',
            border: '1px solid rgba(139, 92, 246, 0.2)',
            marginBottom: '12px'
          }}>
            👑 <strong>특별 권한:</strong> 시스템 관리에서 구성원을 생성하고 각 프로젝트에 배정할 수 있어, 여러 프로젝트에 걸친 구성원 관리가 가능합니다.
          </div>
          <ul style={{
            margin: 0,
            paddingLeft: '20px',
            fontSize: '15px',
            color: theme.text,
            fontWeight: '500'
          }}>
            <li style={{ marginBottom: '6px' }}>🌍 전체 시스템 구성원 통합 관리</li>
            <li style={{ marginBottom: '6px' }}>🔄 프로젝트 간 구성원 이동 및 배정</li>
            <li style={{ marginBottom: '6px' }}>⚙️ 중앙화된 권한 관리</li>
          </ul>
        </div>
      </div>

      {/* 팀원 모집 퀘스트 액션 박스 */}
      <div style={{
        padding: '20px',
        background: 'linear-gradient(135deg, #3b82f6, #1d4ed8)',
        borderRadius: '12px',
        color: 'white',
        textAlign: 'center',
        boxShadow: '0 8px 24px rgba(59, 130, 246, 0.3)',
        cursor: 'pointer',
        transition: 'all 0.3s ease'
      }}
