          style={{
            padding: '16px 32px',
            background: 'linear-gradient(135deg, #6b7280, #4b5563)',
            border: 'none',
            borderRadius: '16px',
            color: 'white',
            fontSize: '16px',
            fontWeight: '600',
            cursor: 'pointer',
            display: 'flex',
            alignItems: 'center',
            gap: '8px',
            boxShadow: '0 8px 20px rgba(107, 114, 128, 0.4)',
            transition: 'all 0.3s ease'
          }}
          onMouseEnter={(e) => {
            (e.currentTarget as HTMLElement).style.transform = 'translateY(-2px)';
            (e.currentTarget as HTMLElement).style.boxShadow = '0 12px 30px rgba(107, 114, 128, 0.5)';
          }}
          onMouseLeave={(e) => {
            (e.currentTarget as HTMLElement).style.transform = 'translateY(0)';
            (e.currentTarget as HTMLElement).style.boxShadow = '0 8px 20px rgba(107, 114, 128, 0.4)';
          }}
        >
          ← {t('quickstart.currentLevel').includes('단계') ? '이전 단계' : 'Previous Step'}
        </button>

        <button
          onClick={handleNext}
          disabled={!understood}
          style={{
            padding: '16px 32px',
            background: understood 
              ? 'linear-gradient(135deg, #10b981, #059669)'
              : 'linear-gradient(135deg, #6b7280, #4b5563)',
            border: 'none',
            borderRadius: '16px',
            color: 'white',
            fontSize: '16px',
            fontWeight: '600',
            cursor: understood ? 'pointer' : 'not-allowed',
            display: 'flex',
            alignItems: 'center',
            gap: '8px',
            boxShadow: understood 
              ? '0 8px 20px rgba(16, 185, 129, 0.4)'
              : '0 8px 20px rgba(107, 114, 128, 0.4)',
            transition: 'all 0.3s ease',
            opacity: understood ? 1 : 0.6
          }}
          onMouseEnter={(e) => {
            if (understood) {
              (e.currentTarget as HTMLElement).style.transform = 'translateY(-2px)';
              (e.currentTarget as HTMLElement).style.boxShadow = '0 12px 30px rgba(16, 185, 129, 0.5)';
            }
          }}
          onMouseLeave={(e) => {
            if (understood) {
              (e.currentTarget as HTMLElement).style.transform = 'translateY(0)';
              (e.currentTarget as HTMLElement).style.boxShadow = '0 8px 20px rgba(16, 185, 129, 0.4)';
            }
          }}
        >
          {t('quickstart.currentLevel').includes('단계') ? '다음 단계' : 'Next Step'} →
        </button>
      </div>
    </div>
  );
};
