import React, { useState, useEffect } from 'react';
import { Cloud, Server, Check, Building, ArrowRight } from 'lucide-react';
import { Card } from '../../components/Card';
import { Badge } from '../../components/Badge';
import { Checkbox } from '../../components/Checkbox';
import { InfoBox } from '../../components/InfoBox';

interface Step1Props {
  onComplete: () => void;
  onVersionSelect: (version: 'saas' | 'private') => void;
  selectedVersion: 'saas' | 'private' | null;
}

export const Step1ProjectCreation: React.FC<Step1Props> = ({
  onComplete,
  onVersionSelect,
  selectedVersion
}) => {
  const [understood, setUnderstood] = useState(false);

  useEffect(() => {
    if (understood && selectedVersion) {
      onComplete();
    }
  }, [understood, selectedVersion, onComplete]);

  const versionOptions = [
    {
      id: 'saas' as const,
      title: 'SaaS 버전',
      subtitle: '클라우드 서비스',
      icon: <Cloud size={32} color="var(--brand-primary)" />,
      badge: { text: '추천', variant: 'primary' as const },
      features: [
        '빠른 시작 (5분 이내)',
        '자동 업데이트',
        '24/7 모니터링',
        '확장 가능한 인프라',
        '데이터 백업 자동화'
      ],
      drawbacks: [
        '네트워크 의존성',
        '데이터 외부 저장'
      ]
    },
    {
      id: 'private' as const,
      title: '프라이빗 버전',
      subtitle: '온프레미스 설치',
      icon: <Server size={32} color="var(--success)" />,
      badge: { text: '보안', variant: 'success' as const },
      features: [
        '완전한 데이터 제어',
        '커스텀 설정 가능',
        '네트워크 격리',
        '내부 보안 정책 적용',
        '규정 준수'
      ],
      drawbacks: [
        '설치 및 설정 시간',
        '직접 관리 필요',
        '하드웨어 요구사항'
      ]
    }
  ];

  const projectCreationSteps = [
    {
      step: 1,
      title: '버전 선택',
      description: '위에서 SaaS 또는 프라이빗 버전을 선택합니다.',
      completed: !!selectedVersion
    },
    {
      step: 2,
      title: '계정 생성',
      description: '선택한 버전에 따라 계정을 생성하거나 설치를 진행합니다.',
      completed: false
    },
    {
      step: 3,
      title: '프로젝트 생성',
      description: '첫 번째 분석 프로젝트를 생성합니다.',
      completed: false
    }
  ];

  return (
    <div>
      {/* Step Title */}
      <div style={{ marginBottom: '32px' }}>
        <div style={{ 
          display: 'flex', 
          alignItems: 'center', 
          gap: '12px',
          marginBottom: '16px' 
        }}>
          <Building size={24} color="var(--brand-primary)" />
          <h3 style={{ 
            fontSize: '24px', 
            fontWeight: '600', 
            color: 'var(--gray-900)',
            margin: 0
          }}>
            1단계: 프로젝트 생성
          </h3>
        </div>
        <p style={{ 
          fontSize: '16px', 
          color: 'var(--gray-600)',
          lineHeight: '1.6',
          margin: 0
        }}>
          사용 중인 버전을 선택해주세요. 각 단계로 진행하여 본격적인 환경을 구축하세요.
        </p>
      </div>

      {/* Version Selection */}
      <div style={{ marginBottom: '32px' }}>
        <h4 style={{ 
          fontSize: '18px', 
          fontWeight: '600', 
          marginBottom: '16px',
          color: 'var(--gray-900)'
        }}>
          사용 중인 버전을 선택해주세요
        </h4>

        <div style={{ 
          display: 'grid', 
          gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', 
          gap: '24px',
          marginBottom: '24px'
        }}>
          {versionOptions.map((option) => (
            <Card
              key={option.id}
              selected={selectedVersion === option.id}
              onClick={() => onVersionSelect(option.id)}
              style={{ height: '100%' }}
            >
              <div style={{ 
                display: 'flex', 
                alignItems: 'flex-start', 
                justifyContent: 'space-between',
                marginBottom: '16px'
              }}>
                <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                  {option.icon}
                  <div>
                    <h5 style={{ 
                      fontSize: '18px', 
                      fontWeight: '600', 
                      margin: '0 0 4px 0',
                      color: 'var(--gray-900)'
                    }}>
                      {option.title}
                    </h5>
                    <p style={{ 
                      fontSize: '14px', 
                      color: 'var(--gray-600)', 
                      margin: 0 
                    }}>
                      {option.subtitle}
                    </p>
                  </div>
                </div>
                <Badge variant={option.badge.variant}>
                  {option.badge.text}
                </Badge>
              </div>

              <div style={{ marginBottom: '16px' }}>
                <h6 style={{ 
                  fontSize: '14px', 
                  fontWeight: '600', 
                  marginBottom: '8px',
                  color: 'var(--success)'
                }}>
                  장점
                </h6>
                <ul style={{ margin: 0, paddingLeft: '16px', fontSize: '14px' }}>
                  {option.features.map((feature, index) => (
                    <li key={index} style={{ marginBottom: '4px', color: 'var(--gray-700)' }}>
                      {feature}
                    </li>
                  ))}
                </ul>
              </div>

              <div>
                <h6 style={{ 
                  fontSize: '14px', 
                  fontWeight: '600', 
                  marginBottom: '8px',
                  color: 'var(--warning)'
                }}>
                  고려사항
                </h6>
                <ul style={{ margin: 0, paddingLeft: '16px', fontSize: '14px' }}>
                  {option.drawbacks.map((drawback, index) => (
                    <li key={index} style={{ marginBottom: '4px', color: 'var(--gray-700)' }}>
                      {drawback}
                    </li>
                  ))}
                </ul>
              </div>
            </Card>
          ))}
        </div>
      </div>

      {/* Project Creation Process */}
      {selectedVersion && (
        <div style={{ marginBottom: '32px' }}>
          <h4 style={{ 
            fontSize: '18px', 
            fontWeight: '600', 
            marginBottom: '16px',
            color: 'var(--gray-900)'
          }}>
            프로젝트 생성 과정
          </h4>

          <div style={{ 
            display: 'flex', 
            flexDirection: 'column', 
            gap: '16px' 
          }}>
            {projectCreationSteps.map((step, index) => (
              <div key={step.step} style={{ 
                display: 'flex', 
                alignItems: 'flex-start', 
                gap: '16px',
                padding: '16px',
                backgroundColor: step.completed ? 'var(--gray-50)' : 'transparent',
                borderRadius: 'var(--border-radius)',
                border: '1px solid var(--gray-200)'
              }}>
                <div style={{
                  width: '32px',
                  height: '32px',
                  borderRadius: '50%',
                  backgroundColor: step.completed ? 'var(--success)' : 'var(--brand-primary)',
                  color: 'white',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontSize: '14px',
                  fontWeight: '600',
                  flexShrink: 0
                }}>
                  {step.completed ? <Check size={16} /> : step.step}
                </div>
                <div style={{ flex: 1 }}>
                  <h5 style={{ 
                    fontSize: '16px', 
                    fontWeight: '600', 
                    margin: '0 0 4px 0',
                    color: 'var(--gray-900)'
                  }}>
                    {step.title}
                  </h5>
                  <p style={{ 
                    fontSize: '14px', 
                    color: 'var(--gray-600)', 
                    margin: 0,
                    lineHeight: '1.5'
                  }}>
                    {step.description}
                  </p>
                </div>
                {index < projectCreationSteps.length - 1 && (
                  <ArrowRight size={16} color="var(--gray-400)" style={{ marginTop: '8px' }} />
                )}
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Info Box */}
      <InfoBox type="info" title="프로젝트 생성 과정에 대한 확인" style={{ marginBottom: '24px' }}>
        <p style={{ margin: 0 }}>
          위 내용을 확인하고 다음 단계인 계정 및 권한 설정으로 진행할 수 있습니다.
        </p>
      </InfoBox>

      {/* Completion Checkbox */}
      <div style={{ 
        padding: '20px', 
        backgroundColor: 'var(--gray-50)', 
        borderRadius: 'var(--border-radius)',
        border: '1px solid var(--gray-200)'
      }}>
        <Checkbox
          checked={understood}
          onChange={setUnderstood}
          label="프로젝트 생성 과정을 이해했으며, 다음 단계로 진행할 준비가 되었습니다."
        />
      </div>
    </div>
  );
};
