import React, { useState, useEffect, useRef } from 'react';

interface Character {
  id: string;
  name: string;
  level: number;
  exp: number;
  maxExp: number;
  hp: number;
  maxHp: number;
  mp: number;
  maxMp: number;
  attack: number;
  defense: number;
  speed: number;
  gold: number;
  class: string;
  skills: Skill[];
  equipment: Equipment;
  stats: CharacterStats;
  prestige: number;
  rebirth: number;
}

interface CharacterStats {
  strength: number;
  agility: number;
  intelligence: number;
  vitality: number;
  luck: number;
  availablePoints: number;
}

interface Skill {
  id: string;
  name: string;
  level: number;
  maxLevel: number;
  damage: number;
  manaCost: number;
  cooldown: number;
  currentCooldown: number;
  type: 'active' | 'passive';
  description: string;
}

interface Equipment {
  weapon?: Item;
  armor?: Item;
  helmet?: Item;
  boots?: Item;
  ring?: Item;
  amulet?: Item;
}

interface Item {
  id: string;
  name: string;
  type: 'weapon' | 'armor' | 'helmet' | 'boots' | 'ring' | 'amulet';
  rarity: 'common' | 'rare' | 'epic' | 'legendary' | 'mythic';
  level: number;
  stats: {
    attack?: number;
    defense?: number;
    hp?: number;
    mp?: number;
    speed?: number;
    critRate?: number;
    critDamage?: number;
  };
  price: number;
  enhancement: number;
}

interface Monster {
  id: string;
  name: string;
  level: number;
  hp: number;
  maxHp: number;
  attack: number;
  defense: number;
  exp: number;
  gold: number;
  dropItems: Item[];
  isBoss: boolean;
}

interface Dungeon {
  id: string;
  name: string;
  level: number;
  floors: number;
  currentFloor: number;
  monsters: Monster[];
  rewards: Item[];
  isUnlocked: boolean;
}

interface Guild {
  id: string;
  name: string;
  level: number;
  members: number;
  maxMembers: number;
  benefits: {
    expBonus: number;
    goldBonus: number;
    dropBonus: number;
  };
}

interface AppProps {
  onAction: (eventType: string, data: any) => void;
  isSimulating: boolean;
  currentAction: string | null;
  userStats: any;
  setUserStats: (stats: any) => void;
  currentUser: any;
}

export const AdvancedIdleRPG: React.FC<AppProps> = ({
  onAction,
  isSimulating,
  currentAction,
  userStats,
  setUserStats,
  currentUser
}) => {
  const [character, setCharacter] = useState<Character>({
    id: '1',
    name: '용사',
    level: 1,
    exp: 0,
    maxExp: 100,
    hp: 100,
    maxHp: 100,
    mp: 50,
    maxMp: 50,
    attack: 20,
    defense: 10,
    speed: 15,
    gold: 100,
    class: '전사',
    skills: [],
    equipment: {},
    stats: {
      strength: 10,
      agility: 8,
      intelligence: 5,
      vitality: 12,
      luck: 7,
      availablePoints: 0
    },
    prestige: 0,
    rebirth: 0
  });

  const [currentTab, setCurrentTab] = useState<'combat' | 'character' | 'inventory' | 'skills' | 'dungeon' | 'guild' | 'shop'>('combat');
  const [isAutoFighting, setIsAutoFighting] = useState(false);
  const [currentMonster, setCurrentMonster] = useState<Monster | null>(null);
  const [combatLog, setCombatLog] = useState<string[]>([]);
  const [inventory, setInventory] = useState<Item[]>([]);
  const [dungeons, setDungeons] = useState<Dungeon[]>([]);
  const [currentDungeon, setCurrentDungeon] = useState<Dungeon | null>(null);
  const [guild, setGuild] = useState<Guild | null>(null);
  const [shop, setShop] = useState<Item[]>([]);
  const [achievements, setAchievements] = useState<string[]>([]);

  const logRef = useRef<HTMLDivElement>(null);

  // 게임 초기화
  useEffect(() => {
    initializeGame();
  }, []);

  const initializeGame = () => {
    // 초기 스킬 설정
    const initialSkills: Skill[] = [
      {
        id: 'slash',
        name: '강력한 베기',
        level: 1,
        maxLevel: 10,
        damage: 30,
        manaCost: 10,
        cooldown: 3000,
        currentCooldown: 0,
        type: 'active',
        description: '강력한 일격을 가합니다.'
      },
      {
        id: 'heal',
        name: '치유',
        level: 1,
        maxLevel: 10,
        damage: 0,
        manaCost: 15,
        cooldown: 5000,
        currentCooldown: 0,
        type: 'active',
        description: 'HP를 회복합니다.'
      },
      {
        id: 'critical',
        name: '치명타 숙련',
        level: 1,
        maxLevel: 10,
        damage: 0,
        manaCost: 0,
        cooldown: 0,
        currentCooldown: 0,
        type: 'passive',
        description: '치명타 확률을 증가시킵니다.'
      }
    ];

    // 던전 초기화
    const initialDungeons: Dungeon[] = [
      {
        id: 'forest',
        name: '어둠의 숲',
        level: 1,
        floors: 10,
        currentFloor: 1,
        monsters: [],
        rewards: [],
        isUnlocked: true
      },
      {
        id: 'cave',
        name: '고블린 동굴',
        level: 5,
        floors: 15,
        currentFloor: 1,
        monsters: [],
        rewards: [],
        isUnlocked: false
      },
      {
        id: 'tower',
        name: '마법사의 탑',
        level: 10,
        floors: 20,
        currentFloor: 1,
        monsters: [],
        rewards: [],
        isUnlocked: false
      }
    ];

    // 상점 아이템 초기화
    const shopItems: Item[] = [
      {
        id: 'sword1',
        name: '강철 검',
        type: 'weapon',
        rarity: 'common',
        level: 1,
        stats: { attack: 15 },
        price: 200,
        enhancement: 0
      },
      {
        id: 'armor1',
        name: '가죽 갑옷',
        type: 'armor',
        rarity: 'common',
        level: 1,
        stats: { defense: 10, hp: 20 },
        price: 150,
        enhancement: 0
      },
      {
        id: 'helmet1',
        name: '철제 투구',
        type: 'helmet',
        rarity: 'common',
        level: 1,
        stats: { defense: 5, hp: 15 },
        price: 100,
        enhancement: 0
      }
    ];

    setCharacter(prev => ({ ...prev, skills: initialSkills }));
    setDungeons(initialDungeons);
    setShop(shopItems);
    spawnMonster();
  };

  // 몬스터 생성
  const spawnMonster = () => {
    const monsters = [
      { name: '슬라임', baseHp: 40, baseAttack: 8, baseDefense: 2, exp: 15, gold: 10 },
      { name: '고블린', baseHp: 60, baseAttack: 12, baseDefense: 4, exp: 25, gold: 18 },
      { name: '오크', baseHp: 100, baseAttack: 18, baseDefense: 8, exp: 40, gold: 30 },
      { name: '트롤', baseHp: 150, baseAttack: 25, baseDefense: 12, exp: 60, gold: 45 },
      { name: '드래곤', baseHp: 300, baseAttack: 40, baseDefense: 20, exp: 120, gold: 100 }
    ];

    const randomMonster = monsters[Math.floor(Math.random() * monsters.length)];
    const levelMultiplier = 1 + (character.level - 1) * 0.3;

    const monster: Monster = {
      id: `monster_${Date.now()}`,
      name: randomMonster.name,
      level: Math.max(1, character.level + Math.floor(Math.random() * 3 - 1)),
      hp: Math.floor(randomMonster.baseHp * levelMultiplier),
      maxHp: Math.floor(randomMonster.baseHp * levelMultiplier),
      attack: Math.floor(randomMonster.baseAttack * levelMultiplier),
      defense: Math.floor(randomMonster.baseDefense * levelMultiplier),
      exp: Math.floor(randomMonster.exp * levelMultiplier),
      gold: Math.floor(randomMonster.gold * levelMultiplier),
      dropItems: generateDropItems(),
      isBoss: Math.random() < 0.1 // 10% 보스 확률
    };

    if (monster.isBoss) {
      monster.name = `보스 ${monster.name}`;
      monster.hp *= 3;
      monster.maxHp *= 3;
      monster.attack *= 2;
      monster.defense *= 2;
      monster.exp *= 5;
      monster.gold *= 3;
    }

    setCurrentMonster(monster);
    addToCombatLog(`${monster.name} Lv.${monster.level}이 나타났다!`);
  };

  // 드롭 아이템 생성
  const generateDropItems = (): Item[] => {
    const dropChance = Math.random();
    if (dropChance < 0.1) { // 10% 확률로 아이템 드롭
      const rarities: Array<'common' | 'rare' | 'epic' | 'legendary' | 'mythic'> = ['common', 'rare', 'epic', 'legendary', 'mythic'];
      const rarity = rarities[Math.floor(Math.random() * rarities.length)];

      return [{
        id: `drop_${Date.now()}`,
        name: generateItemName(rarity),
        type: ['weapon', 'armor', 'helmet', 'boots', 'ring', 'amulet'][Math.floor(Math.random() * 6)] as any,
        rarity,
        level: character.level,
        stats: generateItemStats(rarity),
        price: calculateItemPrice(rarity, character.level),
        enhancement: 0
      }];
    }
    return [];
  };

  const generateItemName = (rarity: string): string => {
    const prefixes = {
      common: ['낡은', '일반적인', '기본'],
      rare: ['날카로운', '튼튼한', '마법의'],
      epic: ['전설적인', '고대의', '신성한'],
      legendary: ['전설의', '영웅의', '불멸의'],
      mythic: ['신화의', '창조의', '절대의']
    };

    const items = ['검', '갑옷', '투구', '신발', '반지', '목걸이'];
    const prefix = prefixes[rarity as keyof typeof prefixes][Math.floor(Math.random() * 3)];
    const item = items[Math.floor(Math.random() * items.length)];

    return `${prefix} ${item}`;
  };

  const generateItemStats = (rarity: string) => {
    const multipliers = {
      common: 1,
      rare: 1.5,
      epic: 2.5,
      legendary: 4,
      mythic: 7
    };

    const base = multipliers[rarity as keyof typeof multipliers];
    return {
      attack: Math.floor(Math.random() * 20 * base),
      defense: Math.floor(Math.random() * 15 * base),
      hp: Math.floor(Math.random() * 50 * base),
      mp: Math.floor(Math.random() * 30 * base),
      speed: Math.floor(Math.random() * 10 * base),
      critRate: Math.floor(Math.random() * 10 * base),
      critDamage: Math.floor(Math.random() * 20 * base)
    };
  };

  const calculateItemPrice = (rarity: string, level: number): number => {
    const basePrice = {
      common: 50,
      rare: 150,
      epic: 500,
      legendary: 2000,
      mythic: 10000
    };

    return Math.floor(basePrice[rarity as keyof typeof basePrice] * (1 + level * 0.2));
  };

  // 전투 시스템
  const attack = () => {
    if (!currentMonster) return;

    const damage = calculateDamage(character.attack, currentMonster.defense);
    const isCritical = Math.random() < 0.1 + (character.stats.luck * 0.01);
    const finalDamage = isCritical ? Math.floor(damage * 1.5) : damage;

    const newMonsterHp = Math.max(0, currentMonster.hp - finalDamage);
    setCurrentMonster(prev => prev ? { ...prev, hp: newMonsterHp } : null);

    addToCombatLog(`${character.name}의 공격! ${finalDamage}${isCritical ? ' (치명타!)' : ''} 데미지!`);

    onAction('combat_attack', {
      user_id: currentUser.user_id,
      damage: finalDamage,
      is_critical: isCritical,
      monster_name: currentMonster.name,
      monster_level: currentMonster.level
    });

    if (newMonsterHp <= 0) {
      defeatMonster();
    } else {
      // 몬스터 반격
      setTimeout(() => monsterAttack(), 1000);
    }
  };

  const monsterAttack = () => {
    if (!currentMonster || currentMonster.hp <= 0) return;

    const damage = calculateDamage(currentMonster.attack, character.defense);
    const newHp = Math.max(0, character.hp - damage);

    setCharacter(prev => ({ ...prev, hp: newHp }));
    addToCombatLog(`${currentMonster.name}의 공격! ${damage} 데미지!`);

    if (newHp <= 0) {
      addToCombatLog('패배했습니다...');
      setIsAutoFighting(false);
      // 부활 (경험치와 골드 일부 손실)
      setTimeout(() => {
        setCharacter(prev => ({
          ...prev,
          hp: Math.floor(prev.maxHp * 0.5),
          exp: Math.max(0, prev.exp - Math.floor(prev.maxExp * 0.1)),
          gold: Math.max(0, prev.gold - Math.floor(prev.gold * 0.1))
        }));
        addToCombatLog('부활했습니다!');
      }, 2000);
    }
  };

  const calculateDamage = (attack: number, defense: number): number => {
    const baseDamage = Math.max(1, attack - Math.floor(defense * 0.5));
    return Math.floor(baseDamage * (0.8 + Math.random() * 0.4));
  };

  const defeatMonster = () => {
    if (!currentMonster) return;

    const expGained = currentMonster.exp;
    const goldGained = currentMonster.gold;

    setCharacter(prev => {
      const newExp = prev.exp + expGained;
      const newGold = prev.gold + goldGained;
      let newLevel = prev.level;
      let newMaxExp = prev.maxExp;
      let newHp = prev.hp;
      let newMaxHp = prev.maxHp;
      let availablePoints = prev.stats.availablePoints;

      // 레벨업 체크
      if (newExp >= prev.maxExp) {
        newLevel += 1;
        newMaxExp = Math.floor(prev.maxExp * 1.5);
        newMaxHp += 20;
        newHp = newMaxHp; // 레벨업 시 체력 완전 회복
        availablePoints += 5;

        addToCombatLog(`🎉 레벨업! Lv.${newLevel}이 되었습니다!`);

        onAction('level_up', {
          user_id: currentUser.user_id,
          new_level: newLevel,
          character_class: prev.class
        });
      }

      return {
        ...prev,
        exp: newExp >= prev.maxExp ? newExp - prev.maxExp : newExp,
        gold: newGold,
        level: newLevel,
        maxExp: newMaxExp,
        hp: newHp,
        maxHp: newMaxHp,
        stats: { ...prev.stats, availablePoints }
      };
    });

    // 아이템 드롭 처리
    if (currentMonster.dropItems.length > 0) {
      setInventory(prev => [...prev, ...currentMonster.dropItems]);
      addToCombatLog(`✨ ${currentMonster.dropItems[0].name}을 획득했습니다!`);
    }

    addToCombatLog(`${currentMonster.name}를 물리쳤다! EXP +${expGained}, 골드 +${goldGained}`);

    onAction('monster_defeated', {
      user_id: currentUser.user_id,
      monster_name: currentMonster.name,
      monster_level: currentMonster.level,
      exp_gained: expGained,
      gold_gained: goldGained,
      items_dropped: currentMonster.dropItems.length
    });

    // 새로운 몬스터 생성
    setTimeout(() => spawnMonster(), 2000);
  };


  // 자동 전투
  useEffect(() => {
    if (isAutoFighting && currentMonster && currentMonster.hp > 0 && character.hp > 0) {
      const autoAttackTimer = setTimeout(() => {
        attack();
      }, 2000);

      return () => clearTimeout(autoAttackTimer);
    }
  }, [isAutoFighting, currentMonster, character.hp]);

  // 스킬 쿨다운 및 MP 회복
  useEffect(() => {
    const interval = setInterval(() => {
      setCharacter(prev => ({
        ...prev,
        mp: Math.min(prev.maxMp, prev.mp + 1),
        skills: prev.skills.map(skill => ({
          ...skill,
          currentCooldown: Math.max(0, skill.currentCooldown - 100)
        }))
      }));
    }, 100);

    return () => clearInterval(interval);
  }, []);

  const addToCombatLog = (message: string) => {
    setCombatLog(prev => {
      const newLog = [...prev, message];
      if (newLog.length > 50) newLog.shift();
      return newLog;
    });

    setTimeout(() => {
      if (logRef.current) {
        logRef.current.scrollTop = logRef.current.scrollHeight;
      }
    }, 0);
  };

  // 스탯 분배
  const allocateStatPoint = (stat: keyof Omit<CharacterStats, 'availablePoints'>) => {
    if (character.stats.availablePoints <= 0) return;

    setCharacter(prev => ({
      ...prev,
      stats: {
        ...prev.stats,
        [stat]: prev.stats[stat] + 1,
        availablePoints: prev.stats.availablePoints - 1
      },
      // 스탯에 따른 능력치 업데이트
      attack: stat === 'strength' ? prev.attack + 2 : prev.attack,
      defense: stat === 'vitality' ? prev.defense + 1 : prev.defense,
      maxHp: stat === 'vitality' ? prev.maxHp + 10 : prev.maxHp,
      maxMp: stat === 'intelligence' ? prev.maxMp + 5 : prev.maxMp,
      speed: stat === 'agility' ? prev.speed + 1 : prev.speed
    }));
  };

  // 아이템 장착
  const equipItem = (item: Item) => {
    setCharacter(prev => {
      const newEquipment = { ...prev.equipment };
      newEquipment[item.type] = item;

      // 장착한 아이템의 스탯 적용
      let newAttack = prev.attack;
      let newDefense = prev.defense;
      let newMaxHp = prev.maxHp;
      let newMaxMp = prev.maxMp;
      let newSpeed = prev.speed;

      if (item.stats.attack) newAttack += item.stats.attack;
      if (item.stats.defense) newDefense += item.stats.defense;
      if (item.stats.hp) newMaxHp += item.stats.hp;
      if (item.stats.mp) newMaxMp += item.stats.mp;
      if (item.stats.speed) newSpeed += item.stats.speed;

      return {
        ...prev,
        equipment: newEquipment,
        attack: newAttack,
        defense: newDefense,
        maxHp: newMaxHp,
        maxMp: newMaxMp,
        speed: newSpeed
      };
    });

    // 인벤토리에서 아이템 제거
    setInventory(prev => prev.filter(invItem => invItem.id !== item.id));
    addToCombatLog(`${item.name}을 장착했습니다!`);
  };

  // 아이템 판매
  const sellItem = (item: Item) => {
    const sellPrice = Math.floor(item.price * 0.7);
    setCharacter(prev => ({ ...prev, gold: prev.gold + sellPrice }));
    setInventory(prev => prev.filter(invItem => invItem.id !== item.id));
    addToCombatLog(`${item.name}을 ${sellPrice} 골드에 판매했습니다!`);
  };

  // 아이템 구매
  const buyItem = (item: Item) => {
    if (character.gold < item.price) return;

    setCharacter(prev => ({ ...prev, gold: prev.gold - item.price }));
    setInventory(prev => [...prev, { ...item, id: `${item.id}_${Date.now()}` }]);
    addToCombatLog(`${item.name}을 구매했습니다!`);
  };

  const getRarityColor = (rarity: string) => {
    const colors = {
      common: '#9ca3af',
      rare: '#3b82f6',
      epic: '#8b5cf6',
      legendary: '#f59e0b',
      mythic: '#ef4444'
    };
    return colors[rarity as keyof typeof colors] || '#9ca3af';
  };

  const renderCombatTab = () => (
    <div style={{ padding: '20px', background: '#1f2937', minHeight: '600px' }}>
      {/* 캐릭터 정보 */}
      <div style={{
        background: 'linear-gradient(135deg, #4f46e5 0%, #7c3aed 100%)',
        borderRadius: '15px',
        padding: '20px',
        marginBottom: '20px',
        color: 'white'
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '15px' }}>
          <div>
            <h2 style={{ margin: '0 0 5px 0', fontSize: '24px' }}>{character.name}</h2>
            <p style={{ margin: '0', opacity: 0.9 }}>Lv.{character.level} {character.class}</p>
          </div>
          <div style={{ textAlign: 'right' }}>
            <div style={{ fontSize: '20px', fontWeight: 'bold' }}>💰 {character.gold.toLocaleString()}</div>
          </div>
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '15px' }}>
          <div>
            <div style={{ marginBottom: '8px' }}>
              <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '14px' }}>
                <span>HP</span>
                <span>{character.hp}/{character.maxHp}</span>
              </div>
              <div style={{
                background: 'rgba(255,255,255,0.2)',
                borderRadius: '10px',
                height: '8px',
                overflow: 'hidden'
              }}>
                <div style={{
                  background: '#ef4444',
                  height: '100%',
                  width: `${(character.hp / character.maxHp) * 100}%`,
                  transition: 'width 0.3s ease'
                }} />
              </div>
            </div>
            <div>
              <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '14px' }}>
                <span>MP</span>
                <span>{character.mp}/{character.maxMp}</span>
              </div>
              <div style={{
                background: 'rgba(255,255,255,0.2)',
                borderRadius: '10px',
                height: '8px',
                overflow: 'hidden'
              }}>
                <div style={{
                  background: '#3b82f6',
                  height: '100%',
                  width: `${(character.mp / character.maxMp) * 100}%`,
                  transition: 'width 0.3s ease'
                }} />
              </div>
            </div>
          </div>
          <div>
            <div style={{ marginBottom: '8px' }}>
              <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '14px' }}>
                <span>EXP</span>
                <span>{character.exp}/{character.maxExp}</span>
              </div>
              <div style={{
                background: 'rgba(255,255,255,0.2)',
                borderRadius: '10px',
                height: '8px',
                overflow: 'hidden'
              }}>
                <div style={{
                  background: '#10b981',
                  height: '100%',
                  width: `${(character.exp / character.maxExp) * 100}%`,
                  transition: 'width 0.3s ease'
                }} />
              </div>
            </div>
            <div style={{ display: 'flex', gap: '20px', fontSize: '14px' }}>
              <span>⚔️ {character.attack}</span>
              <span>🛡️ {character.defense}</span>
              <span>💨 {character.speed}</span>
            </div>
          </div>
        </div>
      </div>

      {/* 전투 영역 */}
      <div style={{
        display: 'grid',
        gridTemplateColumns: '1fr 1fr',
        gap: '20px',
        marginBottom: '20px'
      }}>
        {/* 몬스터 정보 */}
        <div style={{
          background: '#374151',
          borderRadius: '15px',
          padding: '20px',
          textAlign: 'center',
          minHeight: '200px',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center'
        }}>
          {currentMonster ? (
            <>
              <div style={{
                fontSize: '48px',
                marginBottom: '10px',
                filter: currentMonster.isBoss ? 'drop-shadow(0 0 10px #fbbf24)' : 'none'
              }}>
                {currentMonster.isBoss ? '👑' : '👹'}
              </div>
              <h3 style={{ 
                color: currentMonster.isBoss ? '#fbbf24' : 'white', 
                margin: '0 0 10px 0' 
              }}>
                {currentMonster.name} Lv.{currentMonster.level}
              </h3>
              <div style={{ color: '#9ca3af', marginBottom: '15px' }}>
                HP: {currentMonster.hp}/{currentMonster.maxHp}
              </div>
              <div style={{
                background: 'rgba(0,0,0,0.3)',
                borderRadius: '10px',
                height: '10px',
                overflow: 'hidden'
              }}>
                <div style={{
                  background: '#ef4444',
                  height: '100%',
                  width: `${(currentMonster.hp / currentMonster.maxHp) * 100}%`,
                  transition: 'width 0.3s ease'
                }} />
              </div>
            </>
          ) : (
            <div style={{ color: '#9ca3af' }}>몬스터를 찾는 중...</div>
          )}
        </div>

        {/* 스킬 및 액션 */}
        <div style={{
          background: '#374151',
          borderRadius: '15px',
          padding: '20px'
        }}>
          <h3 style={{ color: 'white', margin: '0 0 15px 0' }}>액션</h3>
          <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
            <button
              onClick={attack}
              disabled={!currentMonster || character.hp <= 0}
              style={{
                background: 'linear-gradient(135deg, #ef4444 0%, #dc2626 100%)',
                color: 'white',
                border: 'none',
                borderRadius: '10px',
                padding: '12px',
                fontSize: '16px',
                fontWeight: 'bold',
                cursor: character.hp > 0 ? 'pointer' : 'not-allowed',
                opacity: character.hp > 0 ? 1 : 0.5
              }}
            >
              ⚔️ 공격
            </button>

            <button
              onClick={() => setIsAutoFighting(!isAutoFighting)}
              disabled={character.hp <= 0}
              style={{
                background: isAutoFighting 
                  ? 'linear-gradient(135deg, #ef4444 0%, #dc2626 100%)'
                  : 'linear-gradient(135deg, #10b981 0%, #059669 100%)',
                color: 'white',
                border: 'none',
                borderRadius: '10px',
                padding: '12px',
                fontSize: '16px',
                fontWeight: 'bold',
                cursor: character.hp > 0 ? 'pointer' : 'not-allowed',
                opacity: character.hp > 0 ? 1 : 0.5
              }}
            >
              {isAutoFighting ? '⏹️ 자동 중지' : '▶️ 자동 전투'}
            </button>

            <div style={{ marginTop: '10px' }}>
              <h4 style={{ color: 'white', margin: '0 0 10px 0', fontSize: '14px' }}>스킬</h4>
              <div style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
                {character.skills.map(skill => (
                  <button
                    key={skill.id}
                    onClick={() => {
                      if (character.mp < skill.manaCost || skill.currentCooldown > 0) return;

                      setCharacter(prev => ({ ...prev, mp: prev.mp - skill.manaCost }));

                      if (skill.id === 'heal') {
                        const healAmount = Math.floor(character.maxHp * 0.3);
                        setCharacter(prev => ({ 
                          ...prev, 
                          hp: Math.min(prev.maxHp, prev.hp + healAmount) 
                        }));
                        addToCombatLog(`치유 스킬 사용! HP +${healAmount}`);
                      } else {
                        // 공격 스킬
                        if (currentMonster) {
                          const damage = skill.damage + Math.floor(character.attack * 0.5);
                          const newMonsterHp = Math.max(0, currentMonster.hp - damage);
                          setCurrentMonster(prev => prev ? { ...prev, hp: newMonsterHp } : null);
                          addToCombatLog(`${skill.name} 사용! ${damage} 데미지!`);

                          if (newMonsterHp <= 0) {
                            defeatMonster();
                          }
                        }
                      }

                      // 쿨다운 설정
                      setCharacter(prev => ({
                        ...prev,
                        skills: prev.skills.map(s => 
                          s.id === skill.id ? { ...s, currentCooldown: skill.cooldown } : s
                        )
                      }));
                    }}
                    disabled={character.mp < skill.manaCost || skill.currentCooldown > 0}
                    style={{
                      background: skill.currentCooldown > 0 || character.mp < skill.manaCost 
                        ? '#6b7280' 
                        : 'linear-gradient(135deg, #8b5cf6 0%, #7c3aed 100%)',
                      color: 'white',
                      border: 'none',
                      borderRadius: '8px',
                      padding: '8px 12px',
                      fontSize: '14px',
                      cursor: (character.mp >= skill.manaCost && skill.currentCooldown === 0) ? 'pointer' : 'not-allowed',
                      opacity: (character.mp >= skill.manaCost && skill.currentCooldown === 0) ? 1 : 0.6
                    }}
                  >
                    {skill.name} (MP: {skill.manaCost})
                    {skill.currentCooldown > 0 && ` (${Math.ceil(skill.currentCooldown / 1000)}s)`}
                  </button>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* 전투 로그 */}
      <div style={{
        background: '#374151',
        borderRadius: '15px',
        padding: '20px'
      }}>
        <h3 style={{ color: 'white', margin: '0 0 15px 0' }}>전투 로그</h3>
        <div
          ref={logRef}
          style={{
            height: '150px',
            overflowY: 'auto',
            background: '#1f2937',
            borderRadius: '10px',
            padding: '15px',
            fontFamily: 'monospace',
            fontSize: '14px'
          }}
        >
          {combatLog.map((log, index) => (
            <div
              key={index}
              style={{
                color: log.includes('레벨업') ? '#10b981' :
                       log.includes('치명타') ? '#ef4444' :
                       log.includes('획득') ? '#fbbf24' : '#d1d5db',
                marginBottom: '2px',
                lineHeight: '1.4'
              }}
            >
              {log}
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  const renderCharacterTab = () => (
    <div style={{ padding: '20px', background: '#1f2937', minHeight: '600px' }}>
      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(2, 1fr)',
        gap: '20px'
      }}>
        {/* 스탯 */}
        <div style={{
          background: '#374151',
          borderRadius: '15px',
          padding: '20px'
        }}>
          <h3 style={{ color: 'white', margin: '0 0 15px 0' }}>스탯</h3>
          <div style={{ color: 'white', marginBottom: '15px' }}>
            사용 가능한 포인트: <span style={{ color: '#fbbf24', fontWeight: 'bold' }}>{character.stats.availablePoints}</span>
          </div>

          <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
            {Object.entries(character.stats).map(([key, value]) => {
              if (key === 'availablePoints') return null;

              const statNames = {
                strength: '힘',
                agility: '민첩',
                intelligence: '지능',
                vitality: '체력',
                luck: '운'
              };

              return (
                <div key={key} style={{
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center',
                  background: '#1f2937',
                  padding: '10px 15px',
                  borderRadius: '10px'
                }}>
                  <span>{statNames[key as keyof typeof statNames]}: {value}</span>
                  <button
                    onClick={() => allocateStatPoint(key as keyof Omit<CharacterStats, 'availablePoints'>)}
                    disabled={character.stats.availablePoints <= 0}
                    style={{
                      background: character.stats.availablePoints > 0 ? '#10b981' : '#6b7280',
                      color: 'white',
                      border: 'none',
                      borderRadius: '5px',
                      padding: '5px 10px',
                      cursor: character.stats.availablePoints > 0 ? 'pointer' : 'not-allowed'
                    }}
                  >
                    +
                  </button>
                </div>
              );
            })}
          </div>
        </div>

        {/* 장비 */}
        <div style={{
          background: '#374151',
          borderRadius: '15px',
          padding: '20px'
        }}>
          <h3 style={{ color: 'white', margin: '0 0 15px 0' }}>장비</h3>

          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(3, 1fr)',
            gap: '10px'
          }}>
            {['weapon', 'helmet', 'armor', 'boots', 'ring', 'amulet'].map(slot => {
              const item = character.equipment[slot as keyof Equipment];
              const slotNames = {
                weapon: '무기',
                helmet: '투구',
                armor: '갑옷',
                boots: '신발',
                ring: '반지',
                amulet: '목걸이'
              };

              return (
                <div
                  key={slot}
                  style={{
                    background: item ? getRarityColor(item.rarity) : '#1f2937',
                    borderRadius: '10px',
                    padding: '15px',
                    textAlign: 'center',
                    minHeight: '80px',
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'center',
                    border: `2px solid ${item ? getRarityColor(item.rarity) : '#4b5563'}`
                  }}
                >
                  <div style={{ fontSize: '12px', color: '#9ca3af', marginBottom: '5px' }}>
                    {slotNames[slot as keyof typeof slotNames]}
                  </div>
                  {item ? (
                    <>
                      <div style={{ color: 'white', fontSize: '14px', fontWeight: 'bold' }}>
                        {item.name}
                      </div>
                      {item.enhancement > 0 && (
                        <div style={{ color: '#fbbf24', fontSize: '12px' }}>
                          +{item.enhancement}
                        </div>
                      )}
                    </>
                  ) : (
                    <div style={{ color: '#6b7280', fontSize: '12px' }}>빈 슬롯</div>
                  )}
                </div>
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );

  const renderInventoryTab = () => (
    <div style={{ padding: '20px', background: '#1f2937', minHeight: '600px' }}>
      <div style={{
        background: '#374151',
        borderRadius: '15px',
        padding: '20px'
      }}>
        <h3 style={{ color: 'white', margin: '0 0 15px 0' }}>인벤토리 ({inventory.length}개)</h3>

        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))',
          gap: '15px'
        }}>
          {inventory.map(item => (
            <div
              key={item.id}
              style={{
                background: '#1f2937',
                border: `2px solid ${getRarityColor(item.rarity)}`,
                borderRadius: '10px',
                padding: '15px'
              }}
            >
              <div style={{
                color: getRarityColor(item.rarity),
                fontWeight: 'bold',
                marginBottom: '5px'
              }}>
                {item.name}
              </div>

              <div style={{ color: '#9ca3af', fontSize: '14px', marginBottom: '10px' }}>
                {item.type} • Lv.{item.level}
              </div>

              <div style={{ color: '#d1d5db', fontSize: '12px', marginBottom: '10px' }}>
                {Object.entries(item.stats).map(([key, value]) => 
                  value > 0 && (
                    <div key={key}>
                      {key === 'attack' ? '⚔️' :
                       key === 'defense' ? '🛡️' :
                       key === 'hp' ? '❤️' :
                       key === 'mp' ? '💙' :
                       key === 'speed' ? '💨' :
                       key === 'critRate' ? '💥' : '⭐'} +{value}
                    </div>
                  )
                )}
              </div>

              <div style={{ display: 'flex', gap: '5px' }}>
                <button
                  onClick={() => equipItem(item)}
                  style={{
                    background: '#10b981',
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px',
                    padding: '8px 12px',
                    fontSize: '12px',
                    cursor: 'pointer',
                    flex: 1
                  }}
                >
                  장착
                </button>
                <button
                  onClick={() => sellItem(item)}
                  style={{
                    background: '#ef4444',
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px',
                    padding: '8px 12px',
                    fontSize: '12px',
                    cursor: 'pointer',
                    flex: 1
                  }}
                >
                  판매 ({Math.floor(item.price * 0.7)}G)
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  const renderShopTab = () => (
    <div style={{ padding: '20px', background: '#1f2937', minHeight: '600px' }}>
      <div style={{
        background: '#374151',
        borderRadius: '15px',
        padding: '20px'
      }}>
        <h3 style={{ color: 'white', margin: '0 0 15px 0' }}>상점</h3>

        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
          gap: '15px'
        }}>
          {shop.map(item => (
            <div
              key={item.id}
              style={{
                background: '#1f2937',
                border: `2px solid ${getRarityColor(item.rarity)}`,
                borderRadius: '10px',
                padding: '15px'
              }}
            >
              <div style={{
                color: getRarityColor(item.rarity),
                fontWeight: 'bold',
                marginBottom: '5px'
              }}>
                {item.name}
              </div>

              <div style={{ color: '#9ca3af', fontSize: '14px', marginBottom: '10px' }}>
                {item.type} • Lv.{item.level} • {item.rarity}
              </div>

              <div style={{ color: '#d1d5db', fontSize: '12px', marginBottom: '15px' }}>
                {Object.entries(item.stats).map(([key, value]) => 
                  value > 0 && (
                    <div key={key}>
                      {key === 'attack' ? '⚔️' :
                       key === 'defense' ? '🛡️' :
                       key === 'hp' ? '❤️' :
                       key === 'mp' ? '💙' :
                       key === 'speed' ? '💨' :
                       key === 'critRate' ? '💥' : '⭐'} +{value}
                    </div>
                  )
                )}
              </div>

              <div style={{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center'
              }}>
                <div style={{ color: '#fbbf24', fontWeight: 'bold' }}>
                  💰 {item.price.toLocaleString()}G
                </div>
                <button
                  onClick={() => buyItem(item)}
                  disabled={character.gold < item.price}
                  style={{
                    background: character.gold >= item.price ? '#10b981' : '#6b7280',
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px',
                    padding: '8px 16px',
                    fontSize: '12px',
                    cursor: character.gold >= item.price ? 'pointer' : 'not-allowed',
                    opacity: character.gold >= item.price ? 1 : 0.6
                  }}
                >
                  구매
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  const renderTabs = () => (
    <div style={{
      background: '#374151',
      display: 'flex',
      borderRadius: '15px 15px 0 0',
      overflow: 'hidden'
    }}>
      {[
        { id: 'combat', name: '전투', icon: '⚔️' },
        { id: 'character', name: '캐릭터', icon: '👤' },
        { id: 'inventory', name: '인벤토리', icon: '🎒' },
        { id: 'skills', name: '스킬', icon: '✨' },
        { id: 'dungeon', name: '던전', icon: '🏰' },
        { id: 'guild', name: '길드', icon: '🛡️' },
        { id: 'shop', name: '상점', icon: '🛒' }
      ].map(tab => (
        <button
          key={tab.id}
          onClick={() => setCurrentTab(tab.id as any)}
          style={{
            background: currentTab === tab.id 
              ? 'linear-gradient(135deg, #6366f1 0%, #8b5cf6 100%)'
              : 'transparent',
            color: 'white',
            border: 'none',
            padding: '15px 20px',
            fontSize: '14px',
            cursor: 'pointer',
            flex: 1,
            transition: 'all 0.3s ease'
          }}
        >
          <div>{tab.icon}</div>
          <div style={{ fontSize: '12px', marginTop: '5px' }}>{tab.name}</div>
        </button>
      ))}
    </div>
  );

  return (
    <div style={{
      width: '100%',
      maxWidth: '1200px',
      margin: '0 auto',
      background: '#111827',
      borderRadius: '20px',
      overflow: 'hidden',
      boxShadow: '0 20px 60px rgba(0, 0, 0, 0.3)',
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif'
    }}>
      {/* 게임 타이틀 */}
      <div style={{
        background: 'linear-gradient(135deg, #1e1b4b 0%, #312e81 50%, #1e1b4b 100%)',
        padding: '20px',
        textAlign: 'center',
        position: 'relative',
        overflow: 'hidden'
      }}>
        <div style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'radial-gradient(circle at 50% 50%, rgba(147, 51, 234, 0.3) 0%, transparent 70%)'
        }} />
        <h1 style={{
          margin: 0,
          fontSize: '32px',
          fontWeight: 'bold',
          color: 'white',
          textShadow: '0 4px 20px rgba(147, 51, 234, 0.5)',
          position: 'relative',
          zIndex: 1
        }}>
          ⚔️ 방치형 RPG 어드벤처 ⚔️
        </h1>
        <p style={{
          margin: '10px 0 0 0',
          color: '#e5e7eb',
          fontSize: '16px',
          position: 'relative',
          zIndex: 1
        }}>
          레벨업, 전투, 탐험의 끝없는 모험이 시작됩니다!
        </p>
      </div>

      {/* 탭 메뉴 */}
      {renderTabs()}

      {/* 현재 탭 컨텐츠 */}
      {currentTab === 'combat' && renderCombatTab()}
      {currentTab === 'character' && renderCharacterTab()}
      {currentTab === 'inventory' && renderInventoryTab()}
      {currentTab === 'shop' && renderShopTab()}

      {/* 다른 탭들은 개발 예정 */}
      {['skills', 'dungeon', 'guild'].includes(currentTab) && (
        <div style={{
          padding: '100px 20px',
          textAlign: 'center',
          background: '#1f2937',
          color: '#9ca3af'
        }}>
          <h3>🚧 개발 중입니다 🚧</h3>
          <p>{currentTab === 'skills' ? '스킬 트리' : 
              currentTab === 'dungeon' ? '던전 탐험' : 
              '길드 시스템'} 기능이 곧 추가됩니다!</p>
        </div>
      )}
    </div>
  );
};
