import React, { useState, useEffect, useRef } from 'react';

interface Character {
  id: string;
  name: string;
  level: number;
  exp: number;
  maxExp: number;
  hp: number;
  maxHp: number;
  mp: number;
  maxMp: number;
  attack: number;
  defense: number;
  speed: number;
  gold: number;
  class: string;
  skills: Skill[];
  equipment: Equipment;
  stats: CharacterStats;
  prestige: number;
  rebirth: number;
}

interface CharacterStats {
  strength: number;
  agility: number;
  intelligence: number;
  vitality: number;
  luck: number;
  availablePoints: number;
}

interface Skill {
  id: string;
  name: string;
  level: number;
  maxLevel: number;
  damage: number;
  manaCost: number;
  cooldown: number;
  currentCooldown: number;
  type: 'active' | 'passive';
  description: string;
}

interface Equipment {
  weapon?: Item;
  armor?: Item;
  helmet?: Item;
  boots?: Item;
  ring?: Item;
  amulet?: Item;
}

interface Item {
  id: string;
  name: string;
  type: 'weapon' | 'armor' | 'helmet' | 'boots' | 'ring' | 'amulet';
  rarity: 'common' | 'rare' | 'epic' | 'legendary' | 'mythic';
  level: number;
  stats: {
    attack?: number;
    defense?: number;
    hp?: number;
    mp?: number;
    speed?: number;
    critRate?: number;
    critDamage?: number;
  };
  price: number;
  enhancement: number;
}

interface Monster {
  id: string;
  name: string;
  level: number;
  hp: number;
  maxHp: number;
  attack: number;
  defense: number;
  exp: number;
  gold: number;
  dropItems: Item[];
  isBoss: boolean;
}

interface Dungeon {
  id: string;
  name: string;
  level: number;
  floors: number;
  currentFloor: number;
  monsters: Monster[];
  rewards: Item[];
  isUnlocked: boolean;
}

interface Guild {
  id: string;
  name: string;
  level: number;
  members: number;
  maxMembers: number;
  benefits: {
    expBonus: number;
    goldBonus: number;
    dropBonus: number;
  };
}

interface AppProps {
  onAction: (eventType: string, data: any) => void;
  isSimulating: boolean;
  currentAction: string | null;
  userStats: any;
  setUserStats: (stats: any) => void;
  currentUser: any;
}

export const AdvancedIdleRPG: React.FC<AppProps> = ({
  onAction,
  isSimulating,
  currentAction,
  userStats,
  setUserStats,
  currentUser
}) => {
  const [character, setCharacter] = useState<Character>({
    id: '1',
    name: 'Ïö©ÏÇ¨',
    level: 1,
    exp: 0,
    maxExp: 100,
    hp: 100,
    maxHp: 100,
    mp: 50,
    maxMp: 50,
    attack: 20,
    defense: 10,
    speed: 15,
    gold: 100,
    class: 'Ï†ÑÏÇ¨',
    skills: [],
    equipment: {},
    stats: {
      strength: 10,
      agility: 8,
      intelligence: 5,
      vitality: 12,
      luck: 7,
      availablePoints: 0
    },
    prestige: 0,
    rebirth: 0
  });

  const [currentTab, setCurrentTab] = useState<'combat' | 'character' | 'inventory' | 'skills' | 'dungeon' | 'guild' | 'shop'>('combat');
  const [isAutoFighting, setIsAutoFighting] = useState(false);
  const [currentMonster, setCurrentMonster] = useState<Monster | null>(null);
  const [combatLog, setCombatLog] = useState<string[]>([]);
  const [inventory, setInventory] = useState<Item[]>([]);
  const [dungeons, setDungeons] = useState<Dungeon[]>([]);
  const [currentDungeon, setCurrentDungeon] = useState<Dungeon | null>(null);
  const [guild, setGuild] = useState<Guild | null>(null);
  const [shop, setShop] = useState<Item[]>([]);
  const [achievements, setAchievements] = useState<string[]>([]);

  const logRef = useRef<HTMLDivElement>(null);

  // Í≤åÏûÑ Ï¥àÍ∏∞Ìôî
  useEffect(() => {
    initializeGame();
  }, []);

  const initializeGame = () => {
    // Ï¥àÍ∏∞ Ïä§ÌÇ¨ ÏÑ§Ï†ï
    const initialSkills: Skill[] = [
      {
        id: 'slash',
        name: 'Í∞ïÎ†•Ìïú Î≤†Í∏∞',
        level: 1,
        maxLevel: 10,
        damage: 30,
        manaCost: 10,
        cooldown: 3000,
        currentCooldown: 0,
        type: 'active',
        description: 'Í∞ïÎ†•Ìïú ÏùºÍ≤©ÏùÑ Í∞ÄÌï©ÎãàÎã§.'
      },
      {
        id: 'heal',
        name: 'ÏπòÏú†',
        level: 1,
        maxLevel: 10,
        damage: 0,
        manaCost: 15,
        cooldown: 5000,
        currentCooldown: 0,
        type: 'active',
        description: 'HPÎ•º ÌöåÎ≥µÌï©ÎãàÎã§.'
      },
      {
        id: 'critical',
        name: 'ÏπòÎ™ÖÌÉÄ ÏàôÎ†®',
        level: 1,
        maxLevel: 10,
        damage: 0,
        manaCost: 0,
        cooldown: 0,
        currentCooldown: 0,
        type: 'passive',
        description: 'ÏπòÎ™ÖÌÉÄ ÌôïÎ•†ÏùÑ Ï¶ùÍ∞ÄÏãúÌÇµÎãàÎã§.'
      }
    ];

    // ÎçòÏ†Ñ Ï¥àÍ∏∞Ìôî
    const initialDungeons: Dungeon[] = [
      {
        id: 'forest',
        name: 'Ïñ¥Îë†Ïùò Ïà≤',
        level: 1,
        floors: 10,
        currentFloor: 1,
        monsters: [],
        rewards: [],
        isUnlocked: true
      },
      {
        id: 'cave',
        name: 'Í≥†Î∏îÎ¶∞ ÎèôÍµ¥',
        level: 5,
        floors: 15,
        currentFloor: 1,
        monsters: [],
        rewards: [],
        isUnlocked: false
      },
      {
        id: 'tower',
        name: 'ÎßàÎ≤ïÏÇ¨Ïùò ÌÉë',
        level: 10,
        floors: 20,
        currentFloor: 1,
        monsters: [],
        rewards: [],
        isUnlocked: false
      }
    ];

    // ÏÉÅÏ†ê ÏïÑÏù¥ÌÖú Ï¥àÍ∏∞Ìôî
    const shopItems: Item[] = [
      {
        id: 'sword1',
        name: 'Í∞ïÏ≤† Í≤Ä',
        type: 'weapon',
        rarity: 'common',
        level: 1,
        stats: { attack: 15 },
        price: 200,
        enhancement: 0
      },
      {
        id: 'armor1',
        name: 'Í∞ÄÏ£Ω Í∞ëÏò∑',
        type: 'armor',
        rarity: 'common',
        level: 1,
        stats: { defense: 10, hp: 20 },
        price: 150,
        enhancement: 0
      },
      {
        id: 'helmet1',
        name: 'Ï≤†Ï†ú Ìà¨Íµ¨',
        type: 'helmet',
        rarity: 'common',
        level: 1,
        stats: { defense: 5, hp: 15 },
        price: 100,
        enhancement: 0
      }
    ];

    setCharacter(prev => ({ ...prev, skills: initialSkills }));
    setDungeons(initialDungeons);
    setShop(shopItems);
    spawnMonster();
  };

  // Î™¨Ïä§ÌÑ∞ ÏÉùÏÑ±
  const spawnMonster = () => {
    const monsters = [
      { name: 'Ïä¨ÎùºÏûÑ', baseHp: 40, baseAttack: 8, baseDefense: 2, exp: 15, gold: 10 },
      { name: 'Í≥†Î∏îÎ¶∞', baseHp: 60, baseAttack: 12, baseDefense: 4, exp: 25, gold: 18 },
      { name: 'Ïò§ÌÅ¨', baseHp: 100, baseAttack: 18, baseDefense: 8, exp: 40, gold: 30 },
      { name: 'Ìä∏Î°§', baseHp: 150, baseAttack: 25, baseDefense: 12, exp: 60, gold: 45 },
      { name: 'ÎìúÎûòÍ≥§', baseHp: 300, baseAttack: 40, baseDefense: 20, exp: 120, gold: 100 }
    ];

    const randomMonster = monsters[Math.floor(Math.random() * monsters.length)];
    const levelMultiplier = 1 + (character.level - 1) * 0.3;

    const monster: Monster = {
      id: `monster_${Date.now()}`,
      name: randomMonster.name,
      level: Math.max(1, character.level + Math.floor(Math.random() * 3 - 1)),
      hp: Math.floor(randomMonster.baseHp * levelMultiplier),
      maxHp: Math.floor(randomMonster.baseHp * levelMultiplier),
      attack: Math.floor(randomMonster.baseAttack * levelMultiplier),
      defense: Math.floor(randomMonster.baseDefense * levelMultiplier),
      exp: Math.floor(randomMonster.exp * levelMultiplier),
      gold: Math.floor(randomMonster.gold * levelMultiplier),
      dropItems: generateDropItems(),
      isBoss: Math.random() < 0.1 // 10% Î≥¥Ïä§ ÌôïÎ•†
    };

    if (monster.isBoss) {
      monster.name = `Î≥¥Ïä§ ${monster.name}`;
      monster.hp *= 3;
      monster.maxHp *= 3;
      monster.attack *= 2;
      monster.defense *= 2;
      monster.exp *= 5;
      monster.gold *= 3;
    }

    setCurrentMonster(monster);
    addToCombatLog(`${monster.name} Lv.${monster.level}Ïù¥ ÎÇòÌÉÄÎÇ¨Îã§!`);
  };

  // ÎìúÎ°≠ ÏïÑÏù¥ÌÖú ÏÉùÏÑ±
  const generateDropItems = (): Item[] => {
    const dropChance = Math.random();
    if (dropChance < 0.1) { // 10% ÌôïÎ•†Î°ú ÏïÑÏù¥ÌÖú ÎìúÎ°≠
      const rarities: Array<'common' | 'rare' | 'epic' | 'legendary' | 'mythic'> = ['common', 'rare', 'epic', 'legendary', 'mythic'];
      const rarity = rarities[Math.floor(Math.random() * rarities.length)];

      return [{
        id: `drop_${Date.now()}`,
        name: generateItemName(rarity),
        type: ['weapon', 'armor', 'helmet', 'boots', 'ring', 'amulet'][Math.floor(Math.random() * 6)] as any,
        rarity,
        level: character.level,
        stats: generateItemStats(rarity),
        price: calculateItemPrice(rarity, character.level),
        enhancement: 0
      }];
    }
    return [];
  };

  const generateItemName = (rarity: string): string => {
    const prefixes = {
      common: ['ÎÇ°ÏùÄ', 'ÏùºÎ∞òÏ†ÅÏù∏', 'Í∏∞Î≥∏'],
      rare: ['ÎÇ†Ïπ¥Î°úÏö¥', 'ÌäºÌäºÌïú', 'ÎßàÎ≤ïÏùò'],
      epic: ['Ï†ÑÏÑ§Ï†ÅÏù∏', 'Í≥†ÎåÄÏùò', 'Ïã†ÏÑ±Ìïú'],
      legendary: ['Ï†ÑÏÑ§Ïùò', 'ÏòÅÏõÖÏùò', 'Î∂àÎ©∏Ïùò'],
      mythic: ['Ïã†ÌôîÏùò', 'Ï∞ΩÏ°∞Ïùò', 'Ï†àÎåÄÏùò']
    };

    const items = ['Í≤Ä', 'Í∞ëÏò∑', 'Ìà¨Íµ¨', 'Ïã†Î∞ú', 'Î∞òÏßÄ', 'Î™©Í±∏Ïù¥'];
    const prefix = prefixes[rarity as keyof typeof prefixes][Math.floor(Math.random() * 3)];
    const item = items[Math.floor(Math.random() * items.length)];

    return `${prefix} ${item}`;
  };

  const generateItemStats = (rarity: string) => {
    const multipliers = {
      common: 1,
      rare: 1.5,
      epic: 2.5,
      legendary: 4,
      mythic: 7
    };

    const base = multipliers[rarity as keyof typeof multipliers];
    return {
      attack: Math.floor(Math.random() * 20 * base),
      defense: Math.floor(Math.random() * 15 * base),
      hp: Math.floor(Math.random() * 50 * base),
      mp: Math.floor(Math.random() * 30 * base),
      speed: Math.floor(Math.random() * 10 * base),
      critRate: Math.floor(Math.random() * 10 * base),
      critDamage: Math.floor(Math.random() * 20 * base)
    };
  };

  const calculateItemPrice = (rarity: string, level: number): number => {
    const basePrice = {
      common: 50,
      rare: 150,
      epic: 500,
      legendary: 2000,
      mythic: 10000
    };

    return Math.floor(basePrice[rarity as keyof typeof basePrice] * (1 + level * 0.2));
  };

  // Ï†ÑÌà¨ ÏãúÏä§ÌÖú
  const attack = () => {
    if (!currentMonster) return;

    const damage = calculateDamage(character.attack, currentMonster.defense);
    const isCritical = Math.random() < 0.1 + (character.stats.luck * 0.01);
    const finalDamage = isCritical ? Math.floor(damage * 1.5) : damage;

    const newMonsterHp = Math.max(0, currentMonster.hp - finalDamage);
    setCurrentMonster(prev => prev ? { ...prev, hp: newMonsterHp } : null);

    addToCombatLog(`${character.name}Ïùò Í≥µÍ≤©! ${finalDamage}${isCritical ? ' (ÏπòÎ™ÖÌÉÄ!)' : ''} Îç∞ÎØ∏ÏßÄ!`);

    onAction('combat_attack', {
      user_id: currentUser.user_id,
      damage: finalDamage,
      is_critical: isCritical,
      monster_name: currentMonster.name,
      monster_level: currentMonster.level
    });

    if (newMonsterHp <= 0) {
      defeatMonster();
    } else {
      // Î™¨Ïä§ÌÑ∞ Î∞òÍ≤©
      setTimeout(() => monsterAttack(), 1000);
    }
  };

  const monsterAttack = () => {
    if (!currentMonster || currentMonster.hp <= 0) return;

    const damage = calculateDamage(currentMonster.attack, character.defense);
    const newHp = Math.max(0, character.hp - damage);

    setCharacter(prev => ({ ...prev, hp: newHp }));
    addToCombatLog(`${currentMonster.name}Ïùò Í≥µÍ≤©! ${damage} Îç∞ÎØ∏ÏßÄ!`);

    if (newHp <= 0) {
      addToCombatLog('Ìå®Î∞∞ÌñàÏäµÎãàÎã§...');
      setIsAutoFighting(false);
      // Î∂ÄÌôú (Í≤ΩÌóòÏπòÏôÄ Í≥®Îìú ÏùºÎ∂Ä ÏÜêÏã§)
      setTimeout(() => {
        setCharacter(prev => ({
          ...prev,
          hp: Math.floor(prev.maxHp * 0.5),
          exp: Math.max(0, prev.exp - Math.floor(prev.maxExp * 0.1)),
          gold: Math.max(0, prev.gold - Math.floor(prev.gold * 0.1))
        }));
        addToCombatLog('Î∂ÄÌôúÌñàÏäµÎãàÎã§!');
      }, 2000);
    }
  };

  const calculateDamage = (attack: number, defense: number): number => {
    const baseDamage = Math.max(1, attack - Math.floor(defense * 0.5));
    return Math.floor(baseDamage * (0.8 + Math.random() * 0.4));
  };

  const defeatMonster = () => {
    if (!currentMonster) return;

    const expGained = currentMonster.exp;
    const goldGained = currentMonster.gold;

    setCharacter(prev => {
      const newExp = prev.exp + expGained;
      const newGold = prev.gold + goldGained;
      let newLevel = prev.level;
      let newMaxExp = prev.maxExp;
      let newHp = prev.hp;
      let newMaxHp = prev.maxHp;
      let availablePoints = prev.stats.availablePoints;

      // Î†àÎ≤®ÏóÖ Ï≤¥ÌÅ¨
      if (newExp >= prev.maxExp) {
        newLevel += 1;
        newMaxExp = Math.floor(prev.maxExp * 1.5);
        newMaxHp += 20;
        newHp = newMaxHp; // Î†àÎ≤®ÏóÖ Ïãú Ï≤¥Î†• ÏôÑÏ†Ñ ÌöåÎ≥µ
        availablePoints += 5;

        addToCombatLog(`üéâ Î†àÎ≤®ÏóÖ! Lv.${newLevel}Ïù¥ ÎêòÏóàÏäµÎãàÎã§!`);

        onAction('level_up', {
          user_id: currentUser.user_id,
          new_level: newLevel,
          character_class: prev.class
        });
      }

      return {
        ...prev,
        exp: newExp >= prev.maxExp ? newExp - prev.maxExp : newExp,
        gold: newGold,
        level: newLevel,
        maxExp: newMaxExp,
        hp: newHp,
        maxHp: newMaxHp,
        stats: { ...prev.stats, availablePoints }
      };
    });

    // ÏïÑÏù¥ÌÖú ÎìúÎ°≠ Ï≤òÎ¶¨
    if (currentMonster.dropItems.length > 0) {
      setInventory(prev => [...prev, ...currentMonster.dropItems]);
      addToCombatLog(`‚ú® ${currentMonster.dropItems[0].name}ÏùÑ ÌöçÎìùÌñàÏäµÎãàÎã§!`);
    }

    addToCombatLog(`${currentMonster.name}Î•º Î¨ºÎ¶¨Ï≥§Îã§! EXP +${expGained}, Í≥®Îìú +${goldGained}`);

    onAction('monster_defeated', {
      user_id: currentUser.user_id,
      monster_name: currentMonster.name,
      monster_level: currentMonster.level,
      exp_gained: expGained,
      gold_gained: goldGained,
      items_dropped: currentMonster.dropItems.length
    });

    // ÏÉàÎ°úÏö¥ Î™¨Ïä§ÌÑ∞ ÏÉùÏÑ±
    setTimeout(() => spawnMonster(), 2000);
  };


  // ÏûêÎèô Ï†ÑÌà¨
  useEffect(() => {
    if (isAutoFighting && currentMonster && currentMonster.hp > 0 && character.hp > 0) {
      const autoAttackTimer = setTimeout(() => {
        attack();
      }, 2000);

      return () => clearTimeout(autoAttackTimer);
    }
  }, [isAutoFighting, currentMonster, character.hp]);

  // Ïä§ÌÇ¨ Ïø®Îã§Ïö¥ Î∞è MP ÌöåÎ≥µ
  useEffect(() => {
    const interval = setInterval(() => {
      setCharacter(prev => ({
        ...prev,
        mp: Math.min(prev.maxMp, prev.mp + 1),
        skills: prev.skills.map(skill => ({
          ...skill,
          currentCooldown: Math.max(0, skill.currentCooldown - 100)
        }))
      }));
    }, 100);

    return () => clearInterval(interval);
  }, []);

  const addToCombatLog = (message: string) => {
    setCombatLog(prev => {
      const newLog = [...prev, message];
      if (newLog.length > 50) newLog.shift();
      return newLog;
    });

    setTimeout(() => {
      if (logRef.current) {
        logRef.current.scrollTop = logRef.current.scrollHeight;
      }
    }, 0);
  };

  // Ïä§ÌÉØ Î∂ÑÎ∞∞
  const allocateStatPoint = (stat: keyof Omit<CharacterStats, 'availablePoints'>) => {
    if (character.stats.availablePoints <= 0) return;

    setCharacter(prev => ({
      ...prev,
      stats: {
        ...prev.stats,
        [stat]: prev.stats[stat] + 1,
        availablePoints: prev.stats.availablePoints - 1
      },
      // Ïä§ÌÉØÏóê Îî∞Î•∏ Îä•Î†•Ïπò ÏóÖÎç∞Ïù¥Ìä∏
      attack: stat === 'strength' ? prev.attack + 2 : prev.attack,
      defense: stat === 'vitality' ? prev.defense + 1 : prev.defense,
      maxHp: stat === 'vitality' ? prev.maxHp + 10 : prev.maxHp,
      maxMp: stat === 'intelligence' ? prev.maxMp + 5 : prev.maxMp,
      speed: stat === 'agility' ? prev.speed + 1 : prev.speed
    }));
  };

  // ÏïÑÏù¥ÌÖú Ïû•Ï∞©
  const equipItem = (item: Item) => {
    setCharacter(prev => {
      const newEquipment = { ...prev.equipment };
      newEquipment[item.type] = item;

      // Ïû•Ï∞©Ìïú ÏïÑÏù¥ÌÖúÏùò Ïä§ÌÉØ Ï†ÅÏö©
      let newAttack = prev.attack;
      let newDefense = prev.defense;
      let newMaxHp = prev.maxHp;
      let newMaxMp = prev.maxMp;
      let newSpeed = prev.speed;

      if (item.stats.attack) newAttack += item.stats.attack;
      if (item.stats.defense) newDefense += item.stats.defense;
      if (item.stats.hp) newMaxHp += item.stats.hp;
      if (item.stats.mp) newMaxMp += item.stats.mp;
      if (item.stats.speed) newSpeed += item.stats.speed;

      return {
        ...prev,
        equipment: newEquipment,
        attack: newAttack,
        defense: newDefense,
        maxHp: newMaxHp,
        maxMp: newMaxMp,
        speed: newSpeed
      };
    });

    // Ïù∏Î≤§ÌÜ†Î¶¨ÏóêÏÑú ÏïÑÏù¥ÌÖú Ï†úÍ±∞
    setInventory(prev => prev.filter(invItem => invItem.id !== item.id));
    addToCombatLog(`${item.name}ÏùÑ Ïû•Ï∞©ÌñàÏäµÎãàÎã§!`);
  };

  // ÏïÑÏù¥ÌÖú ÌåêÎß§
  const sellItem = (item: Item) => {
    const sellPrice = Math.floor(item.price * 0.7);
    setCharacter(prev => ({ ...prev, gold: prev.gold + sellPrice }));
    setInventory(prev => prev.filter(invItem => invItem.id !== item.id));
    addToCombatLog(`${item.name}ÏùÑ ${sellPrice} Í≥®ÎìúÏóê ÌåêÎß§ÌñàÏäµÎãàÎã§!`);
  };

  // ÏïÑÏù¥ÌÖú Íµ¨Îß§
  const buyItem = (item: Item) => {
    if (character.gold < item.price) return;

    setCharacter(prev => ({ ...prev, gold: prev.gold - item.price }));
    setInventory(prev => [...prev, { ...item, id: `${item.id}_${Date.now()}` }]);
    addToCombatLog(`${item.name}ÏùÑ Íµ¨Îß§ÌñàÏäµÎãàÎã§!`);
  };

  const getRarityColor = (rarity: string) => {
    const colors = {
      common: '#9ca3af',
      rare: '#3b82f6',
      epic: '#8b5cf6',
      legendary: '#f59e0b',
      mythic: '#ef4444'
    };
    return colors[rarity as keyof typeof colors] || '#9ca3af';
  };

  const renderCombatTab = () => (
    <div style={{ padding: '20px', background: '#1f2937', minHeight: '600px' }}>
      {/* Ï∫êÎ¶≠ÌÑ∞ Ï†ïÎ≥¥ */}
      <div style={{
        background: 'linear-gradient(135deg, #4f46e5 0%, #7c3aed 100%)',
        borderRadius: '15px',
        padding: '20px',
        marginBottom: '20px',
        color: 'white'
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '15px' }}>
          <div>
            <h2 style={{ margin: '0 0 5px 0', fontSize: '24px' }}>{character.name}</h2>
            <p style={{ margin: '0', opacity: 0.9 }}>Lv.{character.level} {character.class}</p>
          </div>
          <div style={{ textAlign: 'right' }}>
            <div style={{ fontSize: '20px', fontWeight: 'bold' }}>üí∞ {character.gold.toLocaleString()}</div>
          </div>
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '15px' }}>
          <div>
            <div style={{ marginBottom: '8px' }}>
              <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '14px' }}>
                <span>HP</span>
                <span>{character.hp}/{character.maxHp}</span>
              </div>
              <div style={{
                background: 'rgba(255,255,255,0.2)',
                borderRadius: '10px',
                height: '8px',
                overflow: 'hidden'
