import React, { useState, useEffect } from 'react';
import { BarChart, TrendingUp, Users, PartyPopper, X, Trophy, Star, Grid3X3, MousePointer, Target } from 'lucide-react';
import { useLanguage } from '../../contexts/LanguageContext';
import { useTheme } from '../../contexts/ThemeContext';

interface Step4Props {
  onComplete: () => void;
  appData: {
    appId: string;
    dataUrl: string;
    selectedVersion: 'saas' | 'private' | null;
  };
}

export const Step4AnalyticsStart: React.FC<Step4Props> = ({
  onComplete,
  appData
}) => {
  const { t } = useLanguage();
  const { colors } = useTheme();
  const [understood, setUnderstood] = useState(false);
  const [celebrationShown, setCelebrationShown] = useState(false);
  const [hasCompleted, setHasCompleted] = useState(false);

  // í…Œë§ˆ ìƒ‰ìƒ ê°€ì ¸ì˜¤ê¸°
  const theme = colors;

  useEffect(() => {
    if (understood && !hasCompleted) {
      onComplete();
      setCelebrationShown(true);
      setHasCompleted(true);

      // íŒì—…ì´ í‘œì‹œë  ë•Œ í™”ë©´ ì¤‘ì•™ìœ¼ë¡œ ë¶€ë“œëŸ½ê²Œ ìŠ¤í¬ë¡¤
      setTimeout(() => {
        const windowHeight = window.innerHeight;
        const documentHeight = document.documentElement.scrollHeight;
        const scrollPosition = Math.max(0, (documentHeight - windowHeight) / 2);

        window.scrollTo({
          top: scrollPosition,
          left: 0,
          behavior: 'smooth'
        });
      }, 100);
    }
  }, [understood, hasCompleted, onComplete]);

  // Thinking Engine íŠœí† ë¦¬ì–¼ ë‹¨ê³„ë“¤
  const tutorialSteps = [
    {
      step: "1",
      title: t('step4.tutorial.step1.title') || "Thinking Engine ì ‘ì†",
      desc: t('step4.tutorial.step1.desc') || "ë¨¼ì € Thinking Engine í”Œë«í¼ì— ì ‘ì†í•˜ì„¸ìš”",
      icon: "ğŸŒ",
      color: "#3b82f6",
      details: [
        "https://te-web-naver.thinkingdata.kr/ ì ‘ì†",
        "ë¡œê·¸ì¸ í›„ ë©”ì¸ ëŒ€ì‹œë³´ë“œ í™•ì¸",
        "í”„ë¡œì íŠ¸ ì„ íƒ ë° í™˜ê²½ ì„¤ì •"
      ]
    },
    {
      step: "2", 
      title: t('step4.tutorial.step2.title') || "ë”ë³´ê¸° ë©”ë‰´ ì°¾ê¸°",
      desc: t('step4.tutorial.step2.desc') || "í™”ë©´ ìš°ì¸¡ ìƒë‹¨ì˜ 9ê°œ ì (3x3 ê²©ì) ë²„íŠ¼ì„ í´ë¦­í•˜ì„¸ìš”",
      icon: "âš™ï¸",
      color: "#8b5cf6",
      details: [
        "í™”ë©´ ìš°ì¸¡ ìƒë‹¨ ëª¨ì„œë¦¬ í™•ì¸",
        "9ê°œì˜ ì ìœ¼ë¡œ êµ¬ì„±ëœ ê²©ì ì•„ì´ì½˜ ì°¾ê¸°",
        "í´ë¦­í•˜ì—¬ ì¶”ê°€ ê¸°ëŠ¥ ë©”ë‰´ ì—´ê¸°"
      ]
    },
    {
      step: "3",
      title: t('step4.tutorial.step3.title') || "í…œí”Œë¦¿ ì„¼í„° ì´ìš©",
      desc: t('step4.tutorial.step3.desc') || "í…œí”Œë¦¿ ì„¼í„°ì—ì„œ ë¯¸ë¦¬ ì œì‘ëœ ëŒ€ì‹œë³´ë“œë¥¼ í™œìš©í•˜ì„¸ìš”",
      icon: "ğŸ“Š",
      color: "#06b6d4",
      details: [
        "ë”ë³´ê¸° ë©”ë‰´ì—ì„œ 'í…œí”Œë¦¿ ì„¼í„°' ì„ íƒ",
        "ë‹¤ì–‘í•œ ëŒ€ì‹œë³´ë“œ í…œí”Œë¦¿ ë‘˜ëŸ¬ë³´ê¸°",
        "í•„ìš”í•œ í…œí”Œë¦¿ ì„ íƒí•˜ì—¬ ì ìš©"
      ]
    },
    {
      step: "4",
      title: t('step4.tutorial.step4.title') || "ì´ë²¤íŠ¸ ë¶„ì„ìœ¼ë¡œ ì§€í‘œ ìƒì„±",
      desc: t('step4.tutorial.step4.desc') || "ì´ë²¤íŠ¸ ë¶„ì„ ê¸°ëŠ¥ìœ¼ë¡œ DAU, ê²°ì œê¸ˆì•¡ ë“± í•µì‹¬ ì§€í‘œë¥¼ ë§Œë“¤ì–´ë³´ì„¸ìš”",
      icon: "ğŸ“ˆ",
      color: "#10b981",
      details: [
        "ë¶„ì„ ë©”ë‰´ì—ì„œ 'ì´ë²¤íŠ¸ ë¶„ì„' ì„ íƒ",
        "DAU (Daily Active Users) ì§€í‘œ ì„¤ì •",
        "ê²°ì œê¸ˆì•¡, ë§¤ì¶œ ë“± ë¹„ì¦ˆë‹ˆìŠ¤ ì§€í‘œ ìƒì„±"
      ]
    }
  ];

  return (
    <div>
      <div style={{ textAlign: 'center', marginBottom: '40px' }}>
        <div style={{
          width: '80px',
