import React, { useState, useEffect } from 'react';
import { PartyPopper, X, Trophy, Star } from 'lucide-react';
import { useLanguage } from '../../contexts/LanguageContext';
import { useTheme } from '../../contexts/ThemeContext';

interface Step4Props {
  onComplete: () => void;
  appData: {
    appId: string;
    dataUrl: string;
    selectedVersion: 'saas' | 'private' | null;
  };
}

export const Step4AnalyticsStart: React.FC<Step4Props> = ({
  onComplete,
  appData
}) => {
  const { t } = useLanguage();
  const { colors } = useTheme();
  const [understood, setUnderstood] = useState(false);
  const [celebrationShown, setCelebrationShown] = useState(false);
  const [hasCompleted, setHasCompleted] = useState(false);

  // 테마 색상 가져오기
  const theme = colors;

  useEffect(() => {
    if (understood && !hasCompleted) {
      onComplete();
      setCelebrationShown(true);
      setHasCompleted(true);

      // 팝업이 표시될 때 화면 중앙으로 부드럽게 스크롤
      setTimeout(() => {
        const windowHeight = window.innerHeight;
        const documentHeight = document.documentElement.scrollHeight;
        const scrollPosition = Math.max(0, (documentHeight - windowHeight) / 2);

        window.scrollTo({
          top: scrollPosition,
          left: 0,
          behavior: 'smooth'
        });
      }, 100);
    }
  }, [understood, hasCompleted, onComplete]);

  // Thinking Engine 튜토리얼 단계들
  const tutorialSteps = [
    {
      step: "1",
      title: t('step4.tutorial.step1.title') || "Thinking Engine 접속",
      desc: t('step4.tutorial.step1.desc') || "먼저 Thinking Engine 플랫폼에 접속하세요",
      icon: "🌐",
      color: "#3b82f6",
      details: [
        "https://te-web-naver.thinkingdata.kr/ 접속",
        "로그인 후 메인 대시보드 확인",
        "프로젝트 선택 및 환경 설정"
      ]
    },
    {
      step: "2", 
      title: t('step4.tutorial.step2.title') || "더보기 메뉴 찾기",
      desc: t('step4.tutorial.step2.desc') || "화면 우측 상단의 9개 점(3x3 격자) 버튼을 클릭하세요",
      icon: "⚙️",
      color: "#8b5cf6",
      details: [
        "화면 우측 상단 모서리 확인",
        "9개의 점으로 구성된 격자 아이콘 찾기",
        "클릭하여 추가 기능 메뉴 열기"
      ]
    },
    {
      step: "3",
      title: t('step4.tutorial.step3.title') || "템플릿 센터 이용",
      desc: t('step4.tutorial.step3.desc') || "템플릿 센터에서 미리 제작된 대시보드를 활용하세요",
      icon: "📊",
      color: "#06b6d4",
      details: [
        "더보기 메뉴에서 '템플릿 센터' 선택",
        "다양한 대시보드 템플릿 둘러보기",
        "필요한 템플릿 선택하여 적용"
      ]
    },
    {
      step: "4",
      title: t('step4.tutorial.step4.title') || "이벤트 분석으로 지표 생성",
