            <button
              onClick={handleNext}
              disabled={!canGoNext}
              style={{
                display: "flex",
                alignItems: "center",
                gap: "8px",
                padding: "12px 24px",
                background: canGoNext
                  ? "linear-gradient(135deg, #8b5cf6, #3b82f6)"
                  : "rgba(139, 92, 246, 0.1)",
                border: "none",
                borderRadius: "12px",
                color: canGoNext ? "white" : theme.textSecondary,
                fontSize: "14px",
                fontWeight: "600",
                cursor: canGoNext ? "pointer" : "not-allowed",
                transition: "all 0.3s ease",
                minWidth: "120px",
                boxShadow: canGoNext
                  ? "0 4px 15px rgba(139, 92, 246, 0.4)"
                  : "none",
              }}
            >
              다음 →
            </button>
          )}
        </div>
      </div>
    </Layout>
  );
};
