          ];
        case 'cave': // ê³ ë¸”ë¦° ë™êµ´
          return [
            { name: 'ê³ ë¸”ë¦° ì „ì‚¬', baseHp: 60, baseAttack: 14, baseDefense: 5 },
            { name: 'ê³ ë¸”ë¦° ê¶ìˆ˜', baseHp: 45, baseAttack: 18, baseDefense: 3 },
            { name: 'ê³ ë¸”ë¦° ë§ˆë²•ì‚¬', baseHp: 50, baseAttack: 20, baseDefense: 4 },
            { name: 'ê³ ë¸”ë¦° ìš°ë‘ë¨¸ë¦¬', baseHp: 180, baseAttack: 30, baseDefense: 15 },
