            { name: 'ìŠ¤ì¼ˆë ˆí†¤ ë©”ì´ì§€', baseHp: 70, baseAttack: 25, baseDefense: 8 },
            { name: 'í”Œë ˆì„ ì—˜ë¦¬ë©˜íƒˆ', baseHp: 110, baseAttack: 28, baseDefense: 10 },
            { name: 'ë¦¬ì¹˜', baseHp: 250, baseAttack: 40, baseDefense: 20 },
            { name: 'ê³ ëŒ€ ë§ˆë²•ì‚¬', baseHp: 500, baseAttack: 60, baseDefense: 35 }
          ];
        default:
          return [
            { name: 'ìŠ¬ë¼ì„', baseHp: 30, baseAttack: 8, baseDefense: 2 },
            { name: 'ê³ ë¸”ë¦°', baseHp: 50, baseAttack: 12, baseDefense: 4 },
            { name: 'ì˜¤í¬', baseHp: 100, baseAttack: 18, baseDefense: 8 },
            { name: 'íŠ¸ë¡¤', baseHp: 150, baseAttack: 25, baseDefense: 12 },
