    }

    // ì¸ë²¤í† ë¦¬ì—ì„œ ë¬¼ì•½ ì œê±° ë˜ëŠ” ìˆ˜ëŸ‰ ê°ì†Œ
    setInventory(prev => {
      const itemIndex = prev.findIndex(invItem => invItem.id === item.id);
      if (itemIndex !== -1) {
