import React, { useState, useEffect } from 'react';
import { Play, Star, Trophy, Zap, Users, Code, Settings, ChevronRight, Mail, Phone, Video, Shield, BookOpen, HelpCircle, Target, Sparkles, Crown, Sword, Search, User, Home, Gamepad2, BarChart3 } from 'lucide-react';
import { Button } from '../components/Button';

export const HomePage: React.FC = () => {
  const [currentTime, setCurrentTime] = useState(new Date());
  const [focusedCard, setFocusedCard] = useState(0);
  const [screenSize, setScreenSize] = useState({
    width: window.innerWidth,
    height: window.innerHeight
  });

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);

    const handleResize = () => {
      setScreenSize({
        width: window.innerWidth,
        height: window.innerHeight
      });
    };

    window.addEventListener('resize', handleResize);
    return () => {
      clearInterval(timer);
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const navigateToQuickStart = () => {
    window.location.href = '/quickstart';
  };

  // Î∞òÏùëÌòï ÏÑ§Ï†ï
  const isMobile = screenSize.width < 768;
  const isTablet = screenSize.width >= 768 && screenSize.width < 1200;
  const isDesktop = screenSize.width >= 1200;

  // Í≤åÏûÑ Ïπ¥ÎìúÎì§ Îç∞Ïù¥ÌÑ∞
  const gameCards = [
    {
      id: 0,
      title: "Îπ†Î•∏ ÏãúÏûë Í∞ÄÏù¥Îìú",
      subtitle: "Îç∞Ïù¥ÌÑ∞ Î∂ÑÏÑùÏùò Í∏∞Ï¥àÎ•º Î∞∞ÏõåÎ≥¥ÏÑ∏Ïöî",
      image: "üöÄ",
      featured: true,
      category: "Quick Start",
      onClick: navigateToQuickStart
    },
    {
      id: 1,
      title: "ThinkingDataÎûÄ?",
      subtitle: "ÌîåÎû´Ìèº ÏÜåÍ∞úÏôÄ ÌïµÏã¨ Í∏∞Îä•",
      image: "üìä",
      featured: false,
      category: "Learning"
    },
    {
      id: 2,
      title: "ÏÇ¨Ïö©Ïûê Í∞ÄÏù¥Îìú", 
      subtitle: "Í∏∞Î≥∏ ÏÇ¨Ïö©Î≤ïÎ∂ÄÌÑ∞ Í≥†Í∏â Í∏∞Îä•ÍπåÏßÄ",
      image: "üë•",
      featured: false,
      category: "Guide"
    },
    {
      id: 3,
      title: "Í∞úÎ∞úÏûê Î¨∏ÏÑú",
      subtitle: "API ÌÜµÌï© Î∞è SDK ÏÇ¨Ïö©Î≤ï",
      image: "üíª",
      featured: false,
      category: "Development"
    },
    {
      id: 4,
      title: "Í¥ÄÎ¶¨Ïûê ÎèÑÍµ¨",
      subtitle: "Í≥†Í∏â ÏÑ§Ï†ï Î∞è Í¥ÄÎ¶¨ Í∏∞Îä•",
      image: "‚öôÔ∏è",
      featured: false,
      category: "Admin"
    },
    {
      id: 5,
      title: "Í≥†Í∞ù ÏßÄÏõê",
      subtitle: "Í∏∞Ïà† ÏßÄÏõê Î∞è Î¨∏Ïùò",
      image: "üí¨",
      featured: false,
      category: "Support"
    }
  ];

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #0a1428 0%, #1a2332 50%, #2d3748 100%)',
      color: 'white',
      fontFamily: 'Segoe UI, system-ui, sans-serif',
      overflow: 'hidden'
    }}>
      {/* Main Container */}
      <div style={{
        maxWidth: isDesktop ? '1400px' : isTablet ? '900px' : '100%',
        margin: '0 auto',
        display: 'grid',
        gridTemplateColumns: isDesktop ? '1fr 2fr 1fr' : isTablet ? '1fr 2fr' : '1fr',
        gridTemplateRows: 'auto auto auto',
        gap: isMobile ? '16px' : '24px',
        gridTemplateAreas: isDesktop 
          ? '"header header header" "sidebar main aside" "footer footer footer"'
          : isTablet 
            ? '"header header" "main sidebar" "footer footer"'
            : '"header" "main" "sidebar" "footer"'
      }}>

        {/* Header */}
        <div style={{
          gridArea: 'header',
          background: 'linear-gradient(135deg, rgba(255,255,255,0.1) 0%, rgba(255,255,255,0.05) 100%)',
          backdropFilter: 'blur(20px)',
          border: '1px solid rgba(255,255,255,0.1)',
          borderRadius: '24px',
          padding: isMobile ? '20px' : '32px',
          boxShadow: '0 8px 32px rgba(0,0,0,0.3)',
          position: 'relative',
          overflow: 'hidden'
        }}>
          {/* Animated Background Elements */}
          <div style={{
            position: 'absolute',
            top: '-50%',
            right: '-10%',
            width: '200px',
            height: '200px',
            background: 'linear-gradient(45deg, #667eea, #764ba2)',
            borderRadius: '50%',
            opacity: 0.1,
            animation: 'float 6s ease-in-out infinite'
          }} />

          <div style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'space-between',
            flexWrap: 'wrap',
            gap: '16px'
          }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '16px' }}>
              <div style={{
