import React, { useRef, useEffect, useState, useCallback } from 'react';

interface GameProps {
  onAction: (eventType: string, data: any) => void;
  isSimulating: boolean;
  currentAction: string | null;
  playerStats: any;
  setPlayerStats: (stats: any) => void;
  currentUser: any;
}

interface Tower {
  id: string;
  x: number;
  y: number;
  type: 'archer' | 'cannon' | 'magic' | 'ice' | 'lightning';
  level: number;
  damage: number;
  range: number;
  attackSpeed: number;
  lastAttack: number;
  cost: number;
  kills: number;
  upgradeCost: number;
}

interface Enemy {
  id: string;
  x: number;
  y: number;
  health: number;
  maxHealth: number;
  speed: number;
  type: 'goblin' | 'orc' | 'troll' | 'dragon' | 'demon_lord' | 'ice_giant' | 'fire_elemental';
  reward: number;
  pathIndex: number;
  alive: boolean;
  isBoss: boolean;
  armor: number;
}

interface GameState {
  stage: number;
  wave: number;
  lives: number;
  gold: number;
  score: number;
  experience: number;
  playerLevel: number;
  waveProgress: number;
  gameStatus: 'playing' | 'paused' | 'gameOver' | 'victory' | 'shop' | 'stageComplete';
  selectedTowerType: Tower['type'] | null;
  selectedTower: Tower | null;
  shopItems: ShopItem[];
  currentMap: number;
}

interface ShopItem {
  id: string;
  name: string;
  description: string;
  cost: number;
  type: 'upgrade' | 'power' | 'tower';
  effect: any;
}

export const AdvancedTowerDefense: React.FC<GameProps> = ({
  onAction,
  playerStats,
  setPlayerStats,
  currentUser
}) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const gameLoopRef = useRef<number>();

  // Different maps for each stage
  const stageMaps = {
    1: [ // Simple path
      { x: 0, y: 200 }, { x: 150, y: 200 }, { x: 150, y: 100 }, 
      { x: 300, y: 100 }, { x: 300, y: 300 }, { x: 450, y: 300 }, 
      { x: 450, y: 150 }, { x: 600, y: 150 }
    ],
    2: [ // Zigzag path
      { x: 0, y: 100 }, { x: 100, y: 100 }, { x: 100, y: 250 },
      { x: 200, y: 250 }, { x: 200, y: 50 }, { x: 350, y: 50 },
      { x: 350, y: 200 }, { x: 500, y: 200 }, { x: 500, y: 350 }, { x: 600, y: 350 }
    ],
    3: [ // Spiral path
      { x: 0, y: 200 }, { x: 80, y: 200 }, { x: 80, y: 120 },
      { x: 200, y: 120 }, { x: 200, y: 280 }, { x: 320, y: 280 },
      { x: 320, y: 80 }, { x: 480, y: 80 }, { x: 480, y: 320 }, { x: 600, y: 320 }
    ],
    4: [ // Double path
      { x: 0, y: 150 }, { x: 150, y: 150 }, { x: 150, y: 100 },
      { x: 300, y: 100 }, { x: 300, y: 200 }, { x: 450, y: 200 }, { x: 600, y: 200 }
    ],
    5: [ // Boss arena
      { x: 0, y: 200 }, { x: 100, y: 200 }, { x: 200, y: 200 },
      { x: 300, y: 200 }, { x: 400, y: 200 }, { x: 500, y: 200 }, { x: 600, y: 200 }
    ]
  };

  const [gameState, setGameState] = useState<GameState>({
    stage: 1,
    wave: 1,
    lives: 20,
    gold: 600,
    score: 0,
    experience: 0,
    playerLevel: 1,
    waveProgress: 0,
    gameStatus: 'playing',
    selectedTowerType: null,
    selectedTower: null,
    shopItems: [],
    currentMap: 1,
  });

  const [towers, setTowers] = useState<Tower[]>([]);
  const [enemies, setEnemies] = useState<Enemy[]>([]);
  const [frameCount, setFrameCount] = useState(0);
  const [nextEnemySpawn, setNextEnemySpawn] = useState(0);

  // Enhanced tower types
  const towerTypes = {
    archer: { cost: 80, damage: 20, range: 120, speed: 25, color: '#8B4513' },
    cannon: { cost: 150, damage: 50, range: 90, speed: 50, color: '#696969' },
    magic: { cost: 220, damage: 35, range: 130, speed: 35, color: '#9400D3' },
    ice: { cost: 180, damage: 15, range: 100, speed: 30, color: '#00BFFF' },
    lightning: { cost: 300, damage: 80, range: 150, speed: 70, color: '#FFD700' }
  };

  // Stage-specific enemies
  const stageEnemies = {
    1: [{ type: 'goblin', count: 8 }],
    2: [{ type: 'goblin', count: 6 }, { type: 'orc', count: 4 }],
    3: [{ type: 'orc', count: 5 }, { type: 'troll', count: 3 }],
    4: [{ type: 'troll', count: 4 }, { type: 'dragon', count: 2 }],
    5: [{ type: 'dragon', count: 3 }, { type: 'demon_lord', count: 1 }] // Boss stage
  };

  const enemyTypes = {
    goblin: { health: 60, speed: 2.5, reward: 12, color: '#228B22', armor: 0 },
    orc: { health: 120, speed: 2, reward: 20, color: '#8B4513', armor: 2 },
    troll: { health: 250, speed: 1.5, reward: 35, color: '#2F4F4F', armor: 5 },
    dragon: { health: 400, speed: 1.2, reward: 60, color: '#DC143C', armor: 8 },
    demon_lord: { health: 1000, speed: 0.8, reward: 200, color: '#8B0000', armor: 15 }, // Boss
    ice_giant: { health: 600, speed: 1, reward: 80, color: '#4682B4', armor: 10 },
    fire_elemental: { health: 300, speed: 2.2, reward: 45, color: '#FF4500', armor: 3 }
  };

  // Generate shop items
  const generateShopItems = (): ShopItem[] => {
    return [
      {
        id: 'damage_boost',
        name: 'ê³µê²©ë ¥ ê°•í™”',
        description: 'ëª¨ë“  íƒ€ì›Œ ë°ë¯¸ì§€ +20%',
        cost: 200,
        type: 'upgrade',
        effect: { type: 'damage', value: 1.2 }
      },
      {
        id: 'range_boost',
        name: 'ì‚¬ê±°ë¦¬ í™•ìž¥',
        description: 'ëª¨ë“  íƒ€ì›Œ ì‚¬ê±°ë¦¬ +25%',
        cost: 180,
        type: 'upgrade',
        effect: { type: 'range', value: 1.25 }
      },
      {
        id: 'gold_boost',
        name: 'ê³¨ë“œ ë°°ìœ¨',
        description: 'ì  ì²˜ì¹˜ ê³¨ë“œ +50%',
        cost: 150,
        type: 'power',
        effect: { type: 'gold_multiplier', value: 1.5 }
      },
      {
        id: 'lightning_tower',
        name: 'ë¼ì´íŠ¸ë‹ íƒ€ì›Œ',
        description: 'ê°•ë ¥í•œ ì „ê¸° ê³µê²©',
        cost: 300,
        type: 'tower',
        effect: { type: 'unlock_tower', tower: 'lightning' }
      }
    ];
  };

  // Initialize shop
  useEffect(() => {
    setGameState(prev => ({ ...prev, shopItems: generateShopItems() }));
  }, []);

  const handleKeyDown = useCallback((e: KeyboardEvent) => {
    if (['1', '2', '3', '4', '5', ' ', 'Enter', 'Escape', 's', 'u'].includes(e.key.toLowerCase())) {
      e.preventDefault();
      e.stopPropagation();

      switch(e.key.toLowerCase()) {
        case '1':
          setGameState(prev => ({ ...prev, selectedTowerType: 'archer', selectedTower: null }));
          break;
        case '2':
          setGameState(prev => ({ ...prev, selectedTowerType: 'cannon', selectedTower: null }));
          break;
        case '3':
          setGameState(prev => ({ ...prev, selectedTowerType: 'magic', selectedTower: null }));
          break;
        case '4':
          setGameState(prev => ({ ...prev, selectedTowerType: 'ice', selectedTower: null }));
          break;
        case '5':
          if (gameState.playerLevel >= 3) {
            setGameState(prev => ({ ...prev, selectedTowerType: 'lightning', selectedTower: null }));
          }
          break;
        case ' ':
          if (gameState.gameStatus === 'playing') {
            startNextWave();
          }
          break;
        case 's':
          if (gameState.gameStatus === 'stageComplete') {
            setGameState(prev => ({ ...prev, gameStatus: 'shop' }));
          }
          break;
        case 'u':
          if (gameState.selectedTower) {
            upgradeTower(gameState.selectedTower);
          }
          break;
        case 'escape':
          setGameState(prev => ({ 
            ...prev, 
            selectedTowerType: null, 
            selectedTower: null,
            gameStatus: gameState.gameStatus === 'shop' ? 'playing' : prev.gameStatus
          }));
          break;
      }
    }
  }, [gameState.playerLevel, gameState.gameStatus, gameState.selectedTower]);

  const handleCanvasClick = useCallback((e: React.MouseEvent<HTMLCanvasElement>) => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const rect = canvas.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    // Check if clicking on shop items
    if (gameState.gameStatus === 'shop') {
      handleShopClick(x, y);
      return;
    }

    // Check if clicking on existing tower
    const clickedTower = towers.find(tower => {
      const distance = Math.sqrt((x - tower.x) ** 2 + (y - tower.y) ** 2);
      return distance < 25;
    });

    if (clickedTower) {
      setGameState(prev => ({ ...prev, selectedTower: clickedTower, selectedTowerType: null }));
      return;
    }

    // Place new tower
    if (gameState.selectedTowerType && canPlaceTower(x, y)) {
      placeTower(x, y, gameState.selectedTowerType);
    }
  }, [gameState.gameStatus, gameState.selectedTowerType, towers]);

  const handleShopClick = (x: number, y: number) => {
    gameState.shopItems.forEach((item, index) => {
      const itemY = 100 + index * 60;
      if (x >= 50 && x <= 550 && y >= itemY && y <= itemY + 50) {
        purchaseShopItem(item);
      }
    });
  };

  const purchaseShopItem = (item: ShopItem) => {
    if (gameState.gold < item.cost) return;

    setGameState(prev => ({ ...prev, gold: prev.gold - item.cost }));

    // Apply item effect
    switch (item.effect.type) {
      case 'damage':
        setTowers(prevTowers => prevTowers.map(tower => ({
          ...tower,
          damage: Math.round(tower.damage * item.effect.value)
        })));
        break;
      case 'range':
        setTowers(prevTowers => prevTowers.map(tower => ({
          ...tower,
          range: Math.round(tower.range * item.effect.value)
        })));
        break;
    }

    onAction('shop_purchase', {
      item_name: item.name,
      item_cost: item.cost,
      remaining_gold: gameState.gold - item.cost,
      stage: gameState.stage,
      player_level: gameState.playerLevel
    });
  };

  const canPlaceTower = (x: number, y: number): boolean => {
    const currentPath = stageMaps[gameState.currentMap as keyof typeof stageMaps];

    // Check distance from path
    for (const point of currentPath) {
      const distance = Math.sqrt((x - point.x) ** 2 + (y - point.y) ** 2);
      if (distance < 45) return false;
    }

    // Check distance from other towers
    for (const tower of towers) {
      const distance = Math.sqrt((x - tower.x) ** 2 + (y - tower.y) ** 2);
      if (distance < 60) return false;
    }

    return x > 30 && x < 570 && y > 70 && y < 330;
  };

  const placeTower = (x: number, y: number, type: Tower['type']) => {
    const towerConfig = towerTypes[type];

    if (gameState.gold < towerConfig.cost) return;

    const newTower: Tower = {
      id: `tower_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`,
      x, y, type,
      level: 1,
      damage: towerConfig.damage,
      range: towerConfig.range,
      attackSpeed: towerConfig.speed,
      lastAttack: 0,
      cost: towerConfig.cost,
      kills: 0,
      upgradeCost: Math.round(towerConfig.cost * 0.6),
    };

    setTowers(prev => [...prev, newTower]);
    setGameState(prev => ({ 
      ...prev, 
      gold: prev.gold - towerConfig.cost,
      selectedTowerType: null 
    }));

    // Only log tower placement - key strategic decision
    onAction('tower_placed', {
      tower_type: type,
      position_x: x,
      position_y: y,
      cost: towerConfig.cost,
      remaining_gold: gameState.gold - towerConfig.cost,
      stage: gameState.stage,
      wave: gameState.wave
    });
  };

  const upgradeTower = (tower: Tower) => {
    if (gameState.gold < tower.upgradeCost) return;

    setTowers(prevTowers => prevTowers.map(t => 
      t.id === tower.id ? {
        ...t,
        level: t.level + 1,
        damage: Math.round(t.damage * 1.4),
        range: Math.round(t.range * 1.1),
        upgradeCost: Math.round(t.upgradeCost * 1.5)
      } : t
    ));

    setGameState(prev => ({ 
      ...prev, 
      gold: prev.gold - tower.upgradeCost,
      selectedTower: null 
    }));

    onAction('tower_upgraded', {
      tower_type: tower.type,
      new_level: tower.level + 1,
      upgrade_cost: tower.upgradeCost,
      stage: gameState.stage
    });
  };

  const startNextWave = () => {
    if (gameState.waveProgress > 0) return;

    setGameState(prev => ({ ...prev, waveProgress: 1 }));
    setNextEnemySpawn(frameCount + 60);

    // Log wave start - key gameplay event
    onAction('wave_started', {
      stage: gameState.stage,
      wave: gameState.wave,
      player_level: gameState.playerLevel,
      towers_count: towers.length,
      gold: gameState.gold
    });
  };

  const spawnEnemy = () => {
    const stageConfig = stageEnemies[gameState.stage as keyof typeof stageEnemies];
    if (!stageConfig) return;

    const enemyPool: string[] = [];
    stageConfig.forEach(config => {
      for (let i = 0; i < config.count; i++) {
        enemyPool.push(config.type);
      }
    });

    const enemyType = enemyPool[Math.floor(Math.random() * enemyPool.length)] as Enemy['type'];
    const enemyConfig = enemyTypes[enemyType];
    const currentPath = stageMaps[gameState.currentMap as keyof typeof stageMaps];

    const newEnemy: Enemy = {
      id: `enemy_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`,
      x: currentPath[0].x,
      y: currentPath[0].y,
      health: enemyConfig.health + (gameState.stage - 1) * 30,
      maxHealth: enemyConfig.health + (gameState.stage - 1) * 30,
      speed: enemyConfig.speed,
      type: enemyType,
      reward: enemyConfig.reward + gameState.stage * 3,
      pathIndex: 0,
      alive: true,
      isBoss: enemyType === 'demon_lord',
      armor: enemyConfig.armor,
    };

    setEnemies(prev => [...prev, newEnemy]);
  };

  const updateEnemies = useCallback(() => {
    const currentPath = stageMaps[gameState.currentMap as keyof typeof stageMaps];

    setEnemies(prev => prev.map(enemy => {
      if (!enemy.alive) return enemy;

      const currentTarget = currentPath[enemy.pathIndex + 1];
      if (currentTarget) {
        const dx = currentTarget.x - enemy.x;
        const dy = currentTarget.y - enemy.y;
        const distance = Math.sqrt(dx * dx + dy * dy);

        if (distance < 5) {
          enemy.pathIndex++;
          if (enemy.pathIndex >= currentPath.length - 1) {
            // Enemy reached end - critical event
            const livesLost = enemy.isBoss ? 5 : 1;
            setGameState(prevState => ({ 
              ...prevState, 
              lives: prevState.lives - livesLost 
            }));

            onAction('life_lost', {
              enemy_type: enemy.type,
              is_boss: enemy.isBoss,
              lives_lost: livesLost,
              remaining_lives: gameState.lives - livesLost,
              stage: gameState.stage
            });

            return { ...enemy, alive: false };
          }
        } else {
          const moveX = (dx / distance) * enemy.speed;
          const moveY = (dy / distance) * enemy.speed;
          enemy.x += moveX;
          enemy.y += moveY;
        }
      }

      return enemy;
    }).filter(enemy => enemy.alive || enemy.pathIndex < currentPath.length - 1));
  }, [gameState.currentMap, gameState.lives, gameState.stage, onAction]);

  const updateTowers = useCallback(() => {
    towers.forEach(tower => {
      if (frameCount - tower.lastAttack < tower.attackSpeed) return;

      const enemiesInRange = enemies.filter(enemy => {
        if (!enemy.alive) return false;
        const distance = Math.sqrt((enemy.x - tower.x) ** 2 + (enemy.y - tower.y) ** 2);
        return distance <= tower.range;
      });

      if (enemiesInRange.length > 0) {
        const target = enemiesInRange.reduce((best, current) => 
          current.pathIndex > best.pathIndex ? current : best
        );

        const actualDamage = Math.max(1, tower.damage - target.armor);

        setEnemies(prevEnemies => prevEnemies.map(enemy => {
          if (enemy.id === target.id) {
            const newHealth = Math.max(0, enemy.health - actualDamage);
            const killed = newHealth <= 0;

            if (killed) {
              const goldGain = enemy.reward;
              const expGain = enemy.isBoss ? 100 : 15;

              setGameState(prevState => ({ 
                ...prevState, 
                gold: prevState.gold + goldGain,
                score: prevState.score + goldGain * 10,
                experience: prevState.experience + expGain
              }));

              setTowers(prevTowers => prevTowers.map(t => 
                t.id === tower.id ? { ...t, kills: t.kills + 1 } : t
              ));

              // Only log boss defeats - significant events
              if (enemy.isBoss) {
                onAction('boss_defeated', {
                  boss_type: enemy.type,
                  stage: gameState.stage,
                  gold_gained: goldGain,
                  exp_gained: expGain
                });
              }

              return { ...enemy, health: 0, alive: false };
            }

            return { ...enemy, health: newHealth };
          }
          return enemy;
        }));

        tower.lastAttack = frameCount;
      }
    });
  }, [towers, enemies, frameCount, gameState.stage, onAction]);

  // Level up system
  useEffect(() => {
    const expNeeded = gameState.playerLevel * 100;
    if (gameState.experience >= expNeeded) {
      setGameState(prev => ({
        ...prev,
        playerLevel: prev.playerLevel + 1,
        experience: prev.experience - expNeeded
      }));

      // Log level up - player progression event
      onAction('level_up', {
        new_level: gameState.playerLevel + 1,
        stage: gameState.stage,
        total_score: gameState.score
      });
    }
  }, [gameState.experience, gameState.playerLevel, gameState.stage, gameState.score, onAction]);

  // Wave and stage management
  useEffect(() => {
    if (gameState.waveProgress > 0 && frameCount >= nextEnemySpawn) {
      const maxEnemies = 8 + gameState.stage * 2;
      const currentEnemies = enemies.filter(e => e.alive).length;

      if (currentEnemies < maxEnemies) {
        spawnEnemy();
        setNextEnemySpawn(frameCount + Math.max(30, 80 - gameState.stage * 10));
      } else if (enemies.filter(e => e.alive).length === 0) {
        // Wave complete
        if (gameState.wave >= 3) {
          // Stage complete
          setGameState(prev => ({ 
            ...prev, 
            gameStatus: 'stageComplete',
            waveProgress: 0
          }));

          // Log stage victory - major milestone
          onAction('stage_victory', {
            stage: gameState.stage,
            final_score: gameState.score,
            player_level: gameState.playerLevel,
            towers_built: towers.length
          });
        } else {
          // Next wave
          setGameState(prev => ({ 
            ...prev, 
            wave: prev.wave + 1, 
            waveProgress: 0,
            gold: prev.gold + 50 + prev.stage * 20
          }));
        }
      }
    }
  }, [frameCount, nextEnemySpawn, gameState, enemies, towers.length, onAction]);

  // Game over check
  useEffect(() => {
    if (gameState.lives <= 0) {
      setGameState(prev => ({ ...prev, gameStatus: 'gameOver' }));

      // Log game over - critical failure event
      onAction('stage_defeat', {
        stage: gameState.stage,
        wave: gameState.wave,
        final_score: gameState.score,
        player_level: gameState.playerLevel,
        cause: 'lives_depleted'
      });
    }
  }, [gameState.lives, gameState.stage, gameState.wave, gameState.score, gameState.playerLevel, onAction]);

  const nextStage = () => {
    if (gameState.stage >= 5) {
      setGameState(prev => ({ ...prev, gameStatus: 'victory' }));

      onAction('game_victory', {
        final_score: gameState.score,
        player_level: gameState.playerLevel,
        total_towers: towers.length
      });
      return;
    }

    setGameState(prev => ({
      ...prev,
      stage: prev.stage + 1,
      wave: 1,
      currentMap: prev.stage + 1,
      gameStatus: 'playing',
      gold: prev.gold + 100,
      lives: Math.min(20, prev.lives + 2)
    }));

    setEnemies([]);
    setTowers([]);

    // Log stage start - new chapter begins
    onAction('stage_started', {
      stage: gameState.stage + 1,
      player_level: gameState.playerLevel,
      starting_gold: gameState.gold + 100
    });
  };

  // Drawing functions
  const drawPath = (ctx: CanvasRenderingContext2D) => {
    const currentPath = stageMaps[gameState.currentMap as keyof typeof stageMaps];

    ctx.strokeStyle = '#8B4513';
    ctx.lineWidth = 25;
    ctx.lineCap = 'round';
    ctx.lineJoin = 'round';

    ctx.beginPath();
    ctx.moveTo(currentPath[0].x, currentPath[0].y);
    currentPath.slice(1).forEach(point => {
      ctx.lineTo(point.x, point.y);
    });
    ctx.stroke();

    ctx.strokeStyle = '#DEB887';
    ctx.lineWidth = 15;
    ctx.beginPath();
    ctx.moveTo(currentPath[0].x, currentPath[0].y);
    currentPath.slice(1).forEach(point => {
      ctx.lineTo(point.x, point.y);
    });
    ctx.stroke();
  };

  const drawTower = (ctx: CanvasRenderingContext2D, tower: Tower) => {
    const config = towerTypes[tower.type];
    const isSelected = gameState.selectedTower?.id === tower.id;

    // Tower base
    ctx.fillStyle = isSelected ? '#FFD700' : '#696969';
    ctx.beginPath();
    ctx.arc(tower.x, tower.y, 22, 0, 2 * Math.PI);
    ctx.fill();

    // Tower body
    ctx.fillStyle = config.color;
    ctx.beginPath();
    ctx.arc(tower.x, tower.y, 18, 0, 2 * Math.PI);
    ctx.fill();

    // Level indicator
    for (let i = 0; i < tower.level; i++) {
      ctx.fillStyle = '#FFD700';
      ctx.fillRect(tower.x - 15 + i * 6, tower.y - 25, 4, 4);
    }

    // Tower icon
    ctx.fillStyle = '#FFFFFF';
    ctx.font = '14px Arial';
    ctx.textAlign = 'center';

    const symbols = { 
      archer: 'ðŸ¹', cannon: 'ðŸ’£', magic: 'âœ¨', 
      ice: 'â„ï¸', lightning: 'âš¡' 
    };
    ctx.fillText(symbols[tower.type], tower.x, tower.y + 4);

    // Range for selected tower
    if (isSelected) {
      ctx.strokeStyle = 'rgba(255, 255, 255, 0.3)';
      ctx.lineWidth = 2;
      ctx.setLineDash([5, 5]);
      ctx.beginPath();
      ctx.arc(tower.x, tower.y, tower.range, 0, 2 * Math.PI);
      ctx.stroke();
      ctx.setLineDash([]);

      // Upgrade info
      ctx.fillStyle = '#FFFFFF';
      ctx.font = '12px Arial';
      ctx.fillText(`Lv.${tower.level}`, tower.x, tower.y - 35);
      ctx.fillText(`$${tower.upgradeCost}`, tower.x, tower.y - 50);
    }

    // Kill count
    if (tower.kills > 0) {
      ctx.fillStyle = '#FFD700';
      ctx.font = '10px Arial';
      ctx.fillText(tower.kills.toString(), tower.x + 20, tower.y - 20);
    }
  };

  const drawEnemy = (ctx: CanvasRenderingContext2D, enemy: Enemy) => {
    if (!enemy.alive) return;

    const config = enemyTypes[enemy.type];
    const sizeMap: Record<string, number> = { 
      goblin: 12, orc: 16, troll: 20, dragon: 24, 
      demon_lord: 30, ice_giant: 26, fire_elemental: 18 
    };
    const size = enemy.isBoss ? 30 : sizeMap[enemy.type] || 16;

    // Boss glow effect
    if (enemy.isBoss) {
      ctx.shadowColor = '#FF0000';
      ctx.shadowBlur = 20;
    }

    // Enemy body
    ctx.fillStyle = config.color;
    ctx.beginPath();
    ctx.arc(enemy.x, enemy.y, size, 0, 2 * Math.PI);
    ctx.fill();

    ctx.shadowBlur = 0;

    // Enemy icon
    ctx.fillStyle = '#FFFFFF';
    ctx.font = `${size}px Arial`;
    ctx.textAlign = 'center';

    const symbols = { 
      goblin: 'ðŸ‘¹', orc: 'ðŸ—¡ï¸', troll: 'ðŸ”ï¸', 
      dragon: 'ðŸ‰', demon_lord: 'ðŸ˜ˆ',
      ice_giant: 'ðŸ§Š', fire_elemental: 'ðŸ”¥'
    };
    ctx.fillText(symbols[enemy.type], enemy.x, enemy.y + size/3);

    // Health bar
    const barWidth = size * 2.5;
    const barHeight = 6;
    const healthPercent = enemy.health / enemy.maxHealth;

    ctx.fillStyle = enemy.isBoss ? '#8B0000' : '#FF0000';
    ctx.fillRect(enemy.x - barWidth/2, enemy.y - size - 15, barWidth, barHeight);
    ctx.fillStyle = enemy.isBoss ? '#FFD700' : '#00FF00';
    ctx.fillRect(enemy.x - barWidth/2, enemy.y - size - 15, barWidth * healthPercent, barHeight);

    // Armor indicator
    if (enemy.armor > 0) {
      ctx.fillStyle = '#C0C0C0';
      ctx.font = '8px Arial';
      ctx.fillText(`âš”${enemy.armor}`, enemy.x, enemy.y + size + 12);
    }
  };

  const drawUI = (ctx: CanvasRenderingContext2D, canvas: HTMLCanvasElement) => {
    // Top status bar
    ctx.fillStyle = 'rgba(0, 0, 0, 0.8)';
    ctx.fillRect(0, 0, canvas.width, 60);

    ctx.fillStyle = '#FFFFFF';
    ctx.font = '16px Arial';
    ctx.textAlign = 'left';
    ctx.fillText(`Stage: ${gameState.stage}`, 10, 25);
    ctx.fillText(`Wave: ${gameState.wave}/3`, 10, 45);
    ctx.fillText(`Lives: ${gameState.lives}`, 120, 25);
    ctx.fillText(`Gold: ${gameState.gold}`, 120, 45);
    ctx.fillText(`Level: ${gameState.playerLevel}`, 220, 25);
    ctx.fillText(`Score: ${gameState.score}`, 220, 45);

    // Experience bar
    const expNeeded = gameState.playerLevel * 100;
    const expPercent = gameState.experience / expNeeded;
    ctx.fillStyle = '#8B4513';
    ctx.fillRect(320, 35, 200, 8);
    ctx.fillStyle = '#FFD700';
    ctx.fillRect(320, 35, 200 * expPercent, 8);

    // Tower selection panel
    ctx.fillStyle = 'rgba(0, 0, 0, 0.9)';
    ctx.fillRect(0, canvas.height - 90, canvas.width, 90);

    const towerButtons = [
      { type: 'archer', x: 60, key: '1', unlocked: true },
      { type: 'cannon', x: 140, key: '2', unlocked: true },
      { type: 'magic', x: 220, key: '3', unlocked: true },
      { type: 'ice', x: 300, key: '4', unlocked: true },
      { type: 'lightning', x: 380, key: '5', unlocked: gameState.playerLevel >= 3 }
    ];

    towerButtons.forEach(button => {
      if (!button.unlocked) return;

      const config = towerTypes[button.type as keyof typeof towerTypes];
      const isSelected = gameState.selectedTowerType === button.type;
      const canAfford = gameState.gold >= config.cost;

      ctx.fillStyle = isSelected ? 'rgba(255, 255, 0, 0.5)' : 
                     canAfford ? 'rgba(255, 255, 255, 0.2)' : 'rgba(255, 0, 0, 0.2)';
      ctx.fillRect(button.x - 30, canvas.height - 80, 60, 70);

      ctx.fillStyle = config.color;
      ctx.beginPath();
      ctx.arc(button.x, canvas.height - 60, 15, 0, 2 * Math.PI);
      ctx.fill();

      ctx.fillStyle = canAfford ? '#FFFFFF' : '#FF6666';
      ctx.font = '12px Arial';
      ctx.textAlign = 'center';
      ctx.fillText(`${button.key}: $${config.cost}`, button.x, canvas.height - 25);
    });

    // Instructions
    ctx.fillStyle = '#FFFFFF';
    ctx.font = '14px Arial';
    ctx.textAlign = 'right';
    ctx.fillText('SPACE: Next Wave', canvas.width - 10, canvas.height - 65);
    ctx.fillText('U: Upgrade Tower', canvas.width - 10, canvas.height - 45);
    ctx.fillText('S: Shop (after stage)', canvas.width - 10, canvas.height - 25);
    ctx.fillText('ESC: Cancel', canvas.width - 10, canvas.height - 5);

    // Selected tower info
    if (gameState.selectedTower) {
      ctx.fillStyle = 'rgba(0, 0, 0, 0.8)';
      ctx.fillRect(canvas.width - 200, 70, 190, 100);

      ctx.fillStyle = '#FFFFFF';
      ctx.font = '14px Arial';
      ctx.textAlign = 'left';
      const tower = gameState.selectedTower;
      ctx.fillText(`${tower.type.toUpperCase()} Lv.${tower.level}`, canvas.width - 190, 90);
      ctx.fillText(`Damage: ${tower.damage}`, canvas.width - 190, 110);
      ctx.fillText(`Range: ${tower.range}`, canvas.width - 190, 130);
      ctx.fillText(`Kills: ${tower.kills}`, canvas.width - 190, 150);
      ctx.fillText(`Upgrade: $${tower.upgradeCost}`, canvas.width - 190, 170);
    }

    // Game status overlays
    if (gameState.gameStatus === 'stageComplete') {
      ctx.fillStyle = 'rgba(0, 0, 0, 0.8)';
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      ctx.fillStyle = '#00FF00';
      ctx.font = '32px Arial';
      ctx.textAlign = 'center';
      ctx.fillText('STAGE COMPLETE!', canvas.width / 2, canvas.height / 2 - 40);

      ctx.fillStyle = '#FFFFFF';
      ctx.font = '18px Arial';
      ctx.fillText('Press S for Shop, ENTER for Next Stage', canvas.width / 2, canvas.height / 2 + 20);
    }

    if (gameState.gameStatus === 'shop') {
      drawShop(ctx, canvas);
    }

    if (gameState.gameStatus === 'gameOver') {
      ctx.fillStyle = 'rgba(0, 0, 0, 0.9)';
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      ctx.fillStyle = '#FF0000';
      ctx.font = '32px Arial';
      ctx.textAlign = 'center';
      ctx.fillText('GAME OVER', canvas.width / 2, canvas.height / 2 - 50);

      ctx.fillStyle = '#FFFFFF';
      ctx.font = '18px Arial';
      ctx.fillText(`Stage Reached: ${gameState.stage}`, canvas.width / 2, canvas.height / 2);
      ctx.fillText(`Final Score: ${gameState.score}`, canvas.width / 2, canvas.height / 2 + 30);
    }

    if (gameState.gameStatus === 'victory') {
      ctx.fillStyle = 'rgba(0, 0, 0, 0.9)';
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      ctx.fillStyle = '#FFD700';
      ctx.font = '32px Arial';
      ctx.textAlign = 'center';
      ctx.fillText('VICTORY!', canvas.width / 2, canvas.height / 2 - 50);

      ctx.fillStyle = '#FFFFFF';
      ctx.font = '18px Arial';
      ctx.fillText('All stages conquered!', canvas.width / 2, canvas.height / 2);
      ctx.fillText(`Final Score: ${gameState.score}`, canvas.width / 2, canvas.height / 2 + 30);
    }
  };

  const drawShop = (ctx: CanvasRenderingContext2D, canvas: HTMLCanvasElement) => {
    ctx.fillStyle = 'rgba(0, 0, 0, 0.9)';
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    ctx.fillStyle = '#FFD700';
    ctx.font = '24px Arial';
    ctx.textAlign = 'center';
    ctx.fillText('ðŸ›’ SHOP', canvas.width / 2, 50);

    gameState.shopItems.forEach((item, index) => {
      const y = 100 + index * 60;
      const canAfford = gameState.gold >= item.cost;

      ctx.fillStyle = canAfford ? 'rgba(255, 255, 255, 0.1)' : 'rgba(255, 0, 0, 0.1)';
      ctx.fillRect(50, y, 500, 50);

      ctx.fillStyle = canAfford ? '#FFFFFF' : '#999999';
      ctx.font = '16px Arial';
      ctx.textAlign = 'left';
      ctx.fillText(item.name, 60, y + 20);
      ctx.font = '12px Arial';
      ctx.fillText(item.description, 60, y + 35);

      ctx.textAlign = 'right';
      ctx.font = '16px Arial';
      ctx.fillText(`$${item.cost}`, 540, y + 25);
    });

    ctx.fillStyle = '#FFFFFF';
    ctx.font = '16px Arial';
    ctx.textAlign = 'center';
    ctx.fillText('Click items to purchase, ESC to close', canvas.width / 2, canvas.height - 30);
  };

  const draw = useCallback(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    // Stage-specific backgrounds
    const stageColors = {
      1: '#1a4c2e', 2: '#2d2d2d', 3: '#1a1a3a', 
      4: '#3a1a1a', 5: '#0f0f0f'
    };
    ctx.fillStyle = stageColors[gameState.stage as keyof typeof stageColors] || '#1a4c2e';
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    if (gameState.gameStatus !== 'shop') {
      drawPath(ctx);
      towers.forEach(tower => drawTower(ctx, tower));
      enemies.forEach(enemy => drawEnemy(ctx, enemy));
    }

    drawUI(ctx, canvas);
  }, [towers, enemies, gameState]);

  // Game loop
  useEffect(() => {
    const gameLoop = () => {
      if (gameState.gameStatus === 'playing') {
        updateEnemies();
        updateTowers();
      }
      draw();
      setFrameCount(prev => prev + 1);
      gameLoopRef.current = requestAnimationFrame(gameLoop);
    };

    gameLoopRef.current = requestAnimationFrame(gameLoop);

    return () => {
      if (gameLoopRef.current) {
        cancelAnimationFrame(gameLoopRef.current);
      }
    };
  }, [updateEnemies, updateTowers, draw, gameState.gameStatus]);

  // Event listeners
  useEffect(() => {
    const handleKeyPress = (e: KeyboardEvent) => {
      if (e.key === 'Enter' && gameState.gameStatus === 'stageComplete') {
        nextStage();
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    document.addEventListener('keydown', handleKeyPress);

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
      document.removeEventListener('keydown', handleKeyPress);
    };
  }, [handleKeyDown, gameState.gameStatus]);

  return (
    <div style={{ 
      display: 'flex', 
      flexDirection: 'column', 
      alignItems: 'center',
      background: '#0f1419',
      borderRadius: '12px',
      padding: '16px',
      border: '2px solid #2d3748',
    }}>
      <canvas
        ref={canvasRef}
        width={600}
        height={400}
        style={{
          border: '2px solid #4a5568',
          borderRadius: '8px',
          cursor: gameState.selectedTowerType ? 'crosshair' : 'default',
        }}
        onClick={handleCanvasClick}
        tabIndex={0}
      />

      <div style={{
        marginTop: '12px',
        color: '#ffffff',
        fontSize: '14px',
        textAlign: 'center',
        maxWidth: '600px',
      }}>
        <p>ðŸ° <strong>Advanced Tower Defense</strong> - 5 stages, boss battles, upgrades & shop!</p>
        <p>Stage {gameState.stage}/5 | Level {gameState.playerLevel} | Score: {gameState.score}</p>
      </div>
    </div>
  );
};
