    if (skill.level > 0) {
      addToCombatLog("Ïù¥ÎØ∏ Î∞∞Ïö¥ Ïä§ÌÇ¨ÏûÖÎãàÎã§!");
      return;
    }

    const cost = 100; // Ïä§ÌÇ¨ Î∞∞Ïö∞Í∏∞ ÎπÑÏö©
    if (character.gold < cost) {
      addToCombatLog("Í≥®ÎìúÍ∞Ä Î∂ÄÏ°±Ìï©ÎãàÎã§!");
      return;
    }

    setCharacter((prev) => ({
      ...prev,
      gold: prev.gold - cost,
      skills: prev.skills.map((s) =>
        s.id === skill.id ? { ...s, level: 1, isLearned: true } : s
