  // Î™¨Ïä§ÌÑ∞ ÏÉùÏÑ±
  const spawnMonster = () => {
    // Ïù¥ÎØ∏ Î™¨Ïä§ÌÑ∞Í∞Ä ÏûàÍ±∞ÎÇò ÎçòÏ†ÑÏóê ÏûàÏúºÎ©¥ ÏÉàÎ°ú ÏÉùÏÑ±ÌïòÏßÄ ÏïäÏùå
    if (currentMonster || inDungeon) {
      return;
    }

    const gameTexts = (window as any).gameTexts || {
      monsters: {
        slime: "Ïä¨ÎùºÏûÑ",
        goblin: "Í≥†Î∏îÎ¶∞",
        orc: "Ïò§ÌÅ¨",
