      },
      combat: { appeared: "Ïù¥ ÎÇòÌÉÄÎÇ¨Îã§!" },
    };
    const monsters = [
      {
        name: gameTexts.monsters.slime,
        baseHp: 40,
        baseAttack: 8,
        baseDefense: 2,
        exp: 15,
        gold: 10,
        minLevel: 1,
        maxLevel: 3,
      },
      {
        name: gameTexts.monsters.goblin,
        baseHp: 60,
