          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(8, 1fr)',
            gap: '6px',
            background: 'rgba(0, 0, 0, 0.3)',
            padding: '15px',
            borderRadius: '10px',
            border: '2px solid #4b5563',
            minHeight: '400px'
          }}>
            {/* ÏÉÅÏ†ê ÏïÑÏù¥ÌÖú Í∑∏Î¶¨Îìú */}
            {Array.from({ length: 32 }, (_, index) => {
              let items: Item[] = [];

              if (shopCategory === 'buy') {
                items = shop;
              } else if (shopCategory === 'sell') {
                items = inventory.filter(item => item.type !== 'consumable');
              } else {
                // Ï†úÏûë ÏïÑÏù¥ÌÖú (ÏûÑÏãúÎ°ú ÏÉÅÏ†ê ÏïÑÏù¥ÌÖú ÏÇ¨Ïö©)
                items = shop.filter(item => item.rarity === 'epic' || item.rarity === 'legendary');
              }

              const item = items[index];
              const isEmpty = !item;
              const canAfford = shopCategory === 'buy' ? character.gold >= (item?.price || 0) : true;

              return (
                <div
                  key={index}
                  onClick={() => item && setSelectedShopItem(item)}
                  style={{
                    width: '50px',
                    height: '50px',
                    background: isEmpty ? 'rgba(255, 255, 255, 0.05)' : 
                      canAfford ? 'rgba(16, 185, 129, 0.1)' : 'rgba(107, 114, 128, 0.1)',
                    border: isEmpty ? '1px solid #374151' : 
                      `2px solid ${getRarityColor(item.rarity)}`,
                    borderRadius: '8px',
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    justifyContent: 'center',
                    cursor: item ? 'pointer' : 'default',
                    position: 'relative',
                    transition: 'all 0.2s ease'
                  }}
                  onMouseEnter={(e) => {
                    if (item) {
                      e.currentTarget.style.transform = 'scale(1.1)';
                      e.currentTarget.style.boxShadow = `0 0 15px ${getRarityColor(item.rarity)}50`;
                    }
                  }}
                  onMouseLeave={(e) => {
                    if (item) {
                      e.currentTarget.style.transform = 'scale(1)';
                      e.currentTarget.style.boxShadow = 'none';
                    }
                  }}
                >
                  {item && (
                    <>
                      <div style={{ 
                        fontSize: '20px',
                        opacity: canAfford ? 1 : 0.5
                      }}>
                        {getItemIcon(item)}
                      </div>
                      {/* Í∞ÄÍ≤©/ÏàòÎüâ ÌëúÏãú */}
                      {shopCategory === 'buy' && (
                        <div style={{
                          position: 'absolute',
                          bottom: '2px',
                          right: '2px',
                          background: canAfford ? '#10b981' : '#dc2626',
                          color: 'white',
                          fontSize: '8px',
                          padding: '1px 3px',
                          borderRadius: '3px',
                          lineHeight: '1'
                        }}>
                          {item.price >= 1000 ? `${Math.floor(item.price/1000)}K` : item.price}
                        </div>
                      )}
                      {shopCategory === 'sell' && (
                        <div style={{
                          position: 'absolute',
                          bottom: '2px',
                          right: '2px',
                          background: '#f59e0b',
                          color: 'white',
                          fontSize: '8px',
                          padding: '1px 3px',
                          borderRadius: '3px',
                          lineHeight: '1'
                        }}>
                          {Math.floor(item.price * 0.7)}
                        </div>
                      )}
                    </>
                  )}
                </div>
              );
            })}
          </div>
        </div>

        {/* Ïö∞Ï∏° ÏïÑÏù¥ÌÖú ÏÉÅÏÑ∏ Ï†ïÎ≥¥ */}
        <div style={{ width: '300px' }}>
          <h3 style={{ color: 'white', marginBottom: '15px', fontSize: '16px' }}>üìã ÏÉÅÌíà Ï†ïÎ≥¥</h3>
          {selectedShopItem ? (
            <div style={{
              background: 'rgba(255, 255, 255, 0.1)',
              border: `2px solid ${getRarityColor(selectedShopItem.rarity)}`,
              borderRadius: '10px',
              padding: '20px'
            }}>
              <div style={{ textAlign: 'center', marginBottom: '15px' }}>
                <div style={{ fontSize: '48px', marginBottom: '10px' }}>
                  {getItemIcon(selectedShopItem)}
                </div>
                <h4 style={{
                  color: getRarityColor(selectedShopItem.rarity),
                  margin: '0 0 5px 0',
                  fontSize: '18px',
                  fontWeight: 'bold'
                }}>
                  {selectedShopItem.name}
                </h4>
                <p style={{
                  color: '#9ca3af',
                  margin: '0 0 15px 0',
                  fontSize: '14px',
                  textTransform: 'capitalize'
                }}>
                  {selectedShopItem.type} ‚Ä¢ Lv.{selectedShopItem.level} ‚Ä¢ {selectedShopItem.rarity}
                </p>
              </div>

              {/* ÏïÑÏù¥ÌÖú Îä•Î†•Ïπò */}
              {selectedShopItem.stats && Object.keys(selectedShopItem.stats).length > 0 && (
                <div style={{ marginBottom: '15px' }}>
                  <h5 style={{ color: 'white', margin: '0 0 10px 0', fontSize: '14px' }}>Îä•Î†•Ïπò</h5>
                  {Object.entries(selectedShopItem.stats).map(([stat, value]) => 
                    value > 0 && (
                      <div key={stat} style={{
                        display: 'flex',
                        justifyContent: 'space-between',
                        color: '#10b981',
                        fontSize: '12px',
                        marginBottom: '5px'
                      }}>
                        <span>
                          {stat === 'attack' ? '‚öîÔ∏è Í≥µÍ≤©Î†•' :
                           stat === 'defense' ? 'üõ°Ô∏è Î∞©Ïñ¥Î†•' :
                           stat === 'hp' ? '‚ù§Ô∏è Ï≤¥Î†•' :
                           stat === 'mp' ? 'üíô ÎßàÎÇò' :
                           stat === 'speed' ? 'üí® ÏÜçÎèÑ' :
                           stat === 'critRate' ? 'üí• ÌÅ¨Î¶¨Ìã∞Ïª¨' : stat}
                        </span>
                        <span>+{value}</span>
                      </div>
                    )
                  )}
                </div>
              )}

              {/* Í∞ÄÍ≤© Ï†ïÎ≥¥ */}
              <div style={{ marginBottom: '15px' }}>
                <h5 style={{ color: 'white', margin: '0 0 10px 0', fontSize: '14px' }}>
                  {shopCategory === 'buy' ? 'Íµ¨Îß§ Í∞ÄÍ≤©' : 
                   shopCategory === 'sell' ? 'ÌåêÎß§ Í∞ÄÍ≤©' : 'Ï†úÏûë ÎπÑÏö©'}
                </h5>
                <p style={{ color: '#f59e0b', fontSize: '14px', margin: 0, fontWeight: 'bold' }}>
                  {shopCategory === 'buy' ? selectedShopItem.price.toLocaleString() :
                   shopCategory === 'sell' ? Math.floor(selectedShopItem.price * 0.7).toLocaleString() :
                   (selectedShopItem.price * 1.5).toLocaleString()}Í≥®Îìú
                </p>
              </div>

              {/* Ïï°ÏÖò Î≤ÑÌäº */}
              <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                {shopCategory === 'buy' && (
                  <button
                    onClick={() => {
                      buyItem(selectedShopItem);
                      setSelectedShopItem(null);
                    }}
                    disabled={character.gold < selectedShopItem.price}
                    style={{
                      background: character.gold >= selectedShopItem.price 
                        ? 'linear-gradient(135deg, #10b981 0%, #059669 100%)' 
                        : '#6b7280',
                      color: 'white',
                      border: 'none',
                      borderRadius: '6px',
                      padding: '12px',
                      fontSize: '14px',
                      cursor: character.gold >= selectedShopItem.price ? 'pointer' : 'not-allowed',
                      fontWeight: 'bold'
                    }}
                  >
                    Íµ¨Îß§ÌïòÍ∏∞ ({selectedShopItem.price.toLocaleString()}G)
                  </button>
                )}

                {shopCategory === 'sell' && (
                  <button
                    onClick={() => {
                      const sellPrice = Math.floor(selectedShopItem.price * 0.7);
                      setCharacter(prev => ({ ...prev, gold: prev.gold + sellPrice }));
                      setInventory(prev => prev.filter(invItem => invItem.id !== selectedShopItem.id));
                      addToCombatLog(`${selectedShopItem.name}ÏùÑ ${sellPrice} Í≥®ÎìúÏóê ÌåêÎß§ÌñàÏäµÎãàÎã§!`);
                      setSelectedShopItem(null);
                    }}
                    style={{
                      background: 'linear-gradient(135deg, #f59e0b 0%, #d97706 100%)',
                      color: 'white',
                      border: 'none',
                      borderRadius: '6px',
                      padding: '12px',
                      fontSize: '14px',
                      cursor: 'pointer',
                      fontWeight: 'bold'
                    }}
                  >
                    ÌåêÎß§ÌïòÍ∏∞ ({Math.floor(selectedShopItem.price * 0.7).toLocaleString()}G)
                  </button>
                )}

                {shopCategory === 'craft' && (
                  <button
                    onClick={() => {
                      const craftPrice = Math.floor(selectedShopItem.price * 1.5);
                      if (character.gold >= craftPrice) {
                        setCharacter(prev => ({ ...prev, gold: prev.gold - craftPrice }));
                        setInventory(prev => [...prev, { ...selectedShopItem, id: `${selectedShopItem.id}_${Date.now()}` }]);
                        addToCombatLog(`${selectedShopItem.name}ÏùÑ Ï†úÏûëÌñàÏäµÎãàÎã§!`);
                        setSelectedShopItem(null);
                      }
                    }}
                    disabled={character.gold < Math.floor(selectedShopItem.price * 1.5)}
                    style={{
                      background: character.gold >= Math.floor(selectedShopItem.price * 1.5)
                        ? 'linear-gradient(135deg, #8b5cf6 0%, #7c3aed 100%)' 
                        : '#6b7280',
                      color: 'white',
                      border: 'none',
                      borderRadius: '6px',
                      padding: '12px',
                      fontSize: '14px',
                      cursor: character.gold >= Math.floor(selectedShopItem.price * 1.5) ? 'pointer' : 'not-allowed',
                      fontWeight: 'bold'
                    }}
                  >
                    Ï†úÏûëÌïòÍ∏∞ ({Math.floor(selectedShopItem.price * 1.5).toLocaleString()}G)
                  </button>
                )}
              </div>
            </div>
          ) : (
            <div style={{
              background: 'rgba(255, 255, 255, 0.05)',
              borderRadius: '10px',
              padding: '40px',
              textAlign: 'center',
              color: '#6b7280'
            }}>
              <p>ÏÉÅÌíàÏùÑ ÌÅ¥Î¶≠ÌïòÏó¨ Ï†ïÎ≥¥Î•º ÌôïÏù∏ÌïòÏÑ∏Ïöî</p>
            </div>
          )}
        </div>
      </div>

      {/* ÌïòÎã® Í≥®Îìú Ï†ïÎ≥¥ */}
      <div style={{
        marginTop: '20px',
        background: 'linear-gradient(135deg, #f59e0b 0%, #d97706 100%)',
        borderRadius: '10px',
        padding: '15px',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        color: 'white'
      }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
          <span style={{ fontSize: '24px' }}>üí∞</span>
          <div>
            <div style={{ fontSize: '14px', opacity: 0.9 }}>Î≥¥Ïú† Í≥®Îìú</div>
            <div style={{ fontSize: '20px', fontWeight: 'bold' }}>
              {character.gold.toLocaleString()}G
            </div>
          </div>
        </div>
        <div style={{ textAlign: 'right' }}>
          <div style={{ fontSize: '12px', opacity: 0.8 }}>
            {shopCategory === 'buy' ? 'Íµ¨Îß§Ìï† Ïàò ÏûàÎäî ÏïÑÏù¥ÌÖúÏùÑ ÏÑ†ÌÉùÌïòÏÑ∏Ïöî' :
             shopCategory === 'sell' ? 'ÌåêÎß§Ìï† ÏïÑÏù¥ÌÖúÏùÑ ÏÑ†ÌÉùÌïòÏÑ∏Ïöî' :
             'Ï†úÏûëÌï† ÏïÑÏù¥ÌÖúÏùÑ ÏÑ†ÌÉùÌïòÏÑ∏Ïöî'}
          </div>
        </div>
      </div>
    </div>
  );

  const renderTabs = () => (
    <div
      style={{
        background: "#374151",
        display: "flex",
        borderRadius: "15px 15px 0 0",
        overflow: "hidden",
      }}
    >
      {[
        { id: "lobby", name: t("rpg.tabs.lobby"), icon: "üè†" },
        { id: "character", name: t("rpg.tabs.character"), icon: "üë§" },
        { id: "inventory", name: t("rpg.tabs.inventory"), icon: "üéí" },
        { id: "skills", name: t("rpg.tabs.skills"), icon: "‚ú®" },
        { id: "dungeon", name: t("rpg.tabs.dungeon"), icon: "üè∞" },
        { id: "shop", name: t("rpg.tabs.shop"), icon: "üõí" },
      ].map((tab) => (
        <button
          key={tab.id}
          onClick={() => setCurrentTab(tab.id as any)}
          style={{
            background: currentTab === tab.id ? colors.accent : "transparent",
            color: colors.text,
            border: "none",
            padding: "15px 20px",
            fontSize: "14px",
            cursor: "pointer",
            flex: 1,
            transition: "all 0.3s ease",
          }}
        >
          <div>{tab.icon}</div>
          <div style={{ fontSize: "12px", marginTop: "5px" }}>{tab.name}</div>
        </button>
      ))}
    </div>
  );

  return (
    <div
      style={{
        width: "100%",
        maxWidth: "1200px",
        margin: "0 auto",
        background: colors.cardBg,
        borderRadius: "20px",
        overflow: "hidden",
        boxShadow: "0 20px 60px rgba(0, 0, 0, 0.3)",
        fontFamily:
          '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
        border: `1px solid ${colors.cardBorder}`,
      }}
    >
      {/* Í≤åÏûÑ ÌÉÄÏù¥ÌãÄ */}
      <div
        style={{
          background: colors.backgroundSecondary,
          padding: "20px",
          textAlign: "center",
          position: "relative",
          overflow: "hidden",
        }}
      >
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background:
              "radial-gradient(circle at 50% 50%, rgba(147, 51, 234, 0.3) 0%, transparent 70%)",
          }}
        />
        <h1
          style={{
            margin: 0,
            fontSize: "32px",
            fontWeight: "bold",
            color: colors.text,
            textShadow: "0 4px 20px rgba(147, 51, 234, 0.5)",
            position: "relative",
            zIndex: 1,
          }}
        >
          {t("rpg.title")}
        </h1>
        <p
          style={{
            margin: "10px 0 0 0",
            color: colors.textSecondary,
            fontSize: "16px",
            position: "relative",
            zIndex: 1,
          }}
        >
          {t("rpg.subtitle")}
        </p>
      </div>

      {/* ÌÉ≠ Î©îÎâ¥ */}
      {renderTabs()}

      {/* ÌòÑÏû¨ ÌÉ≠ Ïª®ÌÖêÏ∏† */}
      {currentTab === "lobby" && renderLobbyTab()}
      {currentTab === "dungeon" && renderDungeonTab()}
      {currentTab === "character" && renderCharacterTab()}
      {currentTab === "inventory" && renderInventoryTab()}
      {currentTab === "skills" && renderSkillsTab()}
      {currentTab === "shop" && renderShopTab()}
    </div>
  );
};
