import React, { useState, useEffect } from 'react';
import { Play, Search, Menu, Sun, Moon, Settings, Bell, User, ChevronRight, Star, Trophy, Gamepad2, Zap, Shield } from 'lucide-react';
import { Layout } from '../components/Layout';

export const HomePage: React.FC = () => {
  const [currentTime, setCurrentTime] = useState(new Date());
  const [isDarkTheme, setIsDarkTheme] = useState(true);
  const [screenSize, setScreenSize] = useState({
    width: window.innerWidth,
    height: window.innerHeight
  });

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);

    const handleResize = () => {
      setScreenSize({
        width: window.innerWidth,
        height: window.innerHeight
      });
    };

    window.addEventListener('resize', handleResize);
    return () => {
      clearInterval(timer);
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const navigateToQuickStart = () => {
    window.location.href = '/quickstart';
  };

  const toggleTheme = () => {
    setIsDarkTheme(!isDarkTheme);
  };

  // 반응형 설정
  const isMobile = screenSize.width < 768;

  // 게이밍 테마 색상
  const theme = {
    light: {
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      cardBg: 'rgba(255, 255, 255, 0.95)',
      cardBorder: 'rgba(255, 255, 255, 0.2)',
      text: '#1a202c',
      textSecondary: '#4a5568',
      headerBg: 'rgba(255, 255, 255, 0.1)',
      accent: '#8b5cf6',
      accentSecondary: '#3b82f6',
      premium: 'linear-gradient(135deg, #f59e0b, #f97316)',
    },
    dark: {
      background: 'radial-gradient(ellipse at top, #1e1b4b 0%, #0f0f23 50%, #000000 100%)',
      cardBg: 'linear-gradient(145deg, rgba(30, 30, 60, 0.9), rgba(15, 15, 35, 0.9))',
      cardBorder: 'rgba(139, 92, 246, 0.3)',
      text: '#ffffff',
      textSecondary: '#a1a1aa',
      headerBg: 'rgba(15, 15, 35, 0.8)',
      accent: '#8b5cf6',
      accentSecondary: '#3b82f6',
      premium: 'linear-gradient(135deg, #f59e0b, #f97316)',
    }
  };

  const currentTheme = isDarkTheme ? theme.dark : theme.light;
  const isDesktop = screenSize.width >= 1200;

  // 게이밍 스타일 가이드 카드들
  const guideCards = [
    {
      id: 1,
      title: "빠른 시작 가이드",
      subtitle: "4단계로 빠르게 시작하기",
      icon: Zap,
      gradient: "linear-gradient(135deg, #8b5cf6, #3b82f6)",
      category: "시작하기",
      level: "초급",
      onClick: navigateToQuickStart,
      featured: true
    },
    {
      id: 2,
      title: "Thinking Engine 소개",
      subtitle: "솔루션에 대해 자세히 알아보기",
      icon: Shield,
      gradient: "linear-gradient(135deg, #3b82f6, #1d4ed8)",
      category: "기본",
      level: "초급"
    },
    {
      id: 3,
      title: "사용자 가이드",
      subtitle: "기본 사용법부터 고급 기능까지",
      icon: User,
      gradient: "linear-gradient(135deg, #06b6d4, #0891b2)",
      category: "사용법",
      level: "중급"
    },
    {
      id: 4,
      title: "개발자 문서",
      subtitle: "SDK 통합 및 API 사용법",
      icon: Settings,
      gradient: "linear-gradient(135deg, #10b981, #059669)",
      category: "개발",
      level: "고급"
    },
    {
      id: 5,
      title: "FAQ & 팁",
      subtitle: "자주 묻는 질문과 프로 팁",
      icon: Star,
      gradient: "linear-gradient(135deg, #f59e0b, #d97706)",
      category: "도움말",
      level: "모든 레벨"
    },
    {
      id: 6,
      title: "고객 지원",
      subtitle: "기술 지원 및 전문가 상담",
      icon: Trophy,
      gradient: "linear-gradient(135deg, #ef4444, #dc2626)",
      category: "지원",
      level: "프리미엄"
    }
  ];

  return (
    <div style={{
      minHeight: '100vh',
      background: currentTheme.background,
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
      transition: 'all 0.3s ease'
    }}>
      {/* 게이밍 스타일 헤더 */}
      <div style={{
        position: 'sticky',
        top: 0,
        zIndex: 100,
        background: currentTheme.headerBg,
        backdropFilter: 'blur(20px)',
        padding: '16px 24px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        borderBottom: `1px solid ${currentTheme.cardBorder}`,
        boxShadow: '0 4px 20px rgba(0, 0, 0, 0.3)'
      }}>
        {/* 왼쪽: 로고와 제목 */}
        <div style={{ display: 'flex', alignItems: 'center', gap: '16px' }}>
          <div style={{
            width: '40px',
            height: '40px',
            background: currentTheme.accent,
            borderRadius: '12px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            boxShadow: '0 4px 12px rgba(139, 92, 246, 0.4)'
          }}>
            <Gamepad2 size={24} style={{ color: 'white' }} />
          </div>
          <div>
            <h1 style={{
              fontSize: '24px',
              fontWeight: '800',
              background: 'linear-gradient(135deg, #8b5cf6, #3b82f6)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              margin: 0,
              lineHeight: '1.2'
            }}>
              Thinking Engine
            </h1>
            <p style={{
              fontSize: '12px',
              color: currentTheme.textSecondary,
              margin: 0,
              fontWeight: '500'
            }}>
              가이드 센터 • {currentTime.toLocaleTimeString('ko-KR', { 
                hour: '2-digit', 
                minute: '2-digit' 
              })}
            </p>
          </div>
        </div>

        {/* 오른쪽: 네비게이션과 액션 버튼들 */}
        <div style={{ display: 'flex', alignItems: 'center', gap: '16px' }}>
          {/* 검색 */}
          <div style={{
            display: 'flex',
            alignItems: 'center',
            background: 'rgba(139, 92, 246, 0.1)',
            border: `1px solid ${currentTheme.cardBorder}`,
            borderRadius: '12px',
            padding: '8px 12px',
            gap: '8px',
            cursor: 'pointer',
            minWidth: '200px'
          }}>
            <Search size={16} style={{ color: currentTheme.textSecondary }} />
            <span style={{ color: currentTheme.textSecondary, fontSize: '14px' }}>가이드 검색...</span>
          </div>

          {/* 프리미엄 버튼 */}
          <div style={{
            background: currentTheme.premium,
            borderRadius: '12px',
            padding: '8px 16px',
            cursor: 'pointer',
            display: 'flex',
            alignItems: 'center',
            gap: '6px',
            boxShadow: '0 4px 12px rgba(245, 158, 11, 0.3)'
          }}>
            <Star size={16} style={{ color: 'white' }} />
            <span style={{ color: 'white', fontSize: '14px', fontWeight: '600' }}>프리미엄</span>
          </div>

          {/* 사용자 아바타 */}
          <div style={{
            width: '36px',
            height: '36px',
            background: 'linear-gradient(135deg, #3b82f6, #1d4ed8)',
            borderRadius: '50%',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            cursor: 'pointer',
            border: `2px solid ${currentTheme.cardBorder}`
          }}>
            <User size={20} style={{ color: 'white' }} />
          </div>
        </div>
      </div>

      {/* 메인 컨텐츠 */}
      <div style={{
        padding: isMobile ? '16px' : '24px',
        maxWidth: '1200px',
        margin: '0 auto'
      }}>

        {/* 히어로 섹션 */}
        <div style={{
          background: currentTheme.cardBg,
          backdropFilter: 'blur(20px)',
          borderRadius: '24px',
          padding: isMobile ? '32px' : '48px',
          marginBottom: '32px',
          border: `1px solid ${currentTheme.cardBorder}`,
          boxShadow: '0 20px 40px rgba(0, 0, 0, 0.3)',
          position: 'relative',
          overflow: 'hidden'
        }}>
          {/* 배경 장식 */}
          <div style={{
            position: 'absolute',
            top: '-50%',
            right: '-20%',
            width: '200px',
            height: '200px',
            background: 'linear-gradient(135deg, rgba(139, 92, 246, 0.1), rgba(59, 130, 246, 0.1))',
            borderRadius: '50%',
            filter: 'blur(40px)'
          }} />

          <div style={{ position: 'relative', zIndex: 1 }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '20px', marginBottom: '24px' }}>
              <div style={{
                width: '80px',
                height: '80px',
                background: 'linear-gradient(135deg, #8b5cf6, #3b82f6)',
                borderRadius: '24px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '36px',
                boxShadow: '0 8px 20px rgba(139, 92, 246, 0.4)'
              }}>
                🚀
              </div>
              <div>
                <h2 style={{
                  fontSize: isMobile ? '28px' : '36px',
                  fontWeight: '800',
                  background: 'linear-gradient(135deg, #8b5cf6, #3b82f6)',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                  margin: 0,
                  marginBottom: '8px'
                }}>
                  데이터 분석의 새로운 경험
                </h2>
                <p style={{
                  fontSize: '18px',
                  color: currentTheme.textSecondary,
                  margin: 0,
                  lineHeight: '1.6'
                }}>
                  Thinking Engine으로 시작하는 프로페셔널한 데이터 여정
                </p>
              </div>
            </div>

            {/* 퀵 스타트 버튼 */}
            <div 
              onClick={navigateToQuickStart}
              style={{
                display: 'inline-flex',
                alignItems: 'center',
                gap: '12px',
                background: 'linear-gradient(135deg, #8b5cf6, #3b82f6)',
                color: 'white',
                padding: '16px 32px',
                borderRadius: '16px',
                fontSize: '16px',
                fontWeight: '600',
                cursor: 'pointer',
                border: 'none',
                boxShadow: '0 8px 20px rgba(139, 92, 246, 0.4)',
                transition: 'all 0.3s ease'
              }}
              onMouseEnter={(e) => {
                (e.currentTarget as HTMLElement).style.transform = 'translateY(-2px)';
                (e.currentTarget as HTMLElement).style.boxShadow = '0 12px 28px rgba(139, 92, 246, 0.6)';
              }}
              onMouseLeave={(e) => {
                (e.currentTarget as HTMLElement).style.transform = 'translateY(0)';
                (e.currentTarget as HTMLElement).style.boxShadow = '0 8px 20px rgba(139, 92, 246, 0.4)';
              }}
            >
              <Play size={20} />
              빠른 시작하기
            </div>
          </div>
        </div>

        {/* 카테고리 섹션 */}
        <div style={{ marginBottom: '32px' }}>
          <h3 style={{
            fontSize: '24px',
            fontWeight: '700',
            color: currentTheme.text,
            marginBottom: '16px',
            display: 'flex',
            alignItems: 'center',
            gap: '12px'
          }}>
            <Gamepad2 size={28} style={{ color: currentTheme.accent }} />
            가이드 컬렉션
          </h3>
          <p style={{
            fontSize: '16px',
            color: currentTheme.textSecondary,
            margin: 0,
            marginBottom: '24px'
          }}>
            레벨별로 정리된 학습 경로를 따라가며 전문가가 되어보세요
          </p>
        </div>

        {/* 게이밍 스타일 가이드 카드 그리드 */}
        <div style={{
          display: 'grid',
          gridTemplateColumns: isMobile ? '1fr' : 'repeat(auto-fit, minmax(350px, 1fr))',
          gap: '20px'
        }}>
          {guideCards.map((guide, index) => {
            const IconComponent = guide.icon;
            return (
              <div
                key={guide.id}
                onClick={guide.onClick}
                style={{
                  background: currentTheme.cardBg,
                  backdropFilter: 'blur(20px)',
                  borderRadius: '20px',
                  padding: '24px',
                  border: `1px solid ${currentTheme.cardBorder}`,
                  cursor: 'pointer',
                  transition: 'all 0.3s ease',
                  position: 'relative',
                  overflow: 'hidden',
                  boxShadow: '0 8px 24px rgba(0, 0, 0, 0.2)'
                }}
                onMouseEnter={(e) => {
                  const element = e.currentTarget as HTMLElement;
                  element.style.transform = 'translateY(-8px)';
                  element.style.boxShadow = '0 20px 40px rgba(0, 0, 0, 0.3)';
                  element.style.borderColor = guide.gradient.includes('#8b5cf6') ? '#8b5cf6' : '#3b82f6';
                }}
                onMouseLeave={(e) => {
                  const element = e.currentTarget as HTMLElement;
                  element.style.transform = 'translateY(0)';
                  element.style.boxShadow = '0 8px 24px rgba(0, 0, 0, 0.2)';
                  element.style.borderColor = currentTheme.cardBorder;
                }}
              >
                {/* Featured 배지 */}
                {guide.featured && (
                  <div style={{
                    position: 'absolute',
                    top: '16px',
                    right: '16px',
                    background: currentTheme.premium,
                    color: 'white',
                    padding: '4px 12px',
                    borderRadius: '12px',
                    fontSize: '12px',
                    fontWeight: '600',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '4px'
                  }}>
                    <Star size={12} />
                    인기
                  </div>
                )}

                {/* 그라데이션 상단 라인 */}
                <div style={{
                  position: 'absolute',
                  top: 0,
                  left: 0,
                  right: 0,
                  height: '4px',
                  background: guide.gradient
                }} />

                <div style={{ display: 'flex', alignItems: 'flex-start', gap: '20px', marginBottom: '20px' }}>
                  <div style={{
                    width: '60px',
                    height: '60px',
                    background: guide.gradient,
                    borderRadius: '18px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    boxShadow: '0 8px 20px rgba(0, 0, 0, 0.2)'
                  }}>
                    <IconComponent size={28} style={{ color: 'white' }} />
                  </div>
                  <div style={{ flex: 1 }}>
                    <div style={{ display: 'flex', alignItems: 'center', gap: '8px', marginBottom: '8px' }}>
                      <span style={{
                        fontSize: '12px',
                        fontWeight: '600',
                        color: currentTheme.textSecondary,
                        background: 'rgba(139, 92, 246, 0.1)',
                        padding: '4px 8px',
                        borderRadius: '8px'
                      }}>
                        {guide.category}
                      </span>
                      <span style={{
                        fontSize: '12px',
                        fontWeight: '600',
                        color: guide.level === '프리미엄' ? '#f59e0b' : currentTheme.textSecondary
                      }}>
                        {guide.level}
                      </span>
                    </div>
                    <h3 style={{
                      fontSize: '20px',
                      fontWeight: '700',
                      color: currentTheme.text,
                      margin: '0 0 8px 0',
                      lineHeight: '1.3'
                    }}>
                      {guide.title}
                    </h3>
                    <p style={{
                      fontSize: '14px',
                      color: currentTheme.textSecondary,
                      margin: 0,
                      lineHeight: '1.5'
                    }}>
                      {guide.subtitle}
                    </p>
                  </div>
                  <ChevronRight size={20} style={{ color: currentTheme.textSecondary, marginTop: '8px' }} />
                </div>

                {/* 프로그레스 바 (첫 번째 카드만) */}
                {index === 0 && (
                  <div style={{
                    background: 'rgba(139, 92, 246, 0.1)',
                    borderRadius: '8px',
                    height: '8px',
                    overflow: 'hidden'
                  }}>
                    <div style={{
                      background: guide.gradient,
                      height: '100%',
                      width: '60%',
                      borderRadius: '8px',
                      transition: 'width 0.3s ease'
                    }} />
                  </div>
                )}
              </div>
            );
          })}
        </div>

        {/* 하단 여백 */}
        <div style={{ height: '40px' }} />
      </div>

      {/* 게이밍 스타일 글로벌 CSS */}
      <style>{`
        * {
          scrollbar-width: thin;
          scrollbar-color: ${isDarkTheme ? 'rgba(139, 92, 246, 0.5)' : 'rgba(0, 0, 0, 0.3)'} transparent;
        }

        *::-webkit-scrollbar {
          width: 8px;
          height: 8px;
        }

        *::-webkit-scrollbar-track {
          background: rgba(0, 0, 0, 0.1);
          border-radius: 8px;
        }

        *::-webkit-scrollbar-thumb {
          background: linear-gradient(135deg, #8b5cf6, #3b82f6);
          border-radius: 8px;
          border: 2px solid transparent;
          background-clip: content-box;
        }

        *::-webkit-scrollbar-thumb:hover {
          background: linear-gradient(135deg, #7c3aed, #2563eb);
          background-clip: content-box;
        }

        @keyframes float {
          0%, 100% { transform: translateY(0px); }
          50% { transform: translateY(-10px); }
        }

        @keyframes glow {
          0%, 100% { box-shadow: 0 0 20px rgba(139, 92, 246, 0.3); }
          50% { box-shadow: 0 0 30px rgba(139, 92, 246, 0.6); }
        }

        @keyframes slideInUp {
          from { opacity: 0; transform: translateY(30px); }
          to { opacity: 1; transform: translateY(0); }
        }

        @keyframes shimmer {
          0% { transform: translateX(-100%); }
          100% { transform: translateX(100%); }
        }

        @keyframes neonPulse {
          0%, 100% { 
            text-shadow: 0 0 5px rgba(139, 92, 246, 0.5),
                         0 0 10px rgba(139, 92, 246, 0.5),
                         0 0 15px rgba(139, 92, 246, 0.5);
