        <CodeBlock
          language="http"
          title={t('step3.jsonApiMethod')}
          code={`POST ${syncJsonUrl}
Content-Type: application/json

{
  "appid": "${appId || 'YOUR_APP_ID'}",
  "debug": 0,
  "data": {
    "#type": "track",
    "#event_name": "product_buy",
