import React from 'react';

interface ButtonProps {
  children: React.ReactNode;
  variant?: 'primary' | 'secondary' | 'outline' | 'ghost';
  size?: 'sm' | 'md' | 'lg';
  disabled?: boolean;
  onClick?: () => void;
  className?: string;
  type?: 'button' | 'submit' | 'reset';
}

export const Button: React.FC<ButtonProps> = ({
  children,
  variant = 'primary',
  size = 'md',
  disabled = false,
  onClick,
  className = '',
  type = 'button'
}) => {
  const getVariantStyles = () => {
    switch (variant) {
      case 'primary':
        return {
          backgroundColor: 'var(--brand-primary)',
          color: 'white',
          border: '1px solid var(--brand-primary)'
        };
      case 'secondary':
        return {
          backgroundColor: 'var(--gray-600)',
          color: 'white',
          border: '1px solid var(--gray-600)'
        };
      case 'outline':
        return {
          backgroundColor: 'transparent',
          color: 'var(--brand-primary)',
          border: '1px solid var(--brand-primary)'
        };
      case 'ghost':
        return {
          backgroundColor: 'transparent',
          color: 'var(--gray-700)',
          border: '1px solid transparent'
        };
      default:
        return {};
    }
  };

  const getSizeStyles = () => {
    switch (size) {
      case 'sm':
        return {
          padding: '8px 16px',
          fontSize: '14px'
        };
      case 'lg':
        return {
          padding: '16px 32px',
          fontSize: '18px'
        };
      default:
        return {
          padding: '12px 24px',
          fontSize: '16px'
        };
    }
  };

  return (
    <button
      type={type}
      onClick={onClick}
      disabled={disabled}
      className={className}
      style={{
        ...getVariantStyles(),
        ...getSizeStyles(),
        borderRadius: 'var(--border-radius)',
        cursor: disabled ? 'not-allowed' : 'pointer',
        fontWeight: '500',
        textAlign: 'center',
        textDecoration: 'none',
        display: 'inline-flex',
        alignItems: 'center',
        justifyContent: 'center',
        gap: '8px',
        transition: 'all 0.2s ease',
        opacity: disabled ? 0.6 : 1,
        outline: 'none',
        fontFamily: 'inherit'
      }}
    >
      {children}
    </button>
  );
};
