import React, { useState, useEffect } from 'react';
import { Users, UserPlus, Shield, Eye, EyeOff } from 'lucide-react';
import { Card } from '../../components/Card';
import { Badge } from '../../components/Badge';
import { Checkbox } from '../../components/Checkbox';
import { InfoBox } from '../../components/InfoBox';
import { Tabs } from '../../components/Tabs';

interface Step2Props {
  onComplete: () => void;
}

export const Step2AccountSetup: React.FC<Step2Props> = ({ onComplete }) => {
  const [understood, setUnderstood] = useState(false);

  useEffect(() => {
    if (understood) {
      onComplete();
    }
  }, [understood, onComplete]);

  const roleTypes = [
    {
      name: '프로젝트 관리자',
      badge: { text: '최고 권한', variant: 'danger' as const },
      permissions: [
        '모든 프로젝트 설정 관리',
        '구성원 및 역할 관리',
        '데이터 연동 설정',
        '리포트 생성 및 공유',
        '시스템 설정'
      ],
      description: '프로젝트의 모든 권한을 가지며 전체적인 관리를 담당합니다.'
    },
    {
      name: '분석 관리자',
      badge: { text: '관리 권한', variant: 'warning' as const },
      permissions: [
        '대시보드 생성 및 편집',
        '리포트 관리',
        '데이터 분석',
        '일반 구성원 관리',
        '분석 도구 설정'
      ],
      description: '분석과 관련된 모든 업무를 관리할 수 있습니다.'
    },
    {
      name: '분석가',
      badge: { text: '분석 권한', variant: 'info' as const },
      permissions: [
        '대시보드 보기 및 생성',
        '데이터 분석',
        '개인 리포트 생성',
        '차트 및 위젯 생성',
        '데이터 내보내기'
      ],
      description: '데이터 분석 업무를 수행할 수 있습니다.'
    },
    {
      name: '일반 구성원',
      badge: { text: '기본 권한', variant: 'secondary' as const },
      permissions: [
        '대시보드 보기',
        '기본 리포트 보기',
        '개인 설정 관리'
      ],
      description: '기본적인 데이터 조회만 가능합니다.'
    }
  ];

  const dataPermissionSettings = [
    {
      name: '표시 설정',
      icon: <Eye size={20} color="var(--info)" />,
      description: '구성원이 볼 수 있는 데이터 범위를 설정합니다.',
      options: [
        '전체 데이터 접근',
        '부서별 데이터만',
        '개인 데이터만',
        '지정된 기간만'
      ]
    },
    {
      name: '범위 설정',
      icon: <Shield size={20} color="var(--warning)" />,
      description: '접근 가능한 기능과 메뉴를 제한합니다.',
      options: [
        '모든 기능 접근',
        '분석 기능만',
        '리포트 조회만',
        '대시보드만'
      ]
    },
    {
      name: '마스킹',
      icon: <EyeOff size={20} color="var(--danger)" />,
      description: '민감한 데이터를 숨기거나 마스킹합니다.',
      options: [
        '마스킹 없음',
        '개인정보 마스킹',
        '결제정보 마스킹',
        '전체 마스킹'
      ]
    }
  ];

  const memberCreationTab = (
    <div>
      <div style={{ marginBottom: '24px' }}>
        <h5 style={{ 
          fontSize: '18px', 
          fontWeight: '600', 
          marginBottom: '16px',
          color: '#303030',
          display: 'flex',
          alignItems: 'center',
          gap: '8px'
        }}>
          <UserPlus size={20} color="#004bff" />
          구성원 생성 단계별 가이드
        </h5>
        <p style={{ 
          fontSize: '14px', 
          color: '#303030', 
          marginBottom: '20px',
          lineHeight: '1.5'
        }}>
          프로젝트 관리에서 새로운 구성원을 생성하고 역할을 할당하는 과정입니다.
        </p>

        <div style={{ 
          display: 'grid', 
          gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
          gap: '16px',
          marginBottom: '24px'
        }}>
          {[
            {
              step: 1,
              title: '프로젝트 관리 접근',
              description: '오른쪽 상단의 톱니바퀴 아이콘을 클릭하여 프로젝트 관리로 이동합니다.',
              color: '#c8faff'
            },
            {
              step: 2,
              title: '구성원 관리 메뉴',
              description: '프로젝트 관리에서 구성원 관리 메뉴를 선택합니다.',
              color: '#c8faff'
            },
            {
              step: 3,
              title: '새 구성원 추가',
              description: '[+ 새 구성원] 버튼을 클릭하여 구성원 생성을 시작합니다.',
              color: '#fff5b3'
            },
            {
              step: 4,
              title: '계정 정보 입력',
              description: '계정 ID(계정명)와 별칭을 입력한 후 [+ 추가] 버튼을 클릭합니다.',
              color: '#fff5b3'
            },
            {
              step: 5,
              title: '역할 지정',
              description: '운영 역할, 분석 역할, 데이터 권한을 각각 선택하여 적절한 권한을 부여합니다.',
              color: '#d1b1ff'
            },
            {
              step: 6,
              title: '계정 생성 및 다운로드',
              description: '[생성 및 다운로드] 버튼을 클릭하면 CSV 파일이 자동으로 다운로드됩니다.',
              color: '#d1b1ff'
            }
          ].map((item) => (
            <div key={item.step} style={{ 
              padding: '16px',
              backgroundColor: item.color,
              borderRadius: '12px',
              border: '1px solid rgba(0, 75, 255, 0.1)'
            }}>
              <div style={{
                display: 'flex',
                alignItems: 'flex-start',
                gap: '12px'
              }}>
                <div style={{
                  width: '28px',
                  height: '28px',
                  borderRadius: '50%',
                  backgroundColor: '#004bff',
                  color: 'white',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontSize: '14px',
                  fontWeight: '600',
                  flexShrink: 0
                }}>
                  {item.step}
                </div>
                <div>
                  <h6 style={{
                    fontSize: '16px',
                    fontWeight: '600',
                    margin: '0 0 6px 0',
                    color: '#303030'
                  }}>
                    {item.title}
                  </h6>
                  <p style={{
                    fontSize: '13px',
                    color: '#303030',
                    margin: 0,
                    lineHeight: '1.4'
                  }}>
                    {item.description}
                  </p>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* CSV 파일 안내 */}
        <div style={{
          padding: '20px',
          backgroundColor: '#f5f5f5',
          borderRadius: '12px',
          marginBottom: '20px'
        }}>
          <h6 style={{
            fontSize: '16px',
            fontWeight: '600',
            marginBottom: '12px',
            color: '#303030',
            display: 'flex',
            alignItems: 'center',
            gap: '8px'
          }}>
            📋 CSV 파일 내용
          </h6>
          <ul style={{
            margin: 0,
            paddingLeft: '20px',
            fontSize: '14px',
            color: '#303030'
          }}>
            <li style={{ marginBottom: '6px' }}>ID: 생성된 계정 ID</li>
            <li style={{ marginBottom: '6px' }}>별칭: 설정한 사용자 별칭</li>
            <li style={{ marginBottom: '6px' }}>초기 비밀번호: 임시로 생성된 비밀번호</li>
          </ul>
          <div style={{
            marginTop: '12px',
            padding: '12px',
            backgroundColor: '#ffe641',
            borderRadius: '8px',
            fontSize: '13px',
            color: '#303030',
            fontWeight: '500'
          }}>
            💡 다운로드한 CSV 파일의 정보를 해당 구성원에게 안전하게 전달하세요.
          </div>
        </div>

        {/* 여러 구성원 동시 생성 */}
        <div style={{
          padding: '20px',
          backgroundColor: '#c8faff',
          borderRadius: '12px',
          marginBottom: '20px'
        }}>
          <h6 style={{
            fontSize: '16px',
            fontWeight: '600',
            marginBottom: '12px',
            color: '#303030',
            display: 'flex',
            alignItems: 'center',
            gap: '8px'
          }}>
            🔄 여러 구성원 동시 생성
          </h6>
          <p style={{
            fontSize: '14px',
            color: '#303030',
            marginBottom: '12px',
            lineHeight: '1.5'
          }}>
            한 번에 여러 구성원을 생성하여 효율적으로 팀을 구성할 수 있습니다.
          </p>
          <div style={{
            padding: '12px',
            backgroundColor: 'white',
            borderRadius: '8px',
            fontSize: '13px',
            color: '#303030',
            fontWeight: '500'
          }}>
            여러 명의 구성원을 먼저 추가한 후, 마지막에 [생성 및 다운로드]를 클릭하면 모든 구성원의 정보가 포함된 CSV 파일을 한 번에 다운로드할 수 있습니다.
          </div>
          <ul style={{
            margin: '12px 0 0 0',
            paddingLeft: '20px',
            fontSize: '14px',
