import React, { useState } from 'react';

interface GameProps {
  onAction: (eventType: string, data: any) => void;
  isSimulating: boolean;
  currentAction: string | null;
  playerStats: any;
  setPlayerStats: (stats: any) => void;
  currentUser: any;
}

interface Monster {
  name: string;
  health: number;
  maxHealth: number;
  attack: number;
  emoji: string;
}

interface GameState {
  scene: 'town' | 'battle' | 'victory';
  currentMonster?: Monster;
  battleLog: string[];
  playerTurn: boolean;
}

export const DragonQuestGame: React.FC<GameProps> = ({ 
  onAction, 
  isSimulating, 
  currentAction, 
  playerStats, 
  setPlayerStats,
  currentUser 
}) => {
  const [gameState, setGameState] = useState<GameState>({
    scene: 'town',
    battleLog: [],
    playerTurn: true
  });

  const [inventory, setInventory] = useState([
    { name: 'Ï≤¥Î†• Ìè¨ÏÖò', count: 3, type: 'potion' },
    { name: 'ÎßàÎ≤ï Í≤Ä', count: 1, type: 'weapon' },
    { name: 'Î∞©Ìå®', count: 1, type: 'armor' }
  ]);

  const monsters = [
    { name: 'Ïä¨ÎùºÏûÑ', health: 30, maxHealth: 30, attack: 5, emoji: 'üü¢' },
    { name: 'Ïò§ÌÅ¨', health: 50, maxHealth: 50, attack: 8, emoji: 'üó°Ô∏è' },
    { name: 'ÎìúÎûòÍ≥§', health: 100, maxHealth: 100, attack: 15, emoji: 'üêâ' },
    { name: 'Í≥†Î∏îÎ¶∞', health: 25, maxHealth: 25, attack: 6, emoji: 'üëπ' }
  ];

  // Î™¨Ïä§ÌÑ∞ÏôÄ Ï†ÑÌà¨ ÏãúÏûë
  const startBattle = (monster: Monster) => {
    setGameState({
      scene: 'battle',
      currentMonster: { ...monster },
      battleLog: [`${monster.emoji} ${monster.name}Ïù¥(Í∞Ä) ÎÇòÌÉÄÎÇ¨Îã§!`],
      playerTurn: true
    });
    onAction('battle_start', {
      monster_name: monster.name,
      monster_health: monster.health,
      player_level: playerStats.level
    });
  };

  // ÌîåÎ†àÏù¥Ïñ¥ Í≥µÍ≤©
  const playerAttack = () => {
    if (!gameState.currentMonster || !gameState.playerTurn) return;

    const damage = Math.floor(Math.random() * 15) + 10;
    const newMonsterHealth = Math.max(0, gameState.currentMonster.health - damage);

    const newLog = [...gameState.battleLog, `ÌîåÎ†àÏù¥Ïñ¥Í∞Ä ${damage} Îç∞ÎØ∏ÏßÄÎ•º ÏûÖÌòîÎã§!`];

    if (newMonsterHealth <= 0) {
      // ÏäπÎ¶¨
      const expGain = gameState.currentMonster.maxHealth * 2;
      const goldGain = Math.floor(Math.random() * 100) + 50;

      setPlayerStats({
        ...playerStats,
        experience: playerStats.experience + expGain,
        gold: playerStats.gold + goldGain
      });

      setGameState({
        ...gameState,
        scene: 'victory',
        battleLog: [...newLog, `${gameState.currentMonster.emoji} ${gameState.currentMonster.name}ÏùÑ(Î•º) Ï≤òÏπòÌñàÎã§!`, `Í≤ΩÌóòÏπò +${expGain}, Í≥®Îìú +${goldGain}`]
      });

      onAction('battle_victory', {
        monster_name: gameState.currentMonster.name,
        damage_dealt: damage,
        exp_gained: expGain,
        gold_gained: goldGain,
        player_level: playerStats.level
      });
    } else {
      // Î™¨Ïä§ÌÑ∞ Î∞òÍ≤©
      const monsterDamage = Math.floor(Math.random() * gameState.currentMonster.attack) + 3;
      const newPlayerHealth = Math.max(0, playerStats.health - monsterDamage);

      setPlayerStats({
        ...playerStats,
        health: newPlayerHealth
      });

      const finalLog = [...newLog, `${gameState.currentMonster.emoji} ${gameState.currentMonster.name}Ïù¥(Í∞Ä) ${monsterDamage} Îç∞ÎØ∏ÏßÄÎ•º ÏûÖÌòîÎã§!`];

      setGameState({
        ...gameState,
        currentMonster: { ...gameState.currentMonster, health: newMonsterHealth },
        battleLog: finalLog,
        playerTurn: true
      });

      onAction('battle_damage', {
        monster_name: gameState.currentMonster.name,
        damage_to_monster: damage,
        damage_to_player: monsterDamage,
        player_health_remaining: newPlayerHealth
      });
    }
  };

  // ÎèÑÎßùÍ∞ÄÍ∏∞
  const runAway = () => {
    setGameState({
      scene: 'town',
      battleLog: [],
      playerTurn: true
    });
    onAction('battle_flee', {
      monster_name: gameState.currentMonster?.name || 'unknown',
      player_level: playerStats.level
    });
  };

  // ÏïÑÏù¥ÌÖú Íµ¨Îß§
  const buyItem = (itemName: string, price: number) => {
    if (playerStats.gold >= price) {
      setPlayerStats({
        ...playerStats,
        gold: playerStats.gold - price
      });

      const existingItem = inventory.find(item => item.name === itemName);
      if (existingItem) {
        existingItem.count++;
      } else {
        setInventory([...inventory, { name: itemName, count: 1, type: 'misc' }]);
      }

      onAction('item_purchase', {
        item_name: itemName,
        item_price: price,
        remaining_gold: playerStats.gold - price,
        player_level: playerStats.level
      });
    }
  };

  // Ï≤¥Î†• Ìè¨ÏÖò ÏÇ¨Ïö©
  const drinkHealthPotion = () => {
    const potion = inventory.find(item => item.name === 'Ï≤¥Î†• Ìè¨ÏÖò');
    if (potion && potion.count > 0 && playerStats.health < 100) {
      potion.count--;
      const healAmount = Math.min(30, 100 - playerStats.health);
      setPlayerStats({
        ...playerStats,
        health: playerStats.health + healAmount
      });

      onAction('item_use', {
        item_name: 'Ï≤¥Î†• Ìè¨ÏÖò',
        heal_amount: healAmount,
        new_health: playerStats.health + healAmount,
        player_level: playerStats.level
      });
    }
  };

  return (
    <div style={{
      background: 'linear-gradient(135deg, #1a1a2e, #16213e)',
      borderRadius: '16px',
      padding: '20px',
      border: '2px solid #3b82f6',
      boxShadow: '0 0 30px rgba(59, 130, 246, 0.3)',
      position: 'relative',
      overflow: 'hidden',
      minHeight: '500px'
    }}>
      {/* Í≤åÏûÑ Î∞∞Í≤Ω Ìö®Í≥º */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        height: '4px',
        background: 'linear-gradient(90deg, #8b5cf6, #3b82f6, #06b6d4, #10b981)',
        animation: 'shimmer 2s ease-in-out infinite'
      }} />

      {/* ÌîåÎ†àÏù¥Ïñ¥ ÏÉÅÌÉú */}
      <div style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginBottom: '20px',
        padding: '12px 16px',
        background: 'rgba(59, 130, 246, 0.1)',
        borderRadius: '12px',
        border: '1px solid rgba(59, 130, 246, 0.3)'
      }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
          <div style={{
            width: '40px',
            height: '40px',
            borderRadius: '50%',
            background: 'linear-gradient(135deg, #8b5cf6, #3b82f6)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '18px'
          }}>
            üßô‚Äç‚ôÇÔ∏è
          </div>
          <div>
            <div style={{ fontSize: '14px', fontWeight: '700', color: 'white' }}>
              ÌîåÎ†àÏù¥Ïñ¥ #{currentUser?.user_id || 'TD001'}
            </div>
            <div style={{ fontSize: '11px', color: '#94a3b8' }}>
              Î†àÎ≤® {playerStats.level} ‚Ä¢ Í≤ΩÌóòÏπò {playerStats.experience}/1000
            </div>
          </div>
        </div>
        <div style={{ display: 'flex', alignItems: 'center', gap: '16px' }}>
          <div style={{ textAlign: 'center' }}>
            <div style={{ fontSize: '11px', color: '#94a3b8' }}>Í≥®Îìú</div>
            <div style={{ fontSize: '14px', fontWeight: '700', color: '#f59e0b' }}>
              üí∞ {playerStats.gold}
            </div>
          </div>
          <div style={{ textAlign: 'center' }}>
            <div style={{ fontSize: '11px', color: '#94a3b8' }}>Ï≤¥Î†•</div>
            <div style={{ fontSize: '14px', fontWeight: '700', color: '#ef4444' }}>
              ‚ù§Ô∏è {playerStats.health}/100
            </div>
          </div>
        </div>
      </div>

      {/* Í≤åÏûÑ ÌôîÎ©¥ */}
      {gameState.scene === 'town' && (
        <div>
          <h4 style={{ color: 'white', textAlign: 'center', marginBottom: '20px' }}>
            üè∞ Î™®ÌóòÍ∞ÄÏùò ÎßàÏùÑ
          </h4>

          {/* Î™¨Ïä§ÌÑ∞ ÏÑ†ÌÉù */}
          <div style={{ marginBottom: '20px' }}>
            <p style={{ color: '#94a3b8', fontSize: '14px', marginBottom: '12px' }}>Ï†ÑÌà¨Ìï† Î™¨Ïä§ÌÑ∞Î•º ÏÑ†ÌÉùÌïòÏÑ∏Ïöî:</p>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '8px' }}>
              {monsters.map((monster, index) => (
                <button
                  key={index}
                  onClick={() => startBattle(monster)}
                  disabled={isSimulating}
                  style={{
                    background: 'linear-gradient(135deg, #ef4444, #dc2626)',
                    border: 'none',
                    borderRadius: '8px',
                    padding: '8px 12px',
                    color: 'white',
                    fontSize: '12px',
                    fontWeight: '600',
                    cursor: isSimulating ? 'not-allowed' : 'pointer',
                    opacity: isSimulating ? 0.6 : 1
                  }}
                >
                  {monster.emoji} {monster.name} (HP: {monster.health})
                </button>
              ))}
            </div>
          </div>

          {/* ÏÉÅÏ†ê */}
          <div style={{ marginBottom: '20px' }}>
            <p style={{ color: '#94a3b8', fontSize: '14px', marginBottom: '12px' }}>üè™ ÏÉÅÏ†ê:</p>
            <div style={{ display: 'flex', gap: '8px', flexWrap: 'wrap' }}>
              <button
                onClick={() => buyItem('Ï≤¥Î†• Ìè¨ÏÖò', 100)}
                disabled={isSimulating || playerStats.gold < 100}
                style={{
                  background: 'linear-gradient(135deg, #10b981, #059669)',
                  border: 'none',
                  borderRadius: '6px',
                  padding: '6px 10px',
                  color: 'white',
                  fontSize: '11px',
                  fontWeight: '600',
                  cursor: (isSimulating || playerStats.gold < 100) ? 'not-allowed' : 'pointer',
                  opacity: (isSimulating || playerStats.gold < 100) ? 0.6 : 1
                }}
              >
                üß™ Ï≤¥Î†• Ìè¨ÏÖò (100G)
              </button>
              <button
                onClick={() => buyItem('ÎßàÎ≤ï Í≤Ä', 500)}
                disabled={isSimulating || playerStats.gold < 500}
                style={{
                  background: 'linear-gradient(135deg, #8b5cf6, #7c3aed)',
                  border: 'none',
                  borderRadius: '6px',
                  padding: '6px 10px',
                  color: 'white',
                  fontSize: '11px',
                  fontWeight: '600',
                  cursor: (isSimulating || playerStats.gold < 500) ? 'not-allowed' : 'pointer',
                  opacity: (isSimulating || playerStats.gold < 500) ? 0.6 : 1
                }}
              >
                ‚öîÔ∏è ÎßàÎ≤ï Í≤Ä (500G)
              </button>
            </div>
          </div>

          {/* Ïù∏Î≤§ÌÜ†Î¶¨ */}
          <div>
            <p style={{ color: '#94a3b8', fontSize: '14px', marginBottom: '12px' }}>üéí Ïù∏Î≤§ÌÜ†Î¶¨:</p>
            <div style={{ display: 'flex', gap: '8px', flexWrap: 'wrap' }}>
              {inventory.filter(item => item.count > 0).map((item, index) => (
                <button
                  key={index}
                  onClick={() => item.name === 'Ï≤¥Î†• Ìè¨ÏÖò' && item.count > 0 && playerStats.health < 100 ? handleHealthPotionUse() : null}
                  disabled={isSimulating || (item.name === 'Ï≤¥Î†• Ìè¨ÏÖò' && playerStats.health >= 100)}
                  style={{
                    background: 'rgba(245, 158, 11, 0.2)',
                    border: '1px solid #f59e0b',
                    borderRadius: '6px',
                    padding: '6px 10px',
                    color: '#f59e0b',
                    fontSize: '11px',
                    fontWeight: '600',
                    cursor: (isSimulating || (item.name === 'Ï≤¥Î†• Ìè¨ÏÖò' && playerStats.health >= 100)) ? 'not-allowed' : 'pointer',
                    opacity: (isSimulating || (item.name === 'Ï≤¥Î†• Ìè¨ÏÖò' && playerStats.health >= 100)) ? 0.6 : 1
                  }}
                >
                  {item.name} x{item.count}
                </button>
              ))}
            </div>
          </div>
        </div>
      )}

      {gameState.scene === 'battle' && gameState.currentMonster && (
        <div>
          <h4 style={{ color: 'white', textAlign: 'center', marginBottom: '20px' }}>
            ‚öîÔ∏è Ï†ÑÌà¨ Ï§ë
          </h4>

          {/* Î™¨Ïä§ÌÑ∞ ÏÉÅÌÉú */}
          <div style={{
            textAlign: 'center',
            marginBottom: '20px',
            padding: '16px',
            background: 'rgba(239, 68, 68, 0.1)',
            borderRadius: '12px',
            border: '1px solid rgba(239, 68, 68, 0.3)'
          }}>
            <div style={{ fontSize: '32px', marginBottom: '8px' }}>
              {gameState.currentMonster.emoji}
            </div>
            <div style={{ color: 'white', fontSize: '16px', fontWeight: '700' }}>
              {gameState.currentMonster.name}
            </div>
            <div style={{ color: '#ef4444', fontSize: '14px' }}>
              HP: {gameState.currentMonster.health}/{gameState.currentMonster.maxHealth}
            </div>
            {/* Ï≤¥Î†•Î∞î */}
            <div style={{
              width: '100%',
              height: '8px',
              background: 'rgba(255, 255, 255, 0.2)',
              borderRadius: '4px',
              marginTop: '8px',
              overflow: 'hidden'
            }}>
              <div style={{
                width: `${(gameState.currentMonster.health / gameState.currentMonster.maxHealth) * 100}%`,
                height: '100%',
                background: 'linear-gradient(90deg, #ef4444, #dc2626)',
                transition: 'width 0.5s ease'
              }} />
            </div>
          </div>

          {/* Ï†ÑÌà¨ Ïï°ÏÖò */}
          <div style={{ display: 'flex', gap: '12px', justifyContent: 'center', marginBottom: '20px' }}>
            <button
              onClick={playerAttack}
              disabled={isSimulating || !gameState.playerTurn}
              style={{
                background: 'linear-gradient(135deg, #ef4444, #dc2626)',
                border: 'none',
                borderRadius: '8px',
                padding: '12px 20px',
                color: 'white',
                fontSize: '14px',
                fontWeight: '600',
                cursor: (isSimulating || !gameState.playerTurn) ? 'not-allowed' : 'pointer',
                opacity: (isSimulating || !gameState.playerTurn) ? 0.6 : 1
              }}
            >
              ‚öîÔ∏è Í≥µÍ≤©
            </button>
            <button
              onClick={runAway}
              disabled={isSimulating}
              style={{
                background: 'rgba(107, 114, 128, 0.8)',
                border: 'none',
                borderRadius: '8px',
                padding: '12px 20px',
                color: 'white',
                fontSize: '14px',
                fontWeight: '600',
                cursor: isSimulating ? 'not-allowed' : 'pointer',
                opacity: isSimulating ? 0.6 : 1
              }}
            >
              üèÉ ÎèÑÎßù
            </button>
          </div>

          {/* Ï†ÑÌà¨ Î°úÍ∑∏ */}
          <div style={{
            background: 'rgba(0, 0, 0, 0.3)',
            borderRadius: '8px',
            padding: '12px',
            maxHeight: '100px',
            overflowY: 'auto'
          }}>
            {gameState.battleLog.map((log, index) => (
              <div key={index} style={{
                color: '#94a3b8',
                fontSize: '12px',
                marginBottom: '4px'
              }}>
                {log}
              </div>
            ))}
          </div>
        </div>
      )}

      {gameState.scene === 'victory' && (
        <div style={{ textAlign: 'center' }}>
          <h4 style={{ color: '#10b981', marginBottom: '20px' }}>
            üéâ ÏäπÎ¶¨!
          </h4>
          <div style={{
            background: 'rgba(16, 185, 129, 0.1)',
            borderRadius: '12px',
            padding: '20px',
            marginBottom: '20px',
            border: '1px solid rgba(16, 185, 129, 0.3)'
          }}>
            <div style={{ color: 'white', fontSize: '14px', marginBottom: '12px' }}>
              Ï†ÑÌà¨ÏóêÏÑú ÏäπÎ¶¨ÌñàÏäµÎãàÎã§!
            </div>
            <div style={{
              background: 'rgba(0, 0, 0, 0.3)',
              borderRadius: '8px',
              padding: '12px',
              maxHeight: '100px',
              overflowY: 'auto'
            }}>
              {gameState.battleLog.map((log, index) => (
                <div key={index} style={{
                  color: '#94a3b8',
                  fontSize: '12px',
                  marginBottom: '4px'
                }}>
                  {log}
                </div>
              ))}
            </div>
          </div>
          <button
            onClick={() => setGameState({ scene: 'town', battleLog: [], playerTurn: true })}
            style={{
              background: 'linear-gradient(135deg, #10b981, #059669)',
              border: 'none',
              borderRadius: '8px',
              padding: '12px 24px',
              color: 'white',
              fontSize: '14px',
              fontWeight: '600',
              cursor: 'pointer'
            }}
          >
            üè∞ ÎßàÏùÑÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞
          </button>
        </div>
      )}
    </div>
  );
};
