          cave: { name: 'Í≥†Î∏îÎ¶∞ ÎèôÍµ¥', description: 'Í≥†Î∏îÎ¶∞Îì§Ïù¥ ÏÑúÏãùÌïòÎäî ÏúÑÌóòÌïú ÎèôÍµ¥ÏûÖÎãàÎã§.' },
          tower: { name: 'ÎßàÎ≤ïÏÇ¨Ïùò ÌÉë', description: 'Í∞ïÎ†•Ìïú ÎßàÎ≤ï Î™¨Ïä§ÌÑ∞Îì§Ïù¥ ÏûàÎäî ÌÉëÏûÖÎãàÎã§.' }
        },
