  // Ïä§ÌÇ¨ ÏÇ¨Ïö© Ìï®Ïàò (ÏàòÏ†ïÎêú Îç∞ÎØ∏ÏßÄ Í≥ÑÏÇ∞ Ìè¨Ìï®)
  const executeSkill = (skill: Skill) => {
    if (!skill.isLearned || skill.level === 0) {
      addToCombatLog('Ïä§ÌÇ¨ÏùÑ Î®ºÏ†Ä Î∞∞ÏõåÏïº Ìï©ÎãàÎã§!');
      return;
    }

    if (character.mp < skill.manaCost) {
      addToCombatLog('ÎßàÎÇòÍ∞Ä Î∂ÄÏ°±Ìï©ÎãàÎã§!');
      return;
    }

    if (skill.currentCooldown > 0) {
      addToCombatLog('Ïä§ÌÇ¨Ïù¥ Ïû¨ÏÇ¨Ïö© ÎåÄÍ∏∞ Ï§ëÏûÖÎãàÎã§!');
      return;
    }

    const damage = calculateSkillDamage(skill, character);

    // Ïä§ÌÇ¨ ÏÇ¨Ïö© Î°úÏßÅ
