        { id: 'skills', name: t('rpg.tabs.skills'), icon: '‚ú®' },
        { id: 'dungeon', name: t('rpg.tabs.dungeon'), icon: 'üè∞' },
        { id: 'shop', name: t('rpg.tabs.shop'), icon: 'üõí' }
      ].map(tab => (
        <button
          key={tab.id}
          onClick={() => setCurrentTab(tab.id as any)}
          style={{
            background: currentTab === tab.id 
              ? colors.accent
              : 'transparent',
            color: colors.text,
            border: 'none',
            padding: '15px 20px',
            fontSize: '14px',
            cursor: 'pointer',
            flex: 1,
            transition: 'all 0.3s ease'
          }}
        >
          <div>{tab.icon}</div>
          <div style={{ fontSize: '12px', marginTop: '5px' }}>{tab.name}</div>
        </button>
      ))}
    </div>
  );

  return (
    <div style={{
      width: '100%',
      maxWidth: '1200px',
      margin: '0 auto',
      background: colors.cardBg,
      borderRadius: '20px',
      overflow: 'hidden',
      boxShadow: '0 20px 60px rgba(0, 0, 0, 0.3)',
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
      border: `1px solid ${colors.cardBorder}`
    }}>
      {/* Í≤åÏûÑ ÌÉÄÏù¥ÌãÄ */}
      <div style={{
        background: colors.backgroundSecondary,
        padding: '20px',
        textAlign: 'center',
        position: 'relative',
        overflow: 'hidden'
      }}>
        <div style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'radial-gradient(circle at 50% 50%, rgba(147, 51, 234, 0.3) 0%, transparent 70%)'
        }} />
        <h1 style={{
          margin: 0,
          fontSize: '32px',
          fontWeight: 'bold',
          color: colors.text,
          textShadow: '0 4px 20px rgba(147, 51, 234, 0.5)',
          position: 'relative',
          zIndex: 1
        }}>
          {t('rpg.title')}
        </h1>
        <p style={{
          margin: '10px 0 0 0',
          color: colors.textSecondary,
          fontSize: '16px',
          position: 'relative',
          zIndex: 1
        }}>
          {t('rpg.subtitle')}
        </p>
      </div>

      {/* ÌÉ≠ Î©îÎâ¥ */}
      {renderTabs()}

      {/* ÌòÑÏû¨ ÌÉ≠ Ïª®ÌÖêÏ∏† */}
      {currentTab === 'lobby' && renderLobbyTab()}
      {currentTab === 'dungeon' && renderDungeonTab()}
      {currentTab === 'character' && renderCharacterTab()}
      {currentTab === 'inventory' && renderInventoryTab()}
      {currentTab === 'skills' && renderSkillsTab()}
      {currentTab === 'shop' && renderShopTab()}

    </div>
  );
};
