import React, { useRef, useEffect, useState, useCallback } from 'react';

interface GameProps {
  onAction: (eventType: string, data: any) => void;
  isSimulating: boolean;
  currentAction: string | null;
  playerStats: any;
  setPlayerStats: (stats: any) => void;
  currentUser: any;
}

interface Player {
  x: number;
  y: number;
  width: number;
  height: number;
  health: number;
  maxHealth: number;
  direction: 'up' | 'down' | 'left' | 'right';
  isMoving: boolean;
  animationFrame: number;
  attackCooldown: number;
}

interface Enemy {
  id: string;
  x: number;
  y: number;
  width: number;
  height: number;
  health: number;
  maxHealth: number;
  type: 'slime' | 'orc' | 'goblin' | 'skeleton';
  lastAttack: number;
  direction: number;
  speed: number;
  alive: boolean;
}

interface GameItem {
  id: string;
  x: number;
  y: number;
  type: 'potion' | 'gold' | 'gem';
  collected: boolean;
}

export const ActionRPGGame: React.FC<GameProps> = ({
  onAction,
  playerStats,
  setPlayerStats,
  currentUser
}) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const gameLoopRef = useRef<number>();
  const keysRef = useRef<Set<string>>(new Set());

  // Game state
  const [gameState, setGameState] = useState({
    scene: 'playing' as 'playing' | 'paused' | 'inventory',
    cameraX: 0,
    cameraY: 0,
    worldWidth: 1200,
    worldHeight: 800,
  });

  // Player state
  const [player, setPlayer] = useState<Player>({
    x: 300,
    y: 250,
    width: 32,
    height: 32,
    health: playerStats.health || 100,
    maxHealth: 100,
    direction: 'down',
    isMoving: false,
    animationFrame: 0,
    attackCooldown: 0,
  });

  // Enemies state
  const [enemies, setEnemies] = useState<Enemy[]>([]);

  // Items state
  const [items, setItems] = useState<GameItem[]>([]);

  // Animation frame counter
  const [frameCount, setFrameCount] = useState(0);

  // Initialize game world
  useEffect(() => {
    generateEnemies();
    generateItems();
  }, []);

  const generateEnemies = () => {
    const newEnemies: Enemy[] = [];
    const enemyTypes: Enemy['type'][] = ['slime', 'orc', 'goblin', 'skeleton'];

    for (let i = 0; i < 8; i++) {
      newEnemies.push({
        id: `enemy_${i}`,
        x: Math.random() * (gameState.worldWidth - 100) + 50,
        y: Math.random() * (gameState.worldHeight - 100) + 50,
        width: 28,
        height: 28,
        health: 30 + Math.random() * 40,
        maxHealth: 50,
        type: enemyTypes[Math.floor(Math.random() * enemyTypes.length)],
        lastAttack: 0,
        direction: Math.random() * Math.PI * 2,
        speed: 0.5 + Math.random() * 1,
        alive: true,
      });
    }
    setEnemies(newEnemies);
  };

  const generateItems = () => {
    const newItems: GameItem[] = [];
    const itemTypes: GameItem['type'][] = ['potion', 'gold', 'gem'];

    for (let i = 0; i < 12; i++) {
      newItems.push({
        id: `item_${i}`,
        x: Math.random() * (gameState.worldWidth - 100) + 50,
        y: Math.random() * (gameState.worldHeight - 100) + 50,
        type: itemTypes[Math.floor(Math.random() * itemTypes.length)],
        collected: false,
      });
    }
    setItems(newItems);
  };

  // Keyboard handling
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      keysRef.current.add(e.key.toLowerCase());
    };

    const handleKeyUp = (e: KeyboardEvent) => {
      keysRef.current.delete(e.key.toLowerCase());
    };

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, []);

  // Game logic
  const updatePlayer = useCallback(() => {
    setPlayer(prev => {
      const newPlayer = { ...prev };
      const keys = keysRef.current;
      const speed = 3;

      let moving = false;
      let newX = newPlayer.x;
      let newY = newPlayer.y;

      // Movement
      if (keys.has('w') || keys.has('arrowup')) {
        newY -= speed;
        newPlayer.direction = 'up';
        moving = true;
      }
      if (keys.has('s') || keys.has('arrowdown')) {
        newY += speed;
        newPlayer.direction = 'down';
        moving = true;
      }
      if (keys.has('a') || keys.has('arrowleft')) {
        newX -= speed;
        newPlayer.direction = 'left';
        moving = true;
      }
      if (keys.has('d') || keys.has('arrowright')) {
        newX += speed;
        newPlayer.direction = 'right';
        moving = true;
      }

      // Boundary checking
      newX = Math.max(16, Math.min(gameState.worldWidth - 16, newX));
      newY = Math.max(16, Math.min(gameState.worldHeight - 16, newY));

      newPlayer.x = newX;
      newPlayer.y = newY;
      newPlayer.isMoving = moving;

      // Attack
      if (keys.has(' ') && newPlayer.attackCooldown <= 0) {
        newPlayer.attackCooldown = 30; // 30 frames cooldown
        performAttack(newPlayer);
      }

      if (newPlayer.attackCooldown > 0) {
        newPlayer.attackCooldown--;
      }

      // Animation
      if (moving) {
        newPlayer.animationFrame = (newPlayer.animationFrame + 1) % 60;
      } else {
        newPlayer.animationFrame = 0;
      }

      return newPlayer;
    });
  }, [gameState.worldWidth, gameState.worldHeight]);

  const performAttack = (playerState: Player) => {
    const attackRange = 50;
    const attackDamage = 15 + Math.random() * 10;

    setEnemies(prev => prev.map(enemy => {
      if (!enemy.alive) return enemy;

      const dx = enemy.x - playerState.x;
      const dy = enemy.y - playerState.y;
      const distance = Math.sqrt(dx * dx + dy * dy);

      if (distance <= attackRange) {
        const newHealth = Math.max(0, enemy.health - attackDamage);
        const killed = newHealth <= 0;

        if (killed) {
          // Enemy defeated
          const expGain = 25 + Math.random() * 15;
          const goldGain = 10 + Math.random() * 20;

          setPlayerStats((prev: any) => ({
            ...prev,
            experience: prev.experience + expGain,
            gold: prev.gold + goldGain,
          }));

          onAction('enemy_defeated', {
            enemy_type: enemy.type,
            enemy_id: enemy.id,
            damage_dealt: attackDamage,
            exp_gained: expGain,
            gold_gained: goldGain,
            player_x: playerState.x,
            player_y: playerState.y,
            enemy_x: enemy.x,
            enemy_y: enemy.y,
          });

          return { ...enemy, health: 0, alive: false };
        } else {
          onAction('enemy_attacked', {
            enemy_type: enemy.type,
            enemy_id: enemy.id,
            damage_dealt: attackDamage,
            enemy_health_remaining: newHealth,
            player_x: playerState.x,
            player_y: playerState.y,
          });

          return { ...enemy, health: newHealth };
        }
      }

      return enemy;
    }));
  };

  const updateEnemies = useCallback(() => {
    setEnemies(prev => prev.map(enemy => {
      if (!enemy.alive) return enemy;

      const dx = player.x - enemy.x;
      const dy = player.y - enemy.y;
      const distance = Math.sqrt(dx * dx + dy * dy);

      // AI behavior
      if (distance < 100) {
        // Chase player
        const angle = Math.atan2(dy, dx);
        enemy.x += Math.cos(angle) * enemy.speed;
        enemy.y += Math.sin(angle) * enemy.speed;

        // Attack player if close enough
        if (distance < 35 && Date.now() - enemy.lastAttack > 1500) {
          const damage = 8 + Math.random() * 12;
          setPlayer(prev => ({
            ...prev,
            health: Math.max(0, prev.health - damage)
          }));

          setPlayerStats((prev: any) => ({
            ...prev,
            health: Math.max(0, prev.health - damage)
          }));

          onAction('player_damaged', {
            damage_taken: damage,
            enemy_type: enemy.type,
            enemy_id: enemy.id,
            player_health_remaining: Math.max(0, player.health - damage),
          });

          enemy.lastAttack = Date.now();
        }
      } else {
        // Wander randomly
        enemy.direction += (Math.random() - 0.5) * 0.2;
        enemy.x += Math.cos(enemy.direction) * enemy.speed * 0.3;
        enemy.y += Math.sin(enemy.direction) * enemy.speed * 0.3;
      }

      // Keep enemies in bounds
      enemy.x = Math.max(14, Math.min(gameState.worldWidth - 14, enemy.x));
      enemy.y = Math.max(14, Math.min(gameState.worldHeight - 14, enemy.y));

      return enemy;
    }));
  }, [player.x, player.y, player.health, gameState.worldWidth, gameState.worldHeight, onAction, setPlayerStats]);

  const checkItemCollisions = useCallback(() => {
    setItems(prev => prev.map(item => {
      if (item.collected) return item;

      const dx = item.x - player.x;
      const dy = item.y - player.y;
      const distance = Math.sqrt(dx * dx + dy * dy);

      if (distance < 25) {
        // Collect item
        let goldGain = 0;
        let healthGain = 0;

        switch (item.type) {
          case 'gold':
            goldGain = 15 + Math.random() * 25;
            setPlayerStats((prev: any) => ({
              ...prev,
              gold: prev.gold + goldGain,
            }));
            break;
          case 'potion':
            healthGain = 30 + Math.random() * 20;
            setPlayerStats((prev: any) => ({
              ...prev,
              health: Math.min(100, prev.health + healthGain),
            }));
            break;
          case 'gem':
            goldGain = 50 + Math.random() * 50;
            setPlayerStats((prev: any) => ({
              ...prev,
              gold: prev.gold + goldGain,
            }));
            break;
        }

        onAction('item_collected', {
          item_type: item.type,
          item_id: item.id,
          gold_gained: goldGain,
          health_gained: healthGain,
          player_x: player.x,
          player_y: player.y,
          item_x: item.x,
          item_y: item.y,
        });

        return { ...item, collected: true };
      }

      return item;
    }));
  }, [player.x, player.y, onAction, setPlayerStats]);

  // Drawing functions
  const drawPlayer = (ctx: CanvasRenderingContext2D, x: number, y: number) => {
    const size = player.width;

    // Body
    ctx.fillStyle = '#4a90e2';
    ctx.fillRect(x - size/2, y - size/2, size, size);

    // Direction indicator
    ctx.fillStyle = '#2c5aa0';
    const dirOffset = 8;
    switch (player.direction) {
      case 'up':
        ctx.fillRect(x - 4, y - size/2 - dirOffset, 8, 6);
        break;
      case 'down':
        ctx.fillRect(x - 4, y + size/2 + dirOffset - 6, 8, 6);
        break;
      case 'left':
        ctx.fillRect(x - size/2 - dirOffset, y - 4, 6, 8);
        break;
      case 'right':
        ctx.fillRect(x + size/2 + dirOffset - 6, y - 4, 6, 8);
        break;
    }

    // Animation effect when moving
    if (player.isMoving) {
      const wobble = Math.sin(player.animationFrame * 0.3) * 2;
      ctx.fillStyle = 'rgba(74, 144, 226, 0.3)';
      ctx.fillRect(x - size/2 + wobble, y - size/2, size, size);
    }

    // Health bar
    const barWidth = 40;
    const barHeight = 6;
    const healthPercent = player.health / player.maxHealth;

    ctx.fillStyle = '#ff4757';
    ctx.fillRect(x - barWidth/2, y - size/2 - 15, barWidth, barHeight);
    ctx.fillStyle = '#2ed573';
    ctx.fillRect(x - barWidth/2, y - size/2 - 15, barWidth * healthPercent, barHeight);
  };

  const drawEnemy = (ctx: CanvasRenderingContext2D, enemy: Enemy) => {
    if (!enemy.alive) return;

    const colors = {
      slime: '#47c83e',
      orc: '#8b4513',
      goblin: '#9b59b6',
      skeleton: '#95a5a6'
    };

    const x = enemy.x - gameState.cameraX;
    const y = enemy.y - gameState.cameraY;
    const size = enemy.width;

    // Enemy body
    ctx.fillStyle = colors[enemy.type];
    ctx.fillRect(x - size/2, y - size/2, size, size);

    // Enemy eyes
    ctx.fillStyle = '#ff4757';
    ctx.fillRect(x - 6, y - 8, 4, 4);
    ctx.fillRect(x + 2, y - 8, 4, 4);

    // Health bar
    const barWidth = 30;
    const barHeight = 4;
    const healthPercent = enemy.health / enemy.maxHealth;

    ctx.fillStyle = '#ff4757';
    ctx.fillRect(x - barWidth/2, y - size/2 - 10, barWidth, barHeight);
    ctx.fillStyle = '#2ed573';
    ctx.fillRect(x - barWidth/2, y - size/2 - 10, barWidth * healthPercent, barHeight);
  };

  const drawItem = (ctx: CanvasRenderingContext2D, item: GameItem) => {
    if (item.collected) return;

    const x = item.x - gameState.cameraX;
    const y = item.y - gameState.cameraY;

    const colors = {
      potion: '#e74c3c',
      gold: '#f1c40f',
      gem: '#9b59b6'
    };

    // Item glow effect
    const glowIntensity = Math.sin(frameCount * 0.1) * 0.3 + 0.7;
    ctx.fillStyle = colors[item.type];
    ctx.globalAlpha = glowIntensity;
    ctx.fillRect(x - 8, y - 8, 16, 16);
    ctx.globalAlpha = 1;

    // Item core
    ctx.fillStyle = colors[item.type];
    ctx.fillRect(x - 6, y - 6, 12, 12);
  };

  const draw = useCallback(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    // Clear canvas
    ctx.fillStyle = '#2d5016';
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    // Update camera to follow player
    const targetCameraX = player.x - canvas.width / 2;
    const targetCameraY = player.y - canvas.height / 2;

    setGameState(prev => ({
      ...prev,
      cameraX: Math.max(0, Math.min(prev.worldWidth - canvas.width, targetCameraX)),
      cameraY: Math.max(0, Math.min(prev.worldHeight - canvas.height, targetCameraY))
    }));

    // Draw grid
    ctx.strokeStyle = 'rgba(255, 255, 255, 0.1)';
    ctx.lineWidth = 1;
    for (let x = 0; x < gameState.worldWidth; x += 50) {
      const screenX = x - gameState.cameraX;
      if (screenX >= -50 && screenX <= canvas.width + 50) {
        ctx.beginPath();
        ctx.moveTo(screenX, 0);
        ctx.lineTo(screenX, canvas.height);
        ctx.stroke();
      }
    }
    for (let y = 0; y < gameState.worldHeight; y += 50) {
      const screenY = y - gameState.cameraY;
      if (screenY >= -50 && screenY <= canvas.height + 50) {
        ctx.beginPath();
        ctx.moveTo(0, screenY);
        ctx.lineTo(canvas.width, screenY);
        ctx.stroke();
      }
    }

    // Draw items
    items.forEach(item => drawItem(ctx, item));

    // Draw enemies
    enemies.forEach(enemy => drawEnemy(ctx, enemy));

    // Draw player
    drawPlayer(ctx, player.x - gameState.cameraX, player.y - gameState.cameraY);

    // Draw UI
    drawUI(ctx, canvas);
  }, [player, enemies, items, gameState, frameCount]);

  const drawUI = (ctx: CanvasRenderingContext2D, canvas: HTMLCanvasElement) => {
    // Stats panel
    ctx.fillStyle = 'rgba(0, 0, 0, 0.7)';
    ctx.fillRect(10, 10, 200, 120);

    ctx.fillStyle = '#ffffff';
    ctx.font = '14px Arial';
    ctx.fillText(`Level: ${playerStats.level}`, 20, 30);
    ctx.fillText(`Health: ${player.health}/100`, 20, 50);
    ctx.fillText(`Gold: ${playerStats.gold}`, 20, 70);
    ctx.fillText(`Experience: ${playerStats.experience}`, 20, 90);

    // Controls
    ctx.fillStyle = '#ffffff';
    ctx.font = '12px Arial';
    ctx.fillText('WASD: Move', 20, 110);
    ctx.fillText('Space: Attack', 20, 125);

    // Enemy count
    const aliveEnemies = enemies.filter(e => e.alive).length;
    ctx.fillText(`Enemies: ${aliveEnemies}`, 20, 145);
  };

  // Game loop
  useEffect(() => {
    const gameLoop = () => {
      updatePlayer();
      updateEnemies();
      checkItemCollisions();
      draw();
      setFrameCount(prev => prev + 1);
      gameLoopRef.current = requestAnimationFrame(gameLoop);
    };

    gameLoopRef.current = requestAnimationFrame(gameLoop);

    return () => {
      if (gameLoopRef.current) {
        cancelAnimationFrame(gameLoopRef.current);
      }
    };
  }, [updatePlayer, updateEnemies, checkItemCollisions, draw]);

  return (
    <div style={{ 
      display: 'flex', 
      flexDirection: 'column', 
      alignItems: 'center',
      background: '#1a1a1a',
      borderRadius: '12px',
      padding: '16px',
    }}>
      <canvas
        ref={canvasRef}
        width={600}
        height={400}
        style={{
          border: '2px solid #444',
          borderRadius: '8px',
          background: '#2d5016',
          cursor: 'crosshair',
        }}
        tabIndex={0}
      />

      <div style={{
        marginTop: '12px',
        color: '#ffffff',
        fontSize: '14px',
        textAlign: 'center',
        maxWidth: '600px',
      }}>
        <p>üéÆ <strong>2D Action RPG</strong> - WASDÎ°ú Ïù¥Îèô, SpaceÎ°ú Í≥µÍ≤©!</p>
        <p>Î™¨Ïä§ÌÑ∞Î•º Ï≤òÏπòÌïòÍ≥† ÏïÑÏù¥ÌÖúÏùÑ ÏàòÏßëÌïòÎ©¥ÏÑú Î†àÎ≤®ÏóÖÌïòÏÑ∏Ïöî!</p>
      </div>
    </div>
  );
};
