import React, { useRef, useEffect, useState, useCallback } from 'react';

interface GameProps {
  onAction: (eventType: string, data: any) => void;
  isSimulating: boolean;
  currentAction: string | null;
  playerStats: any;
  setPlayerStats: (stats: any) => void;
  currentUser: any;
}

interface PlayerData {
  id: string;
  name: string;
  level: number;
  experience: number;
  gold: number;
  gems: number;
  energy: number;
  maxEnergy: number;
  unlockedStages: number;
  inventory: InventoryItem[];
  completedStages: CompletedStage[];
  achievements: string[];
  totalPlayTime: number;
  lastLogin: string;
}

interface InventoryItem {
  id: string;
  name: string;
  type: 'weapon' | 'armor' | 'consumable' | 'material' | 'tower_upgrade' | 'blueprint';
  rarity: 'common' | 'rare' | 'epic' | 'legendary';
  quantity: number;
  stats: any;
  description: string;
  price: number;
  sellPrice: number;
}

interface ShopItem {
  id: string;
  name: string;
  type: 'tower_upgrade' | 'consumable' | 'equipment' | 'blueprint';
  price: number;
  currency: 'gold' | 'gem';
  description: string;
  effect: any;
  requirements?: {
    level?: number;
    completedStages?: number;
  };
  stock?: number;
  category: 'tower' | 'utility' | 'premium';
}

interface CompletedStage {
  stageId: number;
  bestScore: number;
  completionTime: number;
  starsEarned: number;
  completedAt: string;
}

interface StageData {
  id: number;
  name: string;
  difficulty: 'Easy' | 'Normal' | 'Hard' | 'Expert' | 'Hell';
  energyCost: number;
  recommendedLevel: number;
  rewards: StageReward[];
  isUnlocked: boolean;
  path: { x: number; y: number }[];
  enemies: EnemyWave[];
  background: string;
}

interface StageReward {
  type: 'gold' | 'exp' | 'item' | 'gem';
  amount: number;
  item?: InventoryItem;
}

interface Projectile {
  id: string;
  x: number;
  y: number;
  targetX: number;
  targetY: number;
  targetId: string;
  speed: number;
  damage: number;
  type: string;
  towerId: string;
  effects: string[];
  trail: { x: number; y: number }[];
}

interface EnemyWave {
  enemies: Enemy[];
  spawnDelay: number;
}

interface Enemy {
  id: string;
  x: number;
  y: number;
  health: number;
  maxHealth: number;
  speed: number;
  type: string;
  pathIndex: number;
  alive: boolean;
  armor: number;
  reward: StageReward[];
  effects: string[];
  slowDuration: number;
  targetId?: string;
}

interface Tower {
  id: string;
  x: number;
  y: number;
  type: string;
  level: number;
  damage: number;
  range: number;
  attackSpeed: number;
  lastAttack: number;
  cost: number;
  kills: number;
  upgradeCost: number;
  sellValue: number;
  experience: number;
}

interface GameState {
  currentScene: 'lobby' | 'dungeon' | 'stage' | 'shop' | 'inventory' | 'battle' | 'stageComplete' | 'stageFailed';
  selectedStage: number | null;
  battleState: BattleState;
  stageResults: StageResults | null;
  cameraX: number;
  cameraY: number;
  selectedTower: Tower | null;
  selectedTowerType: string | null;
  isPaused: boolean;
  showUpgradeMenu: boolean;
  hoverPosition: { x: number; y: number } | null;
  shopCategory: 'tower' | 'utility' | 'premium';
  selectedShopItem: ShopItem | null;
  inventoryPage: number;
}

interface BattleState {
  wave: number;
  lives: number;
  score: number;
  isWaveActive: boolean;
  towers: Tower[];
  enemies: Enemy[];
  projectiles: Projectile[];
  waveProgress: number;
  battleTime: number;
  totalEnemies: number;
  enemiesSpawned: number;
  nextEnemySpawn: number;
}

interface StageResults {
  victory: boolean;
  score: number;
  starsEarned: number;
  timeBonus: number;
  rewards: StageReward[];
  newUnlocks: string[];
}

export const CompleteGameSystem: React.FC<GameProps> = ({
  onAction,
  playerStats,
  setPlayerStats,
  currentUser
}) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const gameLoopRef = useRef<number>();
  const [frameCount, setFrameCount] = useState(0);

  // Player persistent data
  const [playerData, setPlayerData] = useState<PlayerData>({
    id: currentUser.user_id,
    name: "Hero",
    level: 1,
    experience: 0,
    gold: 1000,
    gems: 50,
    energy: 100,
    maxEnergy: 100,
    unlockedStages: 1,
    inventory: [
      { 
        id: 'sword1', 
        name: 'Iron Sword', 
        type: 'weapon', 
        rarity: 'common', 
        quantity: 1, 
        stats: { attack: 10 }, 
        description: 'A basic iron sword',
        price: 100,
        sellPrice: 50
      },
      { 
        id: 'potion1', 
        name: 'Health Potion', 
        type: 'consumable', 
        rarity: 'common', 
        quantity: 5, 
        stats: { heal: 50 },
        description: 'Restores health',
        price: 25,
        sellPrice: 10
      }
    ],
    completedStages: [],
    achievements: [],
    totalPlayTime: 0,
    lastLogin: new Date().toISOString()
  });

  // Game state
  const [gameState, setGameState] = useState<GameState>({
    currentScene: 'lobby',
    selectedStage: null,
    battleState: {
      wave: 1,
      lives: 20,
      score: 0,
      isWaveActive: false,
      towers: [],
      enemies: [],
      projectiles: [],
      waveProgress: 0,
      battleTime: 0,
      totalEnemies: 0,
      enemiesSpawned: 0,
      nextEnemySpawn: 0
    },
    stageResults: null,
    cameraX: 0,
    cameraY: 0,
    selectedTower: null,
    selectedTowerType: null,
    isPaused: false,
    showUpgradeMenu: false,
    hoverPosition: null,
    shopCategory: 'tower',
    selectedShopItem: null,
    inventoryPage: 0
  });

  // Stage definitions
  const stageDefinitions: StageData[] = [
    {
      id: 1,
      name: "Green Fields",
      difficulty: 'Easy',
      energyCost: 10,
      recommendedLevel: 1,
      rewards: [
        { type: 'gold', amount: 100 },
        { type: 'exp', amount: 50 }
      ],
      isUnlocked: true,
      path: [
        { x: 0, y: 200 }, { x: 150, y: 200 }, { x: 150, y: 100 },
        { x: 300, y: 100 }, { x: 300, y: 300 }, { x: 600, y: 300 }
      ],
      enemies: [
        { enemies: [], spawnDelay: 60 }
      ],
      background: '#2d5016'
    },
    {
      id: 2,
      name: "Dark Forest",
      difficulty: 'Normal',
      energyCost: 15,
      recommendedLevel: 3,
      rewards: [
        { type: 'gold', amount: 200 },
        { type: 'exp', amount: 80 },
        { type: 'gem', amount: 5 }
      ],
      isUnlocked: false,
      path: [
        { x: 0, y: 150 }, { x: 100, y: 150 }, { x: 100, y: 50 },
        { x: 250, y: 50 }, { x: 250, y: 250 }, { x: 400, y: 250 },
        { x: 400, y: 100 }, { x: 600, y: 100 }
      ],
      enemies: [
        { enemies: [], spawnDelay: 50 }
      ],
      background: '#1a3d1a'
    },
    {
      id: 3,
      name: "Ancient Ruins",
      difficulty: 'Hard',
      energyCost: 20,
      recommendedLevel: 5,
      rewards: [
        { type: 'gold', amount: 350 },
        { type: 'exp', amount: 120 },
        { type: 'gem', amount: 10 }
      ],
      isUnlocked: false,
      path: [
        { x: 0, y: 200 }, { x: 80, y: 200 }, { x: 80, y: 80 },
        { x: 200, y: 80 }, { x: 200, y: 320 }, { x: 320, y: 320 },
        { x: 320, y: 120 }, { x: 500, y: 120 }, { x: 500, y: 280 },
        { x: 600, y: 280 }
      ],
      enemies: [
        { enemies: [], spawnDelay: 40 }
      ],
      background: '#4a4a2a'
    },
    {
      id: 4,
      name: "Dragon's Lair",
      difficulty: 'Expert',
      energyCost: 25,
      recommendedLevel: 8,
      rewards: [
        { type: 'gold', amount: 500 },
        { type: 'exp', amount: 200 },
        { type: 'gem', amount: 20 }
      ],
      isUnlocked: false,
      path: [
        { x: 0, y: 200 }, { x: 150, y: 200 }, { x: 150, y: 100 },
        { x: 450, y: 100 }, { x: 450, y: 300 }, { x: 600, y: 300 }
      ],
      enemies: [
        { enemies: [], spawnDelay: 35 }
      ],
      background: '#5d1a1a'
    },
    {
      id: 5,
      name: "Demon Realm",
      difficulty: 'Hell',
      energyCost: 30,
      recommendedLevel: 12,
      rewards: [
        { type: 'gold', amount: 1000 },
        { type: 'exp', amount: 400 },
        { type: 'gem', amount: 50 }
      ],
      isUnlocked: false,
      path: [
        { x: 0, y: 200 }, { x: 100, y: 200 }, { x: 200, y: 200 },
        { x: 300, y: 200 }, { x: 400, y: 200 }, { x: 500, y: 200 },
        { x: 600, y: 200 }
      ],
      enemies: [
        { enemies: [], spawnDelay: 30 }
      ],
      background: '#2d0d0d'
    }
  ];

  // Tower types
  const towerTypes = {
    'archer': { 
      cost: 80, damage: 25, range: 120, speed: 30, 
      color: '#8B4513', icon: 'üèπ', 
      description: 'Fast shooting archer tower',
      upgradeMultiplier: 1.5,
      effects: []
    },
    'cannon': { 
      cost: 150, damage: 60, range: 100, speed: 60, 
      color: '#696969', icon: 'üí£', 
      description: 'Heavy damage explosive cannon',
      upgradeMultiplier: 1.6,
      effects: ['splash']
    },
    'magic': { 
      cost: 220, damage: 40, range: 140, speed: 40, 
      color: '#9400D3', icon: '‚ú®', 
      description: 'Magic tower with penetration',
      upgradeMultiplier: 1.7,
      effects: ['penetrate']
    },
    'ice': { 
      cost: 180, damage: 20, range: 110, speed: 35, 
      color: '#00BFFF', icon: '‚ùÑÔ∏è', 
      description: 'Slows enemies and deals damage',
      upgradeMultiplier: 1.4,
      effects: ['slow']
    },
    'lightning': { 
      cost: 300, damage: 80, range: 150, speed: 50, 
      color: '#FFD700', icon: '‚ö°', 
      description: 'Chain lightning that hits multiple enemies',
      upgradeMultiplier: 1.8,
      effects: ['chain']
    }
  };

  // Enemy types
  const enemyTypes = {
    'goblin': { health: 80, speed: 2, reward: 15, color: '#228B22', icon: 'üëπ', armor: 0, size: 12, description: 'Fast and weak' },
    'orc': { health: 150, speed: 1.5, reward: 25, color: '#8B4513', icon: 'üó°Ô∏è', armor: 2, size: 14, description: 'Balanced warrior' },
    'troll': { health: 300, speed: 1, reward: 40, color: '#2F4F4F', icon: 'üèîÔ∏è', armor: 5, size: 16, description: 'Heavily armored' },
    'dragon': { health: 500, speed: 0.8, reward: 80, color: '#DC143C', icon: 'üêâ', armor: 8, size: 20, description: 'Flying boss' },
    'demon': { health: 800, speed: 0.6, reward: 150, color: '#8B0000', icon: 'üòà', armor: 12, size: 24, description: 'Ultimate boss' }
  };

  // Shop items
  const shopItems: ShopItem[] = [
    // Tower upgrades
    {
      id: 'damage_boost',
      name: 'Damage Boost Potion',
      type: 'tower_upgrade',
      price: 150,
      currency: 'gold',
      description: 'Increases tower damage by 25% for next battle',
      effect: { type: 'damage_boost', value: 0.25, duration: 1 },
      category: 'tower'
    },
    {
      id: 'range_extender',
      name: 'Range Extender',
      type: 'tower_upgrade', 
      price: 200,
      currency: 'gold',
      description: 'Increases tower range by 20% for next battle',
      effect: { type: 'range_boost', value: 0.2, duration: 1 },
      category: 'tower'
    },
    {
      id: 'speed_upgrade',
      name: 'Attack Speed Boost',
      type: 'tower_upgrade',
      price: 175,
      currency: 'gold', 
      description: 'Increases tower attack speed by 30% for next battle',
      effect: { type: 'speed_boost', value: 0.3, duration: 1 },
      category: 'tower'
    },

    // Utility items
    {
      id: 'extra_life',
      name: 'Extra Life',
      type: 'consumable',
      price: 100,
      currency: 'gold',
      description: 'Start next battle with +5 lives',
      effect: { type: 'extra_lives', value: 5 },
      category: 'utility'
    },
    {
      id: 'starting_gold',
      name: 'Treasure Chest',
      type: 'consumable',
      price: 80,
      currency: 'gold',
      description: 'Start next battle with +200 gold',
      effect: { type: 'starting_gold', value: 200 },
      category: 'utility'
    },
    {
      id: 'energy_refill',
      name: 'Energy Potion',
      type: 'consumable',
      price: 5,
      currency: 'gem',
      description: 'Instantly restore 20 energy',
      effect: { type: 'restore_energy', value: 20 },
      category: 'utility'
    },

    // Premium items
    {
      id: 'tower_blueprint_laser',
      name: 'Laser Tower Blueprint',
      type: 'blueprint',
      price: 50,
      currency: 'gem',
      description: 'Unlocks powerful Laser Tower (Permanent)',
      effect: { type: 'unlock_tower', value: 'laser' },
      requirements: { level: 5, completedStages: 3 },
      category: 'premium'
    },
    {
      id: 'double_exp',
      name: 'Experience Multiplier',
      type: 'consumable',
      price: 20,
      currency: 'gem',
      description: 'Double EXP gain for 3 battles',
      effect: { type: 'exp_multiplier', value: 2, duration: 3 },
      category: 'premium'
    },
    {
      id: 'auto_upgrade',
      name: 'Auto-Upgrade Kit',
      type: 'consumable',
      price: 30,
      currency: 'gem',
      description: 'Automatically upgrade first tower placed to level 2',
      effect: { type: 'auto_upgrade', value: 1 },
      category: 'premium'
    }
  ];

  // Initialize session
  useEffect(() => {
    onAction('session_started', {
      player_id: playerData.id,
      player_level: playerData.level,
      total_gold: playerData.gold,
      unlocked_stages: playerData.unlockedStages,
      login_streak: 1
    });
  }, []);

  // Energy regeneration
  useEffect(() => {
    const energyRegen = setInterval(() => {
      setPlayerData(prev => ({
        ...prev,
        energy: Math.min(prev.maxEnergy, prev.energy + 1)
      }));
    }, 60000); // 1 energy per minute

    return () => clearInterval(energyRegen);
  }, []);

  // Level up check
  useEffect(() => {
    const expNeeded = playerData.level * 100;
    if (playerData.experience >= expNeeded) {
      const newLevel = playerData.level + 1;
      setPlayerData(prev => ({
        ...prev,
        level: newLevel,
        experience: prev.experience - expNeeded,
        maxEnergy: prev.maxEnergy + 10
      }));

      onAction('player_level_up', {
        player_id: playerData.id,
        old_level: playerData.level,
        new_level: newLevel,
        new_max_energy: playerData.maxEnergy + 10,
        total_playtime: playerData.totalPlayTime
      });
    }
  }, [playerData.experience, playerData.level, playerData.maxEnergy, playerData.totalPlayTime, playerData.id, onAction]);

  // Keyboard handling
  const handleKeyDown = useCallback((e: KeyboardEvent) => {
    // Prevent page scrolling
    if (['ArrowUp', 'ArrowDown', 'ArrowLeft', 'ArrowRight', 'w', 'a', 's', 'd', ' ', 'Enter', 'Escape'].includes(e.key)) {
      e.preventDefault();
      e.stopPropagation();
    }

    switch (e.key.toLowerCase()) {
      case 'escape':
        if (gameState.currentScene === 'battle') {
          pauseBattle();
        } else if (gameState.currentScene !== 'lobby') {
          setGameState(prev => ({ ...prev, currentScene: 'lobby' }));
        }
        break;
      case ' ':
        if (gameState.currentScene === 'battle') {
          startWave();
        }
        break;
      case '1':
      case '2':
      case '3':
      case '4':
      case '5':
        if (gameState.currentScene === 'battle') {
          const towerIndex = parseInt(e.key) - 1;
          const towerTypeKeys = Object.keys(towerTypes);
          if (towerIndex < towerTypeKeys.length) {
            setGameState(prev => ({
              ...prev,
              selectedTowerType: towerTypeKeys[towerIndex],
              selectedTower: null
            }));
          }
        }
        break;
    }
  }, [gameState.currentScene]);

  // Mouse/Click handling
  const handleCanvasClick = useCallback((e: React.MouseEvent<HTMLCanvasElement>) => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const rect = canvas.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    switch (gameState.currentScene) {
      case 'lobby':
        handleLobbyClick(x, y);
        break;
      case 'dungeon':
        handleDungeonClick(x, y);
        break;
      case 'battle':
        handleBattleClick(x, y);
        break;
      case 'shop':
        handleShopClick(x, y);
        break;
      case 'inventory':
        handleInventoryClick(x, y);
        break;
      case 'stageComplete':
      case 'stageFailed':
        handleResultsClick(x, y);
        break;
    }
  }, [gameState.currentScene]);

  const handleLobbyClick = (x: number, y: number) => {
    // Dungeon button
    if (x >= 250 && x <= 350 && y >= 200 && y <= 250) {
      setGameState(prev => ({ ...prev, currentScene: 'dungeon' }));

      onAction('menu_navigation', {
        player_id: playerData.id,
        from_scene: 'lobby',
        to_scene: 'dungeon',
        player_level: playerData.level
      });
    }

    // Shop button
    if (x >= 100 && x <= 200 && y >= 300 && y <= 350) {
      setGameState(prev => ({ ...prev, currentScene: 'shop' }));

      onAction('menu_navigation', {
        player_id: playerData.id,
        from_scene: 'lobby',
        to_scene: 'shop',
        player_level: playerData.level
      });
    }

    // Inventory button
    if (x >= 400 && x <= 500 && y >= 300 && y <= 350) {
      setGameState(prev => ({ ...prev, currentScene: 'inventory' }));

      onAction('menu_navigation', {
        player_id: playerData.id,
        from_scene: 'lobby',
        to_scene: 'inventory',
        player_level: playerData.level
      });
    }
  };

  const handleDungeonClick = (x: number, y: number) => {
    // Stage selection
    stageDefinitions.forEach((stage, index) => {
      const stageX = 50 + (index % 3) * 200;
      const stageY = 100 + Math.floor(index / 3) * 150;

      if (x >= stageX && x <= stageX + 150 && y >= stageY && y <= stageY + 100) {
        if (stage.id <= playerData.unlockedStages && playerData.energy >= stage.energyCost) {
          enterStage(stage.id);
        }
      }
    });

    // Back button
    if (x >= 50 && x <= 150 && y >= 350 && y <= 380) {
      setGameState(prev => ({ ...prev, currentScene: 'lobby' }));
    }
  };

  const enterStage = (stageId: number) => {
    const stage = stageDefinitions.find(s => s.id === stageId);
    if (!stage) return;

    // Consume energy
    setPlayerData(prev => ({ ...prev, energy: prev.energy - stage.energyCost }));

    // Initialize battle
    setGameState(prev => ({
      ...prev,
      currentScene: 'battle',
      selectedStage: stageId,
      battleState: {
        wave: 1,
        lives: 20,
        score: 0,
        isWaveActive: false,
        towers: [],
        enemies: [],
        projectiles: [],
        waveProgress: 0,
        battleTime: 0,
        totalEnemies: 8 + 1 * 2, // 8 base + wave * 2
        enemiesSpawned: 0,
        nextEnemySpawn: 0
      },
      selectedTower: null,
      selectedTowerType: null,
      isPaused: false,
      showUpgradeMenu: false
    }));

    onAction('stage_entered', {
      player_id: playerData.id,
      stage_id: stageId,
      stage_name: stage.name,
      energy_cost: stage.energyCost,
      remaining_energy: playerData.energy - stage.energyCost,
      player_level: playerData.level,
      recommended_level: stage.recommendedLevel
    });
  };

  const handleBattleClick = (x: number, y: number) => {
    // UI area (bottom 80px)
    if (y > 320) return;

    if (gameState.selectedTowerType) {
      // Place tower
      if (canPlaceTower(x, y)) {
        placeTower(x, y, gameState.selectedTowerType);
      }
    } else {
      // Select existing tower
      const clickedTower = gameState.battleState.towers.find(tower => {
        const distance = Math.sqrt((x - tower.x) ** 2 + (y - tower.y) ** 2);
        return distance < 25;
      });

      if (clickedTower) {
        setGameState(prev => ({
          ...prev,
          selectedTower: clickedTower,
          selectedTowerType: null
        }));
      }
    }
  };

  const canPlaceTower = (x: number, y: number): boolean => {
    if (!gameState.selectedStage) return false;

    const stage = stageDefinitions.find(s => s.id === gameState.selectedStage);
    if (!stage) return false;

    // Check distance from path
    for (const point of stage.path) {
      const distance = Math.sqrt((x - point.x) ** 2 + (y - point.y) ** 2);
      if (distance < 40) return false;
    }

    // Check distance from other towers
    for (const tower of gameState.battleState.towers) {
      const distance = Math.sqrt((x - tower.x) ** 2 + (y - tower.y) ** 2);
      if (distance < 50) return false;
    }

    return x > 25 && x < 575 && y > 25 && y < 295;
  };

  const placeTower = (x: number, y: number, type: string) => {
    const towerConfig = towerTypes[type as keyof typeof towerTypes];
    if (playerData.gold < towerConfig.cost) return;

    const newTower: Tower = {
      id: `tower_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`,
      x, y, type,
      level: 1,
      damage: towerConfig.damage,
      range: towerConfig.range,
      attackSpeed: towerConfig.speed,
      lastAttack: 0,
      cost: towerConfig.cost,
      kills: 0,
      upgradeCost: Math.floor(towerConfig.cost * towerConfig.upgradeMultiplier),
      sellValue: Math.floor(towerConfig.cost * 0.7),
      experience: 0
    };

    setPlayerData(prev => ({ ...prev, gold: prev.gold - towerConfig.cost }));
    setGameState(prev => ({
      ...prev,
      battleState: {
        ...prev.battleState,
        towers: [...prev.battleState.towers, newTower]
      },
      selectedTowerType: null
    }));

    onAction('tower_placed', {
      player_id: playerData.id,
      stage_id: gameState.selectedStage,
      tower_type: type,
      tower_cost: towerConfig.cost,
      position_x: x,
      position_y: y,
      remaining_gold: playerData.gold - towerConfig.cost,
      wave_number: gameState.battleState.wave
    });
  };

  const startWave = () => {
    if (gameState.battleState.isWaveActive) return;

    setGameState(prev => ({
      ...prev,
      battleState: {
        ...prev.battleState,
        isWaveActive: true,
        waveProgress: 0
      }
    }));

    onAction('wave_started', {
      player_id: playerData.id,
      stage_id: gameState.selectedStage,
      wave_number: gameState.battleState.wave,
      towers_count: gameState.battleState.towers.length,
      current_gold: playerData.gold
    });

    // Spawn enemies
    spawnEnemyWave();
  };

  const spawnEnemyWave = () => {
    if (!gameState.selectedStage) return;

    const stage = stageDefinitions.find(s => s.id === gameState.selectedStage);
    if (!stage) return;

    const enemyCount = 8 + gameState.battleState.wave * 2;
    const enemyTypeKeys = Object.keys(enemyTypes);

    setGameState(prev => ({
      ...prev,
      battleState: {
        ...prev.battleState,
        totalEnemies: enemyCount,
        enemiesSpawned: 0,
        nextEnemySpawn: frameCount + 60 // Start spawning in 1 second
      }
    }));
  };

  const spawnSingleEnemy = () => {
    if (!gameState.selectedStage) return;

    const stage = stageDefinitions.find(s => s.id === gameState.selectedStage);
    if (!stage) return;

    const enemyTypeKeys = Object.keys(enemyTypes);
    const waveTypeIndex = Math.min(gameState.battleState.wave - 1, enemyTypeKeys.length - 1);
    const possibleTypes = enemyTypeKeys.slice(0, waveTypeIndex + 1);
    const enemyType = possibleTypes[Math.floor(Math.random() * possibleTypes.length)];
    const enemyConfig = enemyTypes[enemyType as keyof typeof enemyTypes];

    const newEnemy: Enemy = {
      id: `enemy_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`,
      x: stage.path[0].x,
      y: stage.path[0].y,
      health: enemyConfig.health * gameState.selectedStage!,
      maxHealth: enemyConfig.health * gameState.selectedStage!,
      speed: enemyConfig.speed,
      type: enemyType,
      pathIndex: 0,
      alive: true,
      armor: enemyConfig.armor,
      effects: [],
      slowDuration: 0,
      reward: [
        { type: 'gold', amount: enemyConfig.reward },
        { type: 'exp', amount: enemyConfig.reward / 2 }
      ]
    };

    setGameState(prev => ({
      ...prev,
      battleState: {
        ...prev.battleState,
        enemies: [...prev.battleState.enemies, newEnemy],
        enemiesSpawned: prev.battleState.enemiesSpawned + 1,
        nextEnemySpawn: frameCount + Math.max(30, 80 - gameState.battleState.wave * 5)
      }
    }));

    onAction('enemy_spawned', {
      player_id: playerData.id,
      stage_id: gameState.selectedStage,
      enemy_type: enemyType,
      enemy_id: newEnemy.id,
      wave_number: gameState.battleState.wave,
      enemies_spawned: gameState.battleState.enemiesSpawned + 1,
      total_enemies: gameState.battleState.totalEnemies
    });
  };

  const pauseBattle = () => {
    onAction('battle_paused', {
      player_id: playerData.id,
      stage_id: gameState.selectedStage,
      wave_number: gameState.battleState.wave,
      battle_time: gameState.battleState.battleTime,
      current_score: gameState.battleState.score
    });
  };

  const completeStage = (victory: boolean) => {
    if (!gameState.selectedStage) return;

    const stage = stageDefinitions.find(s => s.id === gameState.selectedStage);
    if (!stage) return;

    // Stop all battle updates
    setGameState(prev => ({
      ...prev,
      battleState: {
        ...prev.battleState,
        isWaveActive: false
      },
      isPaused: true
    }));

    const starsEarned = victory ? calculateStars() : 0;
    const rewards = victory ? stage.rewards : [];

    // Apply rewards
    if (victory) {
      let goldGained = 0;
      let expGained = 0;
      let gemsGained = 0;

      rewards.forEach(reward => {
        switch (reward.type) {
          case 'gold':
            goldGained += reward.amount;
            break;
          case 'exp':
            expGained += reward.amount;
            break;
          case 'gem':
            gemsGained += reward.amount;
            break;
        }
      });

      setPlayerData(prev => ({
        ...prev,
        gold: prev.gold + goldGained,
        experience: prev.experience + expGained,
        gems: prev.gems + gemsGained,
        unlockedStages: Math.max(prev.unlockedStages, gameState.selectedStage! + 1)
      }));

      // Record completion
      const completion: CompletedStage = {
        stageId: gameState.selectedStage,
        bestScore: gameState.battleState.score,
        completionTime: gameState.battleState.battleTime,
        starsEarned,
        completedAt: new Date().toISOString()
      };

      setPlayerData(prev => {
        const existingIndex = prev.completedStages.findIndex(c => c.stageId === gameState.selectedStage);
        const newCompletedStages = [...prev.completedStages];

        if (existingIndex >= 0) {
          newCompletedStages[existingIndex] = completion;
        } else {
          newCompletedStages.push(completion);
        }

        return { ...prev, completedStages: newCompletedStages };
      });
    } else {
      // On defeat, refund some energy (50%)
      const energyRefund = Math.floor(stage.energyCost * 0.5);
      setPlayerData(prev => ({
        ...prev,
        energy: Math.min(prev.maxEnergy, prev.energy + energyRefund)
      }));
    }

    const results: StageResults = {
      victory,
      score: gameState.battleState.score,
      starsEarned,
      timeBonus: victory ? Math.max(0, 300 - gameState.battleState.battleTime) : 0,
      rewards: victory ? rewards : [{ type: 'gold', amount: Math.floor(gameState.battleState.score / 100) }], // Small consolation reward
      newUnlocks: victory && gameState.selectedStage === playerData.unlockedStages ? 
        [`Stage ${gameState.selectedStage + 1} Unlocked!`] : []
    };

    // Show results screen with a delay
    setTimeout(() => {
      setGameState(prev => ({
        ...prev,
        currentScene: victory ? 'stageComplete' : 'stageFailed',
        stageResults: results
      }));
    }, 1000);

    onAction(victory ? 'stage_victory' : 'stage_defeat', {
      player_id: playerData.id,
      stage_id: gameState.selectedStage,
      stage_name: stage.name,
      final_score: gameState.battleState.score,
      stars_earned: starsEarned,
      battle_time: gameState.battleState.battleTime,
      towers_built: gameState.battleState.towers.length,
      waves_completed: gameState.battleState.wave,
      enemies_defeated: gameState.battleState.towers.reduce((sum, tower) => sum + tower.kills, 0),
      gold_gained: victory ? rewards.find(r => r.type === 'gold')?.amount || 0 : Math.floor(gameState.battleState.score / 100),
      exp_gained: victory ? rewards.find(r => r.type === 'exp')?.amount || 0 : 0,
      new_unlocks: results.newUnlocks,
      failure_reason: victory ? null : gameState.battleState.lives <= 0 ? 'lives_depleted' : 'unknown'
    });
  };

  const calculateStars = (): number => {
    const baseScore = gameState.battleState.score;
    const timeBonus = Math.max(0, 300 - gameState.battleState.battleTime);
    const livesBonus = gameState.battleState.lives * 10;

    const totalScore = baseScore + timeBonus + livesBonus;

    if (totalScore >= 1000) return 3;
    if (totalScore >= 500) return 2;
    return 1;
  };

  const handleShopClick = (x: number, y: number) => {
    // Category tabs
    const categories = ['tower', 'utility', 'premium'];
    categories.forEach((category, index) => {
      const tabX = 80 + index * 150;
      if (x >= tabX && x <= tabX + 140 && y >= 50 && y <= 80) {
        setGameState(prev => ({ ...prev, shopCategory: category as any }));
      }
    });

    // Shop items
    const categoryItems = shopItems.filter(item => item.category === gameState.shopCategory);
    categoryItems.forEach((item, index) => {
      const itemX = 80 + (index % 2) * 220;
      const itemY = 100 + Math.floor(index / 2) * 100;

      if (x >= itemX && x <= itemX + 200 && y >= itemY && y <= itemY + 90) {
        purchaseItem(item);
      }
    });

    // Back button
    if (x >= 50 && x <= 150 && y >= 350 && y <= 380) {
      setGameState(prev => ({ ...prev, currentScene: 'lobby' }));
    }
  };

  const handleInventoryClick = (x: number, y: number) => {
    // Inventory items
    const itemsPerRow = 5;
    const startY = 100;

    playerData.inventory.forEach((item, index) => {
      const itemX = 50 + (index % itemsPerRow) * 100;
      const itemY = startY + Math.floor(index / itemsPerRow) * 80;

      if (x >= itemX && x <= itemX + 80 && y >= itemY && y <= itemY + 60) {
        useInventoryItem(item);
      }
    });

    // Back button
    if (x >= 50 && x <= 150 && y >= 350 && y <= 380) {
      setGameState(prev => ({ ...prev, currentScene: 'lobby' }));
    }
  };

  const purchaseItem = (item: ShopItem) => {
    // Check requirements
    if (item.requirements) {
      if (item.requirements.level && playerData.level < item.requirements.level) {
        onAction('purchase_failed', {
          player_id: playerData.id,
          item_id: item.id,
          reason: 'level_requirement',
          required_level: item.requirements.level,
          current_level: playerData.level
        });
        return;
      }

      if (item.requirements.completedStages && playerData.completedStages.length < item.requirements.completedStages) {
        onAction('purchase_failed', {
          player_id: playerData.id,
          item_id: item.id,
          reason: 'stage_requirement',
          required_stages: item.requirements.completedStages,
          completed_stages: playerData.completedStages.length
        });
        return;
      }
    }

    // Check currency
    const hasEnoughCurrency = item.currency === 'gold' ? 
      playerData.gold >= item.price : 
      playerData.gems >= item.price;

    if (!hasEnoughCurrency) {
      onAction('purchase_failed', {
        player_id: playerData.id,
        item_id: item.id,
        reason: 'insufficient_currency',
        required_amount: item.price,
        current_amount: item.currency === 'gold' ? playerData.gold : playerData.gems,
        currency: item.currency
      });
      return;
    }

    // Process purchase
    if (item.currency === 'gold') {
      setPlayerData(prev => ({ ...prev, gold: prev.gold - item.price }));
    } else {
      setPlayerData(prev => ({ ...prev, gems: prev.gems - item.price }));
    }

    // Add to inventory or apply effect
    if (item.type === 'consumable' || item.type === 'tower_upgrade') {
      const inventoryItem: InventoryItem = {
        id: item.id,
        name: item.name,
        type: item.type,
        rarity: 'common',
        quantity: 1,
        stats: item.effect,
        description: item.description,
        price: item.price,
        sellPrice: Math.floor(item.price * 0.5)
      };

      setPlayerData(prev => {
        const existingIndex = prev.inventory.findIndex(i => i.id === item.id);
        if (existingIndex >= 0) {
          const newInventory = [...prev.inventory];
          newInventory[existingIndex].quantity += 1;
          return { ...prev, inventory: newInventory };
        } else {
          return { ...prev, inventory: [...prev.inventory, inventoryItem] };
        }
      });
    }

    onAction('item_purchased', {
      player_id: playerData.id,
      item_id: item.id,
      item_name: item.name,
      price: item.price,
      currency: item.currency,
      remaining_gold: item.currency === 'gold' ? playerData.gold - item.price : playerData.gold,
      remaining_gems: item.currency === 'gem' ? playerData.gems - item.price : playerData.gems
    });
  };

  const useInventoryItem = (item: InventoryItem) => {
    if (item.quantity <= 0) return;

    // Apply item effect based on type
    switch (item.stats?.type) {
      case 'restore_energy':
        setPlayerData(prev => ({
          ...prev,
          energy: Math.min(prev.maxEnergy, prev.energy + item.stats.value)
        }));
        break;
      // Other consumable effects would be applied in battle
    }

    // Reduce quantity
    if (item.type === 'consumable') {
      setPlayerData(prev => ({
        ...prev,
        inventory: prev.inventory.map(i => 
          i.id === item.id ? { ...i, quantity: i.quantity - 1 } : i
        ).filter(i => i.quantity > 0)
      }));
    }

    onAction('item_used', {
      player_id: playerData.id,
      item_id: item.id,
      item_name: item.name,
      effect: item.stats
    });
  };

  const handleResultsClick = (x: number, y: number) => {
    // Continue button
    if (x >= 200 && x <= 400 && y >= 300 && y <= 350) {
      returnToLobby();
    }

    // Retry button (only for failures)
    if (!gameState.stageResults?.victory && x >= 100 && x <= 200 && y >= 320 && y <= 350) {
      retryStage();
    }
  };

  const returnToLobby = () => {
    setGameState(prev => ({
      ...prev,
      currentScene: 'lobby',
      stageResults: null,
      selectedStage: null,
      battleState: {
        wave: 1,
        lives: 20,
        score: 0,
        isWaveActive: false,
        towers: [],
        enemies: [],
        projectiles: [],
        waveProgress: 0,
        battleTime: 0,
        totalEnemies: 0,
        enemiesSpawned: 0,
        nextEnemySpawn: 0
      },
      selectedTower: null,
      selectedTowerType: null,
      isPaused: false,
      showUpgradeMenu: false
    }));

    onAction('returned_to_lobby', {
      player_id: playerData.id,
      from_scene: gameState.stageResults?.victory ? 'stage_complete' : 'stage_failed',
      player_level: playerData.level,
      total_gold: playerData.gold,
      total_energy: playerData.energy
    });
  };

  const retryStage = () => {
    if (!gameState.selectedStage) return;
    const stage = stageDefinitions.find(s => s.id === gameState.selectedStage);
    if (!stage || playerData.energy < stage.energyCost) return;

    // Consume energy again
    setPlayerData(prev => ({ ...prev, energy: prev.energy - stage.energyCost }));

    // Reset battle state
    setGameState(prev => ({
      ...prev,
      currentScene: 'battle',
      stageResults: null,
      battleState: {
        wave: 1,
        lives: 20,
        score: 0,
        isWaveActive: false,
        towers: [],
        enemies: [],
        projectiles: [],
        waveProgress: 0,
        battleTime: 0,
        totalEnemies: 8 + 1 * 2,
        enemiesSpawned: 0,
        nextEnemySpawn: 0
      },
      selectedTower: null,
      selectedTowerType: null,
      isPaused: false,
      showUpgradeMenu: false
    }));

    onAction('stage_retried', {
      player_id: playerData.id,
      stage_id: gameState.selectedStage,
      stage_name: stage.name,
      energy_cost: stage.energyCost,
      remaining_energy: playerData.energy - stage.energyCost
    });
  };

  // Game loop updates
  const updateBattle = useCallback(() => {
    if (gameState.currentScene !== 'battle' || gameState.isPaused) return;

    // Update battle time
    setGameState(prev => ({
      ...prev,
      battleState: {
        ...prev.battleState,
        battleTime: prev.battleState.battleTime + 1
      }
    }));

    // Spawn enemies if wave is active
    if (gameState.battleState.isWaveActive && 
        frameCount >= gameState.battleState.nextEnemySpawn &&
        gameState.battleState.enemiesSpawned < gameState.battleState.totalEnemies) {
      spawnSingleEnemy();
    }

    // Update enemies
    updateEnemies();

    // Update towers
    updateTowers();

    // Update projectiles
    updateProjectiles();

    // Check win/lose conditions
    checkBattleEnd();
  }, [gameState.currentScene, gameState.battleState.isWaveActive, gameState.isPaused, frameCount]);

  const updateEnemies = () => {
    if (!gameState.selectedStage) return;

    const stage = stageDefinitions.find(s => s.id === gameState.selectedStage);
    if (!stage) return;

    setGameState(prev => ({
      ...prev,
      battleState: {
        ...prev.battleState,
        enemies: prev.battleState.enemies.map(enemy => {
          if (!enemy.alive) return enemy;

          // Update slow effect
          let currentSpeed = enemy.speed;
          if (enemy.slowDuration > 0) {
            currentSpeed *= 0.5; // 50% speed reduction
            enemy.slowDuration--;
          }

          const currentTarget = stage.path[enemy.pathIndex + 1];
          if (currentTarget) {
            const dx = currentTarget.x - enemy.x;
            const dy = currentTarget.y - enemy.y;
            const distance = Math.sqrt(dx * dx + dy * dy);

            if (distance < 5) {
              enemy.pathIndex++;
              if (enemy.pathIndex >= stage.path.length - 1) {
                // Enemy reached end
                setGameState(prev2 => ({
                  ...prev2,
                  battleState: {
                    ...prev2.battleState,
                    lives: prev2.battleState.lives - 1
                  }
                }));

                onAction('life_lost', {
                  player_id: playerData.id,
                  stage_id: gameState.selectedStage,
                  enemy_type: enemy.type,
                  remaining_lives: gameState.battleState.lives - 1,
                  wave_number: gameState.battleState.wave
                });

                return { ...enemy, alive: false };
              }
            } else {
              const moveX = (dx / distance) * currentSpeed;
              const moveY = (dy / distance) * currentSpeed;
              enemy.x += moveX;
              enemy.y += moveY;
            }
          }

          return enemy;
        }).filter(enemy => enemy.alive || enemy.pathIndex < stage.path.length - 1)
      }
    }));
  };

  const updateProjectiles = () => {
    setGameState(prev => ({
      ...prev,
      battleState: {
        ...prev.battleState,
        projectiles: prev.battleState.projectiles.filter(proj => {
          // Update trail
          proj.trail.push({ x: proj.x, y: proj.y });
          if (proj.trail.length > 5) {
            proj.trail.shift();
          }

          // Move projectile towards target
          const dx = proj.targetX - proj.x;
          const dy = proj.targetY - proj.y;
          const distance = Math.sqrt(dx * dx + dy * dy);

          if (distance < 10) {
            // Hit target - apply damage
            hitTarget(proj);
            return false; // Remove projectile
          }

          // Move projectile
          const moveX = (dx / distance) * proj.speed;
          const moveY = (dy / distance) * proj.speed;
          proj.x += moveX;
          proj.y += moveY;

          return true; // Keep projectile
        })
      }
    }));
  };

  const hitTarget = (projectile: Projectile) => {
    const targetEnemy = gameState.battleState.enemies.find(e => e.id === projectile.targetId);
    if (!targetEnemy || !targetEnemy.alive) return;

    const actualDamage = Math.max(1, projectile.damage - targetEnemy.armor);

    setGameState(prev => ({
      ...prev,
      battleState: {
        ...prev.battleState,
        enemies: prev.battleState.enemies.map(enemy => {
          if (enemy.id === targetEnemy.id) {
            const newHealth = Math.max(0, enemy.health - actualDamage);
            const killed = newHealth <= 0;

            // Apply tower effects
            if (projectile.effects.includes('slow')) {
              enemy.slowDuration = 180; // 3 seconds at 60fps
            }

            if (killed) {
              // Apply rewards
              const goldGain = enemy.reward.find(r => r.type === 'gold')?.amount || 0;
              const expGain = enemy.reward.find(r => r.type === 'exp')?.amount || 0;

              setPlayerData(prev2 => ({
                ...prev2,
                gold: prev2.gold + goldGain,
                experience: prev2.experience + expGain
              }));

              setGameState(prev2 => ({
                ...prev2,
                battleState: {
                  ...prev2.battleState,
                  score: prev2.battleState.score + goldGain * 10,
                  towers: prev2.battleState.towers.map(t => 
                    t.id === projectile.towerId ? { 
                      ...t, 
                      kills: t.kills + 1,
                      experience: t.experience + expGain
                    } : t
                  )
                }
              }));

              onAction('enemy_defeated', {
                player_id: playerData.id,
                stage_id: gameState.selectedStage,
                enemy_type: enemy.type,
                enemy_id: enemy.id,
                tower_type: projectile.type,
                tower_id: projectile.towerId,
                damage_dealt: actualDamage,
                gold_gained: goldGain,
                exp_gained: expGain,
                wave_number: gameState.battleState.wave
              });

              return { ...enemy, health: 0, alive: false };
            } else {
              onAction('enemy_damaged', {
                player_id: playerData.id,
                stage_id: gameState.selectedStage,
                enemy_type: enemy.type,
                enemy_id: enemy.id,
                tower_type: projectile.type,
                damage_dealt: actualDamage,
                enemy_health_remaining: newHealth,
                wave_number: gameState.battleState.wave
              });

              return { ...enemy, health: newHealth };
            }
          }

          // Chain lightning effect
          if (projectile.effects.includes('chain') && enemy.id !== targetEnemy.id && enemy.alive) {
            const distance = Math.sqrt((enemy.x - targetEnemy.x) ** 2 + (enemy.y - targetEnemy.y) ** 2);
            if (distance < 80) {
              const chainDamage = Math.floor(projectile.damage * 0.6);
              const actualChainDamage = Math.max(1, chainDamage - enemy.armor);
              const newHealth = Math.max(0, enemy.health - actualChainDamage);

              return { ...enemy, health: newHealth, alive: newHealth > 0 };
            }
          }

          return enemy;
        })
      }
    }));

    // Splash damage for cannon
    if (projectile.effects.includes('splash')) {
      const splashTargets = gameState.battleState.enemies.filter(enemy => {
        if (enemy.id === targetEnemy.id || !enemy.alive) return false;
        const distance = Math.sqrt((enemy.x - targetEnemy.x) ** 2 + (enemy.y - targetEnemy.y) ** 2);
        return distance < 60;
      });

      splashTargets.forEach(enemy => {
        const splashDamage = Math.floor(projectile.damage * 0.5);
        const actualSplashDamage = Math.max(1, splashDamage - enemy.armor);

        setGameState(prev => ({
          ...prev,
          battleState: {
            ...prev.battleState,
            enemies: prev.battleState.enemies.map(e => 
              e.id === enemy.id ? { 
                ...e, 
                health: Math.max(0, e.health - actualSplashDamage),
                alive: e.health - actualSplashDamage > 0
              } : e
            )
          }
        }));
      });
    }
  };

  const updateTowers = () => {
    gameState.battleState.towers.forEach(tower => {
      if (frameCount - tower.lastAttack < tower.attackSpeed) return;

      const enemiesInRange = gameState.battleState.enemies.filter(enemy => {
        if (!enemy.alive) return false;
        const distance = Math.sqrt((enemy.x - tower.x) ** 2 + (enemy.y - tower.y) ** 2);
        return distance <= tower.range;
      });

      if (enemiesInRange.length > 0) {
        const target = enemiesInRange.reduce((best, current) => 
          current.pathIndex > best.pathIndex ? current : best
        );

        // Create projectile
        const newProjectile: Projectile = {
          id: `proj_${Date.now()}_${Math.random().toString(36).substr(2, 5)}`,
          x: tower.x,
          y: tower.y,
          targetX: target.x,
          targetY: target.y,
          targetId: target.id,
          speed: 8,
          damage: tower.damage,
          type: tower.type,
          towerId: tower.id,
          effects: towerTypes[tower.type as keyof typeof towerTypes].effects,
          trail: []
        };

        setGameState(prev => ({
          ...prev,
          battleState: {
            ...prev.battleState,
            projectiles: [...prev.battleState.projectiles, newProjectile],
            towers: prev.battleState.towers.map(t => 
              t.id === tower.id ? { ...t, lastAttack: frameCount } : t
            )
          }
        }));

        onAction('tower_attack', {
          player_id: playerData.id,
          stage_id: gameState.selectedStage,
          tower_id: tower.id,
          tower_type: tower.type,
          target_enemy_id: target.id,
          damage: tower.damage,
          wave_number: gameState.battleState.wave
        });
      }
    });
  };

  const checkBattleEnd = () => {
    if (gameState.battleState.lives <= 0) {
      completeStage(false);
      return;
    }

    const aliveEnemies = gameState.battleState.enemies.filter(e => e.alive);
    const allEnemiesSpawned = gameState.battleState.enemiesSpawned >= gameState.battleState.totalEnemies;

    if (aliveEnemies.length === 0 && allEnemiesSpawned && gameState.battleState.isWaveActive) {
      if (gameState.battleState.wave >= 3) {
        completeStage(true);
      } else {
        // Next wave
        const nextWave = gameState.battleState.wave + 1;
        const nextTotalEnemies = 8 + nextWave * 2;

        setGameState(prev => ({
          ...prev,
          battleState: {
            ...prev.battleState,
            wave: nextWave,
            isWaveActive: false,
            waveProgress: 0,
            totalEnemies: nextTotalEnemies,
            enemiesSpawned: 0,
            nextEnemySpawn: 0
          }
        }));

        // Wave completion bonus
        const bonusGold = 25 + gameState.battleState.wave * 15;
        setPlayerData(prev => ({
          ...prev,
          gold: prev.gold + bonusGold
        }));

        onAction('wave_completed', {
          player_id: playerData.id,
          stage_id: gameState.selectedStage,
          wave_number: gameState.battleState.wave,
          bonus_gold: bonusGold,
          total_gold: playerData.gold + bonusGold,
          score: gameState.battleState.score,
          towers_count: gameState.battleState.towers.length
        });
      }
    }
  };

  // Drawing functions
  const drawLobby = (ctx: CanvasRenderingContext2D, canvas: HTMLCanvasElement) => {
    // Background
    ctx.fillStyle = '#1a1a2e';
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    // Title
    ctx.fillStyle = '#FFD700';
    ctx.font = 'bold 32px Arial';
    ctx.textAlign = 'center';
    ctx.fillText('üè∞ TOWER DEFENSE', canvas.width / 2, 80);

    // Player info panel
    ctx.fillStyle = 'rgba(255, 255, 255, 0.1)';
    ctx.fillRect(50, 100, 500, 80);

    ctx.fillStyle = '#FFFFFF';
    ctx.font = '16px Arial';
    ctx.textAlign = 'left';
    ctx.fillText(`${playerData.name} - Level ${playerData.level}`, 70, 125);
    ctx.fillText(`Gold: ${playerData.gold} | Gems: ${playerData.gems}`, 70, 145);
    ctx.fillText(`Energy: ${playerData.energy}/${playerData.maxEnergy}`, 70, 165);

    // Experience bar
    const expNeeded = playerData.level * 100;
    const expPercent = playerData.experience / expNeeded;
    ctx.fillStyle = '#444';
    ctx.fillRect(350, 140, 180, 12);
    ctx.fillStyle = '#FFD700';
    ctx.fillRect(350, 140, 180 * expPercent, 12);
    ctx.fillStyle = '#FFFFFF';
    ctx.font = '12px Arial';
    ctx.fillText(`EXP: ${playerData.experience}/${expNeeded}`, 355, 150);

    // Menu buttons
    const buttons = [
      { text: '‚öîÔ∏è DUNGEON', x: 250, y: 200, w: 100, h: 50 },
      { text: 'üõí SHOP', x: 100, y: 300, w: 100, h: 50 },
      { text: 'üéí INVENTORY', x: 400, y: 300, w: 100, h: 50 }
    ];

    buttons.forEach(button => {
      ctx.fillStyle = 'rgba(74, 144, 226, 0.8)';
      ctx.fillRect(button.x, button.y, button.w, button.h);

      ctx.strokeStyle = '#FFFFFF';
      ctx.lineWidth = 2;
      ctx.strokeRect(button.x, button.y, button.w, button.h);

      ctx.fillStyle = '#FFFFFF';
      ctx.font = 'bold 14px Arial';
      ctx.textAlign = 'center';
      ctx.fillText(button.text, button.x + button.w/2, button.y + button.h/2 + 5);
    });
  };

  const drawDungeon = (ctx: CanvasRenderingContext2D, canvas: HTMLCanvasElement) => {
    // Background
    ctx.fillStyle = '#0f1419';
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    // Title
    ctx.fillStyle = '#FFD700';
    ctx.font = 'bold 24px Arial';
    ctx.textAlign = 'center';
    ctx.fillText('üó°Ô∏è DUNGEON SELECT', canvas.width / 2, 40);

    // Stage cards
    stageDefinitions.forEach((stage, index) => {
      const x = 50 + (index % 3) * 200;
      const y = 70 + Math.floor(index / 3) * 120;

      const isUnlocked = stage.id <= playerData.unlockedStages;
      const hasEnergy = playerData.energy >= stage.energyCost;
      const canEnter = isUnlocked && hasEnergy;

      // Stage card background
      ctx.fillStyle = canEnter ? 'rgba(74, 144, 226, 0.8)' : 'rgba(100, 100, 100, 0.5)';
      ctx.fillRect(x, y, 150, 100);

      ctx.strokeStyle = canEnter ? '#4a90e2' : '#666';
      ctx.lineWidth = 2;
      ctx.strokeRect(x, y, 150, 100);

      // Stage info
      ctx.fillStyle = canEnter ? '#FFFFFF' : '#999';
      ctx.font = 'bold 14px Arial';
      ctx.textAlign = 'center';
      ctx.fillText(`Stage ${stage.id}`, x + 75, y + 20);

      ctx.font = '12px Arial';
      ctx.fillText(stage.name, x + 75, y + 35);
      ctx.fillText(stage.difficulty, x + 75, y + 50);
      ctx.fillText(`Energy: ${stage.energyCost}`, x + 75, y + 65);
      ctx.fillText(`Lv.${stage.recommendedLevel} Rec.`, x + 75, y + 80);

      // Lock indicator
      if (!isUnlocked) {
        ctx.fillStyle = '#FF0000';
        ctx.font = 'bold 20px Arial';
        ctx.fillText('üîí', x + 75, y + 55);
      }

      // Completion stars
      const completed = playerData.completedStages.find(c => c.stageId === stage.id);
      if (completed) {
        for (let i = 0; i < completed.starsEarned; i++) {
          ctx.fillStyle = '#FFD700';
          ctx.font = '16px Arial';
          ctx.fillText('‚≠ê', x + 20 + i * 20, y + 95);
        }
      }
    });

    // Back button
    ctx.fillStyle = 'rgba(255, 100, 100, 0.8)';
    ctx.fillRect(50, 350, 100, 30);
    ctx.fillStyle = '#FFFFFF';
    ctx.font = '14px Arial';
    ctx.textAlign = 'center';
    ctx.fillText('‚Üê BACK', 100, 370);
  };

  const drawBattle = (ctx: CanvasRenderingContext2D, canvas: HTMLCanvasElement) => {
    if (!gameState.selectedStage) return;

    const stage = stageDefinitions.find(s => s.id === gameState.selectedStage);
    if (!stage) return;

    // Background
    ctx.fillStyle = stage.background;
    ctx.fillRect(0, 0, canvas.width, 320);

    // Draw path
    ctx.strokeStyle = '#8B4513';
    ctx.lineWidth = 20;
    ctx.lineCap = 'round';
    ctx.lineJoin = 'round';

    ctx.beginPath();
    ctx.moveTo(stage.path[0].x, stage.path[0].y);
    stage.path.slice(1).forEach(point => {
      ctx.lineTo(point.x, point.y);
    });
    ctx.stroke();

    ctx.strokeStyle = '#DEB887';
    ctx.lineWidth = 12;
    ctx.beginPath();
    ctx.moveTo(stage.path[0].x, stage.path[0].y);
    stage.path.slice(1).forEach(point => {
      ctx.lineTo(point.x, point.y);
    });
    ctx.stroke();

    // Draw towers
    gameState.battleState.towers.forEach(tower => {
      const config = towerTypes[tower.type as keyof typeof towerTypes];
      const isSelected = gameState.selectedTower?.id === tower.id;

      // Tower base
      ctx.fillStyle = isSelected ? '#FFD700' : '#696969';
      ctx.beginPath();
      ctx.arc(tower.x, tower.y, 18, 0, 2 * Math.PI);
      ctx.fill();

      // Tower body
      ctx.fillStyle = config.color;
      ctx.beginPath();
      ctx.arc(tower.x, tower.y, 14, 0, 2 * Math.PI);
      ctx.fill();

      // Tower icon
      ctx.fillStyle = '#FFFFFF';
      ctx.font = '12px Arial';
      ctx.textAlign = 'center';
      ctx.fillText(config.icon, tower.x, tower.y + 4);

      // Level indicator
      ctx.fillStyle = '#FFD700';
      ctx.font = '10px Arial';
      ctx.fillText(`Lv.${tower.level}`, tower.x, tower.y - 25);

      // Range for selected tower
      if (isSelected) {
        ctx.strokeStyle = 'rgba(255, 255, 255, 0.3)';
        ctx.lineWidth = 2;
        ctx.setLineDash([5, 5]);
        ctx.beginPath();
        ctx.arc(tower.x, tower.y, tower.range, 0, 2 * Math.PI);
        ctx.stroke();
        ctx.setLineDash([]);
      }
    });

    // Draw enemies
    gameState.battleState.enemies.forEach(enemy => {
      if (!enemy.alive) return;

      const config = enemyTypes[enemy.type as keyof typeof enemyTypes];
      const size = config.size;

      // Enemy shadow
      ctx.fillStyle = 'rgba(0, 0, 0, 0.3)';
      ctx.beginPath();
      ctx.ellipse(enemy.x + 2, enemy.y + 2, size * 0.8, size * 0.4, 0, 0, 2 * Math.PI);
      ctx.fill();

      // Enemy body with slow effect
      if (enemy.slowDuration > 0) {
        ctx.fillStyle = '#87CEEB'; // Light blue for slow effect
        ctx.beginPath();
        ctx.arc(enemy.x, enemy.y, size + 3, 0, 2 * Math.PI);
        ctx.fill();
      }

      ctx.fillStyle = config.color;
      ctx.beginPath();
      ctx.arc(enemy.x, enemy.y, size, 0, 2 * Math.PI);
      ctx.fill();

      // Enemy border
      ctx.strokeStyle = '#000000';
      ctx.lineWidth = 2;
      ctx.stroke();

      // Enemy icon
      ctx.fillStyle = '#FFFFFF';
      ctx.font = `${Math.max(10, size)}px Arial`;
      ctx.textAlign = 'center';
      ctx.fillText(config.icon, enemy.x, enemy.y + 4);

      // Health bar
      const barWidth = size * 1.5;
      const barHeight = 4;
      const healthPercent = enemy.health / enemy.maxHealth;

      ctx.fillStyle = '#000000';
      ctx.fillRect(enemy.x - barWidth/2 - 1, enemy.y - size - 12, barWidth + 2, barHeight + 2);
      ctx.fillStyle = '#FF0000';
      ctx.fillRect(enemy.x - barWidth/2, enemy.y - size - 11, barWidth, barHeight);
      ctx.fillStyle = healthPercent > 0.5 ? '#00FF00' : healthPercent > 0.25 ? '#FFFF00' : '#FF8800';
      ctx.fillRect(enemy.x - barWidth/2, enemy.y - size - 11, barWidth * healthPercent, barHeight);

      // Armor indicator
      if (enemy.armor > 0) {
        ctx.fillStyle = '#C0C0C0';
        ctx.font = '8px Arial';
        ctx.fillText(`üõ°${enemy.armor}`, enemy.x, enemy.y - size - 16);
      }
    });

    // Draw projectiles
    gameState.battleState.projectiles.forEach(proj => {
      const config = towerTypes[proj.type as keyof typeof towerTypes];

      // Draw trail
      proj.trail.forEach((point, index) => {
        const alpha = (index + 1) / proj.trail.length * 0.5;
        ctx.fillStyle = `rgba(255, 255, 255, ${alpha})`;
        ctx.beginPath();
        ctx.arc(point.x, point.y, 2, 0, 2 * Math.PI);
        ctx.fill();
      });

      // Draw projectile based on type
      switch (proj.type) {
        case 'archer':
          ctx.fillStyle = '#8B4513';
          ctx.beginPath();
          ctx.arc(proj.x, proj.y, 3, 0, 2 * Math.PI);
          ctx.fill();
          break;
        case 'cannon':
          ctx.fillStyle = '#000000';
          ctx.beginPath();
          ctx.arc(proj.x, proj.y, 5, 0, 2 * Math.PI);
          ctx.fill();
          ctx.fillStyle = '#FF4500';
          ctx.beginPath();
          ctx.arc(proj.x, proj.y, 3, 0, 2 * Math.PI);
          ctx.fill();
          break;
        case 'magic':
          ctx.fillStyle = '#9400D3';
          ctx.beginPath();
          ctx.arc(proj.x, proj.y, 4, 0, 2 * Math.PI);
          ctx.fill();
          // Magic sparkle effect
          for (let i = 0; i < 3; i++) {
            const angle = (frameCount * 0.1 + i * Math.PI * 2 / 3) % (Math.PI * 2);
            const sparkleX = proj.x + Math.cos(angle) * 8;
            const sparkleY = proj.y + Math.sin(angle) * 8;
            ctx.fillStyle = '#DDA0DD';
            ctx.beginPath();
            ctx.arc(sparkleX, sparkleY, 1, 0, 2 * Math.PI);
            ctx.fill();
          }
          break;
        case 'ice':
          ctx.fillStyle = '#00BFFF';
          ctx.beginPath();
          ctx.arc(proj.x, proj.y, 4, 0, 2 * Math.PI);
          ctx.fill();
          ctx.fillStyle = '#E0FFFF';
          ctx.beginPath();
          ctx.arc(proj.x, proj.y, 2, 0, 2 * Math.PI);
          ctx.fill();
          break;
        case 'lightning':
          ctx.strokeStyle = '#FFD700';
          ctx.lineWidth = 3;
          ctx.beginPath();
          ctx.moveTo(proj.x - 4, proj.y);
          ctx.lineTo(proj.x + 4, proj.y);
          ctx.moveTo(proj.x, proj.y - 4);
          ctx.lineTo(proj.x, proj.y + 4);
          ctx.stroke();

          ctx.fillStyle = '#FFFF00';
          ctx.beginPath();
          ctx.arc(proj.x, proj.y, 3, 0, 2 * Math.PI);
          ctx.fill();
          break;
      }
    });

    // UI Panel
    ctx.fillStyle = 'rgba(0, 0, 0, 0.9)';
    ctx.fillRect(0, 320, canvas.width, 80);

    // Battle stats
    ctx.fillStyle = '#FFFFFF';
    ctx.font = '14px Arial';
    ctx.textAlign = 'left';
    ctx.fillText(`Stage ${gameState.selectedStage}: ${stage.name}`, 10, 340);
    ctx.fillText(`Wave: ${gameState.battleState.wave}/3`, 10, 355);
    ctx.fillText(`Lives: ${gameState.battleState.lives}`, 10, 370);
    ctx.fillText(`Score: ${gameState.battleState.score}`, 10, 385);
    ctx.fillText(`Gold: ${playerData.gold}`, 150, 340);
    ctx.fillText(`Towers: ${gameState.battleState.towers.length}`, 150, 355);

    // Tower selection buttons
    Object.entries(towerTypes).forEach(([type, config], index) => {
      const x = 300 + index * 60;
      const y = 340;
      const isSelected = gameState.selectedTowerType === type;
      const canAfford = playerData.gold >= config.cost;

      ctx.fillStyle = isSelected ? 'rgba(255, 255, 0, 0.5)' : 
                     canAfford ? 'rgba(255, 255, 255, 0.2)' : 'rgba(255, 0, 0, 0.2)';
      ctx.fillRect(x, y, 50, 50);

      ctx.fillStyle = config.color;
      ctx.beginPath();
      ctx.arc(x + 25, y + 20, 12, 0, 2 * Math.PI);
      ctx.fill();

      ctx.fillStyle = canAfford ? '#FFFFFF' : '#FF6666';
      ctx.font = '10px Arial';
      ctx.textAlign = 'center';
      ctx.fillText(`${index + 1}: $${config.cost}`, x + 25, y + 45);
    });

    // Wave control
    if (!gameState.battleState.isWaveActive) {
      ctx.fillStyle = 'rgba(0, 255, 0, 0.8)';
      ctx.fillRect(450, 350, 100, 30);
      ctx.fillStyle = '#FFFFFF';
      ctx.font = 'bold 14px Arial';
      ctx.textAlign = 'center';
      ctx.fillText('START WAVE', 500, 370);
    }

    // Pause button
    ctx.fillStyle = 'rgba(255, 200, 0, 0.8)';
    ctx.fillRect(560, 350, 30, 30);
    ctx.fillStyle = '#000';
    ctx.font = '16px Arial';
    ctx.fillText('‚è∏', 575, 370);
  };

  const drawResults = (ctx: CanvasRenderingContext2D, canvas: HTMLCanvasElement) => {
    if (!gameState.stageResults) return;

    const results = gameState.stageResults;

    // Overlay with animation
    ctx.fillStyle = 'rgba(0, 0, 0, 0.9)';
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    // Results panel with glow effect
    ctx.shadowColor = results.victory ? '#00FF00' : '#FF0000';
    ctx.shadowBlur = 20;
    ctx.fillStyle = 'rgba(30, 30, 30, 0.95)';
    ctx.fillRect(80, 40, 440, 320);

    ctx.shadowBlur = 0;
    ctx.strokeStyle = results.victory ? '#FFD700' : '#FF4444';
    ctx.lineWidth = 3;
    ctx.strokeRect(80, 40, 440, 320);

    // Animated title
    const titleScale = 1 + Math.sin(frameCount * 0.1) * 0.1;
    ctx.save();
    ctx.translate(300, 80);
    ctx.scale(titleScale, titleScale);
    ctx.fillStyle = results.victory ? '#00FF00' : '#FF0000';
    ctx.font = 'bold 28px Arial';
    ctx.textAlign = 'center';
    ctx.fillText(results.victory ? 'üéâ VICTORY!' : 'üíÄ DEFEAT!', 0, 0);
    ctx.restore();

    // Results with better formatting
    ctx.fillStyle = '#FFFFFF';
    ctx.font = '18px Arial';
    ctx.textAlign = 'left';

    let yPos = 120;

    // Score with icon
    ctx.fillText(`üèÜ Score: ${results.score.toLocaleString()}`, 100, yPos);
    yPos += 30;

    if (results.victory) {
      // Stars with animation
      ctx.fillText('Stars Earned:', 100, yPos);
      for (let i = 0; i < results.starsEarned; i++) {
        const starScale = 1 + Math.sin((frameCount + i * 20) * 0.15) * 0.2;
        ctx.save();
        ctx.translate(250 + i * 30, yPos - 5);
        ctx.scale(starScale, starScale);
        ctx.font = '20px Arial';
        ctx.fillText('‚≠ê', 0, 0);
        ctx.restore();
      }
      yPos += 35;

      // Rewards section
      ctx.fillStyle = '#FFD700';
      ctx.font = 'bold 16px Arial';
      ctx.fillText('REWARDS:', 100, yPos);
      yPos += 25;

      ctx.fillStyle = '#FFFFFF';
      ctx.font = '14px Arial';
      results.rewards.forEach(reward => {
        const iconMap: Record<string, string> = { 
          gold: 'üí∞', exp: '‚≠ê', gem: 'üíé', item: 'üéÅ' 
        };
        const icon = iconMap[reward.type] || 'üéÅ';
        ctx.fillText(`${icon} +${reward.amount.toLocaleString()}`, 120, yPos);
        yPos += 22;
      });

      if (results.newUnlocks.length > 0) {
        yPos += 10;
        ctx.fillStyle = '#FFD700';
        ctx.font = 'bold 14px Arial';
        results.newUnlocks.forEach(unlock => {
          ctx.fillText(`üîì ${unlock}`, 100, yPos);
          yPos += 22;
        });
      }
    } else {
      // Defeat specific information
      ctx.fillStyle = '#FFAAAA';
      ctx.font = '16px Arial';
      ctx.fillText(`üí• Wave Reached: ${gameState.battleState.wave}`, 100, yPos);
      yPos += 25;
      ctx.fillText(`üè∞ Towers Built: ${gameState.battleState.towers.length}`, 100, yPos);
      yPos += 25;
      ctx.fillText(`üíÄ Enemies Defeated: ${gameState.battleState.towers.reduce((sum, tower) => sum + tower.kills, 0)}`, 100, yPos);
      yPos += 30;

      // Consolation reward
      if (results.rewards.length > 0) {
        ctx.fillStyle = '#FFFF99';
        ctx.font = '14px Arial';
        ctx.fillText('Consolation Reward:', 100, yPos);
        yPos += 20;
        ctx.fillStyle = '#FFFFFF';
        results.rewards.forEach(reward => {
          ctx.fillText(`üí∞ +${reward.amount} Gold`, 120, yPos);
          yPos += 20;
        });
      }
    }

    // Buttons
    if (!results.victory) {
      // Retry button (only for failures)
      const hasEnergy = gameState.selectedStage ? 
        playerData.energy >= (stageDefinitions.find(s => s.id === gameState.selectedStage)?.energyCost || 0) : false;

      ctx.fillStyle = hasEnergy ? 'rgba(255, 165, 0, 0.8)' : 'rgba(128, 128, 128, 0.5)';
      ctx.fillRect(100, 320, 120, 30);
      ctx.strokeStyle = hasEnergy ? '#FFA500' : '#666666';
      ctx.lineWidth = 2;
      ctx.strokeRect(100, 320, 120, 30);

      ctx.fillStyle = hasEnergy ? '#FFFFFF' : '#AAAAAA';
      ctx.font = 'bold 14px Arial';
      ctx.textAlign = 'center';
      ctx.fillText(hasEnergy ? 'üîÑ RETRY' : 'NO ENERGY', 160, 340);
    }

    // Continue button
    ctx.fillStyle = 'rgba(74, 144, 226, 0.8)';
    ctx.fillRect(results.victory ? 250 : 280, 320, 140, 30);
    ctx.strokeStyle = '#4A90E2';
    ctx.lineWidth = 2;
    ctx.strokeRect(results.victory ? 250 : 280, 320, 140, 30);

    ctx.fillStyle = '#FFFFFF';
    ctx.font = 'bold 14px Arial';
    ctx.textAlign = 'center';
    ctx.fillText('üè† LOBBY', results.victory ? 320 : 350, 340);

    // Instructions
    ctx.fillStyle = '#CCCCCC';
    ctx.font = '12px Arial';
    ctx.textAlign = 'center';
    ctx.fillText('Click buttons to continue', 300, 370);
  };

  const draw = useCallback(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    switch (gameState.currentScene) {
      case 'lobby':
        drawLobby(ctx, canvas);
        break;
      case 'dungeon':
        drawDungeon(ctx, canvas);
        break;
      case 'battle':
        drawBattle(ctx, canvas);
        break;
      case 'stageComplete':
      case 'stageFailed':
        drawBattle(ctx, canvas);
        drawResults(ctx, canvas);
        break;
      case 'shop':
        drawLobby(ctx, canvas);
        // Add shop overlay
        ctx.fillStyle = 'rgba(0, 0, 0, 0.8)';
        ctx.fillRect(0, 0, canvas.width, canvas.height);
        ctx.fillStyle = '#FFD700';
        ctx.font = 'bold 24px Arial';
        ctx.textAlign = 'center';
        drawShop(ctx, canvas); return;
        break;
      case 'inventory':
        drawLobby(ctx, canvas);
        // Add inventory overlay
        ctx.fillStyle = 'rgba(0, 0, 0, 0.8)';
        ctx.fillRect(0, 0, canvas.width, canvas.height);
        ctx.fillStyle = '#FFD700';
        ctx.font = 'bold 24px Arial';
        ctx.textAlign = 'center';
        drawInventory(ctx, canvas); return;
        break;
    }
  }, [gameState, playerData]);

  const drawShop = (ctx: CanvasRenderingContext2D, canvas: HTMLCanvasElement) => {
    // Background
    ctx.fillStyle = '#1a1a2e';
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    // Title
    ctx.fillStyle = '#FFD700';
    ctx.font = 'bold 24px Arial';
    ctx.textAlign = 'center';
    ctx.fillText('üõçÔ∏è MAGIC SHOP', canvas.width / 2, 30);

    // Player currency display
    ctx.fillStyle = '#FFFFFF';
    ctx.font = '14px Arial';
    ctx.textAlign = 'left';
    ctx.fillText(`üí∞ Gold: ${playerData.gold.toLocaleString()}`, 50, 370);
    ctx.fillText(`üíé Gems: ${playerData.gems.toLocaleString()}`, 200, 370);
    ctx.fillText(`Level: ${playerData.level}`, 350, 370);

    // Category tabs
    const categories = [
      { id: 'tower', name: 'TOWER UPGRADES', icon: 'üè∞' },
      { id: 'utility', name: 'UTILITY', icon: 'üß™' },
      { id: 'premium', name: 'PREMIUM', icon: '‚ú®' }
    ];

    categories.forEach((category, index) => {
      const tabX = 80 + index * 150;
      const isSelected = gameState.shopCategory === category.id;

      ctx.fillStyle = isSelected ? 'rgba(255, 215, 0, 0.8)' : 'rgba(100, 100, 100, 0.6)';
      ctx.fillRect(tabX, 50, 140, 30);

      ctx.strokeStyle = isSelected ? '#FFD700' : '#666';
      ctx.lineWidth = 2;
      ctx.strokeRect(tabX, 50, 140, 30);

      ctx.fillStyle = isSelected ? '#000' : '#FFF';
      ctx.font = 'bold 12px Arial';
      ctx.textAlign = 'center';
      ctx.fillText(`${category.icon} ${category.name}`, tabX + 70, 70);
    });

    // Shop items
    const categoryItems = shopItems.filter(item => item.category === gameState.shopCategory);

    categoryItems.forEach((item, index) => {
      const itemX = 80 + (index % 2) * 220;
      const itemY = 100 + Math.floor(index / 2) * 100;

      // Check if player can afford and meets requirements
      const canAfford = item.currency === 'gold' ? 
        playerData.gold >= item.price : 
        playerData.gems >= item.price;

      const meetsRequirements = !item.requirements || (
        (!item.requirements.level || playerData.level >= item.requirements.level) &&
        (!item.requirements.completedStages || playerData.completedStages.length >= item.requirements.completedStages)
      );

      const canPurchase = canAfford && meetsRequirements;

      // Item background
      ctx.fillStyle = canPurchase ? 'rgba(74, 144, 226, 0.8)' : 'rgba(100, 100, 100, 0.5)';
      ctx.fillRect(itemX, itemY, 200, 90);

      ctx.strokeStyle = canPurchase ? '#4A90E2' : '#666';
      ctx.lineWidth = 2;
      ctx.strokeRect(itemX, itemY, 200, 90);

      // Item info
      ctx.fillStyle = canPurchase ? '#FFFFFF' : '#AAAAAA';
      ctx.font = 'bold 14px Arial';
      ctx.textAlign = 'left';
      ctx.fillText(item.name, itemX + 10, itemY + 20);

      ctx.font = '11px Arial';
      const words = item.description.split(' ');
      let line = '';
      let y = itemY + 35;

      words.forEach(word => {
        const testLine = line + word + ' ';
        const metrics = ctx.measureText(testLine);
        if (metrics.width > 180 && line !== '') {
          ctx.fillText(line, itemX + 10, y);
          line = word + ' ';
          y += 12;
        } else {
          line = testLine;
        }
      });
      ctx.fillText(line, itemX + 10, y);

      // Price
      ctx.fillStyle = canAfford ? '#00FF00' : '#FF6666';
      ctx.font = 'bold 12px Arial';
      const currencyIcon = item.currency === 'gold' ? 'üí∞' : 'üíé';
      ctx.fillText(`${currencyIcon} ${item.price}`, itemX + 10, itemY + 80);

      // Requirements warning
      if (!meetsRequirements) {
        ctx.fillStyle = '#FF6666';
        ctx.font = '10px Arial';
        if (item.requirements?.level && playerData.level < item.requirements.level) {
          ctx.fillText(`Requires Level ${item.requirements.level}`, itemX + 100, itemY + 80);
        }
        if (item.requirements?.completedStages && playerData.completedStages.length < item.requirements.completedStages) {
          ctx.fillText(`Complete ${item.requirements.completedStages} stages`, itemX + 100, itemY + 80);
        }
      }
    });

    // Back button
    ctx.fillStyle = 'rgba(255, 100, 100, 0.8)';
    ctx.fillRect(50, 350, 100, 30);
    ctx.fillStyle = '#FFFFFF';
    ctx.font = '14px Arial';
    ctx.textAlign = 'center';
    ctx.fillText('‚Üê BACK', 100, 370);
  };

  const drawInventory = (ctx: CanvasRenderingContext2D, canvas: HTMLCanvasElement) => {
    // Background
    ctx.fillStyle = '#2d1b69';
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    // Title
    ctx.fillStyle = '#FFD700';
    ctx.font = 'bold 24px Arial';
    ctx.textAlign = 'center';
    ctx.fillText('üéí INVENTORY', canvas.width / 2, 30);

    // Player info
    ctx.fillStyle = '#FFFFFF';
    ctx.font = '14px Arial';
    ctx.textAlign = 'left';
    ctx.fillText(`Items: ${playerData.inventory.length}`, 50, 60);
    ctx.fillText(`Total Value: ${playerData.inventory.reduce((sum, item) => sum + item.sellPrice * item.quantity, 0)} gold`, 200, 60);

    // Inventory grid
    const itemsPerRow = 5;
    const startY = 100;

    playerData.inventory.forEach((item, index) => {
      const itemX = 50 + (index % itemsPerRow) * 100;
      const itemY = startY + Math.floor(index / itemsPerRow) * 80;

      // Rarity colors
      const rarityColors = {
        common: '#CCCCCC',
        rare: '#0099FF',
        epic: '#9933FF',
        legendary: '#FF9900'
      };

      // Item slot
      ctx.fillStyle = 'rgba(50, 50, 50, 0.8)';
      ctx.fillRect(itemX, itemY, 80, 60);

      ctx.strokeStyle = rarityColors[item.rarity];
      ctx.lineWidth = 2;
      ctx.strokeRect(itemX, itemY, 80, 60);

      // Item info
      ctx.fillStyle = '#FFFFFF';
      ctx.font = 'bold 10px Arial';
      ctx.textAlign = 'center';

      // Truncate long names
      const displayName = item.name.length > 10 ? item.name.substring(0, 8) + '...' : item.name;
      ctx.fillText(displayName, itemX + 40, itemY + 15);

      // Type icon
      const typeIcons = {
        weapon: '‚öîÔ∏è',
        armor: 'üõ°Ô∏è',
        consumable: 'üß™',
        material: 'üìé',
        tower_upgrade: '‚¨ÜÔ∏è',
        blueprint: 'üìú'
      };

      ctx.font = '16px Arial';
      ctx.fillText(typeIcons[item.type] || '‚ùì', itemX + 40, itemY + 35);

      // Quantity
      if (item.quantity > 1) {
        ctx.fillStyle = '#FFD700';
        ctx.font = 'bold 10px Arial';
        ctx.fillText(`x${item.quantity}`, itemX + 65, itemY + 55);
      }

      // Sell value
      ctx.fillStyle = '#00FF00';
      ctx.font = '8px Arial';
      ctx.fillText(`üí∞${item.sellPrice}`, itemX + 5, itemY + 55);
    });

    // Instructions
    ctx.fillStyle = '#CCCCCC';
    ctx.font = '12px Arial';
    ctx.textAlign = 'center';
    ctx.fillText('Click items to use/equip them', canvas.width / 2, 320);

    // Back button
    ctx.fillStyle = 'rgba(255, 100, 100, 0.8)';
    ctx.fillRect(50, 350, 100, 30);
    ctx.fillStyle = '#FFFFFF';
    ctx.font = '14px Arial';
    ctx.textAlign = 'center';
    ctx.fillText('‚Üê BACK', 100, 370);
  };

  // Game loop
  useEffect(() => {
    const gameLoop = () => {
      updateBattle();
      draw();
      setFrameCount(prev => prev + 1);
      gameLoopRef.current = requestAnimationFrame(gameLoop);
    };

    gameLoopRef.current = requestAnimationFrame(gameLoop);

    return () => {
      if (gameLoopRef.current) {
        cancelAnimationFrame(gameLoopRef.current);
      }
    };
  }, [updateBattle, draw]);

  // Event listeners
  useEffect(() => {
    document.addEventListener('keydown', handleKeyDown);

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
    };
  }, [handleKeyDown]);

  return (
    <div style={{ 
      display: 'flex', 
      flexDirection: 'column', 
      alignItems: 'center',
      background: '#0a0a0a',
      borderRadius: '12px',
      padding: '16px',
      border: '2px solid #2d3748',
      maxHeight: '500px',
      overflowY: 'auto',
      overflowX: 'auto'
    }}>
      <canvas
        ref={canvasRef}
        width={600}
        height={400}
        style={{
          border: '2px solid #4a5568',
          borderRadius: '8px',
          cursor: gameState.selectedTowerType ? 'crosshair' : 'pointer',
          maxWidth: '100%',
          height: 'auto'
        }}
        onClick={handleCanvasClick}
        tabIndex={0}
      />

      <div style={{
        marginTop: '12px',
        color: '#ffffff',
        fontSize: '14px',
        textAlign: 'center',
        maxWidth: '600px',
      }}>
        <p>üè∞ <strong>Complete Tower Defense RPG</strong></p>
        <p>
          {gameState.currentScene === 'lobby' && 'Î°úÎπÑÏóêÏÑú ÎçòÏ†ÑÏùÑ ÏÑ†ÌÉùÌïòÏÑ∏Ïöî!'}
          {gameState.currentScene === 'dungeon' && 'Ïä§ÌÖåÏù¥ÏßÄÎ•º ÏÑ†ÌÉùÌïòÏÑ∏Ïöî!'}
          {gameState.currentScene === 'battle' && `Stage ${gameState.selectedStage} - SpaceÎ°ú Ïõ®Ïù¥Î∏å ÏãúÏûë!`}
          {(gameState.currentScene === 'stageComplete' || gameState.currentScene === 'stageFailed') && 'Í≤∞Í≥ºÎ•º ÌôïÏù∏ÌïòÍ≥† Í≥ÑÏÜçÌïòÏÑ∏Ïöî!'}
        </p>
        <p>Level {playerData.level} | Gold: {playerData.gold} | Energy: {playerData.energy}/{playerData.maxEnergy}</p>
      </div>
    </div>
  );
};
