                });
                setInDungeon(false);
                setCurrentDungeon(null);
                setCurrentTab('lobby');
                setGameMode('lobby');
              }}
              style={{
                background: '#374151',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                padding: '10px 15px',
                cursor: 'pointer'
              }}
            >
              ÎçòÏ†Ñ ÎÇòÍ∞ÄÍ∏∞
            </button>
          </div>

          {/* Ï∏µ ÏßÑÌñâ Ïª®Ìä∏Î°§ */}
          <div style={{
            display: 'flex',
            gap: '10px',
            alignItems: 'center',
            justifyContent: 'center',
            padding: '15px',
            background: 'rgba(0, 0, 0, 0.2)',
            borderRadius: '10px'
          }}>
            <button
              onClick={() => {
                if (currentDungeon.currentFloor > 1) {
                  const updatedDungeon = { ...currentDungeon, currentFloor: currentDungeon.currentFloor - 1 };
                  setCurrentDungeon(updatedDungeon);
                  setCurrentMonster(null);
                  addToCombatLog(`${updatedDungeon.currentFloor}Ï∏µÏúºÎ°ú Ïù¥ÎèôÌñàÏäµÎãàÎã§.`);
                  setTimeout(() => {
                    spawnDungeonMonster(updatedDungeon, true);
                  }, 1000);
                }
              }}
              disabled={currentDungeon.currentFloor <= 1}
              style={{
                background: currentDungeon.currentFloor <= 1 ? '#6b7280' : '#3b82f6',
