                  setCurrentDungeon(updatedDungeon);
                  setCurrentMonster(null);
                  addToCombatLog(`${targetFloor}Ï∏µÏúºÎ°ú Ïù¥ÎèôÌñàÏäµÎãàÎã§.`);
                  setTimeout(() => {
