              }}
            />

            <button
              onClick={() => {
                if (currentDungeon.currentFloor < currentDungeon.floors) {
                  const updatedDungeon = { ...currentDungeon, currentFloor: currentDungeon.currentFloor + 1 };
                  setCurrentDungeon(updatedDungeon);
                  setCurrentMonster(null);
                  addToCombatLog(`${updatedDungeon.currentFloor}Ï∏µÏúºÎ°ú Ïù¥ÎèôÌñàÏäµÎãàÎã§.`);
                  setTimeout(() => {
                    spawnDungeonMonster(updatedDungeon, true);
                  }, 1000);
                }
              }}
              disabled={currentDungeon.currentFloor >= currentDungeon.floors}
              style={{
                background: currentDungeon.currentFloor >= currentDungeon.floors ? '#6b7280' : '#10b981',
                color: 'white',
                border: 'none',
                borderRadius: '6px',
                padding: '8px 12px',
                cursor: currentDungeon.currentFloor >= currentDungeon.floors ? 'not-allowed' : 'pointer',
                fontSize: '14px'
              }}
            >
              ‚¨áÔ∏è Îã§Ïùå Ï∏µ
            </button>
          </div>
        </div>

        {/* Ï∏µ ÏßÑÌñâ ÏÉÅÌÉú ÌëúÏãú */}
      {showDungeonChoice && (
        <div style={{
          background: 'linear-gradient(135deg, #f59e0b 0%, #d97706 100%)',
          borderRadius: '15px',
