    setIsSimulating(true);
    setCurrentAction('📊 30일 히스토리 생성 중...');

    const eventTypes = [
      'battle_start', 'battle_victory', 'battle_defeat', 'level_up', 'item_purchase', 
      'quest_complete', 'shop_visit', 'login', 'logout', 'achievement_unlock',
      'item_craft', 'guild_join', 'pvp_match', 'dungeon_enter', 'boss_defeat'
