      'f293i345-mij6-b497-fc3j-220hi75kjia04',
      'g3a4j456-njk7-c5a8-gd4k-331ij86lkjb15',
      'h4b5k567-okl8-d6b9-he5l-442jk97mlkc26'
    ];

    const batchSize = 200; // 더 많은 데이터 생성

    for (let i = 0; i < batchSize; i++) {
      setTimeout(() => {
        // 랜덤 유저 선택
        const randomUserIndex = Math.floor(Math.random() * userIds.length);
        const randomUserId = userIds[randomUserIndex];
        const randomAccountId = accountIds[randomUserIndex];

        // 랜덤 이벤트 타입 선택
        const randomEventType = eventTypes[Math.floor(Math.random() * eventTypes.length)];

        // 이벤트 타입에 따른 데이터 생성
        let eventData: any = {};
        switch (randomEventType) {
          case 'battle_start':
            eventData = { 
              monster_name: ['슬라임', '오크', '드래곤', '고블린'][Math.floor(Math.random() * 4)],
              difficulty: ['easy', 'normal', 'hard'][Math.floor(Math.random() * 3)]
            };
            break;
          case 'battle_victory':
            eventData = { 
              exp_gained: Math.floor(Math.random() * 100) + 10,
              gold_gained: Math.floor(Math.random() * 50) + 5 
            };
            break;
          case 'level_up':
            eventData = { 
              new_level: Math.floor(Math.random() * 50) + 1,
              skill_points: Math.floor(Math.random() * 5) + 1 
            };
            break;
          case 'item_purchase':
            eventData = { 
              item_name: ['체력 포션', '마법 검', '방패', '갑옷'][Math.floor(Math.random() * 4)],
              item_price: Math.floor(Math.random() * 500) + 50,
              category: ['consumable', 'weapon', 'armor'][Math.floor(Math.random() * 3)]
            };
            break;
          default:
            eventData = { 
              value: Math.floor(Math.random() * 1000),
              session_duration: Math.floor(Math.random() * 3600)
            };
        }

        // 랜덤 타임스탬프 (지난 30일 내)
        const randomTimestamp = new Date(Date.now() - Math.random() * 30 * 24 * 60 * 60 * 1000);

        // 이벤트 생성 (임시 유저 정보로)
        const tempCurrentUser = {
          user_id: randomUserId,
          '#account_id': randomAccountId,
          '#uuid': `550e8400-e29b-41d4-a716-${Math.random().toString(36).substr(2, 12)}`,
          active_time: randomTimestamp.toISOString(),
          reg_time: randomTimestamp.toISOString(),
          session_count: Math.floor(Math.random() * 100) + 1
        };

        // generateEvent 함수를 수정하여 임시 유저로 이벤트 생성
        generateEventWithUser(randomEventType, eventData, tempCurrentUser, randomTimestamp);

        if (i === batchSize - 1) {
          setIsSimulating(false);
          setCurrentAction('📈 30일 데이터 생성 완료!');
          setTimeout(() => setCurrentAction(null), 2000);
        }
      }, i * 50); // 더 빠른 생성
    }
