              </thead>
              <tbody>
                <tr style={{ background: `${theme.cardBorder}20` }}>
                  <td style={{ padding: '6px', fontSize: '10px', color: theme.text, fontFamily: 'monospace' }}>{currentUser.user_id}</td>
                  <td style={{ padding: '6px', fontSize: '9px', color: theme.textSecondary, fontFamily: 'monospace' }}>
                    {currentUser['#account_id'].substring(0, 8)}...
                  </td>
                  <td style={{ padding: '6px', fontSize: '9px', color: theme.accent, fontFamily: 'monospace' }}>
                    {currentUser['#uuid'].substring(0, 8)}...
                  </td>
                  <td style={{ padding: '6px', fontSize: '9px', color: theme.textSecondary, fontFamily: 'monospace' }}>
                    {currentUser.active_time.replace('T', ' ').replace('Z', '')}
                  </td>
                  <td style={{ padding: '6px', fontSize: '9px', color: theme.textSecondary, fontFamily: 'monospace' }}>
                    {currentUser.reg_time.replace('T', ' ').replace('Z', '')}
                  </td>
                  <td style={{ padding: '6px', fontSize: '10px', color: theme.text, fontFamily: 'monospace' }}>{currentUser.session_count}</td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );

  const renderTabContent = () => {
    switch (activeTab) {
      case 'overview':
        return renderOverviewTab();
      case 'data-collection':
        return renderDataCollectionTab();
      default:
        return renderDataCollectionTab();
    }
  };

  return (
    <Layout>
      <div style={{ padding: '24px', maxWidth: '1400px', margin: '0 auto' }}>
        {/* 탭 네비게이션 */}
        <div style={{
          display: 'flex',
          gap: '8px',
          marginBottom: '32px',
          background: theme.cardBg,
          borderRadius: '16px',
          padding: '8px',
          border: `1px solid ${theme.cardBorder}`
        }}>
          {[
            { key: 'overview', icon: <Zap size={20} />, label: 'Overview' },
            { key: 'data-collection', icon: <Database size={20} />, label: 'Data Collection' }
          ].map((tab) => (
            <button
              key={tab.key}
              onClick={() => setActiveTab(tab.key as TabType)}
              style={{
                display: 'flex',
                alignItems: 'center',
                gap: '8px',
                padding: '12px 24px',
                borderRadius: '12px',
                border: 'none',
                background: activeTab === tab.key ? theme.accent : 'transparent',
                color: activeTab === tab.key ? 'white' : theme.text,
                fontSize: '14px',
                fontWeight: '600',
                cursor: 'pointer',
                transition: 'all 0.3s ease'
              }}
            >
              {tab.icon}
              {tab.label}
            </button>
          ))}
        </div>

        {/* 탭 컨텐츠 */}
        {renderTabContent()}
      </div>
    </Layout>
  );
};
