    setUserLogs(prev => {
      const existingUserIndex = prev.findIndex(u => u.user_id === event.user_id);
      const updatedUser = {
        user_id: event.user_id,
        user_name: event.user_name,
        last_login: event.timestamp,
        last_event: event.event_label,
        event_count: existingUserIndex >= 0 ? prev[existingUserIndex].event_count + 1 : 1,
        total_events: existingUserIndex >= 0 ? prev[existingUserIndex].total_events + 1 : 1
      };

      if (existingUserIndex >= 0) {
        const updated = [...prev];
        updated[existingUserIndex] = updatedUser;
        return updated;
      } else {
        return [updatedUser, ...prev.slice(0, 9)];
      }
    });

