    const events: any[] = [];
    const users: any[] = [];

    // 30일간의 데이터 생성 (총 1000개 이벤트)
    for (let i = 0; i < 1000; i++) {
      const daysAgo = Math.floor(Math.random() * 30);
      const hoursAgo = Math.floor(Math.random() * 24);
      const minutesAgo = Math.floor(Math.random() * 60);

      const eventTime = new Date();
      eventTime.setDate(eventTime.getDate() - daysAgo);
      eventTime.setHours(eventTime.getHours() - hoursAgo);
      eventTime.setMinutes(eventTime.getMinutes() - minutesAgo);

      const event = generateRandomEvent();
      event.timestamp = eventTime.toISOString();
      event.json.time = eventTime.getTime();

      events.push(event);

      // 유저 데이터 업데이트
      const existingUserIndex = users.findIndex(u => u.user_id === event.user_id);
      if (existingUserIndex >= 0) {
        users[existingUserIndex].total_events += 1;
        if (eventTime > new Date(users[existingUserIndex].last_login)) {
          users[existingUserIndex].last_login = event.timestamp;
          users[existingUserIndex].last_event = event.event_label;
        }
      } else {
        users.push({
          user_id: event.user_id,
          user_name: event.user_name,
          last_login: event.timestamp,
          last_event: event.event_label,
          event_count: 1,
          total_events: 1
        });
      }
    }

