  const [transformedData, setTransformedData] = useState<any[]>([]);

  // 분석 실행
  const runAnalysis = () => {
    setIsAnalyzing(true);
    setCurrentAction(`${selectedAnalysis} 분석 실행 중...`);

    // 시뮬레이션 데이터 생성
    setTimeout(() => {
      const mockData = generateAnalysisData(selectedAnalysis, selectedEvent, dateRange);
      setAnalysisData(mockData.raw);
      setTransformedData(mockData.transformed);
      setIsAnalyzing(false);
      setCurrentAction(`${selectedAnalysis} 분석 완료`);
    }, 2000);
  };

  // 분석 데이터 생성 함수
  const generateAnalysisData = (analysisType: string, event: string, range: string) => {
    const days = range === '7d' ? 7 : range === '30d' ? 30 : 90;
    const raw: any[] = [];
    const transformed: any[] = [];

    // 원시 데이터 생성
    for (let i = 0; i < days; i++) {
      const date = new Date();
      date.setDate(date.getDate() - (days - 1 - i));

      const dayData = {
        date: date.toISOString().split('T')[0],
        user_count: Math.floor(Math.random() * 1000) + 100,
        event_count: Math.floor(Math.random() * 5000) + 500,
        revenue: Math.floor(Math.random() * 1000000) + 100000
      };
      raw.push(dayData);

      // 변형된 데이터 (분석 결과)
      let transformedEntry: any = {};
      switch (analysisType) {
        case 'event':
          transformedEntry = {
            date: date.toLocaleDateString('ko-KR'),
            total_events: dayData.event_count,
            unique_users: dayData.user_count,
            avg_per_user: Math.round(dayData.event_count / dayData.user_count * 100) / 100,
            conversion_rate: Math.round(Math.random() * 100 * 100) / 100 + '%'
          };
          break;
        case 'retention':
          transformedEntry = {
            cohort_date: date.toLocaleDateString('ko-KR'),
            day_0: '100%',
            day_1: Math.round((50 + Math.random() * 30)) + '%',
            day_7: Math.round((20 + Math.random() * 20)) + '%',
            day_30: Math.round((5 + Math.random() * 15)) + '%'
          };
          break;
        case 'funnel':
          const step1 = dayData.user_count;
          const step2 = Math.floor(step1 * (0.6 + Math.random() * 0.2));
          const step3 = Math.floor(step2 * (0.4 + Math.random() * 0.3));
          transformedEntry = {
            date: date.toLocaleDateString('ko-KR'),
            step_1_visit: step1,
            step_2_signup: step2,
            step_3_purchase: step3,
            conversion_rate: Math.round(step3 / step1 * 10000) / 100 + '%'
          };
          break;
      }
      transformed.push(transformedEntry);
    }

    return { raw, transformed };
  };

