                <thead>
                  <tr style={{ background: `${theme.error}10` }}>
                    <th style={{ padding: '8px', fontSize: '12px', fontWeight: '600', color: theme.text, textAlign: 'left', borderBottom: `1px solid ${theme.cardBorder}` }}>날짜</th>
                    <th style={{ padding: '8px', fontSize: '12px', fontWeight: '600', color: theme.text, textAlign: 'right', borderBottom: `1px solid ${theme.cardBorder}` }}>사용자</th>
                    <th style={{ padding: '8px', fontSize: '12px', fontWeight: '600', color: theme.text, textAlign: 'right', borderBottom: `1px solid ${theme.cardBorder}` }}>이벤트</th>
                  </tr>
                </thead>
                <tbody>
                  {analysisData.slice(0, 10).map((data, index) => (
                    <tr key={index} style={{ 
                      borderBottom: index < 9 ? `1px solid ${theme.cardBorder}` : 'none',
                      background: index % 2 === 0 ? 'transparent' : `${theme.cardBorder}20`
                    }}>
                      <td style={{ padding: '8px', fontSize: '12px', color: theme.text }}>{data.date}</td>
                      <td style={{ padding: '8px', fontSize: '12px', color: theme.textSecondary, textAlign: 'right' }}>{data.user_count.toLocaleString()}</td>
                      <td style={{ padding: '8px', fontSize: '12px', color: theme.textSecondary, textAlign: 'right' }}>{data.event_count.toLocaleString()}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>

          {/* 변형 후 데이터 */}
          <div style={{
            background: theme.cardBg,
            borderRadius: '20px',
            padding: '24px',
            border: `1px solid ${theme.cardBorder}`,
            boxShadow: '0 8px 24px rgba(0, 0, 0, 0.2)'
          }}>
            <h3 style={{
              fontSize: '18px',
              fontWeight: '700',
              color: theme.text,
              margin: '0 0 16px 0',
              display: 'flex',
              alignItems: 'center',
              gap: '8px'
            }}>
              <TrendingUp size={20} style={{ color: '#10b981' }} />
              분석 결과 (Transformed)
            </h3>

            <div style={{
              maxHeight: '300px',
              overflowY: 'auto',
              border: `1px solid ${theme.cardBorder}`,
              borderRadius: '8px'
            }}>
              <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                <thead>
                  <tr style={{ background: `${theme.success}10` }}>
                    {Object.keys(transformedData[0] || {}).map((key, index) => (
                      <th key={index} style={{ 
                        padding: '8px', 
                        fontSize: '12px', 
                        fontWeight: '600', 
                        color: theme.text, 
                        textAlign: index === 0 ? 'left' : 'right', 
                        borderBottom: `1px solid ${theme.cardBorder}` 
                      }}>
                        {key.replace(/_/g, ' ').toUpperCase()}
                      </th>
                    ))}
                  </tr>
                </thead>
                <tbody>
                  {transformedData.slice(0, 10).map((data, index) => (
                    <tr key={index} style={{ 
                      borderBottom: index < 9 ? `1px solid ${theme.cardBorder}` : 'none',
                      background: index % 2 === 0 ? 'transparent' : `${theme.cardBorder}20`
                    }}>
                      {Object.values(data).map((value: any, valueIndex) => (
                        <td key={valueIndex} style={{ 
                          padding: '8px', 
                          fontSize: '12px', 
                          color: valueIndex === 0 ? theme.text : theme.textSecondary, 
                          textAlign: valueIndex === 0 ? 'left' : 'right',
                          fontWeight: valueIndex === 0 ? '600' : '400'
                        }}>
                          {typeof value === 'number' ? value.toLocaleString() : value}
                        </td>
                      ))}
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      )}

      {/* 분석 모델 설명 */}
      <div style={{
        background: theme.cardBg,
        borderRadius: '20px',
        padding: '32px',
        border: `1px solid ${theme.cardBorder}`,
        boxShadow: '0 8px 24px rgba(0, 0, 0, 0.2)'
      }}>
        <h3 style={{
          fontSize: '24px',
          fontWeight: '700',
          color: theme.text,
          margin: '0 0 20px 0',
          display: 'flex',
          alignItems: 'center',
          gap: '12px'
        }}>
          <TrendingUp size={24} style={{ color: '#3b82f6' }} />
          분석 모델별 특징
        </h3>

        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
          gap: '20px'
        }}>
          {/* 이벤트 분석 */}
          <div style={{
            padding: '20px',
            background: `${theme.accent}10`,
            borderRadius: '16px',
            border: `1px solid ${theme.accent}30`
          }}>
            <h4 style={{
              fontSize: '18px',
              fontWeight: '700',
              color: theme.text,
              margin: '0 0 12px 0'
            }}>
              📊 이벤트 분석
            </h4>
            <p style={{ 
              color: theme.textSecondary, 
              fontSize: '14px', 
              lineHeight: '1.6',
              margin: '0 0 12px 0'
            }}>
              사용자의 특정 행동을 분석하여 패턴과 트렌드를 파악합니다.
            </p>
            <ul style={{ 
              color: theme.textSecondary, 
              fontSize: '13px', 
              lineHeight: '1.5',
              paddingLeft: '16px',
              margin: 0
            }}>
              <li>총 이벤트 횟수와 유니크 사용자 수</li>
              <li>사용자당 평균 이벤트 수</li>
              <li>전환율 및 참여도 지표</li>
            </ul>
          </div>

          {/* 리텐션 분석 */}
          <div style={{
            padding: '20px',
            background: `${theme.success}10`,
            borderRadius: '16px',
            border: `1px solid ${theme.success}30`
          }}>
            <h4 style={{
              fontSize: '18px',
              fontWeight: '700',
              color: theme.text,
              margin: '0 0 12px 0'
            }}>
              📈 리텐션 분석
            </h4>
            <p style={{ 
              color: theme.textSecondary, 
              fontSize: '14px', 
              lineHeight: '1.6',
              margin: '0 0 12px 0'
            }}>
              사용자의 재방문율을 코호트별로 추적하여 서비스 만족도를 측정합니다.
            </p>
            <ul style={{ 
              color: theme.textSecondary, 
              fontSize: '13px', 
              lineHeight: '1.5',
              paddingLeft: '16px',
              margin: 0
            }}>
              <li>Day 0 (첫 방문) 대비 재방문율</li>
              <li>1일, 7일, 30일 리텐션 추이</li>
              <li>코호트별 사용자 행동 패턴</li>
            </ul>
          </div>

          {/* 퍼널 분석 */}
          <div style={{
            padding: '20px',
            background: `${theme.warning}10`,
            borderRadius: '16px',
            border: `1px solid ${theme.warning}30`
          }}>
            <h4 style={{
              fontSize: '18px',
              fontWeight: '700',
              color: theme.text,
              margin: '0 0 12px 0'
            }}>
              🎯 퍼널 분석
            </h4>
            <p style={{ 
              color: theme.textSecondary, 
              fontSize: '14px', 
              lineHeight: '1.6',
              margin: '0 0 12px 0'
            }}>
              사용자 여정의 각 단계별 전환율을 분석하여 이탈 구간을 파악합니다.
            </p>
            <ul style={{ 
              color: theme.textSecondary, 
              fontSize: '13px', 
              lineHeight: '1.5',
              paddingLeft: '16px',
              margin: 0
            }}>
              <li>방문 → 가입 → 결제 단계별 전환율</li>
              <li>각 단계별 이탈률과 개선점</li>
              <li>전체 퍼널의 최종 전환율</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );

  const renderTabContent = () => {
    switch (activeTab) {
      case 'overview':
        return renderOverviewTab();
      case 'system':
        return renderSystemTab();
      case 'data-collection':
        return renderDataCollectionTab();
      case 'data-models':
        return renderDataModelsTab();
      default:
        return renderOverviewTab();
    }
  };

  return (
    <Layout>
      <div style={{ 
        padding: '24px',
        maxWidth: '1200px', 
        margin: '0 auto' 
      }}>
        {/* 탭 헤더 */}
        <div style={{
          display: 'flex',
          gap: '8px',
          marginBottom: '32px',
          background: theme.cardBg,
          padding: '8px',
          borderRadius: '16px',
          border: `1px solid ${theme.cardBorder}`,
          boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
          overflowX: 'auto'
        }}>
          {tabs.map((tab) => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              style={{
                display: 'flex',
                alignItems: 'center',
                gap: '8px',
                padding: '12px 20px',
                borderRadius: '12px',
                border: 'none',
                background: activeTab === tab.id 
                  ? `linear-gradient(135deg, ${tab.color}, ${tab.color}dd)` 
                  : 'transparent',
                color: activeTab === tab.id ? 'white' : theme.text,
                fontSize: '14px',
                fontWeight: '600',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                whiteSpace: 'nowrap',
                boxShadow: activeTab === tab.id ? `0 4px 12px ${tab.color}40` : 'none'
              }}
              onMouseEnter={(e) => {
                if (activeTab !== tab.id) {
                  (e.currentTarget as HTMLElement).style.background = `${tab.color}15`;
                }
              }}
              onMouseLeave={(e) => {
                if (activeTab !== tab.id) {
                  (e.currentTarget as HTMLElement).style.background = 'transparent';
                }
              }}
            >
              <div style={{ 
                color: activeTab === tab.id ? 'white' : tab.color,
                display: 'flex',
                alignItems: 'center'
              }}>
                {tab.icon}
              </div>
              {tab.title}
            </button>
          ))}
        </div>

        {/* 탭 콘텐츠 */}
        <div style={{
          minHeight: '500px',
          animation: 'fadeIn 0.3s ease-in-out'
        }}>
          {renderTabContent()}
        </div>

      </div>

      {/* CSS 애니메이션 */}
      <style>{`
        @keyframes fadeIn {
          from { opacity: 0; transform: translateY(20px); }
          to { opacity: 1; transform: translateY(0); }
        }
      `}</style>
    </Layout>
  );
};
