import React, { useState, useEffect, useRef } from 'react';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';

interface Dungeon {
  id: string;
  name: string;
  level: number;
  description: string;
  floors: number;
  currentFloor: number;
  monsters: Monster[];
  rewards: {
    goldRange: [number, number];
    expRange: [number, number];
    itemDropChance: number;
  };
  requiredLevel: number;
  isUnlocked: boolean;
}

interface Monster {
  id: string;
  name: string;
  level: number;
  hp: number;
  maxHp: number;
  attack: number;
  defense: number;
  speed: number;
  exp: number;
  gold: number;
  dropItems: Item[];
  isBoss?: boolean;
}

interface Character {
  id: string;
  name: string;
  level: number;
  exp: number;
  maxExp: number;
  hp: number;
  maxHp: number;
  mp: number;
  maxMp: number;
  attack: number;
  defense: number;
  speed: number;
  gold: number;
  class: string;
  skills: Skill[];
  equipment: Equipment;
  stats: CharacterStats;
  prestige: number;
  rebirth: number;
  buffs: {
    attack: { amount: number; duration: number };
    defense: { amount: number; duration: number };
    speed: { amount: number; duration: number };
  };
}

interface CharacterStats {
  strength: number;
  agility: number;
  intelligence: number;
  vitality: number;
  luck: number;
  availablePoints: number;
}

interface Skill {
  id: string;
  name: string;
  level: number;
  maxLevel: number;
  damage: number;
  manaCost: number;
  cooldown: number;
  currentCooldown: number;
  type: 'active' | 'passive';
  description: string;
}

interface Equipment {
  weapon?: Item;
  armor?: Item;
  helmet?: Item;
  boots?: Item;
  ring?: Item;
  amulet?: Item;
}

interface Item {
  id: string;
  name: string;
  type: 'weapon' | 'armor' | 'helmet' | 'boots' | 'ring' | 'amulet' | 'consumable';
  rarity: 'common' | 'rare' | 'epic' | 'legendary' | 'mythic';
  level: number;
  stats: {
    attack?: number;
    defense?: number;
    hp?: number;
    mp?: number;
    speed?: number;
    critRate?: number;
    critDamage?: number;
  };
  price: number;
  enhancement: number;
  // 물약용 속성들
  consumableEffect?: {
    healHp?: number;
    healMp?: number;
    buffDuration?: number; // 버프 지속시간 (밀리초)
    buffType?: 'attack' | 'defense' | 'speed'; // 버프 타입
    buffAmount?: number; // 버프 수치
  };
  quantity?: number; // 소비 아이템 수량
}



interface Guild {
  id: string;
  name: string;
  level: number;
  members: number;
  maxMembers: number;
  benefits: {
    expBonus: number;
    goldBonus: number;
    dropBonus: number;
  };
}

interface AppProps {
  onAction: (eventType: string, data: any) => void;
  isSimulating: boolean;
  currentAction: string | null;
  userStats: any;
  setUserStats: (stats: any) => void;
  currentUser: any;
}

export const AdvancedIdleRPG: React.FC<AppProps> = ({
  onAction,
  isSimulating,
  currentAction,
  userStats,
  setUserStats,
  currentUser
}) => {
  const { colors } = useTheme();
  const { t } = useLanguage();

  // CSS 애니메이션 추가
  React.useEffect(() => {
    const style = document.createElement('style');
    style.textContent = `
      @keyframes progress {
        0% {
          width: 0%;
        }
        100% {
          width: 100%;
        }
      }
      @keyframes pulse {
        0%, 100% {
          opacity: 1;
        }
        50% {
          opacity: 0.7;
        }
      }
    `;
    document.head.appendChild(style);

    return () => {
      document.head.removeChild(style);
    };
  }, []);
  const [character, setCharacter] = useState<Character>({
    id: '1',
    name: '용사',
    level: 1,
    exp: 0,
    maxExp: 100,
    hp: 100,
    maxHp: 100,
    mp: 50,
    maxMp: 50,
    attack: 20,
    defense: 10,
    speed: 15,
    gold: 100,
    class: '전사',
    skills: [],
    equipment: {},
    stats: {
      strength: 10,
      agility: 8,
      intelligence: 5,
      vitality: 12,
      luck: 7,
      availablePoints: 0
    },
    prestige: 0,
    rebirth: 0,
    buffs: {
      attack: { amount: 0, duration: 0 },
      defense: { amount: 0, duration: 0 },
      speed: { amount: 0, duration: 0 }
    }
  });

  const [currentTab, setCurrentTab] = useState<'lobby' | 'dungeon' | 'character' | 'inventory' | 'skills' | 'shop'>('lobby');
  const [gameMode, setGameMode] = useState<'lobby' | 'dungeon' | 'combat'>('lobby');
  const [isAutoFighting, setIsAutoFighting] = useState(false);
  const [currentMonster, setCurrentMonster] = useState<Monster | null>(null);
  const [combatLog, setCombatLog] = useState<string[]>([]);
  const [selectedDungeon, setSelectedDungeon] = useState<Dungeon | null>(null);
  const [inventory, setInventory] = useState<Item[]>([]);
  const [availableDungeons, setAvailableDungeons] = useState<Dungeon[]>([]);
  const [currentDungeon, setCurrentDungeon] = useState<Dungeon | null>(null);
  const [inDungeon, setInDungeon] = useState(false);
  const [guild, setGuild] = useState<Guild | null>(null);
  const [shop, setShop] = useState<Item[]>([]);
  const [achievements, setAchievements] = useState<string[]>([]);
  const [monstersDefeated, setMonstersDefeated] = useState(0);
  const [totalGoldEarned, setTotalGoldEarned] = useState(0);
  const [showDungeonChoice, setShowDungeonChoice] = useState(false);

  const logRef = useRef<HTMLDivElement>(null);

  // 게임 초기화
  useEffect(() => {
    initializeGame();
  }, []);

