import React, { useState, useEffect, useRef } from 'react';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';

interface Dungeon {
  id: string;
  name: string;
  level: number;
  description: string;
  floors: number;
  currentFloor: number;
  clearedFloors: Set<number>; // Track which floors have been cleared
  monsters: Monster[];
  rewards: {
    goldRange: [number, number];
    expRange: [number, number];
    itemDropChance: number;
  };
  requiredLevel: number;
  isUnlocked: boolean;
  isComingSoon?: boolean; // 업데이트 예정 던전인지
}

interface Monster {
  id: string;
  name: string;
  level: number;
  hp: number;
  maxHp: number;
  attack: number;
  defense: number;
  speed: number;
  exp: number;
  gold: number;
  dropItems: Item[];
  isBoss?: boolean;
}

interface Character {
  id: string;
  name: string;
  level: number;
  exp: number;
  maxExp: number;
  hp: number;
  maxHp: number;
  mp: number;
  maxMp: number;
  attack: number;
  defense: number;
  speed: number;
  gold: number;
  class: string;
  skills: Skill[];
  equipment: Equipment;
  stats: CharacterStats;
  prestige: number;
  rebirth: number;
  buffs: {
    attack: { amount: number; duration: number };
    defense: { amount: number; duration: number };
    speed: { amount: number; duration: number };
  };
}

interface CharacterStats {
  strength: number;
  agility: number;
  intelligence: number;
  vitality: number;
  luck: number;
  availablePoints: number;
}

interface Skill {
  id: string;
  name: string;
  level: number;
  maxLevel: number;
  baseDamage: number;
  damageMultiplier: number; // 레벨당 증가율
  statMultiplier: { attack?: number; defense?: number; hp?: number; mp?: number }; // 스탯 기반 데미지 배율
  manaCost: number;
  cooldown: number;
  currentCooldown: number;
  type: 'active' | 'passive';
  category: 'combat' | 'magic' | 'support' | 'passive'; // 스킬 분류
  description: string;
  requiredLevel?: number;
  isUnlocked?: boolean;
  isLearned?: boolean; // 배운 스킬인지 (0레벨에서 1레벨로 업그레이드했는지)
}

interface Equipment {
  weapon?: Item;
  armor?: Item;
  helmet?: Item;
  boots?: Item;
  ring?: Item;
  amulet?: Item;
}

interface Item {
  id: string;
  name: string;
  type: 'weapon' | 'armor' | 'helmet' | 'boots' | 'ring' | 'amulet' | 'consumable';
  rarity: 'common' | 'rare' | 'epic' | 'legendary' | 'mythic';
  level: number;
  stats: {
    attack?: number;
    defense?: number;
    hp?: number;
    mp?: number;
    speed?: number;
    critRate?: number;
    critDamage?: number;
  };
  price: number;
  enhancement: number;
  // 물약용 속성들
  consumableEffect?: {
    healHp?: number;
    healMp?: number;
    buffDuration?: number; // 버프 지속시간 (밀리초)
    buffType?: 'attack' | 'defense' | 'speed'; // 버프 타입
    buffAmount?: number; // 버프 수치
  };
  quantity?: number; // 소비 아이템 수량
}



interface Guild {
  id: string;
  name: string;
  level: number;
  members: number;
  maxMembers: number;
  benefits: {
    expBonus: number;
    goldBonus: number;
    dropBonus: number;
  };
}

interface AppProps {
  onAction: (eventType: string, data: any) => void;
  isSimulating: boolean;
  currentAction: string | null;
  userStats: any;
  setUserStats: (stats: any) => void;
  currentUser: any;
}

export const AdvancedIdleRPG: React.FC<AppProps> = ({
  onAction,
  isSimulating,
  currentAction,
  userStats,
  setUserStats,
  currentUser
}) => {
  const { colors } = useTheme();
  const { t } = useLanguage();

  // 스킬 데미지 계산 함수
  const calculateSkillDamage = (skill: Skill, character: Character): number => {
    if (skill.level === 0 || !skill.isLearned) return 0;

    let damage = skill.baseDamage;

    // 레벨에 따른 데미지 증가
    damage += skill.baseDamage * skill.damageMultiplier * (skill.level - 1);

    // 스탯에 따른 데미지 증가
    if (skill.statMultiplier.attack) {
      damage += character.attack * skill.statMultiplier.attack * skill.level;
    }
    if (skill.statMultiplier.defense) {
      damage += character.defense * skill.statMultiplier.defense * skill.level;
    }
    if (skill.statMultiplier.hp) {
      damage += character.maxHp * skill.statMultiplier.hp * skill.level;
    }
    if (skill.statMultiplier.mp) {
      damage += character.maxMp * skill.statMultiplier.mp * skill.level;
    }

    return Math.floor(damage);
  };

  // CSS 애니메이션 추가
  React.useEffect(() => {
    const style = document.createElement('style');
    style.textContent = `
      @keyframes progress {
        0% {
          width: 0%;
        }
        100% {
          width: 100%;
        }
      }
      @keyframes pulse {
        0%, 100% {
          opacity: 1;
        }
        50% {
          opacity: 0.7;
        }
      }
    `;
    document.head.appendChild(style);

    return () => {
      document.head.removeChild(style);
    };
  }, []);
  const [character, setCharacter] = useState<Character>({
    id: '1',
    name: '용사',
    level: 1,
    exp: 0,
    maxExp: 100,
    hp: 100,
    maxHp: 100,
    mp: 50,
    maxMp: 50,
    attack: 20,
    defense: 10,
    speed: 15,
    gold: 100,
    class: '전사',
    skills: [],
    equipment: {},
    stats: {
      strength: 10,
      agility: 8,
      intelligence: 5,
      vitality: 12,
      luck: 7,
      availablePoints: 0
    },
    prestige: 0,
    rebirth: 0,
    buffs: {
      attack: { amount: 0, duration: 0 },
      defense: { amount: 0, duration: 0 },
      speed: { amount: 0, duration: 0 }
    }
  });

  const [currentTab, setCurrentTab] = useState<'lobby' | 'dungeon' | 'character' | 'inventory' | 'skills' | 'shop'>('lobby');
  const [gameMode, setGameMode] = useState<'lobby' | 'dungeon' | 'combat'>('lobby');
  const [isAutoFighting, setIsAutoFighting] = useState(false);
  const [currentMonster, setCurrentMonster] = useState<Monster | null>(null);
  const [combatLog, setCombatLog] = useState<string[]>([]);
  const [selectedDungeon, setSelectedDungeon] = useState<Dungeon | null>(null);
  const [inventory, setInventory] = useState<Item[]>([]);
  const [availableDungeons, setAvailableDungeons] = useState<Dungeon[]>([]);
  const [currentDungeon, setCurrentDungeon] = useState<Dungeon | null>(null);
  const [inDungeon, setInDungeon] = useState(false);
  const [guild, setGuild] = useState<Guild | null>(null);
  const [shop, setShop] = useState<Item[]>([]);
  const [achievements, setAchievements] = useState<string[]>([]);
  const [monstersDefeated, setMonstersDefeated] = useState(0);
  const [totalGoldEarned, setTotalGoldEarned] = useState(0);
  const [showDungeonChoice, setShowDungeonChoice] = useState(false);

  const logRef = useRef<HTMLDivElement>(null);

  // 게임 초기화
  useEffect(() => {
    initializeGame();
  }, []);

  // 캐릭터 상태가 변경될 때마다 userStats 업데이트
  useEffect(() => {
    setUserStats({
      level: character.level,
      gold: character.gold,
      attack: character.attack,
      defense: character.defense,
      hp: character.hp,
      maxHp: character.maxHp,
      exp: character.exp,
      maxExp: character.maxExp,
      monsters_defeated: monstersDefeated,
      character_class: character.class,
      total_gold_earned: totalGoldEarned,
      inventory_items: inventory.map(item => ({
        id: item.id,
        name: item.name,
        type: item.type,
        rarity: item.rarity,
        quantity: item.quantity || 1,
        stats: item.stats
      })),
      skills: character.skills.map(skill => ({
        id: skill.id,
        name: skill.name,
        level: skill.level,
        type: skill.type
      })),
      current_buffs: Object.entries(character.buffs)
        .filter(([_, buff]) => buff.duration > 0)
        .map(([type, buff]) => ({
          type,
          amount: buff.amount,
          duration: Math.ceil(buff.duration / 1000)
        }))
    });
  }, [character, inventory, monstersDefeated, totalGoldEarned, setUserStats]);

  const initializeGame = () => {
    // 언어별 텍스트 정의
    const translations = {
      ko: {
        character: {
          name: '용사',
          class: '전사'
        },
        skills: {
          slash: { name: '강력한 베기', description: '강력한 일격을 가합니다.' },
          heal: { name: '치유', description: 'HP를 회복합니다.' },
          critical: { name: '치명타 숙련', description: '치명타 확률을 증가시킵니다.' }
        },
        dungeons: {
          forest: { name: '어둠의 숲', description: '초보자를 위한 던전입니다.' },
          cave: { name: '고블린 동굴', description: '고블린들이 서식하는 위험한 동굴입니다.' },
          tower: { name: '마법사의 탑', description: '강력한 마법 몬스터들이 있는 탑입니다.' }
        },
        items: {
          sword1: '강철 검',
          armor1: '가죽 갑옷',
          helmet1: '철제 투구',
          health_potion: '체력 물약',
          mana_potion: '마나 물약',
          greater_health_potion: '고급 체력 물약',
          strength_potion: '힘의 물약',
          defense_potion: '방어의 물약'
        },
        monsters: {
          slime: '슬라임',
          goblin: '고블린',
          orc: '오크',
          troll: '트롤',
          dragon: '드래곤'
        },
        combat: {
          appeared: '이 나타났다!',
          attack: '의 공격!',
          damage: '데미지!',
          critical: '치명타!',
          levelUp: '🎉 레벨업! Lv.',
          defeated: '를 물리쳤다! EXP +',
          gold: ', 골드 +',
          itemObtained: '✨ 을 획득했습니다!',
          dungeonCleared: '🎉  던전을 클리어했습니다!',
          nextFloor: '층으로 자동 진행합니다!',
          dungeonExited: '던전에서 자동으로 나왔습니다. 체력과 마나가 완전히 회복되었습니다!',
          hpMpRecovered: '체력과 마나가 완전히 회복되었습니다!',
          defeat: '전투에서 패배했습니다...'
        }
      },
      en: {
        character: {
          name: 'Hero',
          class: 'Warrior'
        },
        skills: {
          slash: { name: 'Power Slash', description: 'Delivers a powerful strike.' },
          heal: { name: 'Heal', description: 'Restores HP.' },
          critical: { name: 'Critical Mastery', description: 'Increases critical hit chance.' }
        },
        dungeons: {
          forest: { name: 'Dark Forest', description: 'A dungeon for beginners.' },
          cave: { name: 'Goblin Cave', description: 'A dangerous cave inhabited by goblins.' },
          tower: { name: 'Mage Tower', description: 'A tower with powerful magic monsters.' }
        },
        items: {
          sword1: 'Steel Sword',
          armor1: 'Leather Armor',
          helmet1: 'Iron Helmet',
          health_potion: 'Health Potion',
          mana_potion: 'Mana Potion',
          greater_health_potion: 'Greater Health Potion',
          strength_potion: 'Strength Potion',
          defense_potion: 'Defense Potion'
        },
        monsters: {
          slime: 'Slime',
          goblin: 'Goblin',
          orc: 'Orc',
          troll: 'Troll',
          dragon: 'Dragon'
        },
        combat: {
          appeared: ' appeared!',
          attack: "'s attack!",
          damage: ' damage!',
          critical: ' (Critical!)',
          levelUp: '🎉 Level up! Lv.',
          defeated: ' defeated! EXP +',
          gold: ', Gold +',
          itemObtained: '✨ obtained!',
          dungeonCleared: '🎉  dungeon cleared!',
          nextFloor: 'Proceeding to floor  automatically!',
          dungeonExited: 'Automatically exited dungeon. HP and MP fully restored!',
          hpMpRecovered: 'HP and MP fully restored!',
          defeat: 'Defeated in battle...'
        }
      },
      zh: {
        character: {
          name: '勇者',
          class: '战士'
        },
        skills: {
          slash: { name: '强力斩击', description: '发动强力一击。' },
          heal: { name: '治疗', description: '恢复生命值。' },
          critical: { name: '暴击精通', description: '增加暴击几率。' }
        },
        dungeons: {
          forest: { name: '黑暗森林', description: '适合新手的地下城。' },
          cave: { name: '哥布林洞穴', description: '哥布林栖息的危险洞穴。' },
          tower: { name: '法师塔', description: '有强力魔法怪物的塔。' }
        },
        items: {
          sword1: '钢铁剑',
          armor1: '皮甲',
          helmet1: '铁盔',
