              price: 400,
              enhancement: 0,
              consumableEffect: {
                buffType: 'defense' as const,
                buffAmount: 20,
                buffDuration: 60000
              },
              quantity: 1
            }
          ].filter(item => !existingIds.includes(item.id));

          baseItems.push(...wizardItems);
        }

        return baseItems;
      };

      setShop(getShopItems());
    };

    updateShop();
  }, [availableDungeons]);

  // 레벨업 시 스킬 업데이트
  useEffect(() => {
    const updateSkills = () => {
      setCharacter(prev => {
        // 기존 스킬들의 해금 상태만 업데이트
        const updatedSkills = prev.skills.map(skill => ({
          ...skill,
          isUnlocked: prev.level >= (skill.requiredLevel || 1)
        }));
        return { ...prev, skills: updatedSkills };
      });
    };

    updateSkills();
  }, [character.level]);

  // 스킬 사용 함수 (수정된 데미지 계산 포함)
  const executeSkill = (skill: Skill) => {
    if (!skill.isLearned || skill.level === 0) {
      addToCombatLog('스킬을 먼저 배워야 합니다!');
      return;
    }

    if (character.mp < skill.manaCost) {
      addToCombatLog('마나가 부족합니다!');
      return;
    }

    if (skill.currentCooldown > 0) {
      addToCombatLog('스킬이 재사용 대기 중입니다!');
      return;
    }

    const damage = calculateSkillDamage(skill, character);

    // 스킬 사용 로직
    if (skill.id === 'heal' || skill.id === 'regeneration') {
      // 힐링 스킬
      const healAmount = damage;
      setCharacter(prev => ({
        ...prev,
        hp: Math.min(prev.maxHp, prev.hp + healAmount),
        mp: prev.mp - skill.manaCost
      }));
      addToCombatLog(`${skill.name} 사용! ${healAmount} 체력 회복!`);
    } else if (currentMonster) {
      // 공격 스킬
      const newMonsterHp = Math.max(0, currentMonster.hp - damage);
      setCurrentMonster(prev => prev ? { ...prev, hp: newMonsterHp } : null);
      setCharacter(prev => ({ ...prev, mp: prev.mp - skill.manaCost }));
      addToCombatLog(`${skill.name} 사용! ${damage} 데미지!`);

      if (newMonsterHp === 0) {
        setTimeout(defeatMonster, 500);
      }
    }

    // 쿨다운 설정
    setCharacter(prev => ({
      ...prev,
      skills: prev.skills.map(s => 
        s.id === skill.id ? { ...s, currentCooldown: skill.cooldown } : s
      )
    }));
  };

  // 스킬 배우기 (0레벨에서 1레벨로 업그레이드) 함수
  const learnSkill = (skill: Skill) => {
    if (skill.level > 0) {
      addToCombatLog('이미 배운 스킬입니다!');
      return;
    }

    const cost = 100; // 스킬 배우기 비용
    if (character.gold < cost) {
      addToCombatLog('골드가 부족합니다!');
      return;
    }

    setCharacter(prev => ({
      ...prev,
      gold: prev.gold - cost,
      skills: prev.skills.map(s => 
        s.id === skill.id ? { ...s, level: 1, isLearned: true } : s
      )
    }));
    addToCombatLog(`${skill.name}을(를) 배웠습니다!`);
  };

  // 스킬 렌더링 함수 (분류별)
  // 스킬 아이콘 맵핑
  const getSkillIcon = (skill: Skill) => {
    const iconMap: { [key: string]: string } = {
      // 전투 스킬
      'basic-attack': '⚔️',
      'power-strike': '💥',
      'fire-slash': '🔥',
      'lightning-bolt': '⚡',
      'ice-shard': '❄️',
      'double-attack': '⚔️⚔️',
      // 지원 스킬
      'heal': '💚',
      'mana-recovery': '💙',
      'shield': '🛡️',
      'haste': '💨',
      'blessing': '✨',
      // 패시브 스킬
      'strength-boost': '💪',
      'defense-boost': '🛡️',
      'agility-boost': '🏃',
      'mana-boost': '🔮',
      'critical-chance': '💎',
      'vampire': '🩸'
    };
    return iconMap[skill.id] || '📜';
  };

  // 선택된 스킬 상태
  const [selectedSkill, setSelectedSkill] = useState<Skill | null>(null);

  // 스킬 탭 내부의 서브 탭 상태
  const [skillSubTab, setSkillSubTab] = React.useState<'combat' | 'support' | 'passive'>('combat');

  // 스킬 탭 컨텐츠 렌더링 (게임 스타일 UI)
  const renderSkillsTab = () => (
    <div style={{ padding: '20px', background: '#1f2937', minHeight: '600px' }}>
      {/* 공통 캐릭터 헤더 */}
      {renderCharacterHeader()}

      <div style={{
        background: 'linear-gradient(135deg, #7c3aed 0%, #a855f7 100%)',
        borderRadius: '15px',
        padding: '20px',
        marginBottom: '20px',
        color: 'white',
        textAlign: 'center'
      }}>
        <h2 style={{ margin: '0 0 10px 0', fontSize: '24px' }}>✨ 스킬 트리</h2>
        <p style={{ margin: 0, opacity: 0.9 }}>골드: {character.gold.toLocaleString()}G</p>
      </div>

      <div style={{ display: 'flex', gap: '20px' }}>
        {/* 왼쪽 카테고리 사이드바 */}
        <div style={{ width: '200px' }}>
          <h3 style={{ color: 'white', marginBottom: '15px', fontSize: '16px' }}>📚 스킬 분류</h3>
          <div style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
            {[
              { id: 'combat', label: '전체', count: character.skills.filter(s => s.isUnlocked).length, color: '#8b5cf6' },
              { id: 'combat', label: '전투', count: character.skills.filter(s => s.isUnlocked && (s.category === 'combat' || s.category === 'magic')).length, color: '#dc2626' },
              { id: 'support', label: '지원', count: character.skills.filter(s => s.isUnlocked && s.category === 'support').length, color: '#10b981' },
              { id: 'passive', label: '패시브', count: character.skills.filter(s => s.isUnlocked && s.category === 'passive').length, color: '#f59e0b' }
            ].map((tab, index) => (
              <button
                key={index}
                onClick={() => setSkillSubTab(index === 0 ? 'combat' : tab.id as any)}
                style={{
                  padding: '12px 15px',
                  background: (index === 0 && skillSubTab) || skillSubTab === tab.id 
                    ? `linear-gradient(135deg, ${tab.color} 0%, ${tab.color}CC 100%)`
                    : 'rgba(255, 255, 255, 0.1)',
                  color: (index === 0 && skillSubTab) || skillSubTab === tab.id ? 'white' : '#9ca3af',
                  border: '1px solid #4b5563',
                  borderRadius: '8px',
                  cursor: 'pointer',
                  fontSize: '14px',
                  fontWeight: 'normal',
                  transition: 'all 0.2s ease',
                  textAlign: 'left',
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center'
                }}
              >
                <span>{tab.label}</span>
                <span style={{
                  background: 'rgba(0, 0, 0, 0.3)',
                  color: 'white',
                  fontSize: '11px',
                  padding: '2px 6px',
                  borderRadius: '10px',
                  minWidth: '20px',
                  textAlign: 'center'
                }}>
                  {index === 0 ? character.skills.filter(s => s.isUnlocked).length : tab.count}
                </span>
              </button>
            ))}
          </div>
        </div>

        {/* 중앙 스킬 그리드 */}
        <div style={{ flex: 1 }}>
          <h3 style={{ color: 'white', marginBottom: '15px', fontSize: '16px' }}>
            🎯 {skillSubTab === 'combat' ? '전투 스킬' : 
                skillSubTab === 'support' ? '지원 스킬' : '패시브 스킬'}
          </h3>
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(8, 1fr)',
            gap: '6px',
            background: 'rgba(0, 0, 0, 0.3)',
            padding: '15px',
            borderRadius: '10px',
            border: '2px solid #4b5563',
            minHeight: '400px'
          }}>
            {/* 스킬 슬롯 렌더링 */}
            {Array.from({ length: 32 }, (_, index) => {
              const filteredSkills = character.skills.filter(skill => {
                if (skillSubTab === 'combat') {
                  return skill.isUnlocked && (skill.category === 'combat' || skill.category === 'magic');
                }
                return skill.isUnlocked && skill.category === skillSubTab;
              });

              const skill = filteredSkills[index];
              const isEmpty = !skill;

              return (
                <div
                  key={index}
                  onClick={() => skill && setSelectedSkill(skill)}
                  style={{
                    width: '50px',
                    height: '50px',
                    background: isEmpty ? 'rgba(255, 255, 255, 0.05)' : 
                      skill.isLearned ? 'rgba(16, 185, 129, 0.2)' : 'rgba(107, 114, 128, 0.2)',
                    border: isEmpty ? '1px solid #374151' : 
                      skill.isLearned ? '2px solid #10b981' : '2px solid #6b7280',
                    borderRadius: '8px',
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    justifyContent: 'center',
                    cursor: skill ? 'pointer' : 'default',
                    position: 'relative',
                    transition: 'all 0.2s ease'
                  }}
                  onMouseEnter={(e) => {
                    if (skill) {
                      e.currentTarget.style.transform = 'scale(1.1)';
                      e.currentTarget.style.boxShadow = skill.isLearned 
                        ? '0 0 15px #10b98150' 
                        : '0 0 15px #6b728050';
                    }
                  }}
                  onMouseLeave={(e) => {
                    if (skill) {
                      e.currentTarget.style.transform = 'scale(1)';
                      e.currentTarget.style.boxShadow = 'none';
                    }
                  }}
                >
                  {skill && (
                    <>
                      <div style={{ 
                        fontSize: '20px',
                        filter: skill.isLearned ? 'none' : 'grayscale(1)',
                        opacity: skill.isLearned ? 1 : 0.5
                      }}>
                        {getSkillIcon(skill)}
                      </div>
                      {/* 레벨 표시 */}
                      {skill.level > 0 && (
                        <div style={{
                          position: 'absolute',
                          bottom: '2px',
                          right: '2px',
                          background: '#10b981',
                          color: 'white',
                          fontSize: '10px',
                          padding: '1px 4px',
                          borderRadius: '3px',
                          lineHeight: '1',
                          fontWeight: 'bold'
                        }}>
                          {skill.level}
                        </div>
                      )}
                      {/* 배울 수 있음 표시 */}
                      {skill.level === 0 && character.gold >= 100 && (
                        <div style={{
                          position: 'absolute',
                          top: '2px',
                          right: '2px',
                          background: '#fbbf24',
                          width: '8px',
                          height: '8px',
                          borderRadius: '50%',
                          animation: 'pulse 2s infinite'
                        }} />
                      )}
                    </>
                  )}
                </div>
              );
            })}
          </div>
        </div>

        {/* 오른쪽 스킬 상세 정보 */}
        <div style={{ width: '300px' }}>
          <h3 style={{ color: 'white', marginBottom: '15px', fontSize: '16px' }}>📋 스킬 정보</h3>
          {selectedSkill ? (
            <div style={{
              background: 'rgba(255, 255, 255, 0.1)',
              border: selectedSkill.isLearned ? '2px solid #10b981' : '2px solid #6b7280',
              borderRadius: '10px',
              padding: '20px'
            }}>
              <div style={{ textAlign: 'center', marginBottom: '15px' }}>
                <div style={{ 
                  fontSize: '48px', 
                  marginBottom: '10px',
                  filter: selectedSkill.isLearned ? 'none' : 'grayscale(1)',
                  opacity: selectedSkill.isLearned ? 1 : 0.7
                }}>
                  {getSkillIcon(selectedSkill)}
                </div>
                <h4 style={{
                  color: selectedSkill.isLearned ? '#10b981' : '#9ca3af',
                  margin: '0 0 5px 0',
                  fontSize: '18px',
                  fontWeight: 'bold'
                }}>
                  {selectedSkill.name}
                </h4>
                <p style={{
                  color: '#9ca3af',
                  margin: '0 0 15px 0',
                  fontSize: '14px'
                }}>
                  Lv.{selectedSkill.level}/{selectedSkill.maxLevel} • {selectedSkill.type === 'active' ? '액티브' : '패시브'}
                </p>
              </div>

              {/* 스킬 효과 */}
              <div style={{ marginBottom: '15px' }}>
                <h5 style={{ color: 'white', margin: '0 0 10px 0', fontSize: '14px' }}>효과</h5>
                <p style={{ color: '#d1d5db', fontSize: '12px', lineHeight: '1.4', margin: 0 }}>
                  {selectedSkill.description}
                </p>
                {selectedSkill.level > 0 && selectedSkill.type === 'active' && (
                  <p style={{ 
                    color: '#fbbf24', 
                    fontSize: '12px', 
                    fontWeight: 'bold',
                    marginTop: '8px'
                  }}>
                    데미지: {calculateSkillDamage(selectedSkill, character)}
                  </p>
                )}
              </div>

              {/* 스킬 정보 */}
              {selectedSkill.type === 'active' && (
                <div style={{ marginBottom: '15px' }}>
                  <h5 style={{ color: 'white', margin: '0 0 10px 0', fontSize: '14px' }}>사용 정보</h5>
                  <div style={{ fontSize: '12px', color: '#9ca3af' }}>
                    <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '5px' }}>
                      <span>마나 소모</span>
                      <span style={{ color: '#60a5fa' }}>{selectedSkill.manaCost}</span>
                    </div>
                    <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '5px' }}>
                      <span>쿨다운</span>
                      <span style={{ color: '#f472b6' }}>{selectedSkill.cooldown / 1000}초</span>
                    </div>
                  </div>
                </div>
              )}

              {/* 업그레이드 비용 */}
              <div style={{ marginBottom: '15px' }}>
                <h5 style={{ color: 'white', margin: '0 0 10px 0', fontSize: '14px' }}>
                  {selectedSkill.level === 0 ? '학습 비용' : '업그레이드 비용'}
                </h5>
                <p style={{ color: '#f59e0b', fontSize: '12px', margin: 0 }}>
                  {selectedSkill.level === 0 ? '100' : (selectedSkill.level * 100).toLocaleString()}골드
                </p>
              </div>

              {/* 액션 버튼 */}
              <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                {selectedSkill.level === 0 ? (
                  <button
                    onClick={() => {
                      learnSkill(selectedSkill);
                      setSelectedSkill(null);
                    }}
                    disabled={character.gold < 100}
                    style={{
                      background: character.gold >= 100 
                        ? 'linear-gradient(135deg, #10b981 0%, #059669 100%)' 
                        : '#6b7280',
                      color: 'white',
                      border: 'none',
                      borderRadius: '6px',
                      padding: '12px',
                      fontSize: '14px',
                      cursor: character.gold >= 100 ? 'pointer' : 'not-allowed',
                      fontWeight: 'bold'
                    }}
                  >
                    스킬 배우기 (100G)
                  </button>
                ) : selectedSkill.level < selectedSkill.maxLevel ? (
                  <button
                    onClick={() => {
                      const cost = selectedSkill.level * 100;
                      if (character.gold >= cost) {
                        setCharacter(prev => ({
                          ...prev,
                          gold: prev.gold - cost,
                          skills: prev.skills.map(s => 
                            s.id === selectedSkill.id ? {...s, level: s.level + 1} : s
                          )
                        }));
                        addToCombatLog(`${selectedSkill.name}을(를) 레벨업했다! (Lv.${selectedSkill.level + 1})`);
                        setSelectedSkill(prev => prev ? {...prev, level: prev.level + 1} : null);
                      }
                    }}
                    disabled={character.gold < selectedSkill.level * 100}
                    style={{
                      background: character.gold >= selectedSkill.level * 100 
                        ? 'linear-gradient(135deg, #fbbf24 0%, #f59e0b 100%)' 
                        : '#6b7280',
                      color: 'white',
                      border: 'none',
                      borderRadius: '6px',
                      padding: '12px',
                      fontSize: '14px',
                      cursor: character.gold >= selectedSkill.level * 100 ? 'pointer' : 'not-allowed',
                      fontWeight: 'bold'
                    }}
                  >
                    레벨업 ({(selectedSkill.level * 100).toLocaleString()}G)
                  </button>
                ) : (
                  <div style={{
                    background: '#374151',
                    color: '#9ca3af',
                    border: 'none',
                    borderRadius: '6px',
                    padding: '12px',
                    fontSize: '14px',
                    textAlign: 'center'
                  }}>
                    최대 레벨 달성
                  </div>
                )}
              </div>
            </div>
          ) : (
            <div style={{
              background: 'rgba(255, 255, 255, 0.05)',
              borderRadius: '10px',
              padding: '40px',
              textAlign: 'center',
              color: '#6b7280'
            }}>
              <p>스킬을 클릭하여 정보를 확인하세요</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );

  // 몬스터 생성
  const spawnMonster = () => {
    // 이미 몬스터가 있거나 던전에 있으면 새로 생성하지 않음
    if (currentMonster || inDungeon) {
      return;
    }

    const gameTexts = (window as any).gameTexts || {
      monsters: { slime: '슬라임', goblin: '고블린', orc: '오크', troll: '트롤', dragon: '드래곤' },
      combat: { appeared: '이 나타났다!' }
    };
    const monsters = [
      { name: gameTexts.monsters.slime, baseHp: 40, baseAttack: 8, baseDefense: 2, exp: 15, gold: 10, minLevel: 1, maxLevel: 3 },
      { name: gameTexts.monsters.goblin, baseHp: 60, baseAttack: 12, baseDefense: 4, exp: 25, gold: 18, minLevel: 3, maxLevel: 6 },
      { name: gameTexts.monsters.orc, baseHp: 100, baseAttack: 18, baseDefense: 8, exp: 40, gold: 30, minLevel: 6, maxLevel: 10 },
      { name: gameTexts.monsters.troll, baseHp: 150, baseAttack: 25, baseDefense: 12, exp: 60, gold: 45, minLevel: 10, maxLevel: 15 },
      { name: gameTexts.monsters.dragon, baseHp: 300, baseAttack: 40, baseDefense: 20, exp: 120, gold: 100, minLevel: 15, maxLevel: 999 }
    ];

    // 캐릭터 레벨에 적합한 몬스터만 필터링
    const availableMonsters = monsters.filter(monster => 
      character.level >= monster.minLevel && character.level <= monster.maxLevel
    );

    // 적합한 몬스터가 없으면 레벨 범위를 확장
    const finalMonsters = availableMonsters.length > 0 ? availableMonsters : monsters.filter(monster => 
      Math.abs(character.level - monster.minLevel) <= 2
    );

    const randomMonster = finalMonsters[Math.floor(Math.random() * finalMonsters.length)];
    const levelMultiplier = 1 + (character.level - 1) * 0.3;

    const monster: Monster = {
      id: `monster_${Date.now()}`,
      name: randomMonster.name,
      level: Math.max(1, character.level + Math.floor(Math.random() * 3 - 1)),
      hp: Math.floor(randomMonster.baseHp * levelMultiplier),
      maxHp: Math.floor(randomMonster.baseHp * levelMultiplier),
      attack: Math.floor(randomMonster.baseAttack * levelMultiplier),
      defense: Math.floor(randomMonster.baseDefense * levelMultiplier),
      speed: 10,
      exp: Math.floor(randomMonster.exp * levelMultiplier),
      gold: Math.floor(randomMonster.gold * levelMultiplier),
      dropItems: generateDropItems(),
      isBoss: Math.random() < 0.1 // 10% 보스 확률
    };

    if (monster.isBoss) {
      monster.name = `보스 ${monster.name}`;
      monster.hp *= 3;
      monster.maxHp *= 3;
      monster.attack *= 2;
      monster.defense *= 2;
      monster.exp *= 5;
      monster.gold *= 3;
    }

    setCurrentMonster(monster);
    addToCombatLog(`${monster.name} Lv.${monster.level}${gameTexts.combat.appeared}`);
  };

  // 드롭 아이템 생성
  const generateDropItems = (): Item[] => {
    const dropChance = Math.random();
    if (dropChance < 0.1) { // 10% 확률로 아이템 드롭
      const rarities: Array<'common' | 'rare' | 'epic' | 'legendary' | 'mythic'> = ['common', 'rare', 'epic', 'legendary', 'mythic'];
      const rarity = rarities[Math.floor(Math.random() * rarities.length)];
      const itemTypes = ['weapon', 'armor', 'helmet', 'boots', 'ring', 'amulet'] as const;
      const itemType = itemTypes[Math.floor(Math.random() * itemTypes.length)];

      return [{
        id: `drop_${Date.now()}`,
        name: generateItemName(rarity, itemType),
        type: itemType,
        rarity,
        level: character.level,
        stats: generateItemStats(rarity),
        price: calculateItemPrice(rarity, character.level),
        enhancement: 0
      }];
    }
    return [];
  };

  const generateItemName = (rarity: string, itemType: string): string => {
    const prefixes = {
      common: ['낡은', '일반적인', '기본'],
      rare: ['날카로운', '튼튼한', '마법의'],
      epic: ['전설적인', '고대의', '신성한'],
      legendary: ['전설의', '영웅의', '불멸의'],
      mythic: ['신화의', '창조의', '절대의']
    };

    const itemNames = {
      weapon: ['검', '도끼', '창', '활', '지팡이'],
      armor: ['갑옷', '로브', '사슬갑옷', '가죽갑옷'],
      helmet: ['투구', '모자', '관', '헬름'],
      boots: ['신발', '부츠', '장화', '샌들'],
      ring: ['반지', '링', '밴드'],
      amulet: ['목걸이', '부적', '펜던트']
    };

    const prefix = prefixes[rarity as keyof typeof prefixes][Math.floor(Math.random() * 3)];
    const itemTypeNames = itemNames[itemType as keyof typeof itemNames] || ['아이템'];
    const item = itemTypeNames[Math.floor(Math.random() * itemTypeNames.length)];

    return `${prefix} ${item}`;
  };

  const generateItemStats = (rarity: string) => {
    const multipliers = {
      common: 1,
      rare: 1.5,
      epic: 2.5,
      legendary: 4,
      mythic: 7
    };

    const base = multipliers[rarity as keyof typeof multipliers];
    return {
      attack: Math.floor(Math.random() * 20 * base),
      defense: Math.floor(Math.random() * 15 * base),
      hp: Math.floor(Math.random() * 50 * base),
      mp: Math.floor(Math.random() * 30 * base),
      speed: Math.floor(Math.random() * 10 * base),
      critRate: Math.floor(Math.random() * 10 * base),
      critDamage: Math.floor(Math.random() * 20 * base)
    };
  };

  const calculateItemPrice = (rarity: string, level: number): number => {
    const basePrice = {
      common: 50,
      rare: 150,
      epic: 500,
      legendary: 2000,
      mythic: 10000
    };

    return Math.floor(basePrice[rarity as keyof typeof basePrice] * (1 + level * 0.2));
  };

  // 전투 시스템
  const attack = () => {
    if (!currentMonster) return;

    const buffedAttack = character.attack + character.buffs.attack.amount;
    const damage = calculateDamage(buffedAttack, currentMonster.defense);
    const isCritical = Math.random() < 0.1 + (character.stats.luck * 0.01);
    const finalDamage = isCritical ? Math.floor(damage * 1.5) : damage;

    const newMonsterHp = Math.max(0, currentMonster.hp - finalDamage);
    setCurrentMonster(prev => prev ? { ...prev, hp: newMonsterHp } : null);

    const gameTexts = (window as any).gameTexts || {
      combat: { attack: '의 공격!', damage: '데미지!', critical: '치명타!' }
    };
    addToCombatLog(`${character.name}${gameTexts.combat.attack} ${finalDamage}${isCritical ? ` (${gameTexts.combat.critical})` : ''} ${gameTexts.combat.damage}`);

    onAction('combat_attack', {
      user_id: currentUser.user_id,
      damage: finalDamage,
      is_critical: isCritical,
      monster_name: currentMonster.name,
      monster_level: currentMonster.level,
      character_level: character.level,
      character_attack: character.attack,
      monster_hp_remaining: newMonsterHp,
      dungeon_name: currentDungeon?.name || null,
      dungeon_floor: currentDungeon?.currentFloor || null
    });

    if (newMonsterHp <= 0) {
      defeatMonster();
    } else {
      // 몬스터 반격
      setTimeout(() => monsterAttack(), 1000);
    }
  };

  const monsterAttack = () => {
    if (!currentMonster || currentMonster.hp <= 0) return;

    const buffedDefense = character.defense + character.buffs.defense.amount;
    const damage = calculateDamage(currentMonster.attack, buffedDefense);
    const newHp = Math.max(0, character.hp - damage);

    setCharacter(prev => ({ ...prev, hp: newHp }));
    const gameTexts = (window as any).gameTexts || {
      combat: { attack: '의 공격!', damage: '데미지!' }
    };
    addToCombatLog(`${currentMonster.name}${gameTexts.combat.attack} ${damage} ${gameTexts.combat.damage}`);

    if (newHp <= 0) {
      const gameTexts = (window as any).gameTexts || {
        combat: { defeat: '전투에서 패배했습니다...' }
      };
      addToCombatLog(gameTexts.combat.defeat);
      setIsAutoFighting(false);

      // 전투 실패 이벤트 로깅
      onAction('combat_defeat', {
        monster_name: currentMonster.name,
        monster_level: currentMonster.level,
        character_level: character.level,
        dungeon_id: currentDungeon?.id || 'field',
        dungeon_name: currentDungeon?.name || 'field',
        damage_taken: currentMonster.attack,
        character_defense: character.defense
      });

      // 던전에서 나가고 체력/마나 완전 회복
      setTimeout(() => {
        setCharacter(prev => ({
          ...prev,
          hp: prev.maxHp,
          mp: prev.maxMp
        }));

        if (inDungeon && currentDungeon) {
          // 던전에서 나가기 (죽음으로 인한 자동 퇴장)
          onAction('dungeon_exited', {
            dungeon_id: currentDungeon.id,
            dungeon_name: currentDungeon.name,
            current_floor: currentDungeon.currentFloor,
            total_floors: currentDungeon.floors,
            character_level: character.level,
            exit_reason: 'death',
            time_spent: new Date().getTime()
          });
          setInDungeon(false);
          setCurrentDungeon(null);
          setCurrentTab('lobby');
          setGameMode('lobby');
          addToCombatLog('던전에서 나왔습니다. 체력과 마나가 완전히 회복되었습니다!');
        } else {
          // 일반 필드에서는 새 몬스터 스폰
          addToCombatLog('체력과 마나가 완전히 회복되었습니다!');
          spawnMonster();
        }
      }, 2000);
    }
  };

  const calculateDamage = (attack: number, defense: number): number => {
    const baseDamage = Math.max(1, attack - Math.floor(defense * 0.5));
    return Math.floor(baseDamage * (0.8 + Math.random() * 0.4));
  };

  const defeatMonster = () => {
    if (!currentMonster) return;

    const expGained = currentMonster.exp;
    const goldGained = currentMonster.gold;

    setCharacter(prev => {
      let newExp = prev.exp + expGained;
      const newGold = prev.gold + goldGained;
      let newLevel = prev.level;
      let newMaxExp = prev.maxExp;
      let newHp = prev.hp;
      let newMp = prev.mp;
      let newMaxHp = prev.maxHp;
      let newMaxMp = prev.maxMp;
      let availablePoints = prev.stats.availablePoints;

      // 레벨업 체크
      while (newExp >= newMaxExp) {
        newLevel += 1;
        newExp -= newMaxExp;
        newMaxExp = Math.floor(newMaxExp * 1.5);
        newMaxHp += 20;
        newMaxMp += 10;
        newHp = newMaxHp; // 레벨업 시 체력 완전 회복
        newMp = newMaxMp; // 레벨업 시 마나 완전 회복
        availablePoints += 5;

        addToCombatLog(`🎉 레벨업! Lv.${newLevel}이 되었습니다!`);

        onAction('level_up', {
          user_id: currentUser.user_id,
          new_level: newLevel,
          character_class: prev.class,
          stat_points_gained: 5
        });

        // 던전 해금 체크
        setAvailableDungeons(prevDungeons => 
          prevDungeons.map(dungeon => ({
            ...dungeon,
            isUnlocked: dungeon.isUnlocked || newLevel >= dungeon.requiredLevel
          }))
        );
      }

      return {
        ...prev,
        exp: newExp,
        gold: newGold,
        level: newLevel,
        maxExp: newMaxExp,
        hp: newHp,
        mp: newMp,
        maxHp: newMaxHp,
        maxMp: newMaxMp,
        stats: { ...prev.stats, availablePoints }
      };
    });

    // 아이템 드롭 처리
    if (currentMonster.dropItems.length > 0) {
      setInventory(prev => [...prev, ...currentMonster.dropItems]);
      addToCombatLog(`✨ ${currentMonster.dropItems[0].name}을 획득했습니다!`);
    }

    addToCombatLog(`${currentMonster.name}를 물리쳤다! EXP +${expGained}, 골드 +${goldGained}`);

    // 추적 카운터 업데이트
    setMonstersDefeated(prev => prev + 1);
    setTotalGoldEarned(prev => prev + goldGained);

    onAction('monster_defeated', {
      user_id: currentUser.user_id,
      monster_name: currentMonster.name,
      monster_level: currentMonster.level,
      character_level: character.level,
      exp_gained: expGained,
      gold_gained: goldGained,
      items_dropped: currentMonster.dropItems.length,
      character_attack: character.attack,
      dungeon_name: currentDungeon?.name || null,
      dungeon_floor: currentDungeon?.currentFloor || null
    });

    // 현재 몬스터 제거
    setCurrentMonster(null);

    // 던전에 있는 경우 현재 층에서 새로운 몬스터 생성, 아니면 일반 몬스터 생성
    if (inDungeon && currentDungeon) {
      // 현재 층을 클리어한 것으로 마킹
      const updatedDungeon = {
        ...currentDungeon,
        clearedFloors: new Set([...Array.from(currentDungeon.clearedFloors), currentDungeon.currentFloor])
      };
      setCurrentDungeon(updatedDungeon);

      // 던전 리스트에서도 업데이트
      setAvailableDungeons(prev => prev.map(d => 
        d.id === updatedDungeon.id ? updatedDungeon : d
      ));

      // 던전에서는 현재 층에서 계속 사냥할 수 있도록 새 몬스터 생성
      addToCombatLog(`몬스터를 처치했습니다! 현재 ${currentDungeon.currentFloor}층이 클리어되었습니다. 계속 사냥할 수 있습니다.`);
      setTimeout(() => {
        spawnDungeonMonster(updatedDungeon, true);
      }, 2000);
    } else {
      // 일반 방치형 전투에서 새 몬스터 생성 (던전 상태가 아닐 때만)
      if (!inDungeon) {
        setTimeout(() => {
          spawnMonster();
        }, 2000);
      }
    }
  };

  // 다음 층으로 진행
  const proceedToNextFloor = () => {
    if (!currentDungeon) return;

    const updatedDungeon = { ...currentDungeon, currentFloor: currentDungeon.currentFloor + 1 };
    setCurrentDungeon(updatedDungeon);
    setShowDungeonChoice(false);
    setTimeout(() => {
      spawnDungeonMonster(updatedDungeon, true); // 강제 스폰
      addToCombatLog(`${updatedDungeon.currentFloor}층으로 진행합니다!`);
    }, 500);
  };

  // 던전 나가기
  const exitDungeonAfterVictory = () => {
    if (!currentDungeon) return;

    // 던전 나가기 이벤트 로깅
    onAction('dungeon_exited', {
      dungeon_id: currentDungeon.id,
      dungeon_name: currentDungeon.name,
      current_floor: currentDungeon.currentFloor,
      total_floors: currentDungeon.floors,
      character_level: character.level,
      exit_reason: 'victory_choice',
      time_spent: new Date().getTime()
    });

    // 던전 리셋 (현재 층을 1층으로 되돌림)
    const resetDungeon = { ...currentDungeon, currentFloor: 1 };
    setAvailableDungeons(prev => prev.map(d => 
      d.id === resetDungeon.id ? resetDungeon : d
    ));

    setInDungeon(false);
    setCurrentDungeon(null);
    setCurrentTab('lobby');
    setGameMode('lobby');
    setShowDungeonChoice(false);

    // 체력과 마나 완전 회복
    setCharacter(prev => ({ ...prev, hp: prev.maxHp, mp: prev.maxMp }));
    addToCombatLog('던전에서 나왔습니다. 체력과 마나가 완전히 회복되었습니다!');

    // 새로운 일반 몬스터 생성
    setTimeout(() => {
      spawnMonster();
    }, 1000);
  };


  // 자동 전투
  useEffect(() => {
    if (isAutoFighting && currentMonster && currentMonster.hp > 0 && character.hp > 0) {
      const autoAttackTimer = setTimeout(() => {
        attack();
      }, 2000);

      return () => clearTimeout(autoAttackTimer);
    }
  }, [isAutoFighting, currentMonster, character.hp]);

  // 스킬 쿨다운 및 MP 회복
  useEffect(() => {
    const interval = setInterval(() => {
      setCharacter(prev => ({
        ...prev,
        mp: Math.min(prev.maxMp, prev.mp + 1),
        skills: prev.skills.map(skill => ({
          ...skill,
          currentCooldown: Math.max(0, skill.currentCooldown - 100)
        })),
        buffs: {
          attack: {
            amount: prev.buffs.attack.duration > 0 ? prev.buffs.attack.amount : 0,
            duration: Math.max(0, prev.buffs.attack.duration - 100)
          },
          defense: {
            amount: prev.buffs.defense.duration > 0 ? prev.buffs.defense.amount : 0,
            duration: Math.max(0, prev.buffs.defense.duration - 100)
          },
          speed: {
            amount: prev.buffs.speed.duration > 0 ? prev.buffs.speed.amount : 0,
            duration: Math.max(0, prev.buffs.speed.duration - 100)
          }
        }
      }));
    }, 100);

    return () => clearInterval(interval);
  }, []);

  const addToCombatLog = (message: string) => {
    setCombatLog(prev => {
      const newLog = [...prev, message];
      if (newLog.length > 50) newLog.shift();
      return newLog;
    });

    // Auto-scroll to bottom immediately and with backup timing
    if (logRef.current) {
      logRef.current.scrollTop = logRef.current.scrollHeight;
    }

    // Additional scroll with slight delay to ensure content is rendered
    setTimeout(() => {
      if (logRef.current) {
        logRef.current.scrollTop = logRef.current.scrollHeight;
      }
    }, 10);

    // Final backup scroll
    setTimeout(() => {
      if (logRef.current) {
        logRef.current.scrollTop = logRef.current.scrollHeight;
      }
    }, 100);
  };

  // 스탯 분배
  const allocateStatPoint = (stat: keyof Omit<CharacterStats, 'availablePoints'>) => {
    if (character.stats.availablePoints <= 0) return;

    setCharacter(prev => ({
      ...prev,
      stats: {
        ...prev.stats,
        [stat]: prev.stats[stat] + 1,
        availablePoints: prev.stats.availablePoints - 1
      },
      // 스탯에 따른 능력치 업데이트
      attack: stat === 'strength' ? prev.attack + 2 : prev.attack,
      defense: stat === 'vitality' ? prev.defense + 1 : prev.defense,
      maxHp: stat === 'vitality' ? prev.maxHp + 10 : prev.maxHp,
      maxMp: stat === 'intelligence' ? prev.maxMp + 5 : prev.maxMp,
      speed: stat === 'agility' ? prev.speed + 1 : prev.speed
    }));
  };

  // 아이템 장착
  const equipItem = (item: Item) => {
    // 소비 아이템은 장착할 수 없음
    if (item.type === 'consumable') {
      return;
    }

    // 기존에 장착된 아이템이 있는지 확인
    const slot = item.type as keyof Equipment;
    const currentEquippedItem = character.equipment[slot];

    setCharacter(prev => {
      const newEquipment = { ...prev.equipment };
      newEquipment[slot] = item;

      // 기존 아이템의 스탯 제거
      let newAttack = prev.attack;
      let newDefense = prev.defense;
      let newMaxHp = prev.maxHp;
      let newMaxMp = prev.maxMp;
      let newSpeed = prev.speed;

      if (currentEquippedItem) {
        if (currentEquippedItem.stats.attack) newAttack -= currentEquippedItem.stats.attack;
        if (currentEquippedItem.stats.defense) newDefense -= currentEquippedItem.stats.defense;
        if (currentEquippedItem.stats.hp) newMaxHp -= currentEquippedItem.stats.hp;
        if (currentEquippedItem.stats.mp) newMaxMp -= currentEquippedItem.stats.mp;
        if (currentEquippedItem.stats.speed) newSpeed -= currentEquippedItem.stats.speed;
      }

      // 새 아이템의 스탯 적용
      if (item.stats.attack) newAttack += item.stats.attack;
      if (item.stats.defense) newDefense += item.stats.defense;
      if (item.stats.hp) newMaxHp += item.stats.hp;
      if (item.stats.mp) newMaxMp += item.stats.mp;
      if (item.stats.speed) newSpeed += item.stats.speed;

      // 스탯이 최소값 이하로 내려가지 않도록 보정
      newAttack = Math.max(1, newAttack);
      newDefense = Math.max(0, newDefense);
      newMaxHp = Math.max(100, newMaxHp);
      newMaxMp = Math.max(50, newMaxMp);
      newSpeed = Math.max(1, newSpeed);

      return {
        ...prev,
        equipment: newEquipment,
        attack: newAttack,
        defense: newDefense,
        maxHp: newMaxHp,
        maxMp: newMaxMp,
        speed: newSpeed,
        hp: Math.min(prev.hp, newMaxHp), // 최대 체력이 줄어들 경우 현재 체력도 조정
        mp: Math.min(prev.mp, newMaxMp)  // 최대 마나가 줄어들 경우 현재 마나도 조정
      };
    });

    // 기존에 장착된 아이템이 있었다면 인벤토리로 보내기
    if (currentEquippedItem) {
      setInventory(prev => [...prev, currentEquippedItem]);
      addToCombatLog(`기존 ${currentEquippedItem.name}을 해제하고 ${item.name}을 장착했습니다!`);
    } else {
      addToCombatLog(`${item.name}을 장착했습니다!`);
    }

    // 인벤토리에서 새 아이템 제거
    setInventory(prev => prev.filter(invItem => invItem.id !== item.id));
  };

  // 장비 해제
  const unequipItem = (slot: keyof Equipment) => {
    const equippedItem = character.equipment[slot];
    if (!equippedItem) return;

    setCharacter(prev => {
      const newEquipment = { ...prev.equipment };
      newEquipment[slot] = undefined;

      // 장착 해제한 아이템의 스탯 제거
      let newAttack = prev.attack;
      let newDefense = prev.defense;
      let newMaxHp = prev.maxHp;
      let newMaxMp = prev.maxMp;
      let newSpeed = prev.speed;

      if (equippedItem.stats.attack) newAttack -= equippedItem.stats.attack;
      if (equippedItem.stats.defense) newDefense -= equippedItem.stats.defense;
      if (equippedItem.stats.hp) newMaxHp -= equippedItem.stats.hp;
      if (equippedItem.stats.mp) newMaxMp -= equippedItem.stats.mp;
      if (equippedItem.stats.speed) newSpeed -= equippedItem.stats.speed;

      // HP가 maxHp를 초과하지 않도록 조정
      const adjustedHp = Math.min(prev.hp, newMaxHp);
      const adjustedMp = Math.min(prev.mp, newMaxMp);

      return {
        ...prev,
        equipment: newEquipment,
        attack: Math.max(1, newAttack), // 최소 공격력 1
        defense: Math.max(0, newDefense),
        maxHp: Math.max(100, newMaxHp), // 최소 체력 100
        maxMp: Math.max(50, newMaxMp), // 최소 마나 50
        speed: Math.max(1, newSpeed), // 최소 속도 1
        hp: adjustedHp,
        mp: adjustedMp
      };
    });

    // 인벤토리에 아이템 추가
    setInventory(prev => [...prev, equippedItem]);
    addToCombatLog(`${equippedItem.name}을 해제했습니다!`);
  };


  // 아이템 판매
  const sellItem = (item: Item) => {
    const sellPrice = Math.floor(item.price * 0.7);
    setCharacter(prev => ({ ...prev, gold: prev.gold + sellPrice }));
    setInventory(prev => prev.filter(invItem => invItem.id !== item.id));
    addToCombatLog(`${item.name}을 ${sellPrice} 골드에 판매했습니다!`);
  };

  // 아이템 구매
  const buyItem = (item: Item) => {
    if (character.gold < item.price) return;

    setCharacter(prev => ({ ...prev, gold: prev.gold - item.price }));
    setInventory(prev => [...prev, { ...item, id: `${item.id}_${Date.now()}` }]);
    addToCombatLog(`${item.name}을 구매했습니다!`);
  };

  const getRarityColor = (rarity: string) => {
    const colors = {
      common: '#9ca3af',
      rare: '#3b82f6',
      epic: '#8b5cf6',
      legendary: '#f59e0b',
      mythic: '#ef4444'
    };
    return colors[rarity as keyof typeof colors] || '#9ca3af';
  };

  // 던전 입장 함수
  const enterDungeon = (dungeon: Dungeon) => {
    if (dungeon.isComingSoon) {
      addToCombatLog('이 던전은 아직 업데이트 예정입니다!');
      return;
    }
    if (character.level < dungeon.requiredLevel) {
      addToCombatLog(`레벨 ${dungeon.requiredLevel} 이상이어야 입장 가능합니다.`);
      return;
    }
    setCurrentDungeon(dungeon);
    setInDungeon(true);
    setGameMode('dungeon');
    setCurrentTab('dungeon');
    spawnDungeonMonster(dungeon);

    // 이벤트 로깅
    onAction('dungeon_entered', {
      dungeon_id: dungeon.id,
      dungeon_name: dungeon.name,
      character_level: character.level,
      floor: dungeon.currentFloor,
      character_attack: character.attack,
      character_defense: character.defense,
      character_hp: character.hp,
      character_mp: character.mp,
      entry_time: new Date().getTime()
    });
  };

  // 던전 몬스터 스폰
  const spawnDungeonMonster = (dungeon: Dungeon, forceSpawn = false) => {
    // 이미 몬스터가 있으면 새로 생성하지 않음 (강제 생성이 아닌 경우)
    if (currentMonster && !forceSpawn) return;

    // 강제 생성인 경우 기존 몬스터를 먼저 제거
    if (forceSpawn && currentMonster) {
      setCurrentMonster(null);
    }
    // 던전별 고유 몬스터 타입 정의
    const getDungeonMonsters = (dungeonId: string) => {
      switch (dungeonId) {
        case 'forest': // 어둠의 숲
          return [
            { name: '늑대', baseHp: 40, baseAttack: 10, baseDefense: 3 },
            { name: '곰', baseHp: 80, baseAttack: 16, baseDefense: 6 },
            { name: '거대 거미', baseHp: 120, baseAttack: 22, baseDefense: 10 },
            { name: '숲의 수호자', baseHp: 200, baseAttack: 35, baseDefense: 18 },
            { name: '고대 엔트', baseHp: 350, baseAttack: 45, baseDefense: 25 }
          ];
        case 'cave': // 고블린 동굴
          return [
            { name: '고블린 전사', baseHp: 60, baseAttack: 14, baseDefense: 5 },
            { name: '고블린 궁수', baseHp: 45, baseAttack: 18, baseDefense: 3 },
            { name: '고블린 마법사', baseHp: 50, baseAttack: 20, baseDefense: 4 },
            { name: '고블린 우두머리', baseHp: 180, baseAttack: 30, baseDefense: 15 },
            { name: '고블린 왕', baseHp: 400, baseAttack: 50, baseDefense: 28 }
          ];
        case 'tower': // 마법사의 탑
          return [
            { name: '마법 골렘', baseHp: 100, baseAttack: 20, baseDefense: 12 },
            { name: '스켈레톤 메이지', baseHp: 70, baseAttack: 25, baseDefense: 8 },
            { name: '플레임 엘리멘탈', baseHp: 110, baseAttack: 28, baseDefense: 10 },
            { name: '리치', baseHp: 250, baseAttack: 40, baseDefense: 20 },
            { name: '고대 마법사', baseHp: 500, baseAttack: 60, baseDefense: 35 }
          ];
        default:
          return [
            { name: '슬라임', baseHp: 30, baseAttack: 8, baseDefense: 2 },
            { name: '고블린', baseHp: 50, baseAttack: 12, baseDefense: 4 },
            { name: '오크', baseHp: 100, baseAttack: 18, baseDefense: 8 },
            { name: '트롤', baseHp: 150, baseAttack: 25, baseDefense: 12 },
            { name: '드래곤', baseHp: 300, baseAttack: 40, baseDefense: 20 }
          ];
      }
    };

    const baseMonsters = getDungeonMonsters(dungeon.id);

    // 층수에 따른 점진적 몬스터 선택 로직
    const floorBasedMonsterIndex = () => {
      const currentFloor = dungeon.currentFloor;
      if (currentFloor <= 3) return 0; // 첫 번째 몬스터 (1~3층)
      if (currentFloor <= 7) return 1; // 두 번째 몬스터 (4~7층)  
      if (currentFloor <= 12) return 2; // 세 번째 몬스터 (8~12층)
      if (currentFloor <= 18) return 3; // 네 번째 몬스터 (13~18층)
      return 4; // 다섯 번째 몬스터 (19층 이상)
    };

    // 층수 기반 기본 몬스터 + 약간의 랜덤성 추가
    const baseIndex = floorBasedMonsterIndex();
    const randomVariation = Math.random() < 0.3 && baseIndex > 0 ? -1 : 0; // 30% 확률로 한 단계 낮은 몬스터
    const selectedIndex = Math.max(0, baseIndex + randomVariation);

    const selectedMonster = baseMonsters[selectedIndex];
    const levelMultiplier = 1 + (dungeon.level + dungeon.currentFloor - 1) * 0.2;

    const monster: Monster = {
      id: `dungeon_${dungeon.id}_${Date.now()}`,
      name: selectedMonster.name,
      level: dungeon.level + dungeon.currentFloor,
      hp: Math.floor(selectedMonster.baseHp * levelMultiplier),
      maxHp: Math.floor(selectedMonster.baseHp * levelMultiplier),
      attack: Math.floor(selectedMonster.baseAttack * levelMultiplier),
      defense: Math.floor(selectedMonster.baseDefense * levelMultiplier),
      speed: 10,
      exp: Math.floor((20 + dungeon.level * 8 + dungeon.currentFloor * 3) * levelMultiplier),
      gold: Math.floor((10 + dungeon.level * 3) * levelMultiplier),
      dropItems: generateDropItems(),
      isBoss: dungeon.currentFloor % 5 === 0
    };

    if (monster.isBoss) {
      monster.name = `보스 ${monster.name}`;
      monster.hp *= 2;
      monster.maxHp *= 2;
      monster.attack *= 1.5;
      monster.exp *= 3;
      monster.gold *= 2;
    }

    setCurrentMonster(monster);
    addToCombatLog(`${monster.name} Lv.${monster.level}이 나타났다!`);
  };

  // 물약 사용 함수
  const consumeItem = (item: Item) => {
    if (item.type !== 'consumable' || !item.consumableEffect) return;

    const effect = item.consumableEffect;

    // 체력 회복
    if (effect.healHp) {
      setCharacter(prev => ({
        ...prev,
        hp: Math.min(prev.maxHp, prev.hp + effect.healHp!)
      }));
      addToCombatLog(`${item.name}을 사용하여 HP ${effect.healHp} 회복!`);
    }

    // 마나 회복
    if (effect.healMp) {
      setCharacter(prev => ({
        ...prev,
        mp: Math.min(prev.maxMp, prev.mp + effect.healMp!)
      }));
      addToCombatLog(`${item.name}을 사용하여 MP ${effect.healMp} 회복!`);
    }

    // 버프 효과
    if (effect.buffType && effect.buffAmount && effect.buffDuration) {
      setCharacter(prev => ({
        ...prev,
        buffs: {
          ...prev.buffs,
          [effect.buffType!]: {
            amount: effect.buffAmount!,
            duration: effect.buffDuration!
          }
        }
      }));
      addToCombatLog(`${item.name}을 사용하여 ${effect.buffType === 'attack' ? '공격력' : effect.buffType === 'defense' ? '방어력' : '속도'} ${effect.buffAmount} 증가! (${effect.buffDuration/1000}초)`);
    }

    // 인벤토리에서 물약 제거 또는 수량 감소
    setInventory(prev => {
      const itemIndex = prev.findIndex(invItem => invItem.id === item.id);
      if (itemIndex !== -1) {
        const newInventory = [...prev];
        const currentItem = newInventory[itemIndex];
        if (currentItem.quantity && currentItem.quantity > 1) {
          newInventory[itemIndex] = { ...currentItem, quantity: currentItem.quantity - 1 };
        } else {
          newInventory.splice(itemIndex, 1);
        }
        return newInventory;
      }
      return prev;
    });

    // 이벤트 로깅
    onAction('consumable_used', {
      item_name: item.name,
      item_type: item.type,
      character_level: character.level,
      heal_hp: effect.healHp || 0,
      heal_mp: effect.healMp || 0,
      buff_type: effect.buffType || 'none',
      buff_amount: effect.buffAmount || 0,
      character_hp_before: character.hp,
      character_mp_before: character.mp,
      dungeon_name: currentDungeon?.name || null
    });
  };

  // 스킬 사용 함수
  const castSkill = (skill: Skill) => {
    if (!skill.isLearned || skill.level === 0) {
      addToCombatLog('스킬을 먼저 배워야 합니다!');
      return;
    }
    if (skill.currentCooldown > 0 || character.mp < skill.manaCost) return;

    // 스킬 사용 로직
    if (skill.id === 'heal') {
      const healAmount = 30 + skill.level * 5;
      setCharacter(prev => ({
        ...prev,
        hp: Math.min(prev.maxHp, prev.hp + healAmount),
        mp: prev.mp - skill.manaCost
      }));
      addToCombatLog(`${healAmount}만큼 회복했다!`);
    } else {
      // 공격 스킬
      if (currentMonster) {
        const damage = calculateSkillDamage(skill, character);
        const newMonsterHp = Math.max(0, currentMonster.hp - damage);
        setCurrentMonster(prev => prev ? {...prev, hp: newMonsterHp} : null);
        setCharacter(prev => ({ ...prev, mp: prev.mp - skill.manaCost }));
        addToCombatLog(`${skill.name}로 ${damage} 데미지!`);

        // 몬스터가 죽지 않았으면 반격
        if (newMonsterHp > 0) {
          setTimeout(() => {
            monsterAttack();
          }, 1000);
        } else {
          // 몬스터가 죽었으면 처치 처리
          setTimeout(() => {
            defeatMonster();
          }, 1500);
        }
      }
    }

    // 쿨다운 적용
    setCharacter(prev => ({
      ...prev,
      skills: prev.skills.map(s => 
        s.id === skill.id ? {...s, currentCooldown: s.cooldown} : s
      )
    }));

    // 이벤트 로깅
    onAction('skill_used', {
      skill_name: skill.name,
      skill_level: skill.level,
      mana_cost: skill.manaCost,
      character_level: character.level,
      character_mp_before: character.mp,
      character_mp_after: character.mp - skill.manaCost,
      dungeon_name: currentDungeon?.name || null
    });
  };

  // 스탯 포인트 할당
  const allocateStat = (statName: keyof CharacterStats) => {
    if (character.stats.availablePoints <= 0) return;

    setCharacter(prev => ({
      ...prev,
      stats: {
        ...prev.stats,
        [statName]: prev.stats[statName] + 1,
        availablePoints: prev.stats.availablePoints - 1
      }
    }));

    // 스탯에 따른 능력치 업데이트
    let hpBonus = 0, mpBonus = 0, attackBonus = 0, defenseBonus = 0;
    if (statName === 'vitality') hpBonus = 5;
    if (statName === 'intelligence') mpBonus = 3;
    if (statName === 'strength') attackBonus = 2;
    if (statName === 'agility') { attackBonus = 1; defenseBonus = 1; }

    setCharacter(prev => ({
      ...prev,
      maxHp: prev.maxHp + hpBonus,
      hp: prev.hp + hpBonus,
      maxMp: prev.maxMp + mpBonus,
      mp: prev.mp + mpBonus,
      attack: prev.attack + attackBonus,
      defense: prev.defense + defenseBonus
    }));

    // 이벤트 로깅
    onAction('stat_allocated', {
      stat_name: statName,
      stat_value: character.stats[statName] + 1,
      character_level: character.level,
      available_points: character.stats.availablePoints - 1
    });
  };

  // 공통 캐릭터 정보 헤더 컴포넌트
  const renderCharacterHeader = () => {
    const expPercentage = (character.exp / character.maxExp) * 100;

    return (
      <div style={{
        background: 'linear-gradient(135deg, #4f46e5 0%, #7c3aed 100%)',
        borderRadius: '15px',
        padding: '15px 20px',
        marginBottom: '20px',
        color: 'white'
      }}>
        {/* 기본 스탯 정보 */}
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(120px, 1fr))',
          gap: '15px',
          marginBottom: '15px'
        }}>
          <div style={{ textAlign: 'center' }}>
            <div style={{ fontSize: '20px', fontWeight: 'bold', color: '#ffffff' }}>
              Lv.{character.level}
            </div>
            <div style={{ color: '#e0e7ff', fontSize: '12px' }}>레벨</div>
          </div>
          <div style={{ textAlign: 'center' }}>
            <div style={{ fontSize: '20px', fontWeight: 'bold', color: '#fbbf24' }}>
              {character.gold}G
            </div>
            <div style={{ color: '#e0e7ff', fontSize: '12px' }}>골드</div>
          </div>
          <div style={{ textAlign: 'center' }}>
            <div style={{ fontSize: '16px', fontWeight: 'bold', color: '#f87171' }}>
              {character.attack}
            </div>
            <div style={{ color: '#e0e7ff', fontSize: '12px' }}>공격력</div>
          </div>
          <div style={{ textAlign: 'center' }}>
            <div style={{ fontSize: '16px', fontWeight: 'bold', color: '#60a5fa' }}>
              {character.defense}
            </div>
            <div style={{ color: '#e0e7ff', fontSize: '12px' }}>방어력</div>
          </div>
          <div style={{ textAlign: 'center' }}>
            <div style={{ fontSize: '16px', fontWeight: 'bold', color: '#34d399' }}>
              {character.hp}/{character.maxHp}
            </div>
            <div style={{ color: '#e0e7ff', fontSize: '12px' }}>체력</div>
          </div>
          <div style={{ textAlign: 'center' }}>
            <div style={{ fontSize: '16px', fontWeight: 'bold', color: '#a78bfa' }}>
              {character.mp}/{character.maxMp}
            </div>
            <div style={{ color: '#e0e7ff', fontSize: '12px' }}>마나</div>
          </div>
        </div>

        {/* 경험치 바 */}
        <div style={{ marginTop: '15px' }}>
          <div style={{ 
            display: 'flex', 
            justifyContent: 'space-between', 
            alignItems: 'center',
            marginBottom: '5px'
          }}>
            <span style={{ fontSize: '14px', color: '#e0e7ff' }}>경험치</span>
            <span style={{ fontSize: '14px', color: '#ffffff', fontWeight: 'bold' }}>
              {character.exp} / {character.maxExp} ({Math.floor(expPercentage)}%)
            </span>
          </div>
          <div style={{
            width: '100%',
            height: '8px',
            backgroundColor: 'rgba(255, 255, 255, 0.2)',
            borderRadius: '10px',
            overflow: 'hidden'
          }}>
            <div style={{
              width: `${expPercentage}%`,
              height: '100%',
              background: 'linear-gradient(90deg, #fbbf24 0%, #f59e0b 100%)',
              borderRadius: '10px',
              transition: 'width 0.3s ease'
            }} />
          </div>
        </div>
      </div>
    );
  };

  // 로비 렌더링
  const renderLobbyTab = () => (
    <div style={{ padding: '20px', background: '#1f2937', minHeight: '600px' }}>
      {/* 공통 캐릭터 헤더 */}
      {renderCharacterHeader()}

      <div style={{
        background: 'linear-gradient(135deg, #059669 0%, #10b981 100%)',
        borderRadius: '15px',
        padding: '20px',
        marginBottom: '20px',
        color: 'white',
        textAlign: 'center'
      }}>
        <h2 style={{ margin: '0 0 10px 0', fontSize: '24px' }}>🏠 모험가 로비</h2>
        <p style={{ margin: 0, opacity: 0.9 }}>던전을 선택하여 모험을 시작하세요!</p>
      </div>

      {/* 던전 목록 */}
      <div style={{ marginBottom: '20px' }}>
        <h3 style={{ color: 'white', marginBottom: '15px', fontSize: '20px' }}>🏰 던전 목록</h3>
        <div style={{ display: 'grid', gap: '15px' }}>
          {availableDungeons.map(dungeon => (
            <div
              key={dungeon.id}
              style={{
                background: dungeon.isUnlocked ? '#374151' : '#1f2937',
                borderRadius: '10px',
                padding: '20px',
                border: dungeon.isUnlocked ? '2px solid #10b981' : '2px solid #4b5563',
                opacity: dungeon.isUnlocked ? 1 : 0.6
              }}
            >
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <div>
                  <h4 style={{ color: 'white', margin: '0 0 5px 0', fontSize: '18px' }}>
                    {dungeon.name} (Lv.{dungeon.level})
                  </h4>
                  <p style={{ color: '#9ca3af', margin: '0 0 10px 0', fontSize: '14px' }}>
                    {dungeon.description}
                  </p>
                  <div style={{ fontSize: '12px', color: '#6b7280' }}>
                    필요 레벨: {dungeon.requiredLevel} | 층수: {dungeon.floors}층 | 현재 층: {dungeon.currentFloor}층
                  </div>
                </div>
                <button
                  onClick={() => enterDungeon(dungeon)}
                  disabled={!dungeon.isUnlocked || character.level < dungeon.requiredLevel || dungeon.isComingSoon}
                  style={{
                    background: dungeon.isComingSoon 
                      ? 'linear-gradient(135deg, #7c2d12 0%, #dc2626 100%)'
                      : dungeon.isUnlocked && character.level >= dungeon.requiredLevel
                      ? 'linear-gradient(135deg, #10b981 0%, #059669 100%)'
                      : '#4b5563',
                    color: 'white',
                    border: 'none',
                    borderRadius: '8px',
                    padding: '12px 20px',
                    fontSize: '14px',
                    fontWeight: 'bold',
                    cursor: (dungeon.isUnlocked && character.level >= dungeon.requiredLevel && !dungeon.isComingSoon) ? 'pointer' : 'not-allowed',
                    transition: 'all 0.3s ease'
                  }}
                >
                  {dungeon.isComingSoon ? '업데이트 예정' : 
                   dungeon.isUnlocked && character.level >= dungeon.requiredLevel ? '입장' : '잠금'}
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  // 던전 탭 렌더링
  const renderDungeonTab = () => {
    if (!inDungeon || !currentDungeon) {
      return (
        <div style={{ padding: '100px 20px', textAlign: 'center', background: '#1f2937', color: '#9ca3af' }}>
          <h3>던전에 입장하지 않았습니다</h3>
          <p>로비에서 던전을 선택해주세요</p>
        </div>
      );
    }

    return (
      <div style={{ padding: '20px', background: '#1f2937', minHeight: '600px' }}>
        {/* 공통 캐릭터 헤더 */}
        {renderCharacterHeader()}

        {/* 던전 정보 */}
        <div style={{
          background: 'linear-gradient(135deg, #7c2d12 0%, #dc2626 100%)',
          borderRadius: '15px',
          padding: '20px',
          marginBottom: '20px',
          color: 'white'
        }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '15px' }}>
            <div>
              <h2 style={{ margin: '0 0 5px 0', fontSize: '24px' }}>
                🏰 {currentDungeon.name}
              </h2>
              <p style={{ margin: 0, opacity: 0.9, fontSize: '14px' }}>
                {currentDungeon.currentFloor}층 / {currentDungeon.floors}층
              </p>
            </div>
            <button
              onClick={() => {
                // 던전 나가기 이벤트 로깅
                onAction('dungeon_exited', {
                  dungeon_id: currentDungeon.id,
                  dungeon_name: currentDungeon.name,
                  current_floor: currentDungeon.currentFloor,
                  total_floors: currentDungeon.floors,
                  character_level: character.level,
                  exit_reason: 'manual',
                  time_spent: new Date().getTime()
                });
                setInDungeon(false);
                setCurrentDungeon(null);
                setCurrentTab('lobby');
                setGameMode('lobby');
              }}
              style={{
                background: '#374151',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                padding: '10px 15px',
                cursor: 'pointer'
              }}
            >
              던전 나가기
            </button>
          </div>

          {/* 층 진행 컨트롤 */}
          <div style={{
            display: 'flex',
            gap: '10px',
            alignItems: 'center',
            justifyContent: 'center',
            padding: '15px',
            background: 'rgba(0, 0, 0, 0.2)',
            borderRadius: '10px'
          }}>
            <button
              onClick={() => {
                if (currentDungeon.currentFloor > 1) {
                  const updatedDungeon = { ...currentDungeon, currentFloor: currentDungeon.currentFloor - 1 };
                  setCurrentDungeon(updatedDungeon);
                  setCurrentMonster(null);
                  addToCombatLog(`${updatedDungeon.currentFloor}층으로 이동했습니다.`);
                  setTimeout(() => {
                    spawnDungeonMonster(updatedDungeon, true);
                  }, 1000);
                }
              }}
              disabled={currentDungeon.currentFloor <= 1}
              style={{
                background: currentDungeon.currentFloor <= 1 ? '#6b7280' : '#3b82f6',
                color: 'white',
                border: 'none',
                borderRadius: '6px',
                padding: '8px 12px',
                cursor: currentDungeon.currentFloor <= 1 ? 'not-allowed' : 'pointer',
                fontSize: '14px'
              }}
            >
              ⬆️ 이전 층
            </button>

            <input
              type="number"
              min="1"
              max={currentDungeon.floors}
              value={currentDungeon.currentFloor}
              placeholder="층"
              onChange={(e) => {
                const targetFloor = parseInt(e.target.value);
                if (targetFloor >= 1 && targetFloor <= currentDungeon.floors) {
                  // Check if all previous floors are cleared
                  let canMoveTo = true;
                  for (let i = 1; i < targetFloor; i++) {
                    if (!currentDungeon.clearedFloors.has(i)) {
                      canMoveTo = false;
                      addToCombatLog(`${i}층을 먼저 클리어해야 ${targetFloor}층으로 이동할 수 있습니다.`);
                      break;
                    }
                  }

                  if (canMoveTo) {
                    const updatedDungeon = { ...currentDungeon, currentFloor: targetFloor };
                    setCurrentDungeon(updatedDungeon);
                    setCurrentMonster(null);
                    addToCombatLog(`${targetFloor}층으로 이동했습니다.`);
                    setTimeout(() => {
                      spawnDungeonMonster(updatedDungeon, true);
                    }, 1000);
                  }
                }
              }}
              style={{
                background: 'rgba(255, 255, 255, 0.1)',
                color: 'white',
                border: '1px solid rgba(255, 255, 255, 0.3)',
                borderRadius: '6px',
                padding: '8px 12px',
                fontSize: '16px',
                fontWeight: 'bold',
                textAlign: 'center',
                width: '80px'
              }}
            />

            <button
              onClick={() => {
                const nextFloor = currentDungeon.currentFloor + 1;
                const currentFloorCleared = currentDungeon.clearedFloors.has(currentDungeon.currentFloor);

                if (currentDungeon.currentFloor < currentDungeon.floors && currentFloorCleared) {
                  const updatedDungeon = { ...currentDungeon, currentFloor: nextFloor };
                  setCurrentDungeon(updatedDungeon);
                  setCurrentMonster(null);
                  addToCombatLog(`${updatedDungeon.currentFloor}층으로 이동했습니다.`);
                  setTimeout(() => {
                    spawnDungeonMonster(updatedDungeon, true);
                  }, 1000);
                } else if (!currentFloorCleared) {
                  addToCombatLog(`현재 층에서 적어도 한 마리의 몬스터를 처치해야 다음 층으로 진행할 수 있습니다.`);
                }
              }}
