                       item?.rarity === 'epic' ? '#a855f7' :
                       item?.rarity === 'rare' ? '#3b82f6' :
                       item?.rarity === 'mythic' ? '#ef4444' : '#9ca3af';

    return (
      <div style={{
        width: '70px',
        height: '70px',
        background: item ? 'rgba(16, 185, 129, 0.2)' : 'rgba(71, 85, 105, 0.3)',
