                  onMouseEnter={(e) => {
                    if (item) {
                      e.currentTarget.style.transform = 'scale(1.05)';
                      e.currentTarget.style.boxShadow = `0 0 15px ${getRarityColor(item.rarity)}50`;
                    }
                  }}
                  onMouseLeave={(e) => {
                    if (item) {
                      e.currentTarget.style.transform = 'scale(1)';
                      e.currentTarget.style.boxShadow = 'none';
                    }
                  }}
                >
                  {item && (
                    <>
                      <div style={{ fontSize: '24px' }}>{getItemIcon(item)}</div>
                      {/* 수량 표시 (같은 아이템이 여러 개일 때) */}
                      {inventory.filter(inv => inv.name === item.name).length > 1 && (
                        <div style={{
                          position: 'absolute',
                          bottom: '2px',
                          right: '2px',
                          background: '#000',
                          color: 'white',
                          fontSize: '10px',
                          padding: '1px 3px',
                          borderRadius: '3px',
                          lineHeight: '1'
                        }}>
                          {inventory.filter(inv => inv.name === item.name).length}
                        </div>
                      )}
                    </>
                  )}
                </div>
              );
            })}
          </div>
        </div>

        {/* 아이템 상세 정보 */}
        <div style={{ width: '300px' }}>
          <h3 style={{ color: 'white', marginBottom: '15px' }}>📋 아이템 정보</h3>
          {selectedItem ? (
            <div style={{
              background: 'rgba(255, 255, 255, 0.1)',
              border: `2px solid ${getRarityColor(selectedItem.rarity)}`,
              borderRadius: '10px',
              padding: '20px'
            }}>
              <div style={{ textAlign: 'center', marginBottom: '15px' }}>
                <div style={{ fontSize: '48px', marginBottom: '10px' }}>
                  {getItemIcon(selectedItem)}
                </div>
                <h4 style={{
                  color: getRarityColor(selectedItem.rarity),
                  margin: '0 0 5px 0',
                  fontSize: '18px',
                  fontWeight: 'bold'
                }}>
                  {selectedItem.name}
                </h4>
                <p style={{
                  color: '#9ca3af',
                  margin: '0 0 15px 0',
                  fontSize: '14px',
                  textTransform: 'capitalize'
                }}>
                  {selectedItem.type} • {selectedItem.rarity}
                </p>
              </div>

              {/* 능력치 */}
              {selectedItem.stats && Object.keys(selectedItem.stats).length > 0 && (
                <div style={{ marginBottom: '15px' }}>
                  <h5 style={{ color: 'white', margin: '0 0 10px 0', fontSize: '14px' }}>능력치</h5>
                  {Object.entries(selectedItem.stats).map(([stat, value]) => (
                    <div key={stat} style={{
                      display: 'flex',
                      justifyContent: 'space-between',
                      color: '#10b981',
                      fontSize: '12px',
                      marginBottom: '5px'
                    }}>
                      <span>
                        {stat === 'attack' ? '공격력' :
                         stat === 'defense' ? '방어력' :
                         stat === 'hp' ? '체력' :
                         stat === 'mp' ? '마나' : stat}
                      </span>
                      <span>+{value}</span>
                    </div>
                  ))}
                </div>
              )}


              {/* 가격 */}
              <div style={{ marginBottom: '15px' }}>
                <h5 style={{ color: 'white', margin: '0 0 10px 0', fontSize: '14px' }}>가격</h5>
                <p style={{ color: '#f59e0b', fontSize: '12px', margin: 0 }}>
                  {selectedItem.price.toLocaleString()}G
                </p>
              </div>

              {/* 액션 버튼 */}
              <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                {selectedItem.type !== 'consumable' && (
                  <button
                    onClick={() => {
                      equipItem(selectedItem);
                      setSelectedItem(null);
                    }}
                    style={{
                      background: 'linear-gradient(135deg, #10b981 0%, #059669 100%)',
                      color: 'white',
                      border: 'none',
                      borderRadius: '6px',
                      padding: '10px',
                      fontSize: '14px',
                      cursor: 'pointer',
                      fontWeight: 'bold'
                    }}
                  >
                    장착
                  </button>
                )}
                {selectedItem.type === 'consumable' && (
                  <button
                    onClick={() => {
                      // 사용 로직 (임시)
                      console.log('Use item:', selectedItem.name);
                      setSelectedItem(null);
                    }}
                    style={{
                      background: 'linear-gradient(135deg, #3b82f6 0%, #1d4ed8 100%)',
                      color: 'white',
                      border: 'none',
                      borderRadius: '6px',
                      padding: '10px',
                      fontSize: '14px',
                      cursor: 'pointer',
                      fontWeight: 'bold'
                    }}
                  >
                    사용
                  </button>
                )}
                <button
                  onClick={() => {
                    const sellPrice = Math.floor(selectedItem.price * 0.7);
                    setCharacter(prev => ({ ...prev, gold: prev.gold + sellPrice }));
                    setInventory(prev => prev.filter(invItem => invItem.id !== selectedItem.id));
                    addToCombatLog(`${selectedItem.name}을 ${sellPrice} 골드에 판매했습니다!`);
                    setSelectedItem(null);
                  }}
                  style={{
                    background: '#dc2626',
                    color: 'white',
                    border: 'none',
                    borderRadius: '6px',
                    padding: '10px',
                    fontSize: '14px',
                    cursor: 'pointer'
                  }}
                >
                  판매 ({Math.floor(selectedItem.price * 0.7)}G)
                </button>
              </div>
            </div>
          ) : (
            <div style={{
              background: 'rgba(255, 255, 255, 0.05)',
              borderRadius: '10px',
              padding: '40px',
              textAlign: 'center',
              color: '#6b7280'
            }}>
              <p>아이템을 클릭하여 정보를 확인하세요</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );

  const renderShopTab = () => (
    <div style={{ padding: '20px', background: '#1f2937', minHeight: '600px' }}>
      {/* 공통 캐릭터 헤더 */}
      {renderCharacterHeader()}

      <div style={{
        background: '#374151',
        borderRadius: '15px',
        padding: '20px'
      }}>
        <h3 style={{ color: 'white', margin: '0 0 15px 0' }}>상점</h3>

        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
          gap: '15px'
        }}>
          {shop.map(item => (
            <div
              key={item.id}
              style={{
                background: '#1f2937',
                border: `2px solid ${getRarityColor(item.rarity)}`,
                borderRadius: '10px',
                padding: '15px'
              }}
            >
              <div style={{
                color: getRarityColor(item.rarity),
                fontWeight: 'bold',
                marginBottom: '5px'
              }}>
                {item.name}
              </div>

              <div style={{ color: '#9ca3af', fontSize: '14px', marginBottom: '10px' }}>
                {item.type} • Lv.{item.level} • {item.rarity}
              </div>

              <div style={{ color: '#d1d5db', fontSize: '12px', marginBottom: '15px' }}>
                {Object.entries(item.stats).map(([key, value]) => 
                  value > 0 && (
                    <div key={key}>
                      {key === 'attack' ? '⚔️' :
                       key === 'defense' ? '🛡️' :
                       key === 'hp' ? '❤️' :
                       key === 'mp' ? '💙' :
                       key === 'speed' ? '💨' :
                       key === 'critRate' ? '💥' : '⭐'} +{value}
                    </div>
                  )
                )}
              </div>

              <div style={{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center'
              }}>
                <div style={{ color: '#fbbf24', fontWeight: 'bold' }}>
                  💰 {item.price.toLocaleString()}G
                </div>
                <button
                  onClick={() => buyItem(item)}
                  disabled={character.gold < item.price}
                  style={{
                    background: character.gold >= item.price ? '#10b981' : '#6b7280',
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px',
                    padding: '8px 16px',
                    fontSize: '12px',
                    cursor: character.gold >= item.price ? 'pointer' : 'not-allowed',
                    opacity: character.gold >= item.price ? 1 : 0.6
                  }}
                >
                  구매
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  const renderTabs = () => (
    <div style={{
      background: '#374151',
      display: 'flex',
      borderRadius: '15px 15px 0 0',
      overflow: 'hidden'
    }}>
      {[
        { id: 'lobby', name: t('rpg.tabs.lobby'), icon: '🏠' },
        { id: 'character', name: t('rpg.tabs.character'), icon: '👤' },
        { id: 'inventory', name: t('rpg.tabs.inventory'), icon: '🎒' },
        { id: 'skills', name: t('rpg.tabs.skills'), icon: '✨' },
        { id: 'dungeon', name: t('rpg.tabs.dungeon'), icon: '🏰' },
        { id: 'shop', name: t('rpg.tabs.shop'), icon: '🛒' }
      ].map(tab => (
        <button
          key={tab.id}
          onClick={() => setCurrentTab(tab.id as any)}
          style={{
            background: currentTab === tab.id 
              ? colors.accent
              : 'transparent',
            color: colors.text,
            border: 'none',
            padding: '15px 20px',
            fontSize: '14px',
            cursor: 'pointer',
            flex: 1,
            transition: 'all 0.3s ease'
          }}
        >
          <div>{tab.icon}</div>
          <div style={{ fontSize: '12px', marginTop: '5px' }}>{tab.name}</div>
        </button>
      ))}
    </div>
  );

  return (
    <div style={{
      width: '100%',
      maxWidth: '1200px',
      margin: '0 auto',
      background: colors.cardBg,
      borderRadius: '20px',
      overflow: 'hidden',
      boxShadow: '0 20px 60px rgba(0, 0, 0, 0.3)',
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
      border: `1px solid ${colors.cardBorder}`
    }}>
      {/* 게임 타이틀 */}
      <div style={{
        background: colors.backgroundSecondary,
        padding: '20px',
        textAlign: 'center',
        position: 'relative',
        overflow: 'hidden'
      }}>
        <div style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'radial-gradient(circle at 50% 50%, rgba(147, 51, 234, 0.3) 0%, transparent 70%)'
        }} />
        <h1 style={{
          margin: 0,
          fontSize: '32px',
          fontWeight: 'bold',
          color: colors.text,
          textShadow: '0 4px 20px rgba(147, 51, 234, 0.5)',
          position: 'relative',
          zIndex: 1
        }}>
          {t('rpg.title')}
        </h1>
        <p style={{
          margin: '10px 0 0 0',
          color: colors.textSecondary,
          fontSize: '16px',
          position: 'relative',
          zIndex: 1
        }}>
          {t('rpg.subtitle')}
        </p>
      </div>

      {/* 탭 메뉴 */}
      {renderTabs()}

      {/* 현재 탭 컨텐츠 */}
      {currentTab === 'lobby' && renderLobbyTab()}
      {currentTab === 'dungeon' && renderDungeonTab()}
      {currentTab === 'character' && renderCharacterTab()}
      {currentTab === 'inventory' && renderInventoryTab()}
      {currentTab === 'skills' && renderSkillsTab()}
      {currentTab === 'shop' && renderShopTab()}

    </div>
  );
};
