import React, { useState, useEffect, useRef } from 'react';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';

interface Dungeon {
  id: string;
  name: string;
  level: number;
  description: string;
  floors: number;
  currentFloor: number;
  clearedFloors: Set<number>; // Track which floors have been cleared
  monsters: Monster[];
  rewards: {
    goldRange: [number, number];
    expRange: [number, number];
    itemDropChance: number;
  };
  requiredLevel: number;
  isUnlocked: boolean;
  isComingSoon?: boolean; // ì—…ë°ì´íŠ¸ ì˜ˆì • ë˜ì „ì¸ì§€
}

interface Monster {
  id: string;
  name: string;
  level: number;
  hp: number;
  maxHp: number;
  attack: number;
  defense: number;
  speed: number;
  exp: number;
  gold: number;
  dropItems: Item[];
  isBoss?: boolean;
}

interface Character {
  id: string;
  name: string;
  level: number;
  exp: number;
  maxExp: number;
  hp: number;
  maxHp: number;
  mp: number;
  maxMp: number;
  attack: number;
  defense: number;
  speed: number;
  gold: number;
  class: string;
  skills: Skill[];
  equipment: Equipment;
  stats: CharacterStats;
  prestige: number;
  rebirth: number;
  buffs: {
    attack: { amount: number; duration: number };
    defense: { amount: number; duration: number };
    speed: { amount: number; duration: number };
  };
}

interface CharacterStats {
  strength: number;
  agility: number;
  intelligence: number;
  vitality: number;
  luck: number;
  availablePoints: number;
}

interface Skill {
  id: string;
  name: string;
  level: number;
  maxLevel: number;
  baseDamage: number;
  damageMultiplier: number; // ë ˆë²¨ë‹¹ ì¦ê°€ìœ¨
  statMultiplier: { attack?: number; defense?: number; hp?: number; mp?: number }; // ìŠ¤íƒ¯ ê¸°ë°˜ ë°ë¯¸ì§€ ë°°ìœ¨
  manaCost: number;
  cooldown: number;
  currentCooldown: number;
  type: 'active' | 'passive';
  category: 'combat' | 'magic' | 'support' | 'passive'; // ìŠ¤í‚¬ ë¶„ë¥˜
  description: string;
  requiredLevel?: number;
  isUnlocked?: boolean;
  isLearned?: boolean; // ë°°ìš´ ìŠ¤í‚¬ì¸ì§€ (0ë ˆë²¨ì—ì„œ 1ë ˆë²¨ë¡œ ì—…ê·¸ë ˆì´ë“œí–ˆëŠ”ì§€)
}

interface Equipment {
  weapon?: Item;
  armor?: Item;
  helmet?: Item;
  boots?: Item;
  ring?: Item;
  amulet?: Item;
}

interface Item {
  id: string;
  name: string;
  type: 'weapon' | 'armor' | 'helmet' | 'boots' | 'ring' | 'amulet' | 'consumable';
  rarity: 'common' | 'rare' | 'epic' | 'legendary' | 'mythic';
  level: number;
  stats: {
    attack?: number;
    defense?: number;
    hp?: number;
    mp?: number;
    speed?: number;
    critRate?: number;
    critDamage?: number;
  };
  price: number;
  enhancement: number;
  // ë¬¼ì•½ìš© ì†ì„±ë“¤
  consumableEffect?: {
    healHp?: number;
    healMp?: number;
    buffDuration?: number; // ë²„í”„ ì§€ì†ì‹œê°„ (ë°€ë¦¬ì´ˆ)
    buffType?: 'attack' | 'defense' | 'speed'; // ë²„í”„ íƒ€ì…
    buffAmount?: number; // ë²„í”„ ìˆ˜ì¹˜
  };
  quantity?: number; // ì†Œë¹„ ì•„ì´í…œ ìˆ˜ëŸ‰
}



interface Guild {
  id: string;
  name: string;
  level: number;
  members: number;
  maxMembers: number;
  benefits: {
    expBonus: number;
    goldBonus: number;
    dropBonus: number;
  };
}

interface AppProps {
  onAction: (eventType: string, data: any) => void;
  isSimulating: boolean;
  currentAction: string | null;
  userStats: any;
  setUserStats: (stats: any) => void;
  currentUser: any;
}

export const AdvancedIdleRPG: React.FC<AppProps> = ({
  onAction,
  isSimulating,
  currentAction,
  userStats,
  setUserStats,
  currentUser
}) => {
  const { colors } = useTheme();
  const { t } = useLanguage();

  // ìŠ¤í‚¬ ë°ë¯¸ì§€ ê³„ì‚° í•¨ìˆ˜
  const calculateSkillDamage = (skill: Skill, character: Character): number => {
    if (skill.level === 0 || !skill.isLearned) return 0;

    let damage = skill.baseDamage;

    // ë ˆë²¨ì— ë”°ë¥¸ ë°ë¯¸ì§€ ì¦ê°€
    damage += skill.baseDamage * skill.damageMultiplier * (skill.level - 1);

    // ìŠ¤íƒ¯ì— ë”°ë¥¸ ë°ë¯¸ì§€ ì¦ê°€
    if (skill.statMultiplier.attack) {
      damage += character.attack * skill.statMultiplier.attack * skill.level;
    }
    if (skill.statMultiplier.defense) {
      damage += character.defense * skill.statMultiplier.defense * skill.level;
    }
    if (skill.statMultiplier.hp) {
      damage += character.maxHp * skill.statMultiplier.hp * skill.level;
    }
    if (skill.statMultiplier.mp) {
      damage += character.maxMp * skill.statMultiplier.mp * skill.level;
    }

    return Math.floor(damage);
  };

  // CSS ì• ë‹ˆë©”ì´ì…˜ ì¶”ê°€
  React.useEffect(() => {
    const style = document.createElement('style');
    style.textContent = `
      @keyframes progress {
        0% {
          width: 0%;
        }
        100% {
          width: 100%;
        }
      }
      @keyframes pulse {
        0%, 100% {
          opacity: 1;
        }
        50% {
          opacity: 0.7;
        }
      }
    `;
    document.head.appendChild(style);

    return () => {
      document.head.removeChild(style);
    };
  }, []);
  const [character, setCharacter] = useState<Character>({
    id: '1',
    name: 'ìš©ì‚¬',
    level: 1,
    exp: 0,
    maxExp: 100,
    hp: 100,
    maxHp: 100,
    mp: 50,
    maxMp: 50,
    attack: 20,
    defense: 10,
    speed: 15,
    gold: 100,
    class: 'ì „ì‚¬',
    skills: [],
    equipment: {},
    stats: {
      strength: 10,
      agility: 8,
      intelligence: 5,
      vitality: 12,
      luck: 7,
      availablePoints: 0
    },
    prestige: 0,
    rebirth: 0,
    buffs: {
      attack: { amount: 0, duration: 0 },
      defense: { amount: 0, duration: 0 },
      speed: { amount: 0, duration: 0 }
    }
  });

  const [currentTab, setCurrentTab] = useState<'lobby' | 'dungeon' | 'character' | 'inventory' | 'skills' | 'shop'>('lobby');
  const [gameMode, setGameMode] = useState<'lobby' | 'dungeon' | 'combat'>('lobby');
  const [isAutoFighting, setIsAutoFighting] = useState(false);
  const [currentMonster, setCurrentMonster] = useState<Monster | null>(null);
  const [combatLog, setCombatLog] = useState<string[]>([]);
  const [selectedDungeon, setSelectedDungeon] = useState<Dungeon | null>(null);
  const [inventory, setInventory] = useState<Item[]>([]);
  const [availableDungeons, setAvailableDungeons] = useState<Dungeon[]>([]);
  const [currentDungeon, setCurrentDungeon] = useState<Dungeon | null>(null);
  const [inDungeon, setInDungeon] = useState(false);
  const [guild, setGuild] = useState<Guild | null>(null);
  const [shop, setShop] = useState<Item[]>([]);
  const [achievements, setAchievements] = useState<string[]>([]);
  const [monstersDefeated, setMonstersDefeated] = useState(0);
  const [totalGoldEarned, setTotalGoldEarned] = useState(0);
  const [showDungeonChoice, setShowDungeonChoice] = useState(false);

  const logRef = useRef<HTMLDivElement>(null);

  // ê²Œì„ ì´ˆê¸°í™”
  useEffect(() => {
    initializeGame();
  }, []);

  // ìºë¦­í„° ìƒíƒœê°€ ë³€ê²½ë  ë•Œë§ˆë‹¤ userStats ì—…ë°ì´íŠ¸
  useEffect(() => {
    setUserStats({
      level: character.level,
      gold: character.gold,
      attack: character.attack,
      defense: character.defense,
      hp: character.hp,
      maxHp: character.maxHp,
      exp: character.exp,
      maxExp: character.maxExp,
      monsters_defeated: monstersDefeated,
      character_class: character.class,
      total_gold_earned: totalGoldEarned,
      inventory_items: inventory.map(item => ({
        id: item.id,
        name: item.name,
        type: item.type,
        rarity: item.rarity,
        quantity: item.quantity || 1,
        stats: item.stats
      })),
      skills: character.skills.map(skill => ({
        id: skill.id,
        name: skill.name,
        level: skill.level,
        type: skill.type
      })),
      current_buffs: Object.entries(character.buffs)
        .filter(([_, buff]) => buff.duration > 0)
        .map(([type, buff]) => ({
          type,
          amount: buff.amount,
          duration: Math.ceil(buff.duration / 1000)
        }))
    });
  }, [character, inventory, monstersDefeated, totalGoldEarned, setUserStats]);

  const initializeGame = () => {
    // ì–¸ì–´ë³„ í…ìŠ¤íŠ¸ ì •ì˜
    const translations = {
      ko: {
        character: {
          name: 'ìš©ì‚¬',
          class: 'ì „ì‚¬'
        },
        skills: {
          slash: { name: 'ê°•ë ¥í•œ ë² ê¸°', description: 'ê°•ë ¥í•œ ì¼ê²©ì„ ê°€í•©ë‹ˆë‹¤.' },
          heal: { name: 'ì¹˜ìœ ', description: 'HPë¥¼ íšŒë³µí•©ë‹ˆë‹¤.' },
          critical: { name: 'ì¹˜ëª…íƒ€ ìˆ™ë ¨', description: 'ì¹˜ëª…íƒ€ í™•ë¥ ì„ ì¦ê°€ì‹œí‚µë‹ˆë‹¤.' }
        },
        dungeons: {
          forest: { name: 'ì–´ë‘ ì˜ ìˆ²', description: 'ì´ˆë³´ìë¥¼ ìœ„í•œ ë˜ì „ì…ë‹ˆë‹¤.' },
          cave: { name: 'ê³ ë¸”ë¦° ë™êµ´', description: 'ê³ ë¸”ë¦°ë“¤ì´ ì„œì‹í•˜ëŠ” ìœ„í—˜í•œ ë™êµ´ì…ë‹ˆë‹¤.' },
          tower: { name: 'ë§ˆë²•ì‚¬ì˜ íƒ‘', description: 'ê°•ë ¥í•œ ë§ˆë²• ëª¬ìŠ¤í„°ë“¤ì´ ìˆëŠ” íƒ‘ì…ë‹ˆë‹¤.' }
        },
        items: {
          sword1: 'ê°•ì²  ê²€',
          armor1: 'ê°€ì£½ ê°‘ì˜·',
          helmet1: 'ì² ì œ íˆ¬êµ¬',
          health_potion: 'ì²´ë ¥ ë¬¼ì•½',
          mana_potion: 'ë§ˆë‚˜ ë¬¼ì•½',
          greater_health_potion: 'ê³ ê¸‰ ì²´ë ¥ ë¬¼ì•½',
          strength_potion: 'í˜ì˜ ë¬¼ì•½',
          defense_potion: 'ë°©ì–´ì˜ ë¬¼ì•½'
        },
        monsters: {
          slime: 'ìŠ¬ë¼ì„',
          goblin: 'ê³ ë¸”ë¦°',
          orc: 'ì˜¤í¬',
          troll: 'íŠ¸ë¡¤',
          dragon: 'ë“œë˜ê³¤'
        },
