import React, { useState, useEffect } from 'react';
import { BarChart, TrendingUp, Users, Eye, MousePointer, Clock, PartyPopper, X, Trophy, Star } from 'lucide-react';
import { Card } from '../../components/Card';
import { Badge } from '../../components/Badge';
import { Button } from '../../components/Button';
import { Checkbox } from '../../components/Checkbox';
import { InfoBox } from '../../components/InfoBox';

interface Step4Props {
  onComplete: () => void;
  appData: {
    appId: string;
    dataUrl: string;
    selectedVersion: 'saas' | 'private' | null;
  };
}

export const Step4AnalyticsStart: React.FC<Step4Props> = ({
  onComplete,
  appData
}) => {
  const [understood, setUnderstood] = useState(false);
  const [celebrationShown, setCelebrationShown] = useState(false);
  const [hasCompleted, setHasCompleted] = useState(false);

  // Í≤åÏù¥Î∞ç ÌÖåÎßà
  const theme = {
    background: 'radial-gradient(ellipse at top, #1e1b4b 0%, #0f0f23 50%, #000000 100%)',
    cardBg: 'linear-gradient(145deg, rgba(30, 30, 60, 0.9), rgba(15, 15, 35, 0.9))',
    cardBorder: 'rgba(139, 92, 246, 0.3)',
    text: '#ffffff',
    textSecondary: '#a1a1aa',
    accent: '#8b5cf6',
    accentSecondary: '#3b82f6',
    success: '#10b981'
  };

  useEffect(() => {
    if (understood && !hasCompleted) {
      onComplete();
      setCelebrationShown(true);
      setHasCompleted(true);
    }
  }, [understood, hasCompleted, onComplete]);

  const dashboardSteps = [
    {
      step: 1,
      title: 'ÎåÄÏãúÎ≥¥Îìú ÌÖúÌîåÎ¶ø ÏÑ†ÌÉù',
      description: 'ÏÇ¨Ï†Ñ Íµ¨ÏÑ±Îêú ÌÖúÌîåÎ¶ø Ï§ë ÌïòÎÇòÎ•º ÏÑ†ÌÉùÌïòÍ±∞ÎÇò Îπà ÎåÄÏãúÎ≥¥ÎìúÎ°ú ÏãúÏûëÌïòÏÑ∏Ïöî.',
      icon: <BarChart size={20} color="#334155" />
    },
    {
      step: 2,
      title: 'Í∏∞Î≥∏ Ï∞®Ìä∏ Ï∂îÍ∞Ä',
      description: 'ÏÇ¨Ïö©Ïûê Ïàò, ÌéòÏù¥ÏßÄ Î∑∞, Ïù¥Î≤§Ìä∏ Ï∂îÏ†Å Îì± Í∏∞Î≥∏ Ï∞®Ìä∏Î•º Ï∂îÍ∞ÄÌïòÏÑ∏Ïöî.',
      icon: <TrendingUp size={20} color="var(--success)" />
    },
    {
      step: 3,
      title: 'ÌïÑÌÑ∞ Î∞è Í∏∞Í∞Ñ ÏÑ§Ï†ï',
      description: 'Î∂ÑÏÑùÌïòÍ≥† Ïã∂ÏùÄ Í∏∞Í∞ÑÍ≥º Ï°∞Í±¥ÏùÑ ÏÑ§Ï†ïÌïòÏÑ∏Ïöî.',
      icon: <Clock size={20} color="var(--warning)" />
    },
    {
      step: 4,
      title: 'ÎåÄÏãúÎ≥¥Îìú Í≥µÏú†',
      description: 'ÏôÑÏÑ±Îêú ÎåÄÏãúÎ≥¥ÎìúÎ•º ÌåÄÏõêÎì§Í≥º Í≥µÏú†ÌïòÏÑ∏Ïöî.',
      icon: <Users size={20} color="var(--info)" />
    }
  ];

  const recommendedMetrics = [
    {
      category: 'ÏÇ¨Ïö©Ïûê Î∂ÑÏÑù',
      icon: <Users size={24} color="#334155" />,
      metrics: [
        { name: 'DAU (ÏùºÏùº ÌôúÏÑ± ÏÇ¨Ïö©Ïûê)', description: 'ÌïòÎ£® ÎèôÏïà Ïï±ÏùÑ ÏÇ¨Ïö©Ìïú Í≥†Ïú† ÏÇ¨Ïö©Ïûê Ïàò' },
        { name: 'MAU (ÏõîÍ∞Ñ ÌôúÏÑ± ÏÇ¨Ïö©Ïûê)', description: 'Ìïú Îã¨ ÎèôÏïà Ïï±ÏùÑ ÏÇ¨Ïö©Ìïú Í≥†Ïú† ÏÇ¨Ïö©Ïûê Ïàò' },
        { name: 'Ïã†Í∑ú ÏÇ¨Ïö©Ïûê', description: 'Ï≤òÏùåÏúºÎ°ú Ïï±ÏùÑ ÏÇ¨Ïö©ÌïòÎäî ÏÇ¨Ïö©Ïûê Ïàò' },
        { name: 'Ïû¨Î∞©Î¨∏ ÏÇ¨Ïö©Ïûê', description: 'Ïù¥Ï†ÑÏóê Ïï±ÏùÑ ÏÇ¨Ïö©Ìïú Ï†ÅÏù¥ ÏûàÎäî ÏÇ¨Ïö©Ïûê Ïàò' }
      ]
    },
    {
      category: 'Ï∞∏Ïó¨ÎèÑ Î∂ÑÏÑù',
      icon: <MousePointer size={24} color="var(--success)" />,
      metrics: [
        { name: 'ÌéòÏù¥ÏßÄ Î∑∞', description: 'Í∞Å ÌéòÏù¥ÏßÄÍ∞Ä Ï°∞ÌöåÎêú Ï¥ù ÌöüÏàò' },
        { name: 'ÏÑ∏ÏÖò Ïàò', description: 'ÏÇ¨Ïö©ÏûêÍ∞Ä Ïï±ÏùÑ ÏÇ¨Ïö©Ìïú ÏÑ∏ÏÖòÏùò Ï¥ù Í∞úÏàò' },
        { name: 'ÌèâÍ∑† ÏÑ∏ÏÖò ÏãúÍ∞Ñ', description: 'ÏÇ¨Ïö©ÏûêÍ∞Ä Ìïú Î≤àÏùò ÏÑ∏ÏÖòÏóêÏÑú Î®∏Î¨¥Îäî ÌèâÍ∑† ÏãúÍ∞Ñ' },
        { name: 'Î∞îÏö¥Ïä§Ïú®', description: 'Ìïú ÌéòÏù¥ÏßÄÎßå Î≥¥Í≥† Îñ†ÎÇòÎäî ÏÇ¨Ïö©ÏûêÏùò ÎπÑÏú®' }
      ]
    },
    {
      category: 'ÌñâÎèô Î∂ÑÏÑù',
      icon: <Eye size={24} color="var(--warning)" />,
      metrics: [
        { name: 'ÌÅ¥Î¶≠Î•†', description: 'ÌäπÏ†ï ÏöîÏÜåÍ∞Ä ÌÅ¥Î¶≠ÎêòÎäî ÎπÑÏú®' },
        { name: 'Ï†ÑÌôòÏú®', description: 'Î™©Ìëú ÌñâÎèôÏùÑ ÏôÑÎ£åÌïòÎäî ÏÇ¨Ïö©ÏûêÏùò ÎπÑÏú®' },
        { name: 'ÌçºÎÑê Î∂ÑÏÑù', description: 'ÏÇ¨Ïö©ÏûêÍ∞Ä ÌäπÏ†ï Í≥ºÏ†ïÏùÑ ÏôÑÎ£åÌïòÎäî Îã®Í≥ÑÎ≥Ñ Î∂ÑÏÑù' },
        { name: 'ÌûàÌä∏Îßµ', description: 'ÏÇ¨Ïö©ÏûêÍ∞Ä Í∞ÄÏû• ÎßéÏù¥ ÏÉÅÌò∏ÏûëÏö©ÌïòÎäî ÏòÅÏó≠ ÏãúÍ∞ÅÌôî' }
      ]
    }
  ];

  return (
    <div style={{ color: theme.text }}>
      {/* Í≤åÏù¥Î∞ç Ïä§ÌÉÄÏùº Step Title */}
      <div style={{ marginBottom: '32px' }}>
        <div style={{
          display: 'flex',
          alignItems: 'center',
          gap: '16px',
          marginBottom: '20px',
          padding: '20px',
          background: 'rgba(139, 92, 246, 0.1)',
          borderRadius: '16px',
          border: '1px solid rgba(139, 92, 246, 0.3)',
          position: 'relative',
          overflow: 'hidden'
        }}>
          {/* Î∞∞Í≤Ω Ïû•Ïãù */}
          <div style={{
            position: 'absolute',
            top: '-50%',
            right: '-20%',
            width: '100px',
            height: '100px',
            background: 'linear-gradient(135deg, rgba(139, 92, 246, 0.1), rgba(59, 130, 246, 0.1))',
            borderRadius: '50%',
            filter: 'blur(30px)'
          }} />

          <div style={{
            width: '60px',
            height: '60px',
            background: 'linear-gradient(135deg, #8b5cf6, #3b82f6)',
            borderRadius: '18px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            boxShadow: '0 8px 20px rgba(139, 92, 246, 0.4)',
            position: 'relative',
            zIndex: 1
          }}>
            <BarChart size={28} color="white" />
          </div>
          <div style={{ position: 'relative', zIndex: 1 }}>
            <h3 style={{
              fontSize: '28px',
              fontWeight: '800',
              background: 'linear-gradient(135deg, #8b5cf6, #3b82f6)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              margin: 0,
              marginBottom: '8px'
            }}>
              ÎØ∏ÏÖò 4: Î∂ÑÏÑù ÏãúÏûë üöÄ
            </h3>
            <div style={{
              display: 'flex',
              alignItems: 'center',
              gap: '12px'
            }}>
              <span style={{
                fontSize: '14px',
                color: theme.textSecondary,
                padding: '4px 12px',
                background: 'rgba(139, 92, 246, 0.2)',
                borderRadius: '12px',
                fontWeight: '600'
              }}>
                Î†àÎ≤®: Í≥†Í∏âÏûê
              </span>
              <span style={{
                fontSize: '14px',
                color: '#f59e0b',
                display: 'flex',
                alignItems: 'center',
                gap: '4px'
              }}>
                <Star size={16} />
                Î≥¥ÏÉÅ: +150 XP
              </span>
            </div>
          </div>
        </div>
        <p style={{
          fontSize: '18px',
          color: theme.textSecondary,
          lineHeight: '1.6',
          margin: 0,
          padding: '0 20px'
        }}>
          üéØ <strong>Î™©Ìëú:</strong> Îç∞Ïù¥ÌÑ∞ Î∂ÑÏÑùÏùò ÎßàÏä§ÌÑ∞Í∞Ä ÎêòÏñ¥ Í∞ïÎ†•Ìïú Ïù∏ÏÇ¨Ïù¥Ìä∏Î•º Î∞úÍ≤¨ÌïòÏÑ∏Ïöî!
        </p>
      </div>

      {/* Í≤åÏù¥Î∞ç Ïä§ÌÉÄÏùº Configuration Summary */}
      <div style={{ marginBottom: '32px' }}>
        <div style={{
          padding: '24px',
          background: 'linear-gradient(145deg, rgba(30, 30, 60, 0.9), rgba(15, 15, 35, 0.9))',
          border: '1px solid rgba(139, 92, 246, 0.3)',
          borderRadius: '20px',
          boxShadow: '0 8px 24px rgba(0, 0, 0, 0.2)',
          backdropFilter: 'blur(20px)',
          position: 'relative',
          overflow: 'hidden'
        }}>
          {/* Î∞∞Í≤Ω Ïû•Ïãù */}
          <div style={{
            position: 'absolute',
            top: '-30%',
            left: '-20%',
            width: '150px',
            height: '150px',
            background: 'linear-gradient(135deg, rgba(16, 185, 129, 0.1), rgba(5, 150, 105, 0.1))',
            borderRadius: '50%',
            filter: 'blur(40px)'
          }} />

          <div style={{
            display: 'flex',
            alignItems: 'center',
            gap: '16px',
            marginBottom: '20px',
            position: 'relative',
            zIndex: 1
          }}>
            <div style={{
              width: '48px',
              height: '48px',
              background: 'linear-gradient(135deg, #10b981, #059669)',
              borderRadius: '16px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              boxShadow: '0 8px 20px rgba(16, 185, 129, 0.4)'
            }}>
              <Trophy size={24} color="white" />
            </div>
            <h4 style={{ 
              fontSize: '24px', 
              fontWeight: '700', 
              margin: 0,
              background: 'linear-gradient(135deg, #10b981, #059669)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent'
            }}>
              üèÜ ÏÑ§Ï†ï ÏôÑÎ£åÎêú Íµ¨ÏÑ± - ÌÄòÏä§Ìä∏ Îã¨ÏÑ±!
            </h4>
          </div>

          <div style={{ 
            display: 'grid', 
            gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', 
            gap: '16px',
            marginBottom: '20px',
            position: 'relative',
            zIndex: 1
          }}>
            <div style={{ 
              padding: '16px', 
              background: 'rgba(139, 92, 246, 0.1)',
              boxShadow: '0 4px 15px rgba(139, 92, 246, 0.2)', 
              borderRadius: '16px',
              border: '1px solid rgba(139, 92, 246, 0.3)',
              backdropFilter: 'blur(10px)'
            }}>
              <div style={{ fontSize: '12px', color: theme.textSecondary, marginBottom: '8px', fontWeight: '600' }}>üéÆ Î≤ÑÏ†Ñ</div>
              <div style={{ fontSize: '16px', fontWeight: '700', color: theme.text }}>
                {appData.selectedVersion === 'saas' ? '‚òÅÔ∏è SaaS Î≤ÑÏ†Ñ' : 
                 appData.selectedVersion === 'private' ? 'üè∞ ÌîÑÎùºÏù¥Îπó Î≤ÑÏ†Ñ' : '‚ùå ÎØ∏ÏÑ†ÌÉù'}
              </div>
            </div>
            <div style={{ 
              padding: '16px', 
              background: 'rgba(59, 130, 246, 0.1)',
              boxShadow: '0 4px 15px rgba(59, 130, 246, 0.2)', 
              borderRadius: '16px',
              border: '1px solid rgba(59, 130, 246, 0.3)',
              backdropFilter: 'blur(10px)'
            }}>
              <div style={{ fontSize: '12px', color: theme.textSecondary, marginBottom: '8px', fontWeight: '600' }}>üÜî App ID</div>
              <div style={{ fontSize: '16px', fontWeight: '700', color: theme.text }}>
                {appData.appId || '‚ö†Ô∏è ÎØ∏ÏûÖÎ†•'}
              </div>
            </div>
            <div style={{ 
              padding: '16px', 
              background: 'rgba(16, 185, 129, 0.1)',
              boxShadow: '0 4px 15px rgba(16, 185, 129, 0.2)', 
              borderRadius: '16px',
              border: '1px solid rgba(16, 185, 129, 0.3)',
              backdropFilter: 'blur(10px)'
            }}>
              <div style={{ fontSize: '12px', color: theme.textSecondary, marginBottom: '8px', fontWeight: '600' }}>üåê Data URL</div>
              <div style={{ 
                fontSize: '14px', 
                fontWeight: '600', 
                color: theme.text,
                wordBreak: 'break-all'
              }}>
                {appData.dataUrl || '‚ö†Ô∏è ÎØ∏ÏûÖÎ†•'}
              </div>
            </div>
          </div>

          <div style={{
            padding: '20px',
            background: 'rgba(16, 185, 129, 0.1)',
            borderRadius: '16px',
            border: '1px solid rgba(16, 185, 129, 0.3)',
            position: 'relative',
            zIndex: 1
          }}>
            <div style={{
              display: 'flex',
              alignItems: 'center',
              gap: '12px',
              marginBottom: '12px'
            }}>
              <div style={{
                width: '32px',
                height: '32px',
                background: 'linear-gradient(135deg, #10b981, #059669)',
                borderRadius: '50%',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}>
                <Trophy size={16} color="white" />
              </div>
              <h5 style={{
                fontSize: '18px',
                fontWeight: '700',
                margin: 0,
                color: '#10b981'
              }}>
                ‚úÖ ÏÑ§Ï†ï ÏôÑÎ£å! ÎØ∏ÏÖò Îã¨ÏÑ±!
              </h5>
            </div>
            <p style={{ 
              margin: 0, 
              color: theme.text,
              lineHeight: '1.5',
