import React, { useState, useEffect } from 'react';
import { CodeBlock } from './CodeBlock';

interface CodeTabItem {
  id: string;
  label: string;
  language: string;
  code: string;
}

interface CodeTabsProps {
  tabs: CodeTabItem[];
  title?: string;
}

export const CodeTabs: React.FC<CodeTabsProps> = ({ tabs, title }) => {
  const [activeTab, setActiveTab] = useState(() => tabs[0]?.id || '');
  const [hoveredTab, setHoveredTab] = useState<string | null>(null);

  // Update activeTab if tabs change and current activeTab doesn't exist
  useEffect(() => {
    if (tabs && tabs.length > 0) {
      const currentTabExists = tabs.find(tab => tab.id === activeTab);
      if (!currentTabExists) {
        console.log('Setting active tab to:', tabs[0].id);
        setActiveTab(tabs[0].id);
      }
    }
  }, [tabs, activeTab]);

  const activeTabData = tabs.find(tab => tab.id === activeTab);

  console.log('CodeTabs render - activeTab:', activeTab, 'tabs:', tabs.map(t => t.id), 'activeTabData:', activeTabData);

  return (
    <div style={{ marginBottom: '16px' }}>
      {title && (
        <h6 style={{ 
          fontSize: '14px', 
          fontWeight: '600', 
          marginBottom: '8px', 
          color: '#111827' 
        }}>
          {title}
        </h6>
      )}

      <div style={{
        display: 'flex',
        borderBottom: '2px solid #e5e7eb',
        marginBottom: '12px'
      }}>
        {tabs.map(tab => (
          <button
            key={tab.id}
            onClick={() => setActiveTab(tab.id)}
            onMouseEnter={() => setHoveredTab(tab.id)}
            onMouseLeave={() => setHoveredTab(null)}
            style={{
              padding: '8px 16px',
              border: 'none',
              background: activeTab === tab.id 
                ? '#3b82f6' 
                : hoveredTab === tab.id 
                  ? '#f3f4f6' 
                  : 'transparent',
              color: activeTab === tab.id ? 'white' : '#6b7280',
              fontSize: '14px',
              fontWeight: '500',
              cursor: 'pointer',
              borderRadius: '4px 4px 0 0',
              transition: 'all 0.2s ease'
            }}
          >
            {tab.label}
          </button>
        ))}
      </div>

      {(activeTabData || tabs[0]) && (
        <CodeBlock
          language={(activeTabData || tabs[0]).language}
          code={(activeTabData || tabs[0]).code}
        />
      )}
    </div>
  );
};
