import React, { useState, useEffect, useRef } from 'react';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';

interface Dungeon {
  id: string;
  name: string;
  level: number;
  description: string;
  floors: number;
  currentFloor: number;
  clearedFloors: Set<number>; // Track which floors have been cleared
  monsters: Monster[];
  rewards: {
    goldRange: [number, number];
    expRange: [number, number];
    itemDropChance: number;
  };
  requiredLevel: number;
  isUnlocked: boolean;
}

interface Monster {
  id: string;
  name: string;
  level: number;
  hp: number;
  maxHp: number;
  attack: number;
  defense: number;
  speed: number;
  exp: number;
  gold: number;
  dropItems: Item[];
  isBoss?: boolean;
}

interface Character {
  id: string;
  name: string;
  level: number;
  exp: number;
  maxExp: number;
  hp: number;
  maxHp: number;
  mp: number;
  maxMp: number;
  attack: number;
  defense: number;
  speed: number;
  gold: number;
  class: string;
  skills: Skill[];
  equipment: Equipment;
  stats: CharacterStats;
  prestige: number;
  rebirth: number;
  buffs: {
    attack: { amount: number; duration: number };
    defense: { amount: number; duration: number };
    speed: { amount: number; duration: number };
  };
}

interface CharacterStats {
  strength: number;
  agility: number;
  intelligence: number;
  vitality: number;
  luck: number;
  availablePoints: number;
}

interface Skill {
  id: string;
  name: string;
  level: number;
  maxLevel: number;
  damage: number;
  manaCost: number;
  cooldown: number;
  currentCooldown: number;
  type: 'active' | 'passive';
  description: string;
}

interface Equipment {
  weapon?: Item;
  armor?: Item;
  helmet?: Item;
  boots?: Item;
  ring?: Item;
  amulet?: Item;
}

interface Item {
  id: string;
  name: string;
  type: 'weapon' | 'armor' | 'helmet' | 'boots' | 'ring' | 'amulet' | 'consumable';
  rarity: 'common' | 'rare' | 'epic' | 'legendary' | 'mythic';
  level: number;
  stats: {
    attack?: number;
    defense?: number;
    hp?: number;
    mp?: number;
    speed?: number;
    critRate?: number;
    critDamage?: number;
  };
  price: number;
  enhancement: number;
  // Î¨ºÏïΩÏö© ÏÜçÏÑ±Îì§
  consumableEffect?: {
    healHp?: number;
    healMp?: number;
    buffDuration?: number; // Î≤ÑÌîÑ ÏßÄÏÜçÏãúÍ∞Ñ (Î∞ÄÎ¶¨Ï¥à)
    buffType?: 'attack' | 'defense' | 'speed'; // Î≤ÑÌîÑ ÌÉÄÏûÖ
    buffAmount?: number; // Î≤ÑÌîÑ ÏàòÏπò
  };
  quantity?: number; // ÏÜåÎπÑ ÏïÑÏù¥ÌÖú ÏàòÎüâ
}



interface Guild {
  id: string;
  name: string;
  level: number;
  members: number;
  maxMembers: number;
  benefits: {
    expBonus: number;
    goldBonus: number;
    dropBonus: number;
  };
}

interface AppProps {
  onAction: (eventType: string, data: any) => void;
  isSimulating: boolean;
  currentAction: string | null;
  userStats: any;
  setUserStats: (stats: any) => void;
  currentUser: any;
}

export const AdvancedIdleRPG: React.FC<AppProps> = ({
  onAction,
  isSimulating,
  currentAction,
  userStats,
  setUserStats,
  currentUser
}) => {
  const { colors } = useTheme();
  const { t } = useLanguage();

  // CSS Ïï†ÎãàÎ©îÏù¥ÏÖò Ï∂îÍ∞Ä
  React.useEffect(() => {
    const style = document.createElement('style');
    style.textContent = `
      @keyframes progress {
        0% {
          width: 0%;
        }
        100% {
          width: 100%;
        }
      }
      @keyframes pulse {
        0%, 100% {
          opacity: 1;
        }
        50% {
          opacity: 0.7;
        }
      }
    `;
    document.head.appendChild(style);

    return () => {
      document.head.removeChild(style);
    };
  }, []);
  const [character, setCharacter] = useState<Character>({
    id: '1',
    name: 'Ïö©ÏÇ¨',
    level: 1,
    exp: 0,
    maxExp: 100,
    hp: 100,
    maxHp: 100,
    mp: 50,
    maxMp: 50,
    attack: 20,
    defense: 10,
    speed: 15,
    gold: 100,
    class: 'Ï†ÑÏÇ¨',
    skills: [],
    equipment: {},
    stats: {
      strength: 10,
      agility: 8,
      intelligence: 5,
      vitality: 12,
      luck: 7,
      availablePoints: 0
    },
    prestige: 0,
    rebirth: 0,
    buffs: {
      attack: { amount: 0, duration: 0 },
      defense: { amount: 0, duration: 0 },
      speed: { amount: 0, duration: 0 }
    }
  });

  const [currentTab, setCurrentTab] = useState<'lobby' | 'dungeon' | 'character' | 'inventory' | 'skills' | 'shop'>('lobby');
  const [gameMode, setGameMode] = useState<'lobby' | 'dungeon' | 'combat'>('lobby');
  const [isAutoFighting, setIsAutoFighting] = useState(false);
  const [currentMonster, setCurrentMonster] = useState<Monster | null>(null);
  const [combatLog, setCombatLog] = useState<string[]>([]);
  const [selectedDungeon, setSelectedDungeon] = useState<Dungeon | null>(null);
  const [inventory, setInventory] = useState<Item[]>([]);
  const [availableDungeons, setAvailableDungeons] = useState<Dungeon[]>([]);
  const [currentDungeon, setCurrentDungeon] = useState<Dungeon | null>(null);
  const [inDungeon, setInDungeon] = useState(false);
  const [guild, setGuild] = useState<Guild | null>(null);
  const [shop, setShop] = useState<Item[]>([]);
  const [achievements, setAchievements] = useState<string[]>([]);
  const [monstersDefeated, setMonstersDefeated] = useState(0);
  const [totalGoldEarned, setTotalGoldEarned] = useState(0);
  const [showDungeonChoice, setShowDungeonChoice] = useState(false);

  const logRef = useRef<HTMLDivElement>(null);

  // Í≤åÏûÑ Ï¥àÍ∏∞Ìôî
  useEffect(() => {
    initializeGame();
  }, []);

  // Ï∫êÎ¶≠ÌÑ∞ ÏÉÅÌÉúÍ∞Ä Î≥ÄÍ≤ΩÎê† ÎïåÎßàÎã§ userStats ÏóÖÎç∞Ïù¥Ìä∏
  useEffect(() => {
    setUserStats({
      level: character.level,
      gold: character.gold,
      attack: character.attack,
      defense: character.defense,
      hp: character.hp,
      maxHp: character.maxHp,
      exp: character.exp,
      maxExp: character.maxExp,
      monsters_defeated: monstersDefeated,
      character_class: character.class,
      total_gold_earned: totalGoldEarned,
      inventory_items: inventory.map(item => ({
        id: item.id,
        name: item.name,
        type: item.type,
        rarity: item.rarity,
        quantity: item.quantity || 1,
        stats: item.stats
      })),
      skills: character.skills.map(skill => ({
        id: skill.id,
        name: skill.name,
        level: skill.level,
        type: skill.type
      })),
      current_buffs: Object.entries(character.buffs)
        .filter(([_, buff]) => buff.duration > 0)
        .map(([type, buff]) => ({
          type,
          amount: buff.amount,
          duration: Math.ceil(buff.duration / 1000)
        }))
    });
  }, [character, inventory, monstersDefeated, totalGoldEarned, setUserStats]);

  const initializeGame = () => {
    // Ïñ∏Ïñ¥Î≥Ñ ÌÖçÏä§Ìä∏ Ï†ïÏùò
    const translations = {
      ko: {
        character: {
          name: 'Ïö©ÏÇ¨',
          class: 'Ï†ÑÏÇ¨'
        },
        skills: {
          slash: { name: 'Í∞ïÎ†•Ìïú Î≤†Í∏∞', description: 'Í∞ïÎ†•Ìïú ÏùºÍ≤©ÏùÑ Í∞ÄÌï©ÎãàÎã§.' },
          heal: { name: 'ÏπòÏú†', description: 'HPÎ•º ÌöåÎ≥µÌï©ÎãàÎã§.' },
          critical: { name: 'ÏπòÎ™ÖÌÉÄ ÏàôÎ†®', description: 'ÏπòÎ™ÖÌÉÄ ÌôïÎ•†ÏùÑ Ï¶ùÍ∞ÄÏãúÌÇµÎãàÎã§.' }
        },
        dungeons: {
          forest: { name: 'Ïñ¥Îë†Ïùò Ïà≤', description: 'Ï¥àÎ≥¥ÏûêÎ•º ÏúÑÌïú ÎçòÏ†ÑÏûÖÎãàÎã§.' },
          cave: { name: 'Í≥†Î∏îÎ¶∞ ÎèôÍµ¥', description: 'Í≥†Î∏îÎ¶∞Îì§Ïù¥ ÏÑúÏãùÌïòÎäî ÏúÑÌóòÌïú ÎèôÍµ¥ÏûÖÎãàÎã§.' },
          tower: { name: 'ÎßàÎ≤ïÏÇ¨Ïùò ÌÉë', description: 'Í∞ïÎ†•Ìïú ÎßàÎ≤ï Î™¨Ïä§ÌÑ∞Îì§Ïù¥ ÏûàÎäî ÌÉëÏûÖÎãàÎã§.' }
        },
        items: {
          sword1: 'Í∞ïÏ≤† Í≤Ä',
          armor1: 'Í∞ÄÏ£Ω Í∞ëÏò∑',
          helmet1: 'Ï≤†Ï†ú Ìà¨Íµ¨',
          health_potion: 'Ï≤¥Î†• Î¨ºÏïΩ',
          mana_potion: 'ÎßàÎÇò Î¨ºÏïΩ',
          greater_health_potion: 'Í≥†Í∏â Ï≤¥Î†• Î¨ºÏïΩ',
          strength_potion: 'ÌûòÏùò Î¨ºÏïΩ',
          defense_potion: 'Î∞©Ïñ¥Ïùò Î¨ºÏïΩ'
        },
        monsters: {
          slime: 'Ïä¨ÎùºÏûÑ',
          goblin: 'Í≥†Î∏îÎ¶∞',
          orc: 'Ïò§ÌÅ¨',
          troll: 'Ìä∏Î°§',
          dragon: 'ÎìúÎûòÍ≥§'
        },
        combat: {
          appeared: 'Ïù¥ ÎÇòÌÉÄÎÇ¨Îã§!',
