    setCharacter(prev => ({ ...prev, hp: newHp }));
    const gameTexts = (window as any).gameTexts || {
      combat: { attack: 'Ïùò Í≥µÍ≤©!', damage: 'Îç∞ÎØ∏ÏßÄ!' }
    };
    addToCombatLog(`${currentMonster.name}${gameTexts.combat.attack} ${damage} ${gameTexts.combat.damage}`);

    if (newHp <= 0) {
