            dungeon_id: currentDungeon.id,
            dungeon_name: currentDungeon.name,
            current_floor: currentDungeon.currentFloor,
            total_floors: currentDungeon.floors,
            character_level: character.level,
            exit_reason: 'death',
            time_spent: new Date().getTime()
          });
          setInDungeon(false);
          setCurrentDungeon(null);
          setCurrentTab('lobby');
          setGameMode('lobby');
          addToCombatLog('ÎçòÏ†ÑÏóêÏÑú ÎÇòÏôîÏäµÎãàÎã§. Ï≤¥Î†•Í≥º ÎßàÎÇòÍ∞Ä ÏôÑÏ†ÑÌûà ÌöåÎ≥µÎêòÏóàÏäµÎãàÎã§!');
        } else {
          // ÏùºÎ∞ò ÌïÑÎìúÏóêÏÑúÎäî ÏÉà Î™¨Ïä§ÌÑ∞ Ïä§Ìè∞
          addToCombatLog('Ï≤¥Î†•Í≥º ÎßàÎÇòÍ∞Ä ÏôÑÏ†ÑÌûà ÌöåÎ≥µÎêòÏóàÏäµÎãàÎã§!');
          spawnMonster();
        }
      }, 2000);
    }
  };

  const calculateDamage = (attack: number, defense: number): number => {
    const baseDamage = Math.max(1, attack - Math.floor(defense * 0.5));
    return Math.floor(baseDamage * (0.8 + Math.random() * 0.4));
  };

  const defeatMonster = () => {
    if (!currentMonster) return;

    const expGained = currentMonster.exp;
    const goldGained = currentMonster.gold;

    setCharacter(prev => {
      const newExp = prev.exp + expGained;
      const newGold = prev.gold + goldGained;
      let newLevel = prev.level;
      let newMaxExp = prev.maxExp;
      let newHp = prev.hp;
      let newMaxHp = prev.maxHp;
      let availablePoints = prev.stats.availablePoints;

      // Î†àÎ≤®ÏóÖ Ï≤¥ÌÅ¨
      if (newExp >= prev.maxExp) {
        newLevel += 1;
        newMaxExp = Math.floor(prev.maxExp * 1.5);
        newMaxHp += 20;
        newHp = newMaxHp; // Î†àÎ≤®ÏóÖ Ïãú Ï≤¥Î†• ÏôÑÏ†Ñ ÌöåÎ≥µ
        availablePoints += 5;

        addToCombatLog(`üéâ Î†àÎ≤®ÏóÖ! Lv.${newLevel}Ïù¥ ÎêòÏóàÏäµÎãàÎã§!`);

        onAction('level_up', {
          user_id: currentUser.user_id,
          new_level: newLevel,
          character_class: prev.class,
          stat_points_gained: 5
        });

        // ÎçòÏ†Ñ Ìï¥Í∏à Ï≤¥ÌÅ¨
        setAvailableDungeons(prevDungeons => 
          prevDungeons.map(dungeon => ({
            ...dungeon,
            isUnlocked: dungeon.isUnlocked || newLevel >= dungeon.requiredLevel
          }))
        );
      }

      return {
        ...prev,
        exp: newExp >= prev.maxExp ? newExp - prev.maxExp : newExp,
        gold: newGold,
        level: newLevel,
        maxExp: newMaxExp,
        hp: newHp,
        maxHp: newMaxHp,
        stats: { ...prev.stats, availablePoints }
      };
    });

    // ÏïÑÏù¥ÌÖú ÎìúÎ°≠ Ï≤òÎ¶¨
    if (currentMonster.dropItems.length > 0) {
      setInventory(prev => [...prev, ...currentMonster.dropItems]);
      addToCombatLog(`‚ú® ${currentMonster.dropItems[0].name}ÏùÑ ÌöçÎìùÌñàÏäµÎãàÎã§!`);
    }

    addToCombatLog(`${currentMonster.name}Î•º Î¨ºÎ¶¨Ï≥§Îã§! EXP +${expGained}, Í≥®Îìú +${goldGained}`);

    // Ï∂îÏ†Å Ïπ¥Ïö¥ÌÑ∞ ÏóÖÎç∞Ïù¥Ìä∏
    setMonstersDefeated(prev => prev + 1);
    setTotalGoldEarned(prev => prev + goldGained);

    onAction('monster_defeated', {
      user_id: currentUser.user_id,
      monster_name: currentMonster.name,
      monster_level: currentMonster.level,
      character_level: character.level,
      exp_gained: expGained,
      gold_gained: goldGained,
      items_dropped: currentMonster.dropItems.length,
      character_attack: character.attack,
      dungeon_name: currentDungeon?.name || null,
      dungeon_floor: currentDungeon?.currentFloor || null
    });

    // ÌòÑÏû¨ Î™¨Ïä§ÌÑ∞ Ï†úÍ±∞
    setCurrentMonster(null);

    // ÎçòÏ†ÑÏóê ÏûàÎäî Í≤ΩÏö∞ ÌòÑÏû¨ Ï∏µÏóêÏÑú ÏÉàÎ°úÏö¥ Î™¨Ïä§ÌÑ∞ ÏÉùÏÑ±, ÏïÑÎãàÎ©¥ ÏùºÎ∞ò Î™¨Ïä§ÌÑ∞ ÏÉùÏÑ±
    if (inDungeon && currentDungeon) {
      // ÌòÑÏû¨ Ï∏µÏùÑ ÌÅ¥Î¶¨Ïñ¥Ìïú Í≤ÉÏúºÎ°ú ÎßàÌÇπ
      const updatedDungeon = {
        ...currentDungeon,
        clearedFloors: new Set([...Array.from(currentDungeon.clearedFloors), currentDungeon.currentFloor])
      };
      setCurrentDungeon(updatedDungeon);

      // ÎçòÏ†Ñ Î¶¨Ïä§Ìä∏ÏóêÏÑúÎèÑ ÏóÖÎç∞Ïù¥Ìä∏
      setAvailableDungeons(prev => prev.map(d => 
        d.id === updatedDungeon.id ? updatedDungeon : d
      ));

      // ÎçòÏ†ÑÏóêÏÑúÎäî ÌòÑÏû¨ Ï∏µÏóêÏÑú Í≥ÑÏÜç ÏÇ¨ÎÉ•Ìï† Ïàò ÏûàÎèÑÎ°ù ÏÉà Î™¨Ïä§ÌÑ∞ ÏÉùÏÑ±
      addToCombatLog(`Î™¨Ïä§ÌÑ∞Î•º Ï≤òÏπòÌñàÏäµÎãàÎã§! ÌòÑÏû¨ ${currentDungeon.currentFloor}Ï∏µÏù¥ ÌÅ¥Î¶¨Ïñ¥ÎêòÏóàÏäµÎãàÎã§. Í≥ÑÏÜç ÏÇ¨ÎÉ•Ìï† Ïàò ÏûàÏäµÎãàÎã§.`);
      setTimeout(() => {
        spawnDungeonMonster(updatedDungeon, true);
      }, 2000);
    } else {
      // ÏùºÎ∞ò Î∞©ÏπòÌòï Ï†ÑÌà¨ÏóêÏÑú ÏÉà Î™¨Ïä§ÌÑ∞ ÏÉùÏÑ± (ÎçòÏ†Ñ ÏÉÅÌÉúÍ∞Ä ÏïÑÎãê ÎïåÎßå)
      if (!inDungeon) {
        setTimeout(() => {
          spawnMonster();
        }, 2000);
      }
    }
  };

  // Îã§Ïùå Ï∏µÏúºÎ°ú ÏßÑÌñâ
  const proceedToNextFloor = () => {
    if (!currentDungeon) return;

    const updatedDungeon = { ...currentDungeon, currentFloor: currentDungeon.currentFloor + 1 };
    setCurrentDungeon(updatedDungeon);
    setShowDungeonChoice(false);
    setTimeout(() => {
      spawnDungeonMonster(updatedDungeon, true); // Í∞ïÏ†ú Ïä§Ìè∞
      addToCombatLog(`${updatedDungeon.currentFloor}Ï∏µÏúºÎ°ú ÏßÑÌñâÌï©ÎãàÎã§!`);
    }, 500);
  };

  // ÎçòÏ†Ñ ÎÇòÍ∞ÄÍ∏∞
  const exitDungeonAfterVictory = () => {
    if (!currentDungeon) return;

    // ÎçòÏ†Ñ ÎÇòÍ∞ÄÍ∏∞ Ïù¥Î≤§Ìä∏ Î°úÍπÖ
    onAction('dungeon_exited', {
      dungeon_id: currentDungeon.id,
      dungeon_name: currentDungeon.name,
      current_floor: currentDungeon.currentFloor,
      total_floors: currentDungeon.floors,
      character_level: character.level,
      exit_reason: 'victory_choice',
      time_spent: new Date().getTime()
    });

    // ÎçòÏ†Ñ Î¶¨ÏÖã (ÌòÑÏû¨ Ï∏µÏùÑ 1Ï∏µÏúºÎ°ú ÎêòÎèåÎ¶º)
    const resetDungeon = { ...currentDungeon, currentFloor: 1 };
    setAvailableDungeons(prev => prev.map(d => 
      d.id === resetDungeon.id ? resetDungeon : d
    ));

    setInDungeon(false);
    setCurrentDungeon(null);
    setCurrentTab('lobby');
    setGameMode('lobby');
    setShowDungeonChoice(false);

    // Ï≤¥Î†•Í≥º ÎßàÎÇò ÏôÑÏ†Ñ ÌöåÎ≥µ
    setCharacter(prev => ({ ...prev, hp: prev.maxHp, mp: prev.maxMp }));
    addToCombatLog('ÎçòÏ†ÑÏóêÏÑú ÎÇòÏôîÏäµÎãàÎã§. Ï≤¥Î†•Í≥º ÎßàÎÇòÍ∞Ä ÏôÑÏ†ÑÌûà ÌöåÎ≥µÎêòÏóàÏäµÎãàÎã§!');

    // ÏÉàÎ°úÏö¥ ÏùºÎ∞ò Î™¨Ïä§ÌÑ∞ ÏÉùÏÑ±
    setTimeout(() => {
      spawnMonster();
    }, 1000);
  };


  // ÏûêÎèô Ï†ÑÌà¨
  useEffect(() => {
    if (isAutoFighting && currentMonster && currentMonster.hp > 0 && character.hp > 0) {
      const autoAttackTimer = setTimeout(() => {
        attack();
      }, 2000);

      return () => clearTimeout(autoAttackTimer);
    }
  }, [isAutoFighting, currentMonster, character.hp]);

  // Ïä§ÌÇ¨ Ïø®Îã§Ïö¥ Î∞è MP ÌöåÎ≥µ
  useEffect(() => {
    const interval = setInterval(() => {
      setCharacter(prev => ({
        ...prev,
        mp: Math.min(prev.maxMp, prev.mp + 1),
        skills: prev.skills.map(skill => ({
          ...skill,
          currentCooldown: Math.max(0, skill.currentCooldown - 100)
        })),
        buffs: {
          attack: {
            amount: prev.buffs.attack.duration > 0 ? prev.buffs.attack.amount : 0,
            duration: Math.max(0, prev.buffs.attack.duration - 100)
          },
          defense: {
            amount: prev.buffs.defense.duration > 0 ? prev.buffs.defense.amount : 0,
            duration: Math.max(0, prev.buffs.defense.duration - 100)
          },
          speed: {
            amount: prev.buffs.speed.duration > 0 ? prev.buffs.speed.amount : 0,
            duration: Math.max(0, prev.buffs.speed.duration - 100)
          }
        }
      }));
    }, 100);

    return () => clearInterval(interval);
  }, []);

  const addToCombatLog = (message: string) => {
    setCombatLog(prev => {
      const newLog = [...prev, message];
      if (newLog.length > 50) newLog.shift();
      return newLog;
    });

    // Auto-scroll to bottom immediately and with backup timing
    if (logRef.current) {
      logRef.current.scrollTop = logRef.current.scrollHeight;
    }

    // Additional scroll with slight delay to ensure content is rendered
    setTimeout(() => {
      if (logRef.current) {
        logRef.current.scrollTop = logRef.current.scrollHeight;
      }
    }, 10);

    // Final backup scroll
    setTimeout(() => {
      if (logRef.current) {
        logRef.current.scrollTop = logRef.current.scrollHeight;
      }
    }, 100);
  };

  // Ïä§ÌÉØ Î∂ÑÎ∞∞
  const allocateStatPoint = (stat: keyof Omit<CharacterStats, 'availablePoints'>) => {
    if (character.stats.availablePoints <= 0) return;

    setCharacter(prev => ({
      ...prev,
      stats: {
        ...prev.stats,
        [stat]: prev.stats[stat] + 1,
        availablePoints: prev.stats.availablePoints - 1
      },
      // Ïä§ÌÉØÏóê Îî∞Î•∏ Îä•Î†•Ïπò ÏóÖÎç∞Ïù¥Ìä∏
      attack: stat === 'strength' ? prev.attack + 2 : prev.attack,
      defense: stat === 'vitality' ? prev.defense + 1 : prev.defense,
      maxHp: stat === 'vitality' ? prev.maxHp + 10 : prev.maxHp,
      maxMp: stat === 'intelligence' ? prev.maxMp + 5 : prev.maxMp,
      speed: stat === 'agility' ? prev.speed + 1 : prev.speed
    }));
  };

  // ÏïÑÏù¥ÌÖú Ïû•Ï∞©
  const equipItem = (item: Item) => {
    // ÏÜåÎπÑ ÏïÑÏù¥ÌÖúÏùÄ Ïû•Ï∞©Ìï† Ïàò ÏóÜÏùå
    if (item.type === 'consumable') {
      return;
    }

    setCharacter(prev => {
      const newEquipment = { ...prev.equipment };
      newEquipment[item.type as keyof typeof newEquipment] = item;

      // Ïû•Ï∞©Ìïú ÏïÑÏù¥ÌÖúÏùò Ïä§ÌÉØ Ï†ÅÏö©
      let newAttack = prev.attack;
      let newDefense = prev.defense;
      let newMaxHp = prev.maxHp;
      let newMaxMp = prev.maxMp;
      let newSpeed = prev.speed;

      if (item.stats.attack) newAttack += item.stats.attack;
      if (item.stats.defense) newDefense += item.stats.defense;
      if (item.stats.hp) newMaxHp += item.stats.hp;
      if (item.stats.mp) newMaxMp += item.stats.mp;
      if (item.stats.speed) newSpeed += item.stats.speed;

      return {
        ...prev,
        equipment: newEquipment,
        attack: newAttack,
        defense: newDefense,
        maxHp: newMaxHp,
        maxMp: newMaxMp,
        speed: newSpeed
      };
    });

    // Ïù∏Î≤§ÌÜ†Î¶¨ÏóêÏÑú ÏïÑÏù¥ÌÖú Ï†úÍ±∞
    setInventory(prev => prev.filter(invItem => invItem.id !== item.id));
    addToCombatLog(`${item.name}ÏùÑ Ïû•Ï∞©ÌñàÏäµÎãàÎã§!`);
  };

  // Ïû•ÎπÑ Ìï¥Ï†ú
  const unequipItem = (slot: keyof Equipment) => {
    const equippedItem = character.equipment[slot];
    if (!equippedItem) return;

    setCharacter(prev => {
      const newEquipment = { ...prev.equipment };
      newEquipment[slot] = undefined;

      // Ïû•Ï∞© Ìï¥Ï†úÌïú ÏïÑÏù¥ÌÖúÏùò Ïä§ÌÉØ Ï†úÍ±∞
      let newAttack = prev.attack;
      let newDefense = prev.defense;
      let newMaxHp = prev.maxHp;
      let newMaxMp = prev.maxMp;
      let newSpeed = prev.speed;

      if (equippedItem.stats.attack) newAttack -= equippedItem.stats.attack;
      if (equippedItem.stats.defense) newDefense -= equippedItem.stats.defense;
      if (equippedItem.stats.hp) newMaxHp -= equippedItem.stats.hp;
      if (equippedItem.stats.mp) newMaxMp -= equippedItem.stats.mp;
      if (equippedItem.stats.speed) newSpeed -= equippedItem.stats.speed;

      // HPÍ∞Ä maxHpÎ•º Ï¥àÍ≥ºÌïòÏßÄ ÏïäÎèÑÎ°ù Ï°∞Ï†ï
      const adjustedHp = Math.min(prev.hp, newMaxHp);
      const adjustedMp = Math.min(prev.mp, newMaxMp);

      return {
        ...prev,
        equipment: newEquipment,
        attack: Math.max(1, newAttack), // ÏµúÏÜå Í≥µÍ≤©Î†• 1
        defense: Math.max(0, newDefense),
        maxHp: Math.max(100, newMaxHp), // ÏµúÏÜå Ï≤¥Î†• 100
        maxMp: Math.max(50, newMaxMp), // ÏµúÏÜå ÎßàÎÇò 50
        speed: Math.max(1, newSpeed), // ÏµúÏÜå ÏÜçÎèÑ 1
        hp: adjustedHp,
        mp: adjustedMp
      };
    });

    // Ïù∏Î≤§ÌÜ†Î¶¨Ïóê ÏïÑÏù¥ÌÖú Ï∂îÍ∞Ä
    setInventory(prev => [...prev, equippedItem]);
    addToCombatLog(`${equippedItem.name}ÏùÑ Ìï¥Ï†úÌñàÏäµÎãàÎã§!`);
  };


  // ÏïÑÏù¥ÌÖú ÌåêÎß§
  const sellItem = (item: Item) => {
    const sellPrice = Math.floor(item.price * 0.7);
    setCharacter(prev => ({ ...prev, gold: prev.gold + sellPrice }));
    setInventory(prev => prev.filter(invItem => invItem.id !== item.id));
    addToCombatLog(`${item.name}ÏùÑ ${sellPrice} Í≥®ÎìúÏóê ÌåêÎß§ÌñàÏäµÎãàÎã§!`);
  };

  // ÏïÑÏù¥ÌÖú Íµ¨Îß§
  const buyItem = (item: Item) => {
    if (character.gold < item.price) return;

    setCharacter(prev => ({ ...prev, gold: prev.gold - item.price }));
    setInventory(prev => [...prev, { ...item, id: `${item.id}_${Date.now()}` }]);
    addToCombatLog(`${item.name}ÏùÑ Íµ¨Îß§ÌñàÏäµÎãàÎã§!`);
  };

  const getRarityColor = (rarity: string) => {
    const colors = {
      common: '#9ca3af',
      rare: '#3b82f6',
      epic: '#8b5cf6',
      legendary: '#f59e0b',
      mythic: '#ef4444'
    };
    return colors[rarity as keyof typeof colors] || '#9ca3af';
  };

  // ÎçòÏ†Ñ ÏûÖÏû• Ìï®Ïàò
  const enterDungeon = (dungeon: Dungeon) => {
    if (character.level < dungeon.requiredLevel) {
      addToCombatLog(`Î†àÎ≤® ${dungeon.requiredLevel} Ïù¥ÏÉÅÏù¥Ïñ¥Ïïº ÏûÖÏû• Í∞ÄÎä•Ìï©ÎãàÎã§.`);
      return;
    }
    setCurrentDungeon(dungeon);
    setInDungeon(true);
    setGameMode('dungeon');
    setCurrentTab('dungeon');
    spawnDungeonMonster(dungeon);

    // Ïù¥Î≤§Ìä∏ Î°úÍπÖ
    onAction('dungeon_entered', {
      dungeon_id: dungeon.id,
      dungeon_name: dungeon.name,
      character_level: character.level,
      floor: dungeon.currentFloor,
      character_attack: character.attack,
      character_defense: character.defense,
      character_hp: character.hp,
      character_mp: character.mp,
      entry_time: new Date().getTime()
    });
  };

  // ÎçòÏ†Ñ Î™¨Ïä§ÌÑ∞ Ïä§Ìè∞
  const spawnDungeonMonster = (dungeon: Dungeon, forceSpawn = false) => {
    // Ïù¥ÎØ∏ Î™¨Ïä§ÌÑ∞Í∞Ä ÏûàÏúºÎ©¥ ÏÉàÎ°ú ÏÉùÏÑ±ÌïòÏßÄ ÏïäÏùå (Í∞ïÏ†ú ÏÉùÏÑ±Ïù¥ ÏïÑÎãå Í≤ΩÏö∞)
    if (currentMonster && !forceSpawn) return;

    // Í∞ïÏ†ú ÏÉùÏÑ±Ïù∏ Í≤ΩÏö∞ Í∏∞Ï°¥ Î™¨Ïä§ÌÑ∞Î•º Î®ºÏ†Ä Ï†úÍ±∞
    if (forceSpawn && currentMonster) {
      setCurrentMonster(null);
    }
    // ÎçòÏ†ÑÎ≥Ñ Í≥†Ïú† Î™¨Ïä§ÌÑ∞ ÌÉÄÏûÖ Ï†ïÏùò
    const getDungeonMonsters = (dungeonId: string) => {
      switch (dungeonId) {
        case 'forest': // Ïñ¥Îë†Ïùò Ïà≤
          return [
            { name: 'ÎäëÎåÄ', baseHp: 40, baseAttack: 10, baseDefense: 3 },
            { name: 'Í≥∞', baseHp: 80, baseAttack: 16, baseDefense: 6 },
            { name: 'Í±∞ÎåÄ Í±∞ÎØ∏', baseHp: 120, baseAttack: 22, baseDefense: 10 },
            { name: 'Ïà≤Ïùò ÏàòÌò∏Ïûê', baseHp: 200, baseAttack: 35, baseDefense: 18 },
            { name: 'Í≥†ÎåÄ ÏóîÌä∏', baseHp: 350, baseAttack: 45, baseDefense: 25 }
          ];
        case 'cave': // Í≥†Î∏îÎ¶∞ ÎèôÍµ¥
          return [
            { name: 'Í≥†Î∏îÎ¶∞ Ï†ÑÏÇ¨', baseHp: 60, baseAttack: 14, baseDefense: 5 },
            { name: 'Í≥†Î∏îÎ¶∞ Í∂ÅÏàò', baseHp: 45, baseAttack: 18, baseDefense: 3 },
            { name: 'Í≥†Î∏îÎ¶∞ ÎßàÎ≤ïÏÇ¨', baseHp: 50, baseAttack: 20, baseDefense: 4 },
            { name: 'Í≥†Î∏îÎ¶∞ Ïö∞ÎëêÎ®∏Î¶¨', baseHp: 180, baseAttack: 30, baseDefense: 15 },
            { name: 'Í≥†Î∏îÎ¶∞ Ïôï', baseHp: 400, baseAttack: 50, baseDefense: 28 }
          ];
        case 'tower': // ÎßàÎ≤ïÏÇ¨Ïùò ÌÉë
          return [
            { name: 'ÎßàÎ≤ï Í≥®Î†ò', baseHp: 100, baseAttack: 20, baseDefense: 12 },
            { name: 'Ïä§ÏºàÎ†àÌÜ§ Î©îÏù¥ÏßÄ', baseHp: 70, baseAttack: 25, baseDefense: 8 },
            { name: 'ÌîåÎ†àÏûÑ ÏóòÎ¶¨Î©òÌÉà', baseHp: 110, baseAttack: 28, baseDefense: 10 },
            { name: 'Î¶¨Ïπò', baseHp: 250, baseAttack: 40, baseDefense: 20 },
            { name: 'Í≥†ÎåÄ ÎßàÎ≤ïÏÇ¨', baseHp: 500, baseAttack: 60, baseDefense: 35 }
          ];
        default:
          return [
            { name: 'Ïä¨ÎùºÏûÑ', baseHp: 30, baseAttack: 8, baseDefense: 2 },
            { name: 'Í≥†Î∏îÎ¶∞', baseHp: 50, baseAttack: 12, baseDefense: 4 },
            { name: 'Ïò§ÌÅ¨', baseHp: 100, baseAttack: 18, baseDefense: 8 },
            { name: 'Ìä∏Î°§', baseHp: 150, baseAttack: 25, baseDefense: 12 },
            { name: 'ÎìúÎûòÍ≥§', baseHp: 300, baseAttack: 40, baseDefense: 20 }
          ];
      }
    };

    const baseMonsters = getDungeonMonsters(dungeon.id);

    // Ï∏µÏàòÏóê Îî∞Î•∏ Ï†êÏßÑÏ†Å Î™¨Ïä§ÌÑ∞ ÏÑ†ÌÉù Î°úÏßÅ
    const floorBasedMonsterIndex = () => {
      const currentFloor = dungeon.currentFloor;
      if (currentFloor <= 3) return 0; // Ï≤´ Î≤àÏß∏ Î™¨Ïä§ÌÑ∞ (1~3Ï∏µ)
      if (currentFloor <= 7) return 1; // Îëê Î≤àÏß∏ Î™¨Ïä§ÌÑ∞ (4~7Ï∏µ)  
      if (currentFloor <= 12) return 2; // ÏÑ∏ Î≤àÏß∏ Î™¨Ïä§ÌÑ∞ (8~12Ï∏µ)
      if (currentFloor <= 18) return 3; // ÎÑ§ Î≤àÏß∏ Î™¨Ïä§ÌÑ∞ (13~18Ï∏µ)
      return 4; // Îã§ÏÑØ Î≤àÏß∏ Î™¨Ïä§ÌÑ∞ (19Ï∏µ Ïù¥ÏÉÅ)
    };

    // Ï∏µÏàò Í∏∞Î∞ò Í∏∞Î≥∏ Î™¨Ïä§ÌÑ∞ + ÏïΩÍ∞ÑÏùò ÎûúÎç§ÏÑ± Ï∂îÍ∞Ä
    const baseIndex = floorBasedMonsterIndex();
    const randomVariation = Math.random() < 0.3 && baseIndex > 0 ? -1 : 0; // 30% ÌôïÎ•†Î°ú Ìïú Îã®Í≥Ñ ÎÇÆÏùÄ Î™¨Ïä§ÌÑ∞
    const selectedIndex = Math.max(0, baseIndex + randomVariation);

    const selectedMonster = baseMonsters[selectedIndex];
    const levelMultiplier = 1 + (dungeon.level + dungeon.currentFloor - 1) * 0.2;

    const monster: Monster = {
      id: `dungeon_${dungeon.id}_${Date.now()}`,
      name: selectedMonster.name,
      level: dungeon.level + dungeon.currentFloor,
      hp: Math.floor(selectedMonster.baseHp * levelMultiplier),
      maxHp: Math.floor(selectedMonster.baseHp * levelMultiplier),
      attack: Math.floor(selectedMonster.baseAttack * levelMultiplier),
      defense: Math.floor(selectedMonster.baseDefense * levelMultiplier),
      speed: 10,
      exp: Math.floor((20 + dungeon.level * 8 + dungeon.currentFloor * 3) * levelMultiplier),
      gold: Math.floor((10 + dungeon.level * 3) * levelMultiplier),
      dropItems: generateDropItems(),
      isBoss: dungeon.currentFloor % 5 === 0
    };

    if (monster.isBoss) {
      monster.name = `Î≥¥Ïä§ ${monster.name}`;
      monster.hp *= 2;
      monster.maxHp *= 2;
      monster.attack *= 1.5;
      monster.exp *= 3;
      monster.gold *= 2;
    }

    setCurrentMonster(monster);
    addToCombatLog(`${monster.name} Lv.${monster.level}Ïù¥ ÎÇòÌÉÄÎÇ¨Îã§!`);
  };

  // Î¨ºÏïΩ ÏÇ¨Ïö© Ìï®Ïàò
  const consumeItem = (item: Item) => {
    if (item.type !== 'consumable' || !item.consumableEffect) return;

    const effect = item.consumableEffect;

    // Ï≤¥Î†• ÌöåÎ≥µ
    if (effect.healHp) {
      setCharacter(prev => ({
        ...prev,
        hp: Math.min(prev.maxHp, prev.hp + effect.healHp!)
      }));
      addToCombatLog(`${item.name}ÏùÑ ÏÇ¨Ïö©ÌïòÏó¨ HP ${effect.healHp} ÌöåÎ≥µ!`);
    }

    // ÎßàÎÇò ÌöåÎ≥µ
    if (effect.healMp) {
      setCharacter(prev => ({
        ...prev,
        mp: Math.min(prev.maxMp, prev.mp + effect.healMp!)
      }));
      addToCombatLog(`${item.name}ÏùÑ ÏÇ¨Ïö©ÌïòÏó¨ MP ${effect.healMp} ÌöåÎ≥µ!`);
    }

    // Î≤ÑÌîÑ Ìö®Í≥º
    if (effect.buffType && effect.buffAmount && effect.buffDuration) {
      setCharacter(prev => ({
        ...prev,
        buffs: {
          ...prev.buffs,
          [effect.buffType!]: {
            amount: effect.buffAmount!,
            duration: effect.buffDuration!
          }
        }
      }));
      addToCombatLog(`${item.name}ÏùÑ ÏÇ¨Ïö©ÌïòÏó¨ ${effect.buffType === 'attack' ? 'Í≥µÍ≤©Î†•' : effect.buffType === 'defense' ? 'Î∞©Ïñ¥Î†•' : 'ÏÜçÎèÑ'} ${effect.buffAmount} Ï¶ùÍ∞Ä! (${effect.buffDuration/1000}Ï¥à)`);
    }

    // Ïù∏Î≤§ÌÜ†Î¶¨ÏóêÏÑú Î¨ºÏïΩ Ï†úÍ±∞ ÎòêÎäî ÏàòÎüâ Í∞êÏÜå
    setInventory(prev => {
      const itemIndex = prev.findIndex(invItem => invItem.id === item.id);
      if (itemIndex !== -1) {
        const newInventory = [...prev];
        const currentItem = newInventory[itemIndex];
        if (currentItem.quantity && currentItem.quantity > 1) {
          newInventory[itemIndex] = { ...currentItem, quantity: currentItem.quantity - 1 };
        } else {
          newInventory.splice(itemIndex, 1);
        }
        return newInventory;
      }
      return prev;
    });

    // Ïù¥Î≤§Ìä∏ Î°úÍπÖ
    onAction('consumable_used', {
      item_name: item.name,
      item_type: item.type,
      character_level: character.level,
      heal_hp: effect.healHp || 0,
      heal_mp: effect.healMp || 0,
      buff_type: effect.buffType || 'none',
      buff_amount: effect.buffAmount || 0,
      character_hp_before: character.hp,
      character_mp_before: character.mp,
      dungeon_name: currentDungeon?.name || null
    });
  };

  // Ïä§ÌÇ¨ ÏÇ¨Ïö© Ìï®Ïàò
  const castSkill = (skill: Skill) => {
    if (skill.currentCooldown > 0 || character.mp < skill.manaCost) return;

    // Ïä§ÌÇ¨ ÏÇ¨Ïö© Î°úÏßÅ
    if (skill.id === 'heal') {
      const healAmount = 30 + skill.level * 5;
      setCharacter(prev => ({
        ...prev,
        hp: Math.min(prev.maxHp, prev.hp + healAmount),
        mp: prev.mp - skill.manaCost
      }));
      addToCombatLog(`${healAmount}ÎßåÌÅº ÌöåÎ≥µÌñàÎã§!`);
    } else {
      // Í≥µÍ≤© Ïä§ÌÇ¨
      if (currentMonster) {
        const damage = calculateDamage(skill.damage + skill.level * 3, currentMonster.defense);
        const newMonsterHp = Math.max(0, currentMonster.hp - damage);
        setCurrentMonster(prev => prev ? {...prev, hp: newMonsterHp} : null);
        setCharacter(prev => ({ ...prev, mp: prev.mp - skill.manaCost }));
        addToCombatLog(`${skill.name}Î°ú ${damage} Îç∞ÎØ∏ÏßÄ!`);

        // Î™¨Ïä§ÌÑ∞Í∞Ä Ï£ΩÏßÄ ÏïäÏïòÏúºÎ©¥ Î∞òÍ≤©
        if (newMonsterHp > 0) {
          setTimeout(() => {
            monsterAttack();
          }, 1000);
        } else {
          // Î™¨Ïä§ÌÑ∞Í∞Ä Ï£ΩÏóàÏúºÎ©¥ Ï≤òÏπò Ï≤òÎ¶¨
          setTimeout(() => {
            defeatMonster();
          }, 1500);
        }
      }
    }

    // Ïø®Îã§Ïö¥ Ï†ÅÏö©
    setCharacter(prev => ({
      ...prev,
      skills: prev.skills.map(s => 
        s.id === skill.id ? {...s, currentCooldown: s.cooldown} : s
      )
    }));

    // Ïù¥Î≤§Ìä∏ Î°úÍπÖ
    onAction('skill_used', {
      skill_name: skill.name,
      skill_level: skill.level,
      mana_cost: skill.manaCost,
      character_level: character.level,
      character_mp_before: character.mp,
      character_mp_after: character.mp - skill.manaCost,
      dungeon_name: currentDungeon?.name || null
    });
  };

  // Ïä§ÌÉØ Ìè¨Ïù∏Ìä∏ Ìï†Îãπ
  const allocateStat = (statName: keyof CharacterStats) => {
    if (character.stats.availablePoints <= 0) return;

    setCharacter(prev => ({
      ...prev,
      stats: {
        ...prev.stats,
        [statName]: prev.stats[statName] + 1,
        availablePoints: prev.stats.availablePoints - 1
      }
    }));

    // Ïä§ÌÉØÏóê Îî∞Î•∏ Îä•Î†•Ïπò ÏóÖÎç∞Ïù¥Ìä∏
    let hpBonus = 0, mpBonus = 0, attackBonus = 0, defenseBonus = 0;
    if (statName === 'vitality') hpBonus = 5;
    if (statName === 'intelligence') mpBonus = 3;
    if (statName === 'strength') attackBonus = 2;
    if (statName === 'agility') { attackBonus = 1; defenseBonus = 1; }

    setCharacter(prev => ({
      ...prev,
      maxHp: prev.maxHp + hpBonus,
      hp: prev.hp + hpBonus,
      maxMp: prev.maxMp + mpBonus,
      mp: prev.mp + mpBonus,
      attack: prev.attack + attackBonus,
      defense: prev.defense + defenseBonus
    }));

    // Ïù¥Î≤§Ìä∏ Î°úÍπÖ
    onAction('stat_allocated', {
      stat_name: statName,
      stat_value: character.stats[statName] + 1,
      character_level: character.level,
      available_points: character.stats.availablePoints - 1
    });
  };

