import React, { useState, useEffect, useRef } from 'react';

interface AppProps {
  onAction: (eventType: string, data: any) => void;
  isSimulating: boolean;
  currentAction: string | null;
  playerStats: any;
  setPlayerStats: (stats: any) => void;
  currentUser: any;
}

interface User {
  id: string;
  name: string;
  avatar: string;
  level: number;
  points: number;
  streak: number;
  badges: string[];
  joinDate: string;
}

interface Notification {
  id: string;
  title: string;
  message: string;
  type: 'success' | 'info' | 'warning' | 'error';
  timestamp: number;
  read: boolean;
}

interface Restaurant {
  id: string;
  name: string;
  category: string;
  image: string;
  rating: number;
  deliveryTime: string;
  deliveryFee: number;
  minOrder: number;
  isOpen: boolean;
  tags: string[];
  distance: string;
  discount?: string;
}

interface CartItem {
  id: string;
